'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { useRouter } from 'next/navigation';
import BaseLayout from '@/components/layouts/BaseLayout';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import {
  Globe,
  Plus,
  Shield,
  CheckCircle,
  AlertCircle,
  Loader2,
  Copy,
  ExternalLink,
  Trash,
  Info,
  RefreshCw,
  // Settings,
  Link as LinkIcon,
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useAuthStore } from '@/lib/store/auth-store';
import { usePortfolioStore } from '@/lib/store/portfolio-store';
import { DomainService } from '@/lib/services/domain-service';
import { logger } from '@/lib/utils/logger';
import { AddDomainModal } from './AddDomainModal';
import { DomainSetupInstructions } from './DomainSetupInstructions';
import type { CustomDomain } from '@/types/domains';

export function DomainSettingsContent() {
  const _router = useRouter();
  const { toast } = useToast();
  const { user } = useAuthStore();
  const { portfolios } = usePortfolioStore();

  const [domains, setDomains] = useState<CustomDomain[]>([]);
  const [loading, setLoading] = useState(true);
  const [_selectedDomain, _setSelectedDomain] = useState<CustomDomain | null>(
    null
  );
  const [showAddModal, setShowAddModal] = useState(false);
  const [verifying, setVerifying] = useState<string | null>(null);
  const [activating, setActivating] = useState<string | null>(null);

  const loadDomains = useCallback(async () => {
    try {
      setLoading(true);
      if (!user) throw new Error('User not authenticated');
      const userDomains = await DomainService.getUserDomains(user.id);
      setDomains(userDomains);
    } catch (error) {
      logger.error('Failed to load domains', error as Error);
      toast({
        title: 'Error',
        description: 'Failed to load your domains. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }, [user, toast]);

  useEffect(() => {
    if (user) {
      loadDomains();
    }
  }, [user, loadDomains]);

  const handleAddDomain = async (portfolioId: string, domain: string) => {
    try {
      if (!user) throw new Error('User not authenticated');
      const newDomain = await DomainService.addCustomDomain(
        user.id,
        portfolioId,
        domain
      );

      setDomains([...domains, newDomain]);
      setShowAddModal(false);
      _setSelectedDomain(newDomain);

      toast({
        title: 'Domain Added',
        description:
          'Your domain has been added. Please follow the setup instructions to verify ownership.',
      });
    } catch (error: unknown) {
      toast({
        title: 'Error',
        description:
          error instanceof Error
            ? error.message
            : 'Failed to add domain. Please try again.',
        variant: 'destructive',
      });
    }
  };

  const handleVerifyDomain = async (domain: CustomDomain) => {
    setVerifying(domain.id);
    try {
      const result = await DomainService.verifyDomain(domain.id);

      if (result.verified) {
        // Update domain in state
        setDomains(
          domains.map(d =>
            d.id === domain.id
              ? { ...d, verificationStatus: 'verified', dnsConfigured: true }
              : d
          )
        );

        toast({
          title: 'Domain Verified',
          description: 'Your domain ownership has been verified successfully!',
        });
      } else {
        toast({
          title: 'Verification Failed',
          description:
            "DNS records not found. Please ensure you've added the required records and wait for DNS propagation.",
          variant: 'destructive',
        });
      }
    } catch (_error) {
      logger.error('Failed to verify domain', _error as Error);
      toast({
        title: 'Error',
        description: 'Failed to verify domain. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setVerifying(null);
    }
  };

  const handleActivateDomain = async (domain: CustomDomain) => {
    setActivating(domain.id);
    try {
      const activatedDomain = await DomainService.activateDomain(domain.id);

      // Update domain in state
      setDomains(domains.map(d => (d.id === domain.id ? activatedDomain : d)));

      toast({
        title: 'Domain Activated',
        description: 'Your domain is now active and serving your portfolio!',
      });
    } catch (error: unknown) {
      toast({
        title: 'Error',
        description:
          error instanceof Error
            ? error.message
            : 'Failed to activate domain. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setActivating(null);
    }
  };

  const handleSetPrimary = async (domain: CustomDomain) => {
    try {
      await DomainService.setPrimaryDomain(domain.id, domain.portfolioId);

      // Update domains in state
      setDomains(
        domains.map(d => ({
          ...d,
          isPrimary:
            d.id === domain.id
              ? true
              : d.portfolioId === domain.portfolioId
                ? false
                : d.isPrimary,
        }))
      );

      toast({
        title: 'Primary Domain Set',
        description: `${domain.domain} is now your primary domain.`,
      });
    } catch (_error) {
      logger.error('Failed to set primary domain', _error as Error);
      toast({
        title: 'Error',
        description: 'Failed to set primary domain. Please try again.',
        variant: 'destructive',
      });
    }
  };

  const handleRemoveDomain = async (domain: CustomDomain) => {
    // eslint-disable-next-line no-alert
    if (
      !window.confirm(
        `Are you sure you want to remove ${domain.domain}? This action cannot be undone.`
      )
    ) {
      return;
    }

    try {
      await DomainService.removeDomain(domain.id);
      setDomains(domains.filter(d => d.id !== domain.id));

      toast({
        title: 'Domain Removed',
        description: 'Your domain has been removed successfully.',
      });
    } catch (_error) {
      logger.error('Failed to remove domain', _error as Error);
      toast({
        title: 'Error',
        description: 'Failed to remove domain. Please try again.',
        variant: 'destructive',
      });
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: 'Copied',
      description: 'Value copied to clipboard.',
    });
  };

  const getStatusBadge = (domain: CustomDomain) => {
    if (domain.status === 'active') {
      return (
        <Badge variant="default">
          <CheckCircle className="w-3 h-3 mr-1" />
          Active
        </Badge>
      );
    }
    if (domain.verificationStatus === 'verified') {
      return (
        <Badge variant="secondary">
          <Shield className="w-3 h-3 mr-1" />
          Verified
        </Badge>
      );
    }
    if (domain.verificationStatus === 'failed') {
      return (
        <Badge variant="destructive">
          <AlertCircle className="w-3 h-3 mr-1" />
          Failed
        </Badge>
      );
    }
    return (
      <Badge variant="outline">
        <Info className="w-3 h-3 mr-1" />
        Pending
      </Badge>
    );
  };

  const getSSLBadge = (domain: CustomDomain) => {
    if (domain.sslStatus === 'active') {
      return (
        <Badge variant="default">
          <Shield className="w-3 h-3 mr-1" />
          SSL Active
        </Badge>
      );
    }
    if (domain.sslStatus === 'provisioning') {
      return (
        <Badge variant="secondary">
          <Loader2 className="w-3 h-3 mr-1 animate-spin" />
          SSL Provisioning
        </Badge>
      );
    }
    return null;
  };

  if (loading) {
    return (
      <BaseLayout>
        <div className="min-h-screen flex items-center justify-center">
          <Loader2 className="w-8 h-8 animate-spin" />
        </div>
      </BaseLayout>
    );
  }

  return (
    <BaseLayout>
      <div className="min-h-screen bg-gradient-to-b from-background to-muted/20 pt-24">
        <div className="max-w-4xl mx-auto px-4 py-8">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold mb-2">Custom Domains</h1>
            <p className="text-muted-foreground">
              Connect your own domain to make your portfolio truly yours
            </p>
          </div>

          {/* Add Domain Button */}
          <div className="mb-6">
            <Button onClick={() => setShowAddModal(true)} size="lg">
              <Plus className="w-5 h-5 mr-2" />
              Add Custom Domain
            </Button>
          </div>

          {/* Domain List */}
          {domains.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <Globe className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-semibold mb-2">
                  No custom domains yet
                </h3>
                <p className="text-muted-foreground mb-6 max-w-md mx-auto">
                  Add a custom domain to give your portfolio a professional web
                  address like yourname.com
                </p>
                <Button onClick={() => setShowAddModal(true)}>
                  <Plus className="w-4 h-4 mr-2" />
                  Add Your First Domain
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="space-y-4">
              {domains.map(domain => (
                <Card key={domain.id} className="overflow-hidden">
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div className="space-y-1">
                        <div className="flex items-center gap-3">
                          <CardTitle className="text-xl">
                            {domain.domain}
                          </CardTitle>
                          {getStatusBadge(domain)}
                          {getSSLBadge(domain)}
                          {domain.isPrimary && (
                            <Badge variant="outline">
                              <LinkIcon className="w-3 h-3 mr-1" />
                              Primary
                            </Badge>
                          )}
                        </div>
                        <CardDescription>
                          Connected to:{' '}
                          {portfolios.find(p => p.id === domain.portfolioId)
                            ?.name || 'Unknown Portfolio'}
                        </CardDescription>
                      </div>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => handleRemoveDomain(domain)}
                      >
                        <Trash className="w-4 h-4" />
                      </Button>
                    </div>
                  </CardHeader>

                  <CardContent>
                    <Tabs defaultValue="status" className="w-full">
                      <TabsList className="grid w-full grid-cols-3">
                        <TabsTrigger value="status">Status</TabsTrigger>
                        <TabsTrigger value="setup">Setup</TabsTrigger>
                        <TabsTrigger value="settings">Settings</TabsTrigger>
                      </TabsList>

                      <TabsContent value="status" className="space-y-4">
                        {/* Verification Status */}
                        {domain.verificationStatus !== 'verified' && (
                          <Alert>
                            <AlertCircle className="h-4 w-4" />
                            <AlertDescription>
                              <div className="flex items-center justify-between">
                                <span>
                                  Domain verification pending. Add the required
                                  DNS records.
                                </span>
                                <Button
                                  size="sm"
                                  onClick={() => handleVerifyDomain(domain)}
                                  disabled={verifying === domain.id}
                                >
                                  {verifying === domain.id ? (
                                    <Loader2 className="w-4 h-4 animate-spin" />
                                  ) : (
                                    <>
                                      <RefreshCw className="w-4 h-4 mr-1" />
                                      Verify Now
                                    </>
                                  )}
                                </Button>
                              </div>
                            </AlertDescription>
                          </Alert>
                        )}

                        {/* Activation */}
                        {domain.verificationStatus === 'verified' &&
                          domain.status !== 'active' && (
                            <Alert>
                              <CheckCircle className="h-4 w-4 text-green-600" />
                              <AlertDescription>
                                <div className="flex items-center justify-between">
                                  <span>
                                    Domain verified! Activate it to start using
                                    it.
                                  </span>
                                  <Button
                                    size="sm"
                                    onClick={() => handleActivateDomain(domain)}
                                    disabled={activating === domain.id}
                                  >
                                    {activating === domain.id ? (
                                      <Loader2 className="w-4 h-4 animate-spin" />
                                    ) : (
                                      'Activate Domain'
                                    )}
                                  </Button>
                                </div>
                              </AlertDescription>
                            </Alert>
                          )}

                        {/* Active Status */}
                        {domain.status === 'active' && (
                          <Alert className="border-green-200 bg-green-50 dark:bg-green-900/20">
                            <CheckCircle className="h-4 w-4 text-green-600" />
                            <AlertDescription className="text-green-800 dark:text-green-200">
                              Your domain is active and serving your portfolio!
                              <div className="mt-2">
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() =>
                                    window.open(
                                      `https://${domain.domain}`,
                                      '_blank'
                                    )
                                  }
                                >
                                  <ExternalLink className="w-4 h-4 mr-1" />
                                  Visit Site
                                </Button>
                              </div>
                            </AlertDescription>
                          </Alert>
                        )}

                        {/* DNS Status */}
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <Label className="text-sm text-muted-foreground">
                              DNS Configuration
                            </Label>
                            <p className="font-medium">
                              {domain.dnsConfigured
                                ? 'Configured'
                                : 'Not Configured'}
                            </p>
                          </div>
                          <div>
                            <Label className="text-sm text-muted-foreground">
                              Last Checked
                            </Label>
                            <p className="font-medium">
                              {domain.dnsLastCheckedAt
                                ? new Date(
                                    domain.dnsLastCheckedAt
                                  ).toLocaleString()
                                : 'Never'}
                            </p>
                          </div>
                        </div>
                      </TabsContent>

                      <TabsContent value="setup" className="space-y-4">
                        <DomainSetupInstructions
                          domain={domain.domain}
                          verificationToken={domain.verificationToken}
                          provider="generic"
                        />
                      </TabsContent>

                      <TabsContent value="settings" className="space-y-4">
                        <div className="space-y-4">
                          {/* Primary Domain */}
                          {domain.status === 'active' && !domain.isPrimary && (
                            <div className="flex items-center justify-between p-4 border rounded-lg">
                              <div>
                                <h4 className="font-medium">
                                  Set as Primary Domain
                                </h4>
                                <p className="text-sm text-muted-foreground">
                                  Make this your main portfolio URL
                                </p>
                              </div>
                              <Button
                                variant="outline"
                                onClick={() => handleSetPrimary(domain)}
                              >
                                Set as Primary
                              </Button>
                            </div>
                          )}

                          {/* Force SSL */}
                          <div className="flex items-center justify-between p-4 border rounded-lg">
                            <div>
                              <h4 className="font-medium">Force SSL</h4>
                              <p className="text-sm text-muted-foreground">
                                Always redirect to HTTPS
                              </p>
                            </div>
                            <Badge
                              variant={domain.forceSsl ? 'default' : 'outline'}
                            >
                              {domain.forceSsl ? 'Enabled' : 'Disabled'}
                            </Badge>
                          </div>

                          {/* Domain Details */}
                          <div className="space-y-2">
                            <Label>Verification Token</Label>
                            <div className="flex items-center gap-2">
                              <Input
                                value={`prisma-verify=${domain.verificationToken}`}
                                readOnly
                                className="font-mono text-sm"
                              />
                              <Button
                                variant="outline"
                                size="icon"
                                onClick={() =>
                                  copyToClipboard(
                                    `prisma-verify=${domain.verificationToken}`
                                  )
                                }
                              >
                                <Copy className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>
                        </div>
                      </TabsContent>
                    </Tabs>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Add Domain Modal */}
          {showAddModal && (
            <AddDomainModal
              portfolios={portfolios}
              onAdd={handleAddDomain}
              onClose={() => setShowAddModal(false)}
            />
          )}
        </div>
      </div>
    </BaseLayout>
  );
}

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { useRouter } from 'next/navigation';
import {
  Github,
  Check,
  RefreshCw,
  Database,
  Activity,
  GitBranch,
  GitCommit,
  GitPullRequest,
  Users,
  Settings,
  Link,
  Unlink,
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { logger } from '@/lib/utils/logger';
import { showToast } from '@/lib/utils/toast';
import { formatDistanceToNow } from 'date-fns';

interface GitHubConnectionStatus {
  isConnected: boolean;
  username?: string;
  email?: string;
  avatarUrl?: string;
  installedAt?: string;
  lastSync?: string;
  scope?: string[];
  rateLimit?: {
    limit: number;
    remaining: number;
    reset: string;
  };
}

interface RepositoryStats {
  total: number;
  synced: number;
  lastUpdated?: string;
}

export default function GitHubIntegrationPage() {
  const {} = useLanguage();
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [syncing, setSyncing] = useState(false);
  const [disconnecting, setDisconnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] =
    useState<GitHubConnectionStatus>({
      isConnected: false,
    });
  const [repositoryStats, setRepositoryStats] = useState<RepositoryStats>({
    total: 0,
    synced: 0,
  });

  const fetchRepositoryStats = async () => {
    try {
      const statsResponse = await fetch('/api/v1/analytics/repositories');
      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setRepositoryStats({
          total: statsData.data.repositories.length,
          synced: statsData.data.repositories.length,
          lastUpdated: statsData.data.repositories[0]?.updated_at,
        });
      }
    } catch (error) {
      logger.error(
        'Failed to fetch repository stats:',
        error instanceof Error ? error : new Error(String(error))
      );
    }
  };

  const checkConnectionStatus = useCallback(async () => {
    try {
      const response = await fetch('/api/v1/integrations/github/status');
      if (response.ok) {
        const data = await response.json();
        setConnectionStatus(data);

        if (data.isConnected) {
          await fetchRepositoryStats();
        }
      }
    } catch (error) {
      logger.error(
        'Failed to check GitHub connection status:',
        error instanceof Error ? error : new Error(String(error))
      );
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    checkConnectionStatus();
  }, [checkConnectionStatus]);

  const handleConnect = async () => {
    try {
      const response = await fetch('/api/v1/integrations/github/auth');
      if (response.ok) {
        const { authUrl } = await response.json();
        window.location.href = authUrl;
      } else {
        showToast.error('Failed to initiate GitHub connection');
      }
    } catch (error) {
      logger.error(
        'GitHub connection failed:',
        error instanceof Error ? error : new Error(String(error))
      );
      showToast.error('Failed to connect to GitHub');
    }
  };

  const handleDisconnect = async () => {
    // TODO: Replace with proper modal dialog
    if (
      !window.confirm(
        'Are you sure you want to disconnect GitHub? This will remove all synced repository data.'
      )
    ) {
      return;
    }

    setDisconnecting(true);
    try {
      const response = await fetch('/api/v1/integrations/github/disconnect', {
        method: 'POST',
      });

      if (response.ok) {
        setConnectionStatus({ isConnected: false });
        setRepositoryStats({ total: 0, synced: 0 });
        showToast.success('GitHub disconnected successfully');
      } else {
        showToast.error('Failed to disconnect GitHub');
      }
    } catch (error) {
      logger.error(
        'GitHub disconnect failed:',
        error instanceof Error ? error : new Error(String(error))
      );
      showToast.error('Failed to disconnect GitHub');
    } finally {
      setDisconnecting(false);
    }
  };

  const handleSync = async () => {
    setSyncing(true);
    try {
      const response = await fetch('/api/v1/analytics/repositories', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ force: true }),
      });

      if (response.ok) {
        showToast.success('Repository sync started');
        await checkConnectionStatus();
      } else if (response.status === 429) {
        showToast.error('Please wait before syncing again');
      } else {
        showToast.error('Failed to sync repositories');
      }
    } catch (error) {
      logger.error(
        'Repository sync failed:',
        error instanceof Error ? error : new Error(String(error))
      );
      showToast.error('Failed to sync repositories');
    } finally {
      setSyncing(false);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto p-8 max-w-4xl">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/3 mb-4"></div>
          <div className="h-64 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-8 max-w-4xl space-y-8">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold mb-2">GitHub Integration</h1>
        <p className="text-gray-600 dark:text-gray-400">
          Connect your GitHub account to import repositories and track your
          development analytics
        </p>
      </div>

      {/* Connection Status Card */}
      <Card className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex items-start space-x-4">
            <div
              className={`p-3 rounded-full ${
                connectionStatus.isConnected
                  ? 'bg-green-100 dark:bg-green-900/30'
                  : 'bg-gray-100 dark:bg-gray-800'
              }`}
            >
              <Github
                className={`w-8 h-8 ${
                  connectionStatus.isConnected
                    ? 'text-green-600 dark:text-green-400'
                    : 'text-gray-600 dark:text-gray-400'
                }`}
              />
            </div>

            <div>
              <h2 className="text-xl font-semibold mb-1">
                {connectionStatus.isConnected ? 'Connected' : 'Not Connected'}
              </h2>

              {connectionStatus.isConnected ? (
                <div className="space-y-2">
                  <div className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                    <span className="font-medium">
                      {connectionStatus.username}
                    </span>
                    {connectionStatus.email && (
                      <>
                        <span></span>
                        <span>{connectionStatus.email}</span>
                      </>
                    )}
                  </div>

                  {connectionStatus.installedAt && (
                    <p className="text-sm text-gray-500">
                      Connected{' '}
                      {formatDistanceToNow(
                        new Date(connectionStatus.installedAt)
                      )}{' '}
                      ago
                    </p>
                  )}

                  {connectionStatus.scope && (
                    <div className="flex flex-wrap gap-2 mt-2">
                      {connectionStatus.scope.map(scope => (
                        <Badge key={scope} variant="secondary">
                          {scope}
                        </Badge>
                      ))}
                    </div>
                  )}
                </div>
              ) : (
                <p className="text-gray-600 dark:text-gray-400">
                  Connect your GitHub account to start tracking your
                  repositories
                </p>
              )}
            </div>
          </div>

          <div>
            {connectionStatus.isConnected ? (
              <Button
                variant="destructive"
                size="sm"
                onClick={handleDisconnect}
                disabled={disconnecting}
              >
                {disconnecting ? (
                  <>
                    <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                    Disconnecting...
                  </>
                ) : (
                  <>
                    <Unlink className="w-4 h-4 mr-2" />
                    Disconnect
                  </>
                )}
              </Button>
            ) : (
              <Button variant="default" onClick={handleConnect}>
                <Link className="w-4 h-4 mr-2" />
                Connect GitHub
              </Button>
            )}
          </div>
        </div>

        {/* Rate Limit Info */}
        {connectionStatus.isConnected && connectionStatus.rateLimit && (
          <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
            <div className="flex items-center justify-between text-sm">
              <span className="text-gray-600 dark:text-gray-400">
                API Rate Limit
              </span>
              <div className="flex items-center space-x-2">
                <span className="font-medium">
                  {connectionStatus.rateLimit.remaining} /{' '}
                  {connectionStatus.rateLimit.limit}
                </span>
                <span className="text-gray-500">
                  (Resets{' '}
                  {formatDistanceToNow(
                    new Date(connectionStatus.rateLimit.reset)
                  )}{' '}
                  from now)
                </span>
              </div>
            </div>
            <div className="mt-2 w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2 overflow-hidden">
              <div
                className="bg-blue-600 h-full transition-all duration-300"
                style={{
                  width: `${(connectionStatus.rateLimit.remaining / connectionStatus.rateLimit.limit) * 100}%`,
                }}
              />
            </div>
          </div>
        )}
      </Card>

      {/* Repository Stats */}
      {connectionStatus.isConnected && (
        <>
          <Card className="p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Repository Overview</h3>
              <Button
                variant="secondary"
                size="sm"
                onClick={handleSync}
                disabled={syncing}
              >
                {syncing ? (
                  <>
                    <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                    Syncing...
                  </>
                ) : (
                  <>
                    <RefreshCw className="w-4 h-4 mr-2" />
                    Sync Now
                  </>
                )}
              </Button>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                <div className="flex items-center space-x-3">
                  <Database className="w-5 h-5 text-gray-600 dark:text-gray-400" />
                  <div>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      Total Repositories
                    </p>
                    <p className="text-2xl font-bold">
                      {repositoryStats.total}
                    </p>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                <div className="flex items-center space-x-3">
                  <Check className="w-5 h-5 text-green-600 dark:text-green-400" />
                  <div>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      Synced
                    </p>
                    <p className="text-2xl font-bold">
                      {repositoryStats.synced}
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {repositoryStats.lastUpdated && (
              <p className="mt-4 text-sm text-gray-500">
                Last synced{' '}
                {formatDistanceToNow(new Date(repositoryStats.lastUpdated))} ago
              </p>
            )}
          </Card>

          {/* Features */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">Features</h3>
            <div className="space-y-4">
              <div className="flex items-start space-x-3">
                <GitCommit className="w-5 h-5 text-blue-600 dark:text-blue-400 mt-0.5" />
                <div>
                  <h4 className="font-medium">Commit Analytics</h4>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    Track your commit history and contribution patterns
                  </p>
                </div>
              </div>

              <div className="flex items-start space-x-3">
                <GitPullRequest className="w-5 h-5 text-purple-600 dark:text-purple-400 mt-0.5" />
                <div>
                  <h4 className="font-medium">Pull Request Insights</h4>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    Monitor PR activity and review cycles
                  </p>
                </div>
              </div>

              <div className="flex items-start space-x-3">
                <Users className="w-5 h-5 text-green-600 dark:text-green-400 mt-0.5" />
                <div>
                  <h4 className="font-medium">Contributor Statistics</h4>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    View collaboration metrics and team contributions
                  </p>
                </div>
              </div>

              <div className="flex items-start space-x-3">
                <Activity className="w-5 h-5 text-orange-600 dark:text-orange-400 mt-0.5" />
                <div>
                  <h4 className="font-medium">Activity Tracking</h4>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    Real-time updates on repository activity
                  </p>
                </div>
              </div>
            </div>
          </Card>

          {/* Quick Actions */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <Button
                variant="secondary"
                className="w-full justify-start"
                onClick={() => router.push('/analytics')}
              >
                <Activity className="w-4 h-4 mr-2" />
                View Analytics Dashboard
              </Button>

              <Button
                variant="secondary"
                className="w-full justify-start"
                onClick={() => router.push('/editor/new?import=github')}
              >
                <GitBranch className="w-4 h-4 mr-2" />
                Import Repositories to Portfolio
              </Button>

              <Button
                variant="secondary"
                className="w-full justify-start"
                onClick={() =>
                  window.open('https://github.com/settings/apps', '_blank')
                }
              >
                <Settings className="w-4 h-4 mr-2" />
                Manage GitHub App Permissions
              </Button>
            </div>
          </Card>
        </>
      )}

      {/* Not Connected State */}
      {!connectionStatus.isConnected && (
        <Card className="p-8 text-center">
          <Github className="w-16 h-16 mx-auto mb-4 text-gray-400" />
          <h3 className="text-xl font-semibold mb-2">
            Connect Your GitHub Account
          </h3>
          <p className="text-gray-600 dark:text-gray-400 mb-6 max-w-md mx-auto">
            Link your GitHub account to import repositories, track development
            metrics, and showcase your coding activity in your portfolio.
          </p>

          <div className="space-y-4 max-w-sm mx-auto text-left">
            <div className="flex items-center space-x-3">
              <Check className="w-5 h-5 text-green-500 flex-shrink-0" />
              <span className="text-sm">
                Import repository information automatically
              </span>
            </div>
            <div className="flex items-center space-x-3">
              <Check className="w-5 h-5 text-green-500 flex-shrink-0" />
              <span className="text-sm">
                Track commits, PRs, and contributors
              </span>
            </div>
            <div className="flex items-center space-x-3">
              <Check className="w-5 h-5 text-green-500 flex-shrink-0" />
              <span className="text-sm">Generate development analytics</span>
            </div>
          </div>

          <Button
            variant="default"
            size="lg"
            className="mt-6"
            onClick={handleConnect}
          >
            <Github className="w-5 h-5 mr-2" />
            Connect GitHub Account
          </Button>
        </Card>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import {
  TrendingUp,
  TrendingDown,
  Users,
  FileText,
  Eye,
  Globe,
  Clock,
  Target,
  Award,
  BarChart3,
  Calendar,
  Activity,
} from 'lucide-react';

import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { usePortfolioStore } from '@/lib/store/portfolio-store';
import { useAuthStore } from '@/lib/store/auth-store';
import { cn } from '@/lib/utils';
import type { Portfolio } from '@/types/portfolio';

interface Metric {
  label: string;
  value: number | string;
  change?: number;
  trend?: 'up' | 'down' | 'neutral';
  icon: React.ReactNode;
  format?: 'number' | 'percentage' | 'time' | 'currency';
}

interface Goal {
  id: string;
  title: string;
  current: number;
  target: number;
  unit: string;
  deadline?: Date;
  status: 'on-track' | 'at-risk' | 'completed' | 'behind';
}

export function SuccessMetrics() {
  const { t } = useLanguage();
  const { portfolios } = usePortfolioStore();
  const { user: _user } = useAuthStore();
  const [timeRange, setTimeRange] = useState<'7d' | '30d' | '90d'>('30d');
  const [isLoading, setIsLoading] = useState(true);

  // Mock data for demonstration - in production, this would come from analytics API
  const [metrics, setMetrics] = useState<{
    overview: Metric[];
    engagement: Metric[];
    conversion: Metric[];
  }>({
    overview: [],
    engagement: [],
    conversion: [],
  });

  const [goals, setGoals] = useState<Goal[]>([]);

  useEffect(() => {
    // Simulate loading metrics
    setTimeout(() => {
      // Calculate real metrics from portfolio data
      const publishedPortfolios = portfolios.filter(
        p => p.status === 'published'
      );
      const totalViews = publishedPortfolios.reduce(
        (sum, p) => sum + (p.views || 0),
        0
      );
      const calculatePortfolioCompletion = (portfolio: Portfolio): number => {
        const sections = ['experience', 'projects', 'skills', 'education'];
        const completedSections = sections.filter(s => {
          const sectionData = portfolio[s as keyof typeof portfolio];
          return Array.isArray(sectionData)
            ? sectionData.length > 0
            : !!sectionData;
        });
        return (completedSections.length / sections.length) * 100;
      };

      const avgCompletionRate =
        portfolios.length > 0
          ? portfolios.reduce(
              (sum, p) => sum + calculatePortfolioCompletion(p),
              0
            ) / portfolios.length
          : 0;

      setMetrics({
        overview: [
          {
            label: t.totalPortfolios || 'Total Portfolios',
            value: portfolios.length,
            change: 12,
            trend: 'up',
            icon: <FileText className="w-4 h-4" />,
            format: 'number',
          },
          {
            label: t.publishedPortfolios || 'Published',
            value: publishedPortfolios.length,
            change: 8,
            trend: 'up',
            icon: <Globe className="w-4 h-4" />,
            format: 'number',
          },
          {
            label: t.totalViews || 'Total Views',
            value: totalViews,
            change: 24,
            trend: 'up',
            icon: <Eye className="w-4 h-4" />,
            format: 'number',
          },
          {
            label: t.avgCompletion || 'Avg Completion',
            value: `${Math.round(avgCompletionRate)}%`,
            change: 5,
            trend: 'up',
            icon: <Target className="w-4 h-4" />,
            format: 'percentage',
          },
        ],
        engagement: [
          {
            label: t.avgTimeOnPage || 'Avg Time on Page',
            value: '3m 24s',
            change: 15,
            trend: 'up',
            icon: <Clock className="w-4 h-4" />,
            format: 'time',
          },
          {
            label: t.bounceRate || 'Bounce Rate',
            value: '42%',
            change: -5,
            trend: 'down',
            icon: <Activity className="w-4 h-4" />,
            format: 'percentage',
          },
          {
            label: t.returningVisitors || 'Returning Visitors',
            value: '28%',
            change: 12,
            trend: 'up',
            icon: <Users className="w-4 h-4" />,
            format: 'percentage',
          },
          {
            label: t.shareRate || 'Share Rate',
            value: '8.5%',
            change: 3,
            trend: 'up',
            icon: <BarChart3 className="w-4 h-4" />,
            format: 'percentage',
          },
        ],
        conversion: [
          {
            label: t.contactFormSubmissions || 'Contact Submissions',
            value: 47,
            change: 18,
            trend: 'up',
            icon: <Award className="w-4 h-4" />,
            format: 'number',
          },
          {
            label: t.cvDownloads || 'CV Downloads',
            value: 156,
            change: 22,
            trend: 'up',
            icon: <FileText className="w-4 h-4" />,
            format: 'number',
          },
          {
            label: t.socialClicks || 'Social Media Clicks',
            value: 342,
            change: 31,
            trend: 'up',
            icon: <Users className="w-4 h-4" />,
            format: 'number',
          },
          {
            label: t.projectViews || 'Project Views',
            value: 892,
            change: 45,
            trend: 'up',
            icon: <Eye className="w-4 h-4" />,
            format: 'number',
          },
        ],
      });

      setGoals([
        {
          id: '1',
          title: t.completeAllSections || 'Complete All Portfolio Sections',
          current: Math.round(avgCompletionRate),
          target: 100,
          unit: '%',
          status:
            avgCompletionRate >= 80
              ? 'on-track'
              : avgCompletionRate >= 60
                ? 'at-risk'
                : 'behind',
        },
        {
          id: '2',
          title: t.reach1000Views || 'Reach 1,000 Portfolio Views',
          current: totalViews,
          target: 1000,
          unit: 'views',
          deadline: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000),
          status:
            totalViews >= 800
              ? 'on-track'
              : totalViews >= 500
                ? 'at-risk'
                : 'behind',
        },
        {
          id: '3',
          title: t.publishPortfolio || 'Publish Your Portfolio',
          current: publishedPortfolios.length,
          target: 1,
          unit: 'portfolio',
          status: publishedPortfolios.length >= 1 ? 'completed' : 'behind',
        },
        {
          id: '4',
          title: t.getContactSubmissions || 'Get 10 Contact Submissions',
          current: 7,
          target: 10,
          unit: 'contacts',
          deadline: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000),
          status: 'on-track',
        },
      ]);

      setIsLoading(false);
    }, 1000);
  }, [portfolios, t]);

  const formatMetricValue = (metric: Metric) => {
    if (metric.format === 'currency') {
      return `$${metric.value.toLocaleString()}`;
    }
    if (metric.format === 'percentage' || metric.format === 'time') {
      return metric.value;
    }
    return metric.value.toLocaleString();
  };

  const getStatusColor = (status: Goal['status']) => {
    switch (status) {
      case 'completed':
        return 'text-green-600 bg-green-50';
      case 'on-track':
        return 'text-blue-600 bg-blue-50';
      case 'at-risk':
        return 'text-yellow-600 bg-yellow-50';
      case 'behind':
        return 'text-red-600 bg-red-50';
    }
  };

  const renderMetricCard = (metric: Metric) => (
    <Card key={metric.label}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className="p-2 bg-primary/10 rounded-lg text-primary">
            {metric.icon}
          </div>
          {metric.change !== undefined && (
            <div
              className={cn(
                'flex items-center text-sm font-medium',
                metric.trend === 'up'
                  ? 'text-green-600'
                  : metric.trend === 'down'
                    ? 'text-red-600'
                    : 'text-gray-600'
              )}
            >
              {metric.trend === 'up' ? (
                <TrendingUp className="w-4 h-4 mr-1" />
              ) : metric.trend === 'down' ? (
                <TrendingDown className="w-4 h-4 mr-1" />
              ) : null}
              {Math.abs(metric.change)}%
            </div>
          )}
        </div>
        <div>
          <p className="text-2xl font-bold">{formatMetricValue(metric)}</p>
          <p className="text-sm text-muted-foreground">{metric.label}</p>
        </div>
      </CardContent>
    </Card>
  );

  const renderGoalCard = (goal: Goal) => {
    const progress = (goal.current / goal.target) * 100;

    return (
      <Card key={goal.id}>
        <CardContent className="p-6">
          <div className="flex items-start justify-between mb-4">
            <h4 className="font-medium">{goal.title}</h4>
            <Badge className={cn('ml-2', getStatusColor(goal.status))}>
              {goal.status.replace('-', ' ')}
            </Badge>
          </div>

          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span className="text-muted-foreground">
                {goal.current} / {goal.target} {goal.unit}
              </span>
              <span className="font-medium">{Math.round(progress)}%</span>
            </div>
            <Progress value={progress} className="h-2" />
            {goal.deadline && (
              <div className="flex items-center text-xs text-muted-foreground mt-2">
                <Calendar className="w-3 h-3 mr-1" />
                {new Date(goal.deadline).toLocaleDateString()}
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-2xl font-bold">
            {t.successMetrics || 'Success Metrics'}
          </h3>
          <p className="text-muted-foreground">
            {t.trackYourProgress ||
              'Track your portfolio performance and growth'}
          </p>
        </div>

        {/* Time Range Selector */}
        <div className="flex gap-2">
          {(['7d', '30d', '90d'] as const).map(range => (
            <Button
              key={range}
              variant={timeRange === range ? 'default' : 'outline'}
              size="sm"
              onClick={() => setTimeRange(range)}
            >
              {range === '7d'
                ? t.last7Days || 'Last 7 days'
                : range === '30d'
                  ? t.last30Days || 'Last 30 days'
                  : t.last90Days || 'Last 90 days'}
            </Button>
          ))}
        </div>
      </div>

      {/* Metrics Tabs */}
      <Tabs defaultValue="overview" className="space-y-4">
        <TabsList>
          <TabsTrigger value="overview">{t.overview || 'Overview'}</TabsTrigger>
          <TabsTrigger value="engagement">
            {t.engagement || 'Engagement'}
          </TabsTrigger>
          <TabsTrigger value="conversion">
            {t.conversion || 'Conversion'}
          </TabsTrigger>
          <TabsTrigger value="goals">{t.goals || 'Goals'}</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-4">
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
            {metrics.overview.map(renderMetricCard)}
          </div>
        </TabsContent>

        <TabsContent value="engagement" className="space-y-4">
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
            {metrics.engagement.map(renderMetricCard)}
          </div>
        </TabsContent>

        <TabsContent value="conversion" className="space-y-4">
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
            {metrics.conversion.map(renderMetricCard)}
          </div>
        </TabsContent>

        <TabsContent value="goals" className="space-y-4">
          <div className="grid gap-4 md:grid-cols-2">
            {goals.map(renderGoalCard)}
          </div>

