import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { ImageUpload } from '@/components/ui/image-upload';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { Certification } from '@/types/portfolio';
import { cn } from '@/lib/utils';
import { usePortfolioStore } from '@/lib/store/portfolio-store';

interface CertificationsSectionProps {
  certifications: Certification[];
  onUpdate: (certifications: Certification[]) => void;
}

interface CertificationFormData {
  name: string;
  issuer: string;
  issueDate: string;
  expiryDate?: string;
  credentialId?: string;
  credentialUrl?: string;
  imageUrl?: string;
}

export function CertificationsSection({
  certifications = [],
  onUpdate,
}: CertificationsSectionProps) {
  const { t } = useLanguage();
  const { currentPortfolio } = usePortfolioStore();
  const [isAdding, setIsAdding] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [formData, setFormData] = useState<CertificationFormData>({
    name: '',
    issuer: '',
    issueDate: '',
    expiryDate: '',
    credentialId: '',
    credentialUrl: '',
    imageUrl: '',
  });

  const resetForm = () => {
    setFormData({
      name: '',
      issuer: '',
      issueDate: '',
      expiryDate: '',
      credentialId: '',
      credentialUrl: '',
      imageUrl: '',
    });
    setIsAdding(false);
    setEditingId(null);
  };

  const handleAdd = () => {
    setIsAdding(true);
    setEditingId(null);
    resetForm();
  };

  const handleEdit = (cert: Certification) => {
    setEditingId(cert.id);
    setIsAdding(false);
    setFormData({
      name: cert.name,
      issuer: cert.issuer,
      issueDate: cert.issueDate,
      expiryDate: cert.expiryDate || '',
      credentialId: cert.credentialId || '',
      credentialUrl: cert.credentialUrl || '',
    });
  };

  const handleSave = () => {
    if (!formData.name || !formData.issuer || !formData.issueDate) {
      return;
    }

    const newCertification: Certification = {
      id: editingId || `cert_${Date.now()}`,
      name: formData.name,
      issuer: formData.issuer,
      issueDate: formData.issueDate,
      expiryDate: formData.expiryDate || undefined,
      credentialId: formData.credentialId || undefined,
      credentialUrl: formData.credentialUrl || undefined,
    };

    let updatedCertifications: Certification[];
    if (editingId) {
      updatedCertifications = certifications.map(cert =>
        cert.id === editingId ? newCertification : cert
      );
    } else {
      updatedCertifications = [...certifications, newCertification];
    }

    // Sort by issue date (newest first)
    updatedCertifications.sort(
      (a, b) =>
        new Date(b.issueDate).getTime() - new Date(a.issueDate).getTime()
    );

    onUpdate(updatedCertifications);
    resetForm();
  };

  const handleDelete = (id: string) => {
    if (
      window.confirm(
        t.confirmDeleteCertification ||
          'Are you sure you want to delete this certification?'
      )
    ) {
      onUpdate(certifications.filter(cert => cert.id !== id));
    }
  };

  const formatDateForDisplay = (dateString: string) => {
    if (!dateString) return '';
    try {
      return format(new Date(dateString), 'MMM yyyy');
    } catch {
      return dateString;
    }
  };

  const isExpired = (expiryDate?: string) => {
    if (!expiryDate) return false;
    return new Date(expiryDate) < new Date();
  };

  const isExpiringSoon = (expiryDate?: string) => {
    if (!expiryDate) return false;
    const expiry = new Date(expiryDate);
    const threeMonthsFromNow = new Date();
    threeMonthsFromNow.setMonth(threeMonthsFromNow.getMonth() + 3);
    return expiry > new Date() && expiry <= threeMonthsFromNow;
  };

  // Popular certification issuers for autocomplete
  const popularIssuers = [
    'Microsoft',
    'Google',
    'Amazon Web Services (AWS)',
    'Cisco',
    'CompTIA',
    'Oracle',
    'IBM',
    'Salesforce',
    'Adobe',
    'PMI',
    'Scrum Alliance',
    'Linux Foundation',
    'HashiCorp',
    'Meta',
    'Coursera',
    'Udacity',
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">
            {t.certifications || 'Certifications'}
          </h3>
          <p className="text-sm text-muted-foreground">
            {t.certificationsDescription ||
              'Professional certifications and credentials'}
          </p>
        </div>
        {!isAdding && !editingId && (
          <Button onClick={handleAdd} size="sm">
            <Plus className="h-4 w-4 mr-2" />
            {t.addCertification || 'Add Certification'}
          </Button>
        )}
      </div>

      {/* Certification Form */}
      {(isAdding || editingId) && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">
              {editingId
                ? t.editCertification || 'Edit Certification'
                : t.addCertification || 'Add Certification'}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="name">
                {t.certificationName || 'Certification Name'} *
              </Label>
              <Input
                id="name"
                value={formData.name}
                onChange={e =>
                  setFormData({ ...formData, name: e.target.value })
                }
                placeholder={
                  t.certNamePlaceholder ||
                  'e.g. AWS Certified Solutions Architect'
                }
              />
            </div>

            <div>
              <Label htmlFor="issuer">
                {t.issuingOrganization || 'Issuing Organization'} *
              </Label>
              <Input
                id="issuer"
                value={formData.issuer}
                onChange={e =>
                  setFormData({ ...formData, issuer: e.target.value })
                }
                placeholder={t.issuerPlaceholder || 'e.g. Amazon Web Services'}
                list="issuer-suggestions"
              />
              <datalist id="issuer-suggestions">
                {popularIssuers.map((issuer, index) => (
                  <option key={index} value={issuer} />
                ))}
              </datalist>
            </div>

            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="issueDate">
                  {t.issueDate || 'Issue Date'} *
                </Label>
                <Input
                  id="issueDate"
                  type="month"
                  value={formData.issueDate}
                  onChange={e =>
                    setFormData({ ...formData, issueDate: e.target.value })
                  }
                />
              </div>
              <div>
                <Label htmlFor="expiryDate">
                  {t.expiryDate || 'Expiry Date'}
                </Label>
                <Input
                  id="expiryDate"
                  type="month"
                  value={formData.expiryDate}
                  onChange={e =>
                    setFormData({ ...formData, expiryDate: e.target.value })
                  }
                  min={formData.issueDate}
                />
                <p className="text-xs text-muted-foreground mt-1">
                  {t.leaveEmptyIfNoExpiry ||
                    'Leave empty if certification does not expire'}
                </p>
              </div>
            </div>

            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="credentialId">
                  {t.credentialId || 'Credential ID'}
                </Label>
                <Input
                  id="credentialId"
                  value={formData.credentialId}
                  onChange={e =>
                    setFormData({ ...formData, credentialId: e.target.value })
                  }
                  placeholder={
                    t.credentialIdPlaceholder || 'e.g. ABCD-1234-EFGH-5678'
                  }
                />
              </div>
              <div>
                <Label htmlFor="credentialUrl">
                  {t.credentialUrl || 'Credential URL'}
                </Label>
                <Input
                  id="credentialUrl"
                  type="url"
                  value={formData.credentialUrl}
                  onChange={e =>
                    setFormData({ ...formData, credentialUrl: e.target.value })
                  }
                  placeholder={t.credentialUrlPlaceholder || 'https://...'}
                />
              </div>
            </div>

            <div>
              <Label>{t.certificateImage || 'Certificate Image'}</Label>
              <ImageUpload
                value={formData.imageUrl}
                onChange={url =>
                  setFormData({ ...formData, imageUrl: url || '' })
                }
                type="certificate"
                portfolioId={currentPortfolio?.id || ''}
                aspectRatio="auto"
                className="mt-2"
              />
            </div>

            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={resetForm}>
                {t.cancel || 'Cancel'}
              </Button>
              <Button onClick={handleSave}>
                {editingId
                  ? t.saveChanges || 'Save Changes'
                  : t.addCertification || 'Add Certification'}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Certifications List */}
      <div className="space-y-4">
        {certifications.map(cert => {
          const expired = isExpired(cert.expiryDate);
          const expiringSoon = isExpiringSoon(cert.expiryDate);

          return (
            <Card
              key={cert.id}
              className={cn('group relative', expired && 'opacity-60')}
            >
              <CardContent className="p-6">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <Award className="h-5 w-5 text-primary" />
                      <div className="flex-1">
                        <h4 className="font-semibold flex items-center gap-2">
                          {cert.name}
                          {expired && (
                            <Badge
                              variant="secondary"
                              className="bg-red-100 text-red-800"
                            >
                              {t.expired || 'Expired'}
                            </Badge>
                          )}
                          {expiringSoon && (
                            <Badge
                              variant="secondary"
                              className="bg-yellow-100 text-yellow-800"
                            >
                              {t.expiringSoon || 'Expiring Soon'}
                            </Badge>
                          )}
                        </h4>
                        <p className="text-muted-foreground">{cert.issuer}</p>
                      </div>
                    </div>

                    <div className="ml-8 space-y-1">
                      <div className="flex items-center gap-4 text-sm text-muted-foreground">
                        <div className="flex items-center gap-1">
                          <Calendar className="h-3 w-3" />
                          <span>
                            {t.issued || 'Issued'}:{' '}
                            {formatDateForDisplay(cert.issueDate)}
                          </span>
                        </div>
                        {cert.expiryDate && (
                          <div className="flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            <span>
                              {t.expires || 'Expires'}:{' '}
                              {formatDateForDisplay(cert.expiryDate)}
                            </span>
                          </div>
                        )}
                      </div>

                      {cert.credentialId && (
                        <p className="text-sm text-muted-foreground">
                          {t.credentialId || 'Credential ID'}:{' '}
                          {cert.credentialId}
                        </p>
                      )}

                      {cert.credentialUrl && (
                        <a
                          href={cert.credentialUrl}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="inline-flex items-center gap-1 text-sm text-primary hover:underline"
                        >
                          {t.viewCredential || 'View Credential'}
                          <ExternalLink className="h-3 w-3" />
                        </a>
                      )}
                    </div>
                  </div>

                  <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleEdit(cert)}
                    >
                      <Edit2 className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleDelete(cert.id)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {certifications.length === 0 && !isAdding && (
        <Card>
          <CardContent className="text-center py-12">
            <Award className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground mb-4">
              {t.noCertifications || 'No certifications added yet'}
            </p>
            <Button onClick={handleAdd}>
              <Plus className="h-4 w-4 mr-2" />
              {t.addFirstCertification || 'Add Your First Certification'}
            </Button>
          </CardContent>
        </Card>
      )}

      {/* Certification Tips */}
      {certifications.length > 0 && (
        <Card>
          <CardContent className="py-4">
            <p className="text-sm text-muted-foreground">
              <strong>{t.tip || 'Tip'}:</strong>{' '}
              {t.certificationTip ||
                'Keep your certifications up to date. Expired certifications are shown but marked clearly. Consider renewing certifications that are expiring soon.'}
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import {
  Mail,
  Phone,
  MapPin,
  Globe,
  Linkedin,
  Github,
  Twitter,
  Instagram,
  Youtube,
  Link,
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { ContactInfo, SocialLinks } from '@/types/portfolio';

interface ContactSectionProps {
  contact: ContactInfo;
  social: SocialLinks;
  onUpdate: (data: { contact?: ContactInfo; social?: SocialLinks }) => void;
}

const SOCIAL_PLATFORMS = [
  {
    key: 'linkedin' as keyof SocialLinks,
    label: 'LinkedIn',
    icon: Linkedin,
    placeholder: 'https://linkedin.com/in/yourname',
    color: 'text-[#0077B5]',
  },
  {
    key: 'github' as keyof SocialLinks,
    label: 'GitHub',
    icon: Github,
    placeholder: 'https://github.com/yourname',
    color: 'text-gray-900',
  },
  {
    key: 'twitter' as keyof SocialLinks,
    label: 'Twitter',
    icon: Twitter,
    placeholder: 'https://twitter.com/yourname',
    color: 'text-[#1DA1F2]',
  },
  {
    key: 'instagram' as keyof SocialLinks,
    label: 'Instagram',
    icon: Instagram,
    placeholder: 'https://instagram.com/yourname',
    color: 'text-[#E4405F]',
  },
  {
    key: 'youtube' as keyof SocialLinks,
    label: 'YouTube',
    icon: Youtube,
    placeholder: 'https://youtube.com/@yourname',
    color: 'text-[#FF0000]',
  },
  {
    key: 'website' as keyof SocialLinks,
    label: 'Website',
    icon: Globe,
    placeholder: 'https://yourwebsite.com',
    color: 'text-blue-600',
  },
  {
    key: 'dribbble' as keyof SocialLinks,
    label: 'Dribbble',
    icon: Globe,
    placeholder: 'https://dribbble.com/yourname',
    color: 'text-[#EA4C89]',
  },
  {
    key: 'behance' as keyof SocialLinks,
    label: 'Behance',
    icon: Globe,
    placeholder: 'https://behance.net/yourname',
    color: 'text-[#1769FF]',
  },
];

export function ContactSection({
  contact = {},
  social = {},
  onUpdate,
}: ContactSectionProps) {
  const { t } = useLanguage();
  const [showCustomLink, setShowCustomLink] = useState(false);
  const [customLink, setCustomLink] = useState({ label: '', url: '' });
  const [contactVisibility, setContactVisibility] = useState({
    email: true,
    phone: true,
    location: true,
  });

  const handleContactChange = (field: keyof ContactInfo, value: string) => {
    onUpdate({
      contact: {
        ...contact,
        [field]: value,
      },
    });
  };

  const handleSocialChange = (platform: keyof SocialLinks, value: string) => {
    onUpdate({
      social: {
        ...social,
        [platform]: value || undefined,
      },
    });
  };

  const handleAddCustomLink = () => {
    if (!customLink.label || !customLink.url) return;

    const customLinks = social.custom || [];
    onUpdate({
      social: {
        ...social,
        custom: [...customLinks, { ...customLink }],
      },
    });
    setCustomLink({ label: '', url: '' });
    setShowCustomLink(false);
  };

  const handleRemoveCustomLink = (index: number) => {
    const customLinks = social.custom || [];
    onUpdate({
      social: {
        ...social,
        custom: customLinks.filter((_, i) => i !== index),
      },
    });
  };

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-semibold">
          {t.contactInformation || 'Contact Information'}
        </h3>
        <p className="text-sm text-muted-foreground">
          {t.contactDescription || 'How people can reach you'}
        </p>
      </div>

      {/* Contact Information */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base">
            {t.contactDetails || 'Contact Details'}
          </CardTitle>
          <CardDescription>
            {t.contactDetailsDescription ||
              'Your professional contact information'}
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <div className="flex items-center justify-between mb-2">
              <Label htmlFor="email" className="flex items-center gap-2">
                <Mail className="h-4 w-4" />
                {t.email || 'Email'}
              </Label>
              <div className="flex items-center space-x-2">
                <Label htmlFor="showEmail" className="text-sm font-normal">
                  {t.showOnPortfolio || 'Show on portfolio'}
                </Label>
                <Switch
                  id="showEmail"
                  checked={contactVisibility.email}
                  onCheckedChange={checked =>
                    setContactVisibility({
                      ...contactVisibility,
                      email: checked,
                    })
                  }
                />
              </div>
            </div>
            <Input
              id="email"
              type="email"
              value={contact.email || ''}
              onChange={e => handleContactChange('email', e.target.value)}
              placeholder={t.emailPlaceholder || 'your@email.com'}
            />
          </div>

          <div>
            <div className="flex items-center justify-between mb-2">
              <Label htmlFor="phone" className="flex items-center gap-2">
                <Phone className="h-4 w-4" />
                {t.phone || 'Phone'}
              </Label>
              <div className="flex items-center space-x-2">
                <Label htmlFor="showPhone" className="text-sm font-normal">
                  {t.showOnPortfolio || 'Show on portfolio'}
                </Label>
                <Switch
                  id="showPhone"
                  checked={contactVisibility.phone}
                  onCheckedChange={checked =>
                    setContactVisibility({
                      ...contactVisibility,
                      phone: checked,
                    })
                  }
                />
              </div>
            </div>
            <Input
              id="phone"
              type="tel"
              value={contact.phone || ''}
              onChange={e => handleContactChange('phone', e.target.value)}
              placeholder={t.phonePlaceholder || '+1 (555) 123-4567'}
            />
          </div>

          <div>
            <div className="flex items-center justify-between mb-2">
              <Label htmlFor="location" className="flex items-center gap-2">
                <MapPin className="h-4 w-4" />
                {t.location || 'Location'}
              </Label>
              <div className="flex items-center space-x-2">
                <Label htmlFor="showLocation" className="text-sm font-normal">
                  {t.showOnPortfolio || 'Show on portfolio'}
                </Label>
                <Switch
                  id="showLocation"
                  checked={contactVisibility.location}
                  onCheckedChange={checked =>
                    setContactVisibility({
                      ...contactVisibility,
                      location: checked,
                    })
                  }
                />
              </div>
            </div>
            <Input
              id="location"
              value={contact.location || ''}
              onChange={e => handleContactChange('location', e.target.value)}
              placeholder={t.locationPlaceholder || 'San Francisco, CA'}
            />
          </div>

          <div>
            <Label htmlFor="availability" className="flex items-center gap-2">
              <Globe className="h-4 w-4" />
              {t.availability || 'Availability'}
            </Label>
            <Input
              id="availability"
              value={contact.availability || ''}
              onChange={e =>
                handleContactChange('availability', e.target.value)
              }
              placeholder={
                t.availabilityPlaceholder || 'Available for freelance work'
              }
              className="mt-2"
            />
          </div>
        </CardContent>
      </Card>

      {/* Social Links */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base">
            {t.socialLinks || 'Social Links'}
          </CardTitle>
          <CardDescription>
            {t.socialLinksDescription || 'Connect your social media profiles'}
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {SOCIAL_PLATFORMS.map(platform => {
            const Icon = platform.icon;
            return (
              <div key={platform.key}>
                <Label
                  htmlFor={platform.key}
                  className="flex items-center gap-2 mb-2"
                >
                  <Icon className={`h-4 w-4 ${platform.color}`} />
                  {platform.label}
                </Label>
                <Input
                  id={platform.key}
                  type="url"
                  value={(social[platform.key] as string) || ''}
                  onChange={e =>
                    handleSocialChange(platform.key, e.target.value)
                  }
                  placeholder={platform.placeholder}
                />
              </div>
            );
          })}

          {/* Custom Links */}
          {social.custom && social.custom.length > 0 && (
            <div className="space-y-3 pt-4 border-t">
              <Label>{t.customLinks || 'Custom Links'}</Label>
              {social.custom.map((link, index) => (
                <div key={index} className="flex items-center gap-2">
                  <Link className="h-4 w-4 text-muted-foreground" />
                  <span className="text-sm font-medium">{link.label}:</span>
                  <span className="text-sm text-muted-foreground flex-1 truncate">
                    {link.url}
                  </span>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => handleRemoveCustomLink(index)}
                  >
                    {t.remove || 'Remove'}
                  </Button>
                </div>
              ))}
            </div>
          )}

          {/* Add Custom Link */}
          {showCustomLink ? (
            <div className="space-y-3 pt-4 border-t">
              <Label>{t.addCustomLink || 'Add Custom Link'}</Label>
              <div className="grid gap-3">
                <Input
                  placeholder={t.linkLabel || 'Link Label'}
                  value={customLink.label}
                  onChange={e =>
                    setCustomLink({ ...customLink, label: e.target.value })
                  }
                />
                <Input
                  type="url"
                  placeholder={t.linkUrl || 'https://...'}
                  value={customLink.url}
                  onChange={e =>
                    setCustomLink({ ...customLink, url: e.target.value })
                  }
                />
                <div className="flex gap-2">
                  <Button
                    size="sm"
                    onClick={handleAddCustomLink}
                    disabled={!customLink.label || !customLink.url}
                  >
                    {t.add || 'Add'}
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => {
                      setShowCustomLink(false);
                      setCustomLink({ label: '', url: '' });
                    }}
                  >
                    {t.cancel || 'Cancel'}
                  </Button>
                </div>
              </div>
            </div>
          ) : (
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowCustomLink(true)}
              className="w-full"
            >
              <Link className="h-4 w-4 mr-2" />
              {t.addCustomLink || 'Add Custom Link'}
            </Button>
          )}
        </CardContent>
      </Card>

      {/* Privacy Notice */}
      <Card className="bg-muted/50">
        <CardContent className="py-4">
          <p className="text-sm text-muted-foreground">
            <strong>{t.privacyNote || 'Privacy Note'}:</strong>{' '}
            {t.contactPrivacyMessage ||
              'Only the information you mark as visible will be displayed on your public portfolio. Email addresses can be protected with a contact form.'}
          </p>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Plus, Edit2, Trash2, GraduationCap, X } from 'lucide-react';
import { format } from 'date-fns';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { Education } from '@/types/portfolio';

interface EducationSectionProps {
  education: Education[];
  onUpdate: (education: Education[]) => void;
}

interface EducationFormData {
  institution: string;
  degree: string;
  field: string;
  startDate: string;
  endDate?: string;
  current?: boolean;
  description?: string;
  achievements?: string[];
}

export function EducationSection({
  education = [],
  onUpdate,
}: EducationSectionProps) {
  const { t } = useLanguage();
  const [isAdding, setIsAdding] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [formData, setFormData] = useState<EducationFormData>({
    institution: '',
    degree: '',
    field: '',
    startDate: '',
    endDate: '',
    current: false,
    description: '',
    achievements: [],
  });

  const resetForm = () => {
    setFormData({
      institution: '',
      degree: '',
      field: '',
      startDate: '',
      endDate: '',
      current: false,
      description: '',
      achievements: [],
    });
    setIsAdding(false);
    setEditingId(null);
  };

  const handleAdd = () => {
    setIsAdding(true);
    setEditingId(null);
    resetForm();
  };

  const handleEdit = (edu: Education) => {
    setEditingId(edu.id);
    setIsAdding(false);
    setFormData({
      institution: edu.institution,
      degree: edu.degree,
      field: edu.field,
      startDate: edu.startDate,
      endDate: edu.endDate || '',
      current: edu.current || false,
      description: edu.description || '',
      achievements: edu.achievements || [],
    });
  };

  const handleSave = () => {
    if (
      !formData.institution ||
      !formData.degree ||
      !formData.field ||
      !formData.startDate
    ) {
      return;
    }

    const newEducation: Education = {
      id: editingId || `edu_${Date.now()}`,
      institution: formData.institution,
      degree: formData.degree,
      field: formData.field,
      startDate: formData.startDate,
      endDate: formData.current ? undefined : formData.endDate,
      current: formData.current,
      description: formData.description,
      achievements: formData.achievements?.filter(a => a.trim()) || [],
    };

    let updatedEducation: Education[];
    if (editingId) {
      updatedEducation = education.map(edu =>
        edu.id === editingId ? newEducation : edu
      );
    } else {
      updatedEducation = [...education, newEducation];
    }

    onUpdate(updatedEducation);
    resetForm();
  };

  const handleDelete = (id: string) => {
    if (
      window.confirm(
        t.confirmDeleteEducation ||
          'Are you sure you want to delete this education?'
      )
    ) {
      onUpdate(education.filter(edu => edu.id !== id));
    }
  };

  const handleAchievementChange = (value: string, index: number) => {
    const newAchievements = [...(formData.achievements || [])];
    newAchievements[index] = value;
    setFormData({ ...formData, achievements: newAchievements });
  };

  const addAchievement = () => {
    setFormData({
      ...formData,
      achievements: [...(formData.achievements || []), ''],
    });
  };

  const removeAchievement = (index: number) => {
    const newAchievements =
      formData.achievements?.filter((_, i) => i !== index) || [];
    setFormData({ ...formData, achievements: newAchievements });
  };

  const formatDateForDisplay = (dateString: string) => {
    if (!dateString) return '';
    try {
      return format(new Date(dateString), 'MMM yyyy');
    } catch {
      return dateString;
    }
  };

  const degreeExamples = [
    t.bachelorOfScience || 'Bachelor of Science',
    t.masterOfScience || 'Master of Science',
    t.bachelorOfArts || 'Bachelor of Arts',
    t.masterOfBusinessAdministration || 'MBA',
    t.doctorOfPhilosophy || 'Ph.D.',
    t.associateDegree || 'Associate Degree',
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">
            {t.education || 'Education'}
          </h3>
          <p className="text-sm text-muted-foreground">
            {t.educationDescription ||
              'Add your academic background and achievements'}
          </p>
        </div>
        {!isAdding && !editingId && (
          <Button onClick={handleAdd} size="sm">
            <Plus className="h-4 w-4 mr-2" />
            {t.addEducation || 'Add Education'}
          </Button>
        )}
      </div>

      {/* Education Form */}
      {(isAdding || editingId) && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">
              {editingId
                ? t.editEducation || 'Edit Education'
                : t.addEducation || 'Add Education'}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="institution">
                {t.institution || 'Institution'} *
              </Label>
              <Input
                id="institution"
                value={formData.institution}
                onChange={e =>
                  setFormData({ ...formData, institution: e.target.value })
                }
                placeholder={
                  t.institutionPlaceholder || 'e.g. Stanford University'
                }
              />
            </div>

            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="degree">{t.degree || 'Degree'} *</Label>
                <Input
                  id="degree"
                  value={formData.degree}
                  onChange={e =>
                    setFormData({ ...formData, degree: e.target.value })
                  }
                  placeholder={
                    t.degreePlaceholder || 'e.g. Bachelor of Science'
                  }
                  list="degree-suggestions"
                />
                <datalist id="degree-suggestions">
                  {degreeExamples.map((degree, index) => (
                    <option key={index} value={degree} />
                  ))}
                </datalist>
              </div>
              <div>
                <Label htmlFor="field">
                  {t.fieldOfStudy || 'Field of Study'} *
                </Label>
                <Input
                  id="field"
                  value={formData.field}
                  onChange={e =>
                    setFormData({ ...formData, field: e.target.value })
                  }
                  placeholder={t.fieldPlaceholder || 'e.g. Computer Science'}
                />
              </div>
            </div>

            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="startDate">
                  {t.startDate || 'Start Date'} *
                </Label>
                <Input
                  id="startDate"
                  type="month"
                  value={formData.startDate}
                  onChange={e =>
                    setFormData({ ...formData, startDate: e.target.value })
                  }
                />
              </div>
              <div>
                <div className="flex items-center justify-between mb-2">
                  <Label htmlFor="endDate">{t.endDate || 'End Date'}</Label>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="current"
                      checked={formData.current}
                      onCheckedChange={checked =>
                        setFormData({ ...formData, current: checked })
                      }
                    />
                    <Label
                      htmlFor="current"
                      className="text-sm font-normal cursor-pointer"
                    >
                      {t.currentlyStudying || 'Currently studying'}
                    </Label>
                  </div>
                </div>
                <Input
                  id="endDate"
                  type="month"
                  value={formData.endDate}
                  onChange={e =>
                    setFormData({ ...formData, endDate: e.target.value })
                  }
                  disabled={formData.current}
                />
              </div>
            </div>

            <div>
              <Label htmlFor="description">
                {t.description || 'Description'}
              </Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={e =>
                  setFormData({ ...formData, description: e.target.value })
                }
                placeholder={
                  t.educationDescriptionPlaceholder ||
                  'Describe your studies, focus areas, thesis topic, etc.'
                }
                rows={3}
              />
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <Label>{t.achievements || 'Achievements & Activities'}</Label>
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={addAchievement}
                >
                  <Plus className="h-4 w-4 mr-1" />
                  {t.addAchievement || 'Add'}
                </Button>
              </div>
              <div className="space-y-2">
                {formData.achievements?.map((achievement, index) => (
                  <div key={index} className="flex items-center gap-2">
                    <Input
                      value={achievement}
                      onChange={e =>
                        handleAchievementChange(e.target.value, index)
                      }
                      placeholder={
                        t.achievementPlaceholder ||
                        "e.g. Dean's List, GPA 3.9/4.0, Student Council President"
                      }
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      onClick={() => removeAchievement(index)}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={resetForm}>
                {t.cancel || 'Cancel'}
              </Button>
              <Button onClick={handleSave}>
                {editingId
                  ? t.saveChanges || 'Save Changes'
                  : t.addEducation || 'Add Education'}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Education List */}
      <div className="space-y-4">
        {education.map(edu => (
          <Card key={edu.id} className="group relative">
            <CardContent className="p-6">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <GraduationCap className="h-5 w-5 text-muted-foreground" />
                    <div>
                      <h4 className="font-semibold">
                        {edu.degree} in {edu.field}
                      </h4>
                      <p className="text-muted-foreground">{edu.institution}</p>
                    </div>
                    {edu.current && (
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                        {t.currentStudent || 'Current'}
                      </span>
                    )}
                  </div>
                  <p className="text-sm text-muted-foreground ml-8">
                    {formatDateForDisplay(edu.startDate)} -{' '}
                    {edu.current
                      ? t.present || 'Present'
                      : formatDateForDisplay(edu.endDate || '')}
                  </p>
                  {edu.description && (
                    <p className="mt-3 text-sm ml-8">{edu.description}</p>
                  )}
                  {edu.achievements && edu.achievements.length > 0 && (
                    <ul className="mt-3 space-y-1 ml-8">
                      {edu.achievements.map((achievement, i) => (
                        <li key={i} className="text-sm flex items-start">
                          <span className="text-primary mr-2"></span>
                          <span>{achievement}</span>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
                <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => handleEdit(edu)}
                  >
                    <Edit2 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => handleDelete(edu.id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {education.length === 0 && !isAdding && (
        <Card>
          <CardContent className="text-center py-12">
            <GraduationCap className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground mb-4">
              {t.noEducation || 'No education added yet'}
            </p>
            <Button onClick={handleAdd}>
              <Plus className="h-4 w-4 mr-2" />
              {t.addFirstEducation || 'Add Your Education'}
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Plus, Edit2, Trash2, X } from 'lucide-react';
import { format } from 'date-fns';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { ImageUpload } from '@/components/ui/image-upload';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { Experience, EmploymentType } from '@/types/portfolio';
import { usePortfolioStore } from '@/lib/store/portfolio-store';

interface ExperienceSectionProps {
  experiences: Experience[];
  onUpdate: (experiences: Experience[]) => void;
}

interface ExperienceFormData {
  company: string;
  companyLogo?: string;
  position: string;
  location?: string;
  employmentType?: EmploymentType;
  startDate: string;
  endDate?: string;
  current: boolean;
  description: string;
  highlights?: string[];
  technologies?: string[];
}

export function ExperienceSection({
  experiences = [],
  onUpdate,
}: ExperienceSectionProps) {
  const { t } = useLanguage();
  const { currentPortfolio } = usePortfolioStore();
  const [isAdding, setIsAdding] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [formData, setFormData] = useState<ExperienceFormData>({
    company: '',
    companyLogo: '',
    position: '',
    location: '',
    employmentType: 'full-time',
    startDate: '',
    endDate: '',
    current: false,
    description: '',
    highlights: [],
    technologies: [],
  });

  const employmentTypes: Array<{ value: EmploymentType; label: string }> = [
    { value: 'full-time', label: t.fullTime || 'Full-time' },
    { value: 'part-time', label: t.partTime || 'Part-time' },
    { value: 'contract', label: t.contract || 'Contract' },
    { value: 'freelance', label: t.freelance || 'Freelance' },
    { value: 'internship', label: t.internship || 'Internship' },
  ];

  const resetForm = () => {
    setFormData({
      company: '',
      position: '',
      location: '',
      employmentType: 'full-time',
      startDate: '',
      endDate: '',
      current: false,
      description: '',
      highlights: [],
      technologies: [],
    });
    setIsAdding(false);
    setEditingId(null);
  };

  const handleAdd = () => {
    setIsAdding(true);
    setEditingId(null);
    resetForm();
  };

  const handleEdit = (experience: Experience) => {
    setEditingId(experience.id);
    setIsAdding(false);
    setFormData({
      company: experience.company,
      position: experience.position,
      location: experience.location || '',
      employmentType: experience.employmentType || 'full-time',
      startDate: experience.startDate,
      endDate: experience.endDate || '',
      current: experience.current,
      description: experience.description,
      highlights: experience.highlights || [],
      technologies: experience.technologies || [],
    });
  };

  const handleSave = () => {
    if (!formData.company || !formData.position || !formData.startDate) {
      return;
    }

    const newExperience: Experience = {
      id: editingId || `exp_${Date.now()}`,
      company: formData.company,
      position: formData.position,
      startDate: formData.startDate,
      endDate: formData.current ? undefined : formData.endDate,
      current: formData.current,
      description: formData.description,
      highlights: formData.highlights?.filter(h => h.trim()) || [],
      technologies: formData.technologies?.filter(t => t.trim()) || [],
    };

    // Add additional fields as extended properties
    const extendedExperience = {
      ...newExperience,
      location: formData.location,
      employmentType: formData.employmentType,
    };

    let updatedExperiences: Experience[];
    if (editingId) {
      updatedExperiences = experiences.map(exp =>
        exp.id === editingId ? extendedExperience : exp
      );
    } else {
      updatedExperiences = [...experiences, extendedExperience];
    }

    onUpdate(updatedExperiences);
    resetForm();
  };

  const handleDelete = (id: string) => {
    if (
      // eslint-disable-next-line no-alert
      confirm(
        t.confirmDeleteExperience ||
          'Are you sure you want to delete this experience?'
      )
    ) {
      onUpdate(experiences.filter(exp => exp.id !== id));
    }
  };

  const handleHighlightChange = (value: string, index: number) => {
    const newHighlights = [...(formData.highlights || [])];
    newHighlights[index] = value;
    setFormData({ ...formData, highlights: newHighlights });
  };

  const addHighlight = () => {
    setFormData({
      ...formData,
      highlights: [...(formData.highlights || []), ''],
    });
  };

  const removeHighlight = (index: number) => {
    const newHighlights =
      formData.highlights?.filter((_, i) => i !== index) || [];
    setFormData({ ...formData, highlights: newHighlights });
  };

  const formatDateForDisplay = (dateString: string) => {
    if (!dateString) return '';
    try {
      return format(new Date(dateString), 'MMM yyyy');
    } catch {
      return dateString;
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">
            {t.workExperience || 'Work Experience'}
          </h3>
          <p className="text-sm text-muted-foreground">
            {t.workExperienceDescription ||
              'Add your professional work history'}
          </p>
        </div>
        {!isAdding && !editingId && (
          <Button onClick={handleAdd} size="sm">
            <Plus className="h-4 w-4 mr-2" />
            {t.addExperience || 'Add Experience'}
          </Button>
        )}
      </div>

      {/* Experience Form */}
      {(isAdding || editingId) && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">
              {editingId
                ? t.editExperience || 'Edit Experience'
                : t.addExperience || 'Add Experience'}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="company">{t.company || 'Company'} *</Label>
                <Input
                  id="company"
                  value={formData.company}
                  onChange={e =>
                    setFormData({ ...formData, company: e.target.value })
                  }
                  placeholder={t.companyPlaceholder || 'e.g. Google'}
                />
              </div>
              <div>
                <Label htmlFor="position">{t.position || 'Position'} *</Label>
                <Input
                  id="position"
                  value={formData.position}
                  onChange={e =>
                    setFormData({ ...formData, position: e.target.value })
                  }
                  placeholder={
                    t.positionPlaceholder || 'e.g. Senior Software Engineer'
                  }
                />
              </div>
            </div>

            <div>
              <Label>{t.companyLogo || 'Company Logo'}</Label>
              <ImageUpload
                value={formData.companyLogo}
                onChange={url =>
                  setFormData({ ...formData, companyLogo: url || '' })
                }
                type="company"
                portfolioId={currentPortfolio?.id || ''}
                aspectRatio="square"
                className="mt-2 max-w-[200px]"
              />
            </div>

            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="location">{t.location || 'Location'}</Label>
                <Input
                  id="location"
                  value={formData.location}
                  onChange={e =>
                    setFormData({ ...formData, location: e.target.value })
                  }
                  placeholder={
                    t.locationPlaceholder || 'e.g. San Francisco, CA'
                  }
                />
              </div>
              <div>
                <Label htmlFor="employmentType">
                  {t.employmentType || 'Employment Type'}
                </Label>
                <select
                  id="employmentType"
                  className="w-full px-3 py-2 border rounded-md"
                  value={formData.employmentType}
                  onChange={e =>
                    setFormData({
                      ...formData,
                      employmentType: e.target.value as EmploymentType,
                    })
                  }
                >
                  {employmentTypes.map(type => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="grid gap-4 md:grid-cols-2">
              <div>
                <Label htmlFor="startDate">
                  {t.startDate || 'Start Date'} *
                </Label>
                <Input
                  id="startDate"
                  type="month"
                  value={formData.startDate}
                  onChange={e =>
                    setFormData({ ...formData, startDate: e.target.value })
                  }
                />
              </div>
              <div>
                <div className="flex items-center justify-between mb-2">
                  <Label htmlFor="endDate">{t.endDate || 'End Date'}</Label>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="current"
                      checked={formData.current}
                      onCheckedChange={checked =>
                        setFormData({ ...formData, current: checked })
                      }
                    />
                    <Label
                      htmlFor="current"
                      className="text-sm font-normal cursor-pointer"
                    >
                      {t.currentlyWorking || 'Currently working here'}
                    </Label>
                  </div>
                </div>
                <Input
                  id="endDate"
                  type="month"
                  value={formData.endDate}
                  onChange={e =>
                    setFormData({ ...formData, endDate: e.target.value })
                  }
                  disabled={formData.current}
                />
              </div>
            </div>

            <div>
              <Label htmlFor="description">
                {t.description || 'Description'}
              </Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={e =>
                  setFormData({ ...formData, description: e.target.value })
                }
                placeholder={
                  t.descriptionPlaceholder ||
                  'Describe your role and responsibilities...'
                }
                rows={4}
              />
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <Label>{t.keyHighlights || 'Key Highlights'}</Label>
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={addHighlight}
                >
                  <Plus className="h-4 w-4 mr-1" />
                  {t.addHighlight || 'Add'}
                </Button>
              </div>
              <div className="space-y-2">
                {formData.highlights?.map((highlight, index) => (
                  <div key={index} className="flex items-center gap-2">
                    <Input
                      value={highlight}
                      onChange={e =>
                        handleHighlightChange(e.target.value, index)
                      }
                      placeholder={
                        t.highlightPlaceholder || 'e.g. Led team of 5 engineers'
                      }
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      onClick={() => removeHighlight(index)}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>
            </div>

            <div>
              <Label htmlFor="technologies">
                {t.technologies || 'Technologies Used'}
              </Label>
              <Input
                id="technologies"
                value={formData.technologies?.join(', ') || ''}
                onChange={e =>
                  setFormData({
                    ...formData,
                    technologies: e.target.value
                      .split(',')
                      .map(t => t.trim())
                      .filter(Boolean),
                  })
                }
                placeholder={
                  t.technologiesPlaceholder || 'e.g. React, Node.js, PostgreSQL'
                }
              />
              <p className="text-xs text-muted-foreground mt-1">
                {t.technologiesHint || 'Separate with commas'}
              </p>
            </div>

            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={resetForm}>
                {t.cancel || 'Cancel'}
              </Button>
              <Button onClick={handleSave}>
                {editingId
                  ? t.saveChanges || 'Save Changes'
                  : t.addExperience || 'Add Experience'}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Experience List */}
      <div className="space-y-4">
        {experiences.map(experience => (
          <Card key={experience.id} className="group relative">
            <CardContent className="p-6">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h4 className="font-semibold">{experience.position}</h4>
                    {experience.current && (
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                        {t.current || 'Current'}
                      </span>
                    )}
                  </div>
                  <p className="text-muted-foreground mb-1">
                    {experience.company}
                  </p>
                  <p className="text-sm text-muted-foreground">
                    {formatDateForDisplay(experience.startDate)} -{' '}
                    {experience.current
                      ? t.present || 'Present'
                      : formatDateForDisplay(experience.endDate || '')}
                    {experience.location && `  ${experience.location}`}
                  </p>
                  {experience.description && (
                    <p className="mt-3 text-sm">{experience.description}</p>
                  )}
                  {experience.highlights &&
                    experience.highlights.length > 0 && (
                      <ul className="mt-3 space-y-1">
                        {experience.highlights.map((highlight, i) => (
                          <li key={i} className="text-sm flex items-start">
                            <span className="text-primary mr-2"></span>
                            <span>{highlight}</span>
                          </li>
                        ))}
                      </ul>
                    )}
                  {experience.technologies &&
                    experience.technologies.length > 0 && (
                      <div className="mt-3 flex flex-wrap gap-2">
                        {experience.technologies.map((tech, i) => (
                          <span
                            key={i}
                            className="inline-flex items-center px-2 py-1 rounded-md text-xs font-medium bg-muted"
                          >
                            {tech}
                          </span>
                        ))}
                      </div>
                    )}
                </div>
                <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => handleEdit(experience)}
                  >
                    <Edit2 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => handleDelete(experience.id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {experiences.length === 0 && !isAdding && (
        <Card>
          <CardContent className="text-center py-12">
            <p className="text-muted-foreground mb-4">
              {t.noExperience || 'No work experience added yet'}
            </p>
            <Button onClick={handleAdd}>
              <Plus className="h-4 w-4 mr-2" />
              {t.addFirstExperience || 'Add Your First Experience'}
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

