interface ProjectsSectionProps {
  projects: Project[];
  onUpdate: (projects: Project[]) => void;
}

interface ProjectFormData {
  title: string;
  description: string;
  imageUrl?: string;
  projectUrl?: string;
  liveUrl?: string;
