// Sortable Project Item Component
function SortableProjectItem({
  project,
  index: _index,
  projectsLength: _projectsLength,
  onEdit,
  onDelete,
  t,
}: {
  project: Project;
  index: number;
  projectsLength: number;
  onEdit: (project: Project) => void;
  onDelete: (id: string) => void;
  t: Record<string, string>;
}) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: project.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <Card
      ref={setNodeRef}
      style={style}
      className={`group relative overflow-hidden ${isDragging ? 'opacity-50' : ''}`}
    >
      <CardContent className="p-6">
        <div className="flex items-start gap-4">
          {/* Drag Handle */}
          <div
            {...attributes}
            {...listeners}
            className="flex-shrink-0 mt-2 cursor-grab hover:text-primary focus:outline-none focus:text-primary"
          >
            <GripVertical className="h-5 w-5 text-muted-foreground" />
          </div>

          {/* Project Image */}
          {project.imageUrl && (
            <div className="w-32 h-24 bg-muted rounded-lg overflow-hidden flex-shrink-0">
              <img
                src={project.imageUrl}
                alt={project.title}
                className="w-full h-full object-cover"
              />
            </div>
          )}

          {/* Project Content */}
          <div className="flex-1">
            <div className="flex items-start justify-between mb-2">
              <div>
                <div className="flex items-center gap-2 mb-1">
                  <h4 className="font-semibold">{project.title}</h4>
                  {project.featured && (
                    <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                      <Star className="h-3 w-3 mr-1" />
                      {t.featured || 'Featured'}
                    </span>
                  )}
                </div>
                <p className="text-sm text-muted-foreground mb-3">
                  {project.description}
                </p>
              </div>
              <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={() => onEdit(project)}
                >
                  <Edit2 className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={() => onDelete(project.id)}
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            </div>

            {/* Project Highlights */}
            {project.highlights && project.highlights.length > 0 && (
              <ul className="mb-3 space-y-1">
                {project.highlights.map((highlight, i) => (
                  <li key={i} className="text-sm flex items-start">
                    <span className="text-primary mr-2"></span>
                    <span>{highlight}</span>
                  </li>
                ))}
              </ul>
            )}

            {/* Technologies and Links */}
            <div className="flex items-center gap-4">
              {project.technologies && project.technologies.length > 0 && (
                <div className="flex flex-wrap gap-2 flex-1">
                  {project.technologies.map((tech, i) => (
                    <span
                      key={i}
                      className="inline-flex items-center px-2 py-1 rounded-md text-xs font-medium bg-muted"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              )}
              <div className="flex items-center gap-2">
                {project.githubUrl && (
                  <a
                    href={project.githubUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-muted-foreground hover:text-foreground"
                    title="View on GitHub"
                  >
                    <Github className="h-4 w-4" />
                  </a>
                )}
                {project.liveUrl && (
                  <a
                    href={project.liveUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-muted-foreground hover:text-foreground"
                    title="View live demo"
                  >
                    <ExternalLink className="h-4 w-4" />
                  </a>
                )}
                {project.projectUrl && (
                  <a
                    href={project.projectUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-muted-foreground hover:text-foreground"
                    title="View project"
                  >
                    <Link className="h-4 w-4" />
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

export function ProjectsSection({
  projects = [],
  onUpdate,
}: ProjectsSectionProps) {
  const { t } = useLanguage();
  const { toast } = useToast();
  const { currentPortfolio } = usePortfolioStore();
  const [isAdding, setIsAdding] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [formData, setFormData] = useState<ProjectFormData>({
    title: '',
    description: '',
    imageUrl: '',
    projectUrl: '',
    liveUrl: '',
    githubUrl: '',
    technologies: [],
    highlights: [],
    featured: false,
  });

  // Drag and drop sensors
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      imageUrl: '',
      projectUrl: '',
      liveUrl: '',
      githubUrl: '',
      technologies: [],
      highlights: [],
      featured: false,
    });
    setIsAdding(false);
    setEditingId(null);
  };

  const handleAdd = () => {
    setIsAdding(true);
    setEditingId(null);
    resetForm();
  };

  const handleEdit = (project: Project) => {
    setEditingId(project.id);
    setIsAdding(false);
    setFormData({
      title: project.title,
      description: project.description,
      imageUrl: project.imageUrl || '',
      projectUrl: project.projectUrl || '',
      liveUrl: project.liveUrl || '',
      githubUrl: project.githubUrl || '',
      technologies: project.technologies || [],
      highlights: project.highlights || [],
      featured: project.featured || false,
    });
  };

  const handleSave = () => {
    if (!formData.title || !formData.description) {
      toast({
        title: t.error || 'Error',
        description:
          t.fillRequiredFields || 'Please fill in all required fields',
        variant: 'destructive',
      });
      return;
    }

    const newProject: Project = {
      id: editingId || `proj_${Date.now()}`,
      title: formData.title,
      description: formData.description,
      imageUrl: formData.imageUrl || undefined,
      projectUrl: formData.projectUrl || undefined,
      liveUrl: formData.liveUrl || undefined,
      githubUrl: formData.githubUrl || undefined,
      technologies: formData.technologies.filter(t => t.trim()),
      highlights: formData.highlights?.filter(h => h.trim()) || [],
      featured: formData.featured,
      order: editingId
        ? projects.find(p => p.id === editingId)?.order
        : projects.length,
    };

    let updatedProjects: Project[];
    if (editingId) {
      updatedProjects = projects.map(proj =>
        proj.id === editingId ? newProject : proj
      );
    } else {
      updatedProjects = [...projects, newProject];
    }

    onUpdate(updatedProjects);
    resetForm();
  };

  const handleDelete = (id: string) => {
    if (
      window.confirm(
        t.confirmDeleteProject ||
          'Are you sure you want to delete this project?'
      )
    ) {
      onUpdate(projects.filter(proj => proj.id !== id));
    }
  };

  const handleHighlightChange = (value: string, index: number) => {
    const newHighlights = [...(formData.highlights || [])];
    newHighlights[index] = value;
    setFormData({ ...formData, highlights: newHighlights });
  };

  const addHighlight = () => {
    setFormData({
      ...formData,
      highlights: [...(formData.highlights || []), ''],
    });
  };

  const removeHighlight = (index: number) => {
    const newHighlights =
      formData.highlights?.filter((_, i) => i !== index) || [];
    setFormData({ ...formData, highlights: newHighlights });
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;

    if (active.id !== over?.id) {
      const oldIndex = projects.findIndex(project => project.id === active.id);
      const newIndex = projects.findIndex(project => project.id === over?.id);

      const newProjects = arrayMove(projects, oldIndex, newIndex);

      // Update order values
      const updatedProjects = newProjects.map((proj, i) => ({
        ...proj,
        order: i,
      }));

      onUpdate(updatedProjects);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">{t.projects || 'Projects'}</h3>
          <p className="text-sm text-muted-foreground">
            {t.projectsDescription ||
              'Showcase your best work and accomplishments'}
          </p>
        </div>
        {!isAdding && !editingId && (
          <Button onClick={handleAdd} size="sm">
            <Plus className="h-4 w-4 mr-2" />
            {t.addProject || 'Add Project'}
          </Button>
        )}
      </div>

      {/* Project Form */}
      {(isAdding || editingId) && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">
              {editingId
                ? t.editProject || 'Edit Project'
                : t.addProject || 'Add Project'}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="title">
                {t.projectTitle || 'Project Title'} *
              </Label>
              <Input
                id="title"
                value={formData.title}
                onChange={e =>
                  setFormData({ ...formData, title: e.target.value })
                }
                placeholder={
                  t.projectTitlePlaceholder || 'e.g. E-commerce Platform'
                }
              />
            </div>

            <div>
              <Label htmlFor="description">
                {t.description || 'Description'} *
              </Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={e =>
                  setFormData({ ...formData, description: e.target.value })
                }
                placeholder={
                  t.projectDescriptionPlaceholder ||
                  'Describe what the project does, your role, and the impact...'
                }
                rows={4}
              />
            </div>

            <div>
              <Label>{t.projectImage || 'Project Image'}</Label>
              <ImageUpload
                value={formData.imageUrl}
                onChange={url =>
                  setFormData({ ...formData, imageUrl: url || '' })
                }
                type="project"
                portfolioId={currentPortfolio?.id || ''}
                aspectRatio="video"
                className="mt-2"
              />
            </div>

            <div className="grid gap-4 md:grid-cols-3">
              <div>
                <Label htmlFor="projectUrl">
                  {t.projectUrl || 'Project URL'}
                </Label>
                <Input
                  id="projectUrl"
                  type="url"
                  value={formData.projectUrl}
                  onChange={e =>
                    setFormData({ ...formData, projectUrl: e.target.value })
                  }
                  placeholder="https://..."
                />
              </div>
              <div>
                <Label htmlFor="liveUrl">{t.liveUrl || 'Live Demo URL'}</Label>
                <Input
                  id="liveUrl"
                  type="url"
                  value={formData.liveUrl}
                  onChange={e =>
                    setFormData({ ...formData, liveUrl: e.target.value })
                  }
                  placeholder="https://..."
                />
              </div>
              <div>
                <Label htmlFor="githubUrl">{t.githubUrl || 'GitHub URL'}</Label>
                <Input
                  id="githubUrl"
                  type="url"
                  value={formData.githubUrl}
                  onChange={e =>
                    setFormData({ ...formData, githubUrl: e.target.value })
                  }
                  placeholder="https://github.com/..."
                />
              </div>
            </div>

            <div>
              <Label htmlFor="technologies">
                {t.technologies || 'Technologies Used'} *
              </Label>
              <Input
                id="technologies"
                value={formData.technologies.join(', ')}
                onChange={e =>
                  setFormData({
                    ...formData,
                    technologies: e.target.value
                      .split(',')
                      .map(t => t.trim())
                      .filter(Boolean),
                  })
                }
                placeholder={
                  t.technologiesPlaceholder || 'e.g. React, Node.js, PostgreSQL'
                }
              />
              <p className="text-xs text-muted-foreground mt-1">
                {t.separateWithCommas || 'Separate with commas'}
              </p>
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <Label>{t.keyHighlights || 'Key Highlights'}</Label>
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={addHighlight}
                >
                  <Plus className="h-4 w-4 mr-1" />
                  {t.addHighlight || 'Add'}
                </Button>
              </div>
              <div className="space-y-2">
                {formData.highlights?.map((highlight, index) => (
                  <div key={index} className="flex items-center gap-2">
                    <Input
                      value={highlight}
                      onChange={e =>
                        handleHighlightChange(e.target.value, index)
                      }
                      placeholder={
                        t.highlightPlaceholder ||
                        'e.g. Increased performance by 50%'
                      }
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      onClick={() => removeHighlight(index)}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <Switch
                id="featured"
                checked={formData.featured}
                onCheckedChange={checked =>
                  setFormData({ ...formData, featured: checked })
                }
              />
              <Label htmlFor="featured" className="cursor-pointer">
                {t.featureThisProject || 'Feature this project'}
              </Label>
            </div>

            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={resetForm}>
                {t.cancel || 'Cancel'}
              </Button>
              <Button onClick={handleSave}>
                {editingId
                  ? t.saveChanges || 'Save Changes'
                  : t.addProject || 'Add Project'}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Projects List with Drag and Drop */}
      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={handleDragEnd}
      >
        <div className="space-y-4">
          <SortableContext
            items={projects.map(p => p.id)}
            strategy={verticalListSortingStrategy}
          >
            {projects
              .sort((a, b) => (a.order || 0) - (b.order || 0))
              .map((project, index) => (
                <SortableProjectItem
                  key={project.id}
                  project={project}
                  index={index}
                  projectsLength={projects.length}
                  onEdit={handleEdit}
                  onDelete={handleDelete}
                  t={t}
                />
              ))}
          </SortableContext>
        </div>
      </DndContext>

      {projects.length === 0 && !isAdding && (
        <Card>
          <CardContent className="text-center py-12">
            <p className="text-muted-foreground mb-4">
              {t.noProjects || 'No projects added yet'}
            </p>
            <Button onClick={handleAdd}>
              <Plus className="h-4 w-4 mr-2" />
              {t.addFirstProject || 'Add Your First Project'}
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Plus, Edit2, X, Sparkles } from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { Skill } from '@/types/portfolio';
import { cn } from '@/lib/utils';

interface SkillsSectionProps {
  skills: Skill[];
  onUpdate: (skills: Skill[]) => void;
}

interface SkillFormData {
  name: string;
  level?: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  category?: string;
}

const DEFAULT_CATEGORIES = [
  'Programming Languages',
  'Frameworks & Libraries',
  'Databases',
  'Tools & Platforms',
  'Design',
  'Soft Skills',
  'Languages',
];

const SKILL_LEVELS = [
  { value: 'beginner', label: 'Beginner', color: 'bg-gray-100 text-gray-800' },
  {
    value: 'intermediate',
    label: 'Intermediate',
    color: 'bg-blue-100 text-blue-800',
  },
  {
    value: 'advanced',
    label: 'Advanced',
    color: 'bg-green-100 text-green-800',
  },
  { value: 'expert', label: 'Expert', color: 'bg-purple-100 text-purple-800' },
];

export function SkillsSection({ skills = [], onUpdate }: SkillsSectionProps) {
  const { t } = useLanguage();
  const [isAdding, setIsAdding] = useState(false);
  const [editingSkill, setEditingSkill] = useState<string | null>(null);
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [formData, setFormData] = useState<SkillFormData>({
    name: '',
    level: 'intermediate',
    category: '',
  });

  // Get unique categories from existing skills plus defaults
  const categories = Array.from(
    new Set([
      ...DEFAULT_CATEGORIES,
      ...(skills.map(s => s.category).filter(Boolean) as string[]),
    ])
  ).sort();

  const resetForm = () => {
    setFormData({
      name: '',
      level: 'intermediate',
      category: selectedCategory || '',
    });
    setIsAdding(false);
    setEditingSkill(null);
  };

  const handleAdd = (category?: string) => {
    setSelectedCategory(category || '');
    setFormData({
      name: '',
      level: 'intermediate',
      category: category || '',
    });
    setIsAdding(true);
    setEditingSkill(null);
  };

  const handleEdit = (skill: Skill) => {
    setEditingSkill(skill.name);
    setFormData({
      name: skill.name,
      level: skill.level || 'intermediate',
      category: skill.category || '',
    });
    setIsAdding(true);
  };

  const handleSave = () => {
    if (!formData.name.trim()) return;

    const newSkill: Skill = {
      name: formData.name.trim(),
      level: formData.level,
      category: formData.category || 'Other',
    };

    let updatedSkills: Skill[];
    if (editingSkill) {
      updatedSkills = skills.map(skill =>
        skill.name === editingSkill ? newSkill : skill
      );
    } else {
      // Check if skill already exists
      if (
        skills.some(s => s.name.toLowerCase() === newSkill.name.toLowerCase())
      ) {
        return;
      }
      updatedSkills = [...skills, newSkill];
    }

    onUpdate(updatedSkills);
    resetForm();
  };

  const handleDelete = (skillName: string) => {
    onUpdate(skills.filter(skill => skill.name !== skillName));
  };

  const handleQuickAdd = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && formData.name.trim()) {
      handleSave();
    }
  };

  const handleBulkAdd = () => {
    // eslint-disable-next-line no-alert
    const skillsText = prompt(
      t.bulkAddPrompt ||
        'Enter skills separated by commas (e.g., React, Node.js, TypeScript):'
    );

    if (!skillsText) return;

    const newSkills = skillsText
      .split(',')
      .map(s => s.trim())
      .filter(Boolean)
      .filter(
        name => !skills.some(s => s.name.toLowerCase() === name.toLowerCase())
      )
      .map(name => ({
        name,
        level: 'intermediate' as const,
        category: selectedCategory || 'Other',
      }));

    if (newSkills.length > 0) {
      onUpdate([...skills, ...newSkills]);
    }
  };

  const groupedSkills = skills.reduce(
    (acc, skill) => {
      const category = skill.category || 'Other';
      if (!acc[category]) acc[category] = [];
      acc[category].push(skill);
      return acc;
    },
    {} as Record<string, Skill[]>
  );

  const getLevelInfo = (level?: string) => {
    return SKILL_LEVELS.find(l => l.value === level) || SKILL_LEVELS[1];
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">{t.skills || 'Skills'}</h3>
          <p className="text-sm text-muted-foreground">
            {t.skillsDescription ||
              'List your professional skills and expertise'}
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Button onClick={handleBulkAdd} size="sm" variant="outline">
            <Sparkles className="h-4 w-4 mr-2" />
            {t.bulkAdd || 'Bulk Add'}
          </Button>
          {!isAdding && (
            <Button onClick={() => handleAdd()} size="sm">
              <Plus className="h-4 w-4 mr-2" />
              {t.addSkill || 'Add Skill'}
            </Button>
          )}
        </div>
      </div>

      {/* Quick Add Form */}
      {isAdding && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">
              {editingSkill
                ? t.editSkill || 'Edit Skill'
                : t.addSkill || 'Add Skill'}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid gap-4 md:grid-cols-3">
              <div>
                <Label htmlFor="skillName">
                  {t.skillName || 'Skill Name'} *
                </Label>
                <Input
                  id="skillName"
                  value={formData.name}
                  onChange={e =>
                    setFormData({ ...formData, name: e.target.value })
                  }
                  onKeyDown={handleQuickAdd}
                  placeholder={t.skillPlaceholder || 'e.g. JavaScript'}
                  autoFocus
                />
              </div>
              <div>
                <Label htmlFor="category">{t.category || 'Category'}</Label>
                <select
                  id="category"
                  className="w-full px-3 py-2 border rounded-md"
                  value={formData.category}
                  onChange={e =>
                    setFormData({ ...formData, category: e.target.value })
                  }
                >
                  <option value="">
                    {t.selectCategory || 'Select category'}
                  </option>
                  {categories.map(cat => (
                    <option key={cat} value={cat}>
                      {cat}
                    </option>
                  ))}
                  <option value="new">
                    {t.newCategory || '+ New category'}
                  </option>
                </select>
              </div>
              <div>
                <Label htmlFor="level">
                  {t.proficiencyLevel || 'Proficiency Level'}
                </Label>
                <select
                  id="level"
                  className="w-full px-3 py-2 border rounded-md"
                  value={formData.level}
                  onChange={e =>
                    setFormData({
                      ...formData,
                      level: e.target.value as
                        | 'beginner'
                        | 'intermediate'
                        | 'advanced'
                        | 'expert',
                    })
                  }
                >
                  {SKILL_LEVELS.map(level => (
                    <option key={level.value} value={level.value}>
                      {level.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {formData.category === 'new' && (
              <div>
                <Label htmlFor="newCategory">
                  {t.newCategoryName || 'New Category Name'}
                </Label>
                <Input
                  id="newCategory"
                  placeholder={t.enterCategoryName || 'Enter category name'}
                  onChange={e =>
                    setFormData({ ...formData, category: e.target.value })
                  }
                />
              </div>
            )}

            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={resetForm}>
                {t.cancel || 'Cancel'}
              </Button>
              <Button onClick={handleSave} disabled={!formData.name.trim()}>
                {editingSkill
                  ? t.saveChanges || 'Save Changes'
                  : t.addSkill || 'Add Skill'}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Skills by Category */}
      <div className="space-y-6">
        {Object.entries(groupedSkills).map(([category, categorySkills]) => (
          <Card key={category}>
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <CardTitle className="text-base">{category}</CardTitle>
                <span className="text-sm text-muted-foreground">
                  {categorySkills.length}{' '}
                  {categorySkills.length === 1
                    ? t.skill || 'skill'
                    : t.skills || 'skills'}
                </span>
              </div>
            </CardHeader>
            <CardContent>
              <div className="flex flex-wrap gap-2">
                {categorySkills.map(skill => {
                  const levelInfo = getLevelInfo(skill.level);
                  return (
                    <div
                      key={skill.name}
                      className="group relative inline-flex items-center"
                    >
                      <Badge
                        variant="secondary"
                        className={cn(
                          'pr-8 transition-colors',
                          skill.level && levelInfo?.color
                        )}
                      >
                        {skill.name}
                        {skill.level && (
                          <span className="ml-2 text-xs opacity-75">
                            ({levelInfo?.label})
                          </span>
                        )}
                      </Badge>
                      <div className="absolute right-1 flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                        <button
                          onClick={() => handleEdit(skill)}
                          className="p-1 hover:bg-muted rounded"
                          title={t.edit || 'Edit'}
                        >
                          <Edit2 className="h-3 w-3" />
                        </button>
                        <button
                          onClick={() => handleDelete(skill.name)}
                          className="p-1 hover:bg-muted rounded"
                          title={t.delete || 'Delete'}
                        >
                          <X className="h-3 w-3" />
                        </button>
                      </div>
                    </div>
                  );
                })}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleAdd(category)}
                  className="h-8"
                >
                  <Plus className="h-3 w-3 mr-1" />
                  {t.add || 'Add'}
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {skills.length === 0 && !isAdding && (
        <Card>
          <CardContent className="text-center py-12">
            <p className="text-muted-foreground mb-4">
              {t.noSkills || 'No skills added yet'}
            </p>
            <div className="flex justify-center gap-2">
              <Button onClick={() => handleAdd()}>
                <Plus className="h-4 w-4 mr-2" />
                {t.addFirstSkill || 'Add Your First Skill'}
              </Button>
              <Button variant="outline" onClick={handleBulkAdd}>
                <Sparkles className="h-4 w-4 mr-2" />
                {t.bulkAdd || 'Bulk Add'}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Skill Level Legend */}
      {skills.length > 0 && (
        <Card>
          <CardContent className="py-4">
            <p className="text-sm text-muted-foreground mb-2">
              {t.proficiencyLevels || 'Proficiency Levels:'}
            </p>
            <div className="flex flex-wrap gap-2">
              {SKILL_LEVELS.map(level => (
                <Badge
                  key={level.value}
                  variant="secondary"
                  className={level.color}
                >
                  {level.label}
                </Badge>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

