            <div className="space-y-8">
              {portfolio.experience.map((exp, index) => (
                <div
                  key={index}
                  className="border-l-2 border-gray-700 pl-6 hover:border-cyan-400 transition-colors"
                >
                  <h3 className="text-xl font-semibold text-white">
                    {exp.position}
                  </h3>
                  <p className="text-cyan-400 mb-2">{exp.company}</p>
                  <p className="text-sm text-gray-400 mb-3">
                    {exp.startDate} - {exp.current ? 'Present' : exp.endDate}
                    {exp.location && `  ${exp.location}`}
                  </p>
                  {exp.description && (
                    <p className="text-gray-300">{exp.description}</p>
                  )}
                  {exp.achievements && exp.achievements.length > 0 && (
                    <ul className="mt-3 space-y-1">
                      {exp.achievements.map((achievement, i) => (
                        <li key={i} className="text-gray-300 flex items-start">
                          <span className="text-cyan-400 mr-2"></span>
                          {achievement}
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              ))}
            </div>
          </div>
        </section>
      ),

    projects: () =>
      portfolio.projects?.length > 0 && (
        <section className="py-20 px-6">
          <div className="max-w-6xl mx-auto">
            <h2 className="text-3xl font-bold mb-12 text-cyan-400">Projects</h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {portfolio.projects.map((project, index) => (
                <div
                  key={index}
                  className="bg-gray-900 border border-gray-800 rounded-lg p-6 hover:border-cyan-400 transition-all"
                >
                  <h3 className="text-xl font-semibold mb-3">
                    {project.title}
                  </h3>
                  <p className="text-gray-400 mb-4 line-clamp-3">
                    {project.description}
                  </p>
                  {project.technologies && (
                    <div className="flex flex-wrap gap-2 mb-4">
                      {project.technologies.map((tech, i) => (
                        <span
                          key={i}
                          className="text-xs px-2 py-1 bg-gray-800 text-cyan-400 rounded"
                        >
                          {tech}
                        </span>
                      ))}
                    </div>
                  )}
                  {(project.projectUrl ||
                    project.liveUrl ||
                    project.githubUrl) && (
                    <a
                      href={
                        project.projectUrl ||
                        project.liveUrl ||
                        project.githubUrl ||
                        '#'
                      }
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center text-cyan-400 hover:text-cyan-300"
                    >
                      View Project <ArrowUpRight className="w-4 h-4 ml-1" />
                    </a>
                  )}
                </div>
              ))}
            </div>
          </div>
        </section>
      ),

    skills: () =>
      portfolio.skills?.length > 0 && (
        <section className="py-20 px-6">
          <div className="max-w-4xl mx-auto">
            <h2 className="text-3xl font-bold mb-12 text-cyan-400">Skills</h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
              {portfolio.skills.map((skill, index) => {
                const skillName =
                  typeof skill === 'string' ? skill : skill.name;
                const skillLevel =
                  typeof skill === 'object' && skill.level
                    ? skill.level
                    : 'advanced';
                const category =
                  typeof skill === 'object' && skill.category
                    ? skill.category
                    : 'General';

                return (
                  <div
                    key={index}
                    className="bg-gray-900 border border-gray-700 rounded-lg p-4 hover:border-cyan-400 transition-colors"
                  >
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-white font-medium">
                        {skillName}
                      </span>
                      <span className="text-cyan-400 text-sm capitalize">
                        {skillLevel}
                      </span>
                    </div>
                    <div className="text-xs text-gray-400">{category}</div>
                    <div className="w-full bg-gray-700 rounded-full h-2 mt-2">
                      <div
                        className="bg-gradient-to-r from-cyan-400 to-blue-500 h-2 rounded-full transition-all duration-1000"
                        style={{
                          width:
                            skillLevel === 'expert'
                              ? '95%'
                              : skillLevel === 'advanced'
                                ? '85%'
                                : skillLevel === 'intermediate'
                                  ? '70%'
                                  : '60%',
                        }}
                      />
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </section>
      ),

    education: () =>
      portfolio.education?.length > 0 && (
        <section className="py-20 px-6">
          <div className="max-w-4xl mx-auto">
            <h2 className="text-3xl font-bold mb-12 text-cyan-400">
              Education
            </h2>
            <div className="space-y-6">
              {portfolio.education.map((edu, index) => (
                <div key={index} className="border-l-2 border-gray-700 pl-6">
                  <h3 className="text-xl font-semibold">{edu.degree}</h3>
                  <p className="text-cyan-400">{edu.institution}</p>
                  <p className="text-sm text-gray-400">
                    {edu.startDate} - {edu.endDate}
                  </p>
                  {edu.description && (
                    <p className="text-gray-300 mt-2">{edu.description}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        </section>
      ),

    certifications: () =>
      portfolio.certifications?.length > 0 && (
        <section className="py-20 px-6">
          <div className="max-w-4xl mx-auto">
            <h2 className="text-3xl font-bold mb-12 text-cyan-400">
              Certifications
            </h2>
            <div className="grid md:grid-cols-2 gap-6">
              {portfolio.certifications.map((cert, index) => (
                <div
                  key={index}
                  className="bg-gray-900 border border-gray-800 rounded-lg p-6 hover:border-cyan-400 transition-all"
                >
                  <Trophy className="w-8 h-8 text-cyan-400 mb-3" />
                  <h3 className="text-lg font-semibold mb-2">{cert.name}</h3>
                  <p className="text-gray-400 text-sm">{cert.issuer}</p>
                  <p className="text-gray-500 text-xs mt-1">{cert.date}</p>
                  {(cert.credentialUrl || cert.imageUrl) && (
                    <a
                      href={cert.credentialUrl || cert.imageUrl || '#'}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center text-cyan-400 hover:text-cyan-300 mt-3 text-sm"
                    >
                      View Certificate <ExternalLink className="w-3 h-3 ml-1" />
                    </a>
                  )}
                </div>
              ))}
            </div>
          </div>
        </section>
      ),

    contact: () => (
      <section className="py-20 px-6">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-3xl font-bold mb-8 text-cyan-400">
            Get In Touch
          </h2>
          <p className="text-gray-300 mb-8">
            I'm always open to discussing new opportunities and interesting
            projects.
          </p>
          <div className="flex justify-center gap-6">
            {portfolio.contact?.email && (
              <a
                href={`mailto:${portfolio.contact.email}`}
                className="inline-flex items-center px-6 py-3 bg-cyan-400 text-black font-medium rounded-lg hover:bg-cyan-300 transition-colors"
              >
                <Mail className="w-5 h-5 mr-2" />
                Email Me
              </a>
            )}
            {portfolio.social?.linkedin && (
              <a
                href={portfolio.social.linkedin}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center px-6 py-3 border border-cyan-400 text-cyan-400 font-medium rounded-lg hover:bg-cyan-400 hover:text-black transition-all"
              >
                <Linkedin className="w-5 h-5 mr-2" />
                LinkedIn
              </a>
            )}
          </div>
        </div>
      </section>
    ),
  };

  return (
    <div className="min-h-screen bg-black text-white" style={getThemeStyles()}>
      <style jsx global>{`
        body {
          font-family: var(--font-family);
          font-size: var(--font-size);
        }

        .portfolio-section {
          padding: var(--spacing) 0;
        }

        * {
          --tw-border-radius: var(--border-radius);
        }

        /* Custom scrollbar for dark theme */
        ::-webkit-scrollbar {
          width: 8px;
        }

        ::-webkit-scrollbar-track {
          background: #1a1a1a;
        }

        ::-webkit-scrollbar-thumb {
          background: #333;
          border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: #555;
        }
      `}</style>

      {renderSections(portfolio, sectionRenderMap)}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  ArrowRight,
  ArrowLeft,
  Check,
  Sparkles,
  Briefcase,
  Code,
  Palette,
  GraduationCap,
  Camera,
  Building,
  Zap,
  Layout,
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { useLanguage } from '@/lib/i18n/refactored-context';
import { cn } from '@/lib/utils';
import { TemplateType } from '@/types/portfolio';

interface TemplateSelectionWizardProps {
  onSelect: (template: TemplateType) => void;
  onCancel?: () => void;
  currentTemplate?: TemplateType;
}

interface TemplateOption {
  id: TemplateType;
  name: string;
  description: string;
  icon: React.ReactNode;
  features: string[];
  industries: string[];
  preview: string;
  recommended?: boolean;
}

const templates: TemplateOption[] = [
  {
    id: 'modern',
    name: 'Modern',
    description: 'Sleek design with dark theme and glassmorphism effects',
    icon: <Zap className="w-6 h-6" />,
    features: ['Dark theme', 'Animations', 'Grid layout', 'Tech-focused'],
    industries: ['Technology', 'Startup', 'Developer'],
    preview: '/templates/modern-preview.png',
    recommended: true,
  },
  {
    id: 'minimal',
    name: 'Minimal',
    description: 'Clean and focused on typography with plenty of whitespace',
    icon: <Layout className="w-6 h-6" />,
    features: ['Clean design', 'Typography', 'Simple layout', 'Content-first'],
    industries: ['Writer', 'Designer', 'Consultant'],
    preview: '/templates/minimal-preview.png',
  },
  {
    id: 'business',
    name: 'Business',
    description: 'Professional look with structured sections and metrics',
    icon: <Briefcase className="w-6 h-6" />,
    features: ['Corporate', 'Metrics', 'Testimonials', 'Professional'],
    industries: ['Consultant', 'Executive', 'Sales'],
    preview: '/templates/business-preview.png',
  },
  {
    id: 'creative',
    name: 'Creative',
    description: 'Vibrant and artistic with visual portfolio focus',
    icon: <Palette className="w-6 h-6" />,
    features: ['Colorful', 'Gallery', 'Visual focus', 'Artistic'],
    industries: ['Designer', 'Artist', 'Photographer'],
    preview: '/templates/creative-preview.png',
  },
  {
    id: 'developer',
    name: 'Developer',
    description: 'Code-focused with GitHub integration and project showcase',
    icon: <Code className="w-6 h-6" />,
    features: ['Code blocks', 'GitHub stats', 'Tech stack', 'Projects'],
    industries: ['Developer', 'Engineer', 'DevOps'],
    preview: '/templates/developer-preview.png',
  },
  {
    id: 'educator',
    name: 'Educator',
    description: 'Academic style with publications and course sections',
    icon: <GraduationCap className="w-6 h-6" />,
    features: ['Publications', 'Courses', 'Research', 'Academic'],
    industries: ['Teacher', 'Professor', 'Researcher'],
    preview: '/templates/educator-preview.png',
  },
];

type WizardStep = 'industry' | 'style' | 'features' | 'preview';

interface WizardState {
  industry?: string;
  style?: 'visual' | 'content' | 'balanced';
  features?: string[];
  recommendation?: TemplateType;
}

export function TemplateSelectionWizard({
  onSelect,
  onCancel,
  currentTemplate,
}: TemplateSelectionWizardProps) {
  const { t } = useLanguage();
  const [currentStep, setCurrentStep] = useState<WizardStep>('industry');
  const [wizardState, setWizardState] = useState<WizardState>({});
  const [selectedTemplate, setSelectedTemplate] = useState<TemplateType | null>(
    currentTemplate || null
  );

  const steps: WizardStep[] = ['industry', 'style', 'features', 'preview'];
  const currentStepIndex = steps.indexOf(currentStep);

  const handleNext = () => {
    if (currentStepIndex < steps.length - 1) {
      const nextStep = steps[currentStepIndex + 1];
      if (nextStep) {
        setCurrentStep(nextStep);

        // Calculate recommendation after features step
        if (currentStep === 'features') {
          const recommendation = calculateRecommendation(wizardState);
          setWizardState({ ...wizardState, recommendation });
          setSelectedTemplate(recommendation);
        }
      }
    }
  };

  const handleBack = () => {
    if (currentStepIndex > 0) {
      const prevStep = steps[currentStepIndex - 1];
      if (prevStep) {
        setCurrentStep(prevStep);
      }
    }
  };

  const handleFinish = () => {
    if (selectedTemplate) {
      onSelect(selectedTemplate);
    }
  };

  const calculateRecommendation = (state: WizardState): TemplateType => {
    // Simple scoring algorithm
    const scores: Record<TemplateType, number> = {
      modern: 0,
      minimal: 0,
      business: 0,
      creative: 0,
      developer: 0,
      educator: 0,
      designer: 0,
      consultant: 0,
    };

    // Industry matching
    templates.forEach(template => {
      if (state.industry && template.industries.includes(state.industry)) {
        scores[template.id] += 3;
      }
    });

    // Style preference
    if (state.style === 'visual') {
      scores.creative += 2;
      scores.designer += 2;
      scores.modern += 1;
    } else if (state.style === 'content') {
      scores.minimal += 2;
      scores.educator += 2;
      scores.business += 1;
    } else {
      scores.modern += 1;
      scores.business += 1;
    }

    // Feature matching
    if (state.features?.includes('portfolio')) {
      scores.creative += 1;
      scores.designer += 1;
    }
    if (state.features?.includes('metrics')) {
      scores.business += 1;
      scores.consultant += 1;
    }
    if (state.features?.includes('code')) {
      scores.developer += 2;
      scores.modern += 1;
    }

    // Find highest score
    const bestTemplate = Object.entries(scores)
      .filter(([template]) => templates.some(t => t.id === template))
      .reduce(
        (best, [template, score]) =>
          score > best[1] ? [template as TemplateType, score] : best,
        ['modern' as TemplateType, 0]
      )[0];

    return bestTemplate as TemplateType;
  };

  const industries = [
    {
      value: 'Technology',
      label: t.industryTech || 'Technology',
      icon: <Code />,
    },
    { value: 'Design', label: t.industryDesign || 'Design', icon: <Palette /> },
    {
      value: 'Business',
      label: t.industryBusiness || 'Business',
      icon: <Building />,
    },
    {
      value: 'Education',
      label: t.industryEducation || 'Education',
      icon: <GraduationCap />,
    },
    {
      value: 'Creative',
      label: t.industryCreative || 'Creative Arts',
      icon: <Camera />,
    },
  ];

  const styleOptions = [
    {
      value: 'visual',
      label: t.styleVisual || 'Visual & Creative',
      description:
        t.styleVisualDesc || 'Image-heavy, colorful, artistic layouts',
    },
    {
      value: 'content',
      label: t.styleContent || 'Content-Focused',
      description:
        t.styleContentDesc || 'Text-heavy, minimal distractions, clean',
    },
    {
      value: 'balanced',
      label: t.styleBalanced || 'Balanced',
      description: t.styleBalancedDesc || 'Mix of visuals and content',
    },
  ];

  const featureOptions = [
    { value: 'portfolio', label: t.featurePortfolio || 'Project Portfolio' },
    { value: 'blog', label: t.featureBlog || 'Blog/Articles' },
    { value: 'metrics', label: t.featureMetrics || 'Achievements & Metrics' },
    { value: 'testimonials', label: t.featureTestimonials || 'Testimonials' },
    { value: 'code', label: t.featureCode || 'Code Samples' },
    { value: 'resume', label: t.featureResume || 'Downloadable Resume' },
  ];

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        className="w-full max-w-4xl mx-4"
      >
        <Card className="overflow-hidden">
          <CardHeader className="border-b">
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>
                  {t.selectTemplate || 'Select Your Template'}
                </CardTitle>
                <p className="text-sm text-muted-foreground mt-1">
                  {t.wizardSubtitle ||
                    'Let us help you find the perfect template'}
                </p>
              </div>
              <Button variant="ghost" size="sm" onClick={onCancel}>
                {t.skip || 'Skip'}
              </Button>
            </div>

            {/* Progress indicators */}
            <div className="flex items-center gap-2 mt-4">
              {steps.map((step, index) => (
                <div
                  key={step}
                  className={cn(
                    'flex-1 h-2 rounded-full transition-colors',
                    index <= currentStepIndex ? 'bg-primary' : 'bg-muted'
                  )}
                />
              ))}
            </div>
          </CardHeader>

          <CardContent className="p-6">
            <AnimatePresence mode="wait">
              {/* Industry Selection */}
              {currentStep === 'industry' && (
                <motion.div
                  key="industry"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  className="space-y-6"
                >
                  <div>
                    <h3 className="text-lg font-semibold mb-2">
                      {t.whatIndustry || 'What industry are you in?'}
                    </h3>
                    <p className="text-sm text-muted-foreground">
                      {t.industryHelp ||
                        'This helps us recommend templates that work well in your field'}
                    </p>
                  </div>

                  <RadioGroup
                    value={wizardState.industry}
                    onValueChange={value =>
                      setWizardState({ ...wizardState, industry: value })
                    }
                  >
                    <div className="grid gap-3">
                      {industries.map(industry => (
                        <Label
                          key={industry.value}
                          htmlFor={industry.value}
                          className="flex items-center space-x-3 rounded-lg border p-4 cursor-pointer hover:bg-muted/50 transition-colors"
                        >
                          <RadioGroupItem
                            value={industry.value}
                            id={industry.value}
                          />
                          <div className="text-primary">{industry.icon}</div>
                          <span className="font-medium">{industry.label}</span>
                        </Label>
                      ))}
                    </div>
                  </RadioGroup>
                </motion.div>
              )}

              {/* Style Preference */}
              {currentStep === 'style' && (
                <motion.div
                  key="style"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  className="space-y-6"
                >
                  <div>
                    <h3 className="text-lg font-semibold mb-2">
                      {t.preferredStyle || "What's your preferred style?"}
                    </h3>
                    <p className="text-sm text-muted-foreground">
                      {t.styleHelp ||
                        'Choose the approach that best matches your content'}
                    </p>
                  </div>

                  <RadioGroup
                    value={wizardState.style}
                    onValueChange={value =>
                      setWizardState({
                        ...wizardState,
                        style: value as 'visual' | 'content' | 'balanced',
                      })
                    }
                  >
                    <div className="grid gap-3">
                      {styleOptions.map(style => (
                        <Label
                          key={style.value}
                          htmlFor={style.value}
                          className="flex flex-col space-y-1 rounded-lg border p-4 cursor-pointer hover:bg-muted/50 transition-colors"
                        >
                          <div className="flex items-center space-x-3">
                            <RadioGroupItem
                              value={style.value}
                              id={style.value}
                            />
                            <span className="font-medium">{style.label}</span>
                          </div>
                          <p className="text-sm text-muted-foreground ml-6">
                            {style.description}
                          </p>
                        </Label>
                      ))}
                    </div>
                  </RadioGroup>
                </motion.div>
              )}

              {/* Features Selection */}
              {currentStep === 'features' && (
                <motion.div
                  key="features"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  className="space-y-6"
                >
                  <div>
                    <h3 className="text-lg font-semibold mb-2">
                      {t.importantFeatures ||
                        'What features are important to you?'}
                    </h3>
                    <p className="text-sm text-muted-foreground">
                      {t.featuresHelp || 'Select all that apply'}
                    </p>
                  </div>

                  <div className="grid gap-3">
                    {featureOptions.map(feature => (
                      <Label
                        key={feature.value}
                        htmlFor={feature.value}
                        className="flex items-center space-x-3 rounded-lg border p-4 cursor-pointer hover:bg-muted/50 transition-colors"
                      >
                        <input
                          type="checkbox"
                          id={feature.value}
                          value={feature.value}
                          checked={wizardState.features?.includes(
                            feature.value
                          )}
                          onChange={e => {
                            const features = wizardState.features || [];
                            if (e.target.checked) {
                              setWizardState({
                                ...wizardState,
                                features: [...features, feature.value],
                              });
                            } else {
                              setWizardState({
                                ...wizardState,
                                features: features.filter(
                                  f => f !== feature.value
                                ),
                              });
                            }
                          }}
                          className="rounded border-gray-300"
                        />
                        <span className="font-medium">{feature.label}</span>
                      </Label>
                    ))}
                  </div>
                </motion.div>
              )}

              {/* Template Preview */}
              {currentStep === 'preview' && (
                <motion.div
                  key="preview"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  className="space-y-6"
                >
                  <div>
                    <h3 className="text-lg font-semibold mb-2">
                      {t.recommendedTemplate || 'Recommended Templates'}
                    </h3>
                    <p className="text-sm text-muted-foreground">
                      {t.previewHelp ||
                        'Based on your preferences, these templates would work best'}
                    </p>
                  </div>

                  <RadioGroup
                    value={selectedTemplate || undefined}
                    onValueChange={value =>
                      setSelectedTemplate(value as TemplateType)
                    }
                  >
                    <div className="grid gap-4 md:grid-cols-2">
                      {templates
                        .filter(
                          t =>
                            t.id === wizardState.recommendation || t.recommended
                        )
                        .map(template => (
                          <Label
                            key={template.id}
                            htmlFor={template.id}
                            className="relative rounded-lg border p-4 cursor-pointer hover:bg-muted/50 transition-colors"
                          >
                            <RadioGroupItem
                              value={template.id}
                              id={template.id}
                              className="absolute top-4 right-4"
                            />

                            {template.id === wizardState.recommendation && (
                              <Badge
                                className="absolute top-4 left-4"
                                variant="default"
                              >
                                <Sparkles className="w-3 h-3 mr-1" />
                                {t.recommended || 'Recommended'}
                              </Badge>
                            )}

                            <div className="pr-8">
                              <div className="flex items-center gap-3 mb-2 mt-2">
                                <div className="text-primary">
                                  {template.icon}
                                </div>
                                <h4 className="font-semibold">
                                  {template.name}
                                </h4>
                              </div>

                              <p className="text-sm text-muted-foreground mb-3">
                                {template.description}
                              </p>

                              <div className="flex flex-wrap gap-1">
                                {template.features.slice(0, 3).map(feature => (
                                  <Badge
                                    key={feature}
                                    variant="secondary"
                                    className="text-xs"
                                  >
                                    {feature}
                                  </Badge>
                                ))}
                              </div>
                            </div>
                          </Label>
                        ))}
                    </div>

                    <div className="mt-4">
                      <Button
                        variant="link"
                        onClick={() => setSelectedTemplate(null)}
                        className="text-sm"
                      >
                        {t.viewAllTemplates || 'View all templates instead'}
                      </Button>
                    </div>
                  </RadioGroup>
                </motion.div>
              )}
            </AnimatePresence>

            {/* Navigation */}
            <div className="flex justify-between mt-8">
              <Button
                variant="outline"
                onClick={handleBack}
                disabled={currentStepIndex === 0}
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                {t.back || 'Back'}
              </Button>

              {currentStep === 'preview' ? (
                <Button onClick={handleFinish} disabled={!selectedTemplate}>
                  <Check className="w-4 h-4 mr-2" />
                  {t.useTemplate || 'Use This Template'}
                </Button>
              ) : (
                <Button
                  onClick={handleNext}
                  disabled={
                    (currentStep === 'industry' && !wizardState.industry) ||
                    (currentStep === 'style' && !wizardState.style)
                  }
                >
                  {t.next || 'Next'}
                  <ArrowRight className="w-4 h-4 ml-2" />
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
}

