import {
  AIService,
  EnhancedContent,
  BioContext,
  ProjectEnhancement,
  TemplateRecommendation,
  UserProfile,
  QualityScore,
  AIServiceError,
} from './types';
import { DeepSeekConfig, DeepSeekUsageStats } from './deepseek/types';
import { DeepSeekAPIClient } from './deepseek/api-client';
import {
  buildBioPrompt,
  buildProjectPrompt,
  buildTemplatePrompt,
  buildScoringPrompt,
} from './deepseek/prompt-builders';
import {
  parseBioResponse,
  parseProjectResponse,
  parseTemplateResponse,
  parseScoringResponse,
} from './deepseek/response-parsers';

/**
 * DeepSeek AI Service Implementation
 * Provides AI-powered content enhancement using DeepSeek reasoning models
 */

export class DeepSeekService implements AIService {
  private config: DeepSeekConfig;
  private apiClient: DeepSeekAPIClient;
  private usageStats: DeepSeekUsageStats = {
    requestsToday: 0,
    costToday: 0,
    totalResponseTime: 0,
    successfulRequests: 0,
    failedRequests: 0,
  };

  constructor(config?: Partial<DeepSeekConfig>) {
    this.config = {
      apiKey: config?.apiKey || process.env.DEEPSEEK_API_KEY || '',
      baseUrl: config?.baseUrl || 'https://api.deepseek.com/v1',
      model: config?.model || 'deepseek-reasoner',
      maxTokens: config?.maxTokens || 1024,
      temperature: config?.temperature || 0.7,
    };

    this.apiClient = new DeepSeekAPIClient(this.config);
  }

  /**
   * Enhance bio using DeepSeek reasoning models
   */
  async enhanceBio(bio: string, context: BioContext): Promise<EnhancedContent> {
    const startTime = Date.now();
    this.usageStats.requestsToday++;

    try {
      const prompt = buildBioPrompt(bio, context);
      const response = await this.apiClient.callAPI(prompt);

      this.updateUsageStats(response, Date.now() - startTime);
      return parseBioResponse(response, bio);
    } catch (error) {
      this.usageStats.failedRequests++;
      if (error instanceof AIServiceError) {
        throw error;
      }
      throw new AIServiceError(
        `Bio enhancement failed: ${error}`,
        'ENHANCEMENT_ERROR',
        'deepseek'
      );
    }
  }

  /**
   * Optimize project description using DeepSeek
   */
  async optimizeProjectDescription(
    description: string,
    technologies: string[],
    industryContext?: string
  ): Promise<ProjectEnhancement> {
    const startTime = Date.now();
    this.usageStats.requestsToday++;

    try {
      const prompt = buildProjectPrompt(
        description,
        technologies,
        industryContext
      );
      const response = await this.apiClient.callAPI(prompt);

      this.updateUsageStats(response, Date.now() - startTime);
      return parseProjectResponse(response, description, technologies);
    } catch (error) {
      this.usageStats.failedRequests++;
      if (error instanceof AIServiceError) {
        throw error;
      }
      throw new AIServiceError(
        `Project optimization failed: ${error}`,
        'OPTIMIZATION_ERROR',
        'deepseek'
      );
    }
  }

  /**
   * Recommend template using DeepSeek reasoning
   */
  async recommendTemplate(
    profile: UserProfile
  ): Promise<TemplateRecommendation> {
    const startTime = Date.now();
    this.usageStats.requestsToday++;

    try {
      const prompt = buildTemplatePrompt(profile);
      const response = await this.apiClient.callAPI(prompt);

      this.updateUsageStats(response, Date.now() - startTime);
      return parseTemplateResponse(response);
    } catch (error) {
      this.usageStats.failedRequests++;
      if (error instanceof AIServiceError) {
        throw error;
      }
      throw new AIServiceError(
        `Template recommendation failed: ${error}`,
        'RECOMMENDATION_ERROR',
        'deepseek'
      );
    }
  }

  /**
   * Score content quality using DeepSeek analysis
   */
  async scoreContent(content: string, type: string): Promise<QualityScore> {
    const startTime = Date.now();
    this.usageStats.requestsToday++;

    try {
      const prompt = buildScoringPrompt(content, type);
      const response = await this.apiClient.callAPI(prompt);

      this.updateUsageStats(response, Date.now() - startTime);
      return parseScoringResponse(response);
    } catch (error) {
      this.usageStats.failedRequests++;
      if (error instanceof AIServiceError) {
        throw error;
      }
      throw new AIServiceError(
        `Content scoring failed: ${error}`,
        'SCORING_ERROR',
        'deepseek'
      );
    }
  }

  /**
   * Health check for DeepSeek service
   */
  healthCheck(): Promise<boolean> {
    return this.apiClient.healthCheck();
  }

  /**
   * Get usage statistics
   */
  async getUsageStats() {
    const avgResponseTime =
      this.usageStats.successfulRequests > 0
        ? this.usageStats.totalResponseTime / this.usageStats.successfulRequests
        : 0;

    const successRate =
      this.usageStats.requestsToday > 0
        ? (this.usageStats.successfulRequests / this.usageStats.requestsToday) *
          100
        : 0;

    return {
      requestsToday: this.usageStats.requestsToday,
      costToday: this.usageStats.costToday,
      avgResponseTime,
      successRate,
    };
  }

  /**
   * Update usage statistics after successful API call
   */
  private updateUsageStats(
    response: { usage?: { total_tokens: number } },
    responseTime: number
  ): void {
    this.usageStats.successfulRequests++;
    this.usageStats.totalResponseTime += responseTime;
    this.usageStats.costToday += this.calculateCost(
      response.usage?.total_tokens || 0
    );
  }

  /**
   * Calculate cost based on token usage
   */
  private calculateCost(tokens: number): number {
    // DeepSeek pricing: approximately $0.14 per 1M tokens
    return (tokens / 1000000) * 0.14;
  }
}

// Export singleton instance
export const deepSeekService = new DeepSeekService();

/**
 * Suggestion Generator
 * Generates improvement suggestions based on GEO analysis
 */

interface Suggestion {
  type: 'keyword' | 'readability' | 'structure' | 'length' | 'style';
  priority: 'high' | 'medium' | 'low';
  message: string;
  impact: number;
}

interface AnalysisMetrics {
  keywordScore: number;
  readabilityScore: number;
  structureScore: number;
  lengthScore: number;
  uniquenessScore: number;
  wordCount: number;
  sentenceCount: number;
  keywordDensity: number;
}

export class SuggestionGenerator {
  /**
   * Generate improvement suggestions
   */
  generateSuggestions(
    content: string,
    keywords: string[],
    scoreBreakdown: any,
    contentType: string
  ): Suggestion[] {
    const suggestions: Suggestion[] = [];
    const metrics = this.analyzeContent(content, keywords);

    // Generate suggestions based on scores
    if (scoreBreakdown.keyword < 70) {
      suggestions.push(...this.generateKeywordSuggestions(metrics, keywords));
    }

    if (scoreBreakdown.readability < 70) {
      suggestions.push(...this.generateReadabilitySuggestions(metrics));
    }

    if (scoreBreakdown.structure < 70) {
      suggestions.push(
        ...this.generateStructureSuggestions(metrics, contentType)
      );
    }

    if (scoreBreakdown.length < 70) {
      suggestions.push(...this.generateLengthSuggestions(metrics, contentType));
    }

    // Sort by priority and impact
    return suggestions.sort((a, b) => {
      const priorityOrder = { high: 3, medium: 2, low: 1 };
      const priorityDiff =
        priorityOrder[b.priority] - priorityOrder[a.priority];
      return priorityDiff !== 0 ? priorityDiff : b.impact - a.impact;
    });
  }

  /**
   * Analyze content metrics
   */
  private analyzeContent(content: string, keywords: string[]): AnalysisMetrics {
    const words = content.split(/\s+/).filter(w => w.length > 0);
    const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 0);

    let keywordCount = 0;
    const contentLower = content.toLowerCase();
    for (const keyword of keywords) {
      const regex = new RegExp(`\\b${keyword.toLowerCase()}\\b`, 'g');
      const matches = contentLower.match(regex) || [];
      keywordCount += matches.length;
    }

    return {
      keywordScore: 0, // Will be set from scoreBreakdown
      readabilityScore: 0,
      structureScore: 0,
      lengthScore: 0,
      uniquenessScore: 0,
      wordCount: words.length,
      sentenceCount: sentences.length,
      keywordDensity:
        words.length > 0 ? (keywordCount / words.length) * 100 : 0,
    };
  }

  /**
   * Generate keyword-related suggestions
   */
  private generateKeywordSuggestions(
    metrics: AnalysisMetrics,
    keywords: string[]
  ): Suggestion[] {
    const suggestions: Suggestion[] = [];

    if (metrics.keywordDensity < 1) {
      suggestions.push({
        type: 'keyword',
        priority: 'high',
        message: `Include keywords "${keywords.join('", "')}" more naturally in your content`,
        impact: 25,
      });
    } else if (metrics.keywordDensity > 3) {
      suggestions.push({
        type: 'keyword',
        priority: 'high',
        message: 'Reduce keyword repetition to avoid over-optimization',
        impact: 20,
      });
    }

    if (keywords.length > 0 && metrics.keywordDensity === 0) {
      suggestions.push({
        type: 'keyword',
        priority: 'high',
        message: "Your content doesn't include any of the target keywords",
        impact: 30,
      });
    }

    return suggestions;
  }

  /**
   * Generate readability suggestions
   */
  private generateReadabilitySuggestions(
    metrics: AnalysisMetrics
  ): Suggestion[] {
    const suggestions: Suggestion[] = [];
    const avgWordsPerSentence =
      metrics.wordCount / Math.max(1, metrics.sentenceCount);

    if (avgWordsPerSentence > 20) {
      suggestions.push({
        type: 'readability',
        priority: 'high',
        message: 'Break down long sentences for better readability',
        impact: 20,
      });
    }

    if (avgWordsPerSentence < 10) {
      suggestions.push({
        type: 'readability',
        priority: 'medium',
        message: 'Combine short sentences for better flow',
        impact: 15,
      });
    }

    // Check for passive voice indicators
    const passiveIndicators = ['was', 'were', 'been', 'being'];
    const passiveCount = passiveIndicators.reduce((count, word) => {
      const regex = new RegExp(`\\b${word}\\b`, 'gi');
      return count + (metrics.wordCount.toString().match(regex) || []).length;
    }, 0);

    if (passiveCount > metrics.sentenceCount * 0.3) {
      suggestions.push({
        type: 'readability',
        priority: 'medium',
        message: 'Use more active voice for stronger impact',
        impact: 15,
      });
    }

    return suggestions;
  }

  /**
   * Generate structure suggestions
   */
  private generateStructureSuggestions(
    metrics: AnalysisMetrics,
    contentType: string
  ): Suggestion[] {
    const suggestions: Suggestion[] = [];

    // Check for basic structure elements
    const hasHeaders = /^#{1,3}\s/m.test(contentType);
    const hasLists =
      /^[\-*]\s/m.test(contentType) || /^\d+\.\s/m.test(contentType);

    if (!hasHeaders && metrics.wordCount > 100) {
      suggestions.push({
        type: 'structure',
        priority: 'medium',
        message: 'Add section headers to organize your content',
        impact: 15,
      });
    }

    if (!hasLists && contentType === 'experience') {
      suggestions.push({
        type: 'structure',
        priority: 'high',
        message:
          'Use bullet points to highlight achievements and responsibilities',
        impact: 20,
      });
    }

    // Content-specific suggestions
    if (contentType === 'project') {
      const hasOverview = /overview|description|about/i.test(contentType);
      const hasTech = /technolog|tools?|stack|built with/i.test(contentType);
      const hasOutcome = /result|outcome|impact|achievement/i.test(contentType);

      if (!hasOverview) {
        suggestions.push({
          type: 'structure',
          priority: 'high',
          message: 'Add a project overview section',
          impact: 15,
        });
      }

      if (!hasTech) {
        suggestions.push({
          type: 'structure',
          priority: 'medium',
          message: 'Include technologies used in the project',
          impact: 10,
        });
      }

      if (!hasOutcome) {
        suggestions.push({
          type: 'structure',
          priority: 'high',
          message: 'Highlight project outcomes and achievements',
          impact: 20,
        });
      }
    }

    return suggestions;
  }

  /**
   * Generate length suggestions
   */
  private generateLengthSuggestions(
    metrics: AnalysisMetrics,
    contentType: string
  ): Suggestion[] {
    const suggestions: Suggestion[] = [];

    const idealRanges: Record<string, { min: number; max: number }> = {
      bio: { min: 50, max: 150 },
      project: { min: 100, max: 300 },
      experience: { min: 150, max: 400 },
      default: { min: 100, max: 250 },
    };

    const range = idealRanges[contentType] || idealRanges.default;

    if (range && metrics.wordCount < range.min) {
      const deficit = range.min - metrics.wordCount;
      suggestions.push({
        type: 'length',
        priority: 'high',
        message: `Add ${deficit} more words to meet minimum length`,
        impact: 25,
      });

      // Specific expansion suggestions
      if (contentType === 'bio') {
        suggestions.push({
          type: 'length',
          priority: 'medium',
          message: 'Include your key skills and achievements',
          impact: 15,
        });
      } else if (contentType === 'project') {
        suggestions.push({
          type: 'length',
          priority: 'medium',
          message: 'Expand on challenges faced and solutions implemented',
          impact: 15,
        });
      }
    } else if (range && metrics.wordCount > range.max) {
      suggestions.push({
        type: 'length',
        priority: 'medium',
        message: `Reduce content by ${metrics.wordCount - range.max} words for optimal length`,
        impact: 15,
      });

      suggestions.push({
        type: 'length',
        priority: 'low',
        message: 'Focus on most impactful points and remove redundancy',
        impact: 10,
      });
    }

    return suggestions;
  }

  /**
   * Generate style suggestions
   */
  generateStyleSuggestions(content: string, tone: string): Suggestion[] {
    const suggestions: Suggestion[] = [];

    // Check tone consistency
    const casualIndicators = ['gonna', 'wanna', 'hey', 'cool', 'awesome'];
    const formalIndicators = ['furthermore', 'moreover', 'therefore', 'hence'];

    const hasCasual = casualIndicators.some(word =>
      new RegExp(`\\b${word}\\b`, 'i').test(content)
    );
    const hasFormal = formalIndicators.some(word =>
      new RegExp(`\\b${word}\\b`, 'i').test(content)
    );

    if (tone === 'professional' && hasCasual) {
      suggestions.push({
        type: 'style',
        priority: 'medium',
        message: 'Replace casual language with more professional alternatives',
        impact: 15,
      });
    }

    if (tone === 'casual' && hasFormal) {
      suggestions.push({
        type: 'style',
        priority: 'low',
        message: 'Consider using more conversational language',
        impact: 10,
      });
    }

    return suggestions;
  }
}

import crypto from 'crypto';

import { cache, CACHE_KEYS } from '@/lib/cache/redis-cache.server';
import { env } from '@/lib/config';
import { logger } from '@/lib/utils/logger';

import { ContentScorer } from './huggingface/ContentScorer';
import { ModelManager } from './huggingface/ModelManager';
import { PromptBuilder } from './huggingface/PromptBuilder';
import {
  AIService,
  EnhancedContent,
  BioContext,
  ProjectEnhancement,
  TemplateRecommendation,
  UserProfile,
  QualityScore,
  AIServiceError,
  ModelUnavailableError,
  QuotaExceededError,
  ModelResponse,
} from './types';

/**
 * Hugging Face AI Service Implementation
 * Open-source model integration for content enhancement
 */
export class HuggingFaceService implements AIService {
  private readonly apiKey: string;
  private readonly baseUrl = 'https://api-inference.huggingface.co/models';
  private modelManager: ModelManager;
  private promptBuilder: PromptBuilder;
  private contentScorer: ContentScorer;

  constructor(apiKey?: string, userModelPreferences?: Record<string, string>) {
    this.apiKey = apiKey || env.HUGGINGFACE_API_KEY || '';

    if (!this.apiKey) {
      logger.warn(
        'Hugging Face API key not provided. Service will operate in demo mode.'
      );
    }

    // Initialize managers
    this.modelManager = new ModelManager(userModelPreferences);
    this.promptBuilder = new PromptBuilder();
    this.contentScorer = new ContentScorer();
  }

  /**
   * Generate cache key for AI requests
   */
  private generateCacheKey(
    type: string,
    content: string,
    context?: BioContext | Record<string, unknown>
  ): string {
    const hash = crypto
      .createHash('md5')
      .update(JSON.stringify({ content, context }))
      .digest('hex')
      .substring(0, 8);
    return `${CACHE_KEYS.AI_RESULT}${type}:${hash}`;
  }

  /**
   * Get all available models with live updates
   */
  getAvailableModels() {
    return this.modelManager.getAvailableModels();
  }

  /**
   * Get current model selection for user
   */
  getSelectedModels() {
    return this.modelManager.getSelectedModels();
  }

  /**
   * Update user's model preferences
   */
  updateModelSelection(taskType: string, modelId: string): void {
    this.modelManager.updateModelSelection(taskType, modelId);
  }

  /**
   * Enhance professional bio
   */
  async enhanceBio(bio: string, context: BioContext): Promise<EnhancedContent> {
    try {
      const cacheKey = this.generateCacheKey('bio', bio, context);

      // Check cache
      const cached = await cache.get(cacheKey);
      if (cached) {
        return JSON.parse(cached as string);
      }

      // Select model and build prompt
      const model = this.modelManager.getRecommendedModel('bio');
      const prompt = this.promptBuilder.buildBioPrompt(bio, context);

      // Call AI model
      const response = await this.callModel(model, prompt);
      const enhanced = this.promptBuilder.extractBioFromResponse(
        response.content
      );

      // Score the result
      const score = await this.contentScorer.scoreContent(enhanced, 'bio');

      const result: EnhancedContent = {
        content: enhanced,
        confidence: this.calculateConfidence(score.overall, response),
        suggestions: this.generateBioSuggestions(bio, enhanced),
        wordCount: enhanced.split(/\s+/).length,
        qualityScore: score.overall,
        enhancementType: 'bio',
      };

      // Cache for 24 hours
      await cache.set(cacheKey, JSON.stringify(result), 86400);

      return result;
    } catch (error) {
      logger.error(
        'Bio enhancement failed',
        error instanceof Error ? error : new Error(String(error))
      );
      throw this.handleError(error, 'bio enhancement');
    }
  }

  /**
   * Optimize project description
   */
  async optimizeProjectDescription(
    description: string,
    skills: string[],
    industryContext?: string
  ): Promise<ProjectEnhancement> {
    try {
      const cacheKey = this.generateCacheKey('project', description, {
        skills,
        industryContext,
      });

      // Check cache
      const cached = await cache.get(cacheKey);
      if (cached) {
        return JSON.parse(cached as string);
      }

      // Select model and build prompt
      const model = this.modelManager.getRecommendedModel('project');
      const prompt = this.promptBuilder.buildProjectPrompt(
        description,
        skills,
        industryContext
      );

      // Call AI model
      const response = await this.callModel(model, prompt);
      const parsed = this.promptBuilder.parseProjectResponse(response.content);

      const result: ProjectEnhancement = {
        original: description,
        enhanced: parsed.enhanced || description,
        keyAchievements: parsed.keyAchievements || [],
        technologies: parsed.technologies || skills,
        impactMetrics: parsed.impactMetrics || [],
        confidence: this.calculateConfidence(80, response), // Base confidence
      };

      // Cache for 24 hours
      await cache.set(cacheKey, JSON.stringify(result), 86400);

      return result;
    } catch (error) {
      logger.error(
        'Project optimization failed',
        error instanceof Error ? error : new Error(String(error))
      );
      throw this.handleError(error, 'project optimization');
    }
  }

  /**
   * Recommend template based on user profile
   */
  async recommendTemplate(
    profile: UserProfile
  ): Promise<TemplateRecommendation> {
    try {
      // Simple rule-based recommendation with scoring
      const templates = [
        'developer',
        'designer',
        'consultant',
        'business',
        'creative',
        'minimal',
        'educator',
        'modern',
      ];

      // Score each template based on profile
      const scores = templates.map(template => ({
        template,
        score: this.scoreTemplateMatch(template, profile),
        reasoning: this.getTemplateReasoning(template, profile),
      }));

      const bestMatch = scores.reduce((best, current) =>
        current.score > best.score ? current : best
      );

      return {
        recommendedTemplate: bestMatch.template,
        confidence: bestMatch.score / 100,
        reasoning: bestMatch.reasoning,
        alternatives: scores
          .filter(s => s.template !== bestMatch.template)
          .sort((a, b) => b.score - a.score)
          .slice(0, 2)
          .map(s => ({
            template: s.template,
            score: s.score / 100,
            reasons: [s.reasoning],
          })),
      };
    } catch (error) {
      logger.error(
        'Template recommendation failed',
        error instanceof Error ? error : new Error(String(error))
      );
      throw this.handleError(error, 'template recommendation');
    }
  }

  /**
   * Score content quality
   */
  async scoreContent(content: string, type: string): Promise<QualityScore> {
    return this.contentScorer.scoreContent(content, type);
  }

  /**
   * Health check for the service
   */
  async healthCheck(): Promise<boolean> {
    try {
      const testModel = this.modelManager.getRecommendedModel('bio');
      const response = await fetch(`${this.baseUrl}/${testModel}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${this.apiKey}`,
        },
      });

      return response.status === 200;
    } catch {
      return false;
    }
  }

  /**
   * Get usage statistics
   */
  async getUsageStats(): Promise<{
    requestsToday: number;
    costToday: number;
    avgResponseTime: number;
    successRate: number;
  }> {
    // This would typically come from a monitoring service
    return {
      requestsToday: 0,
      costToday: 0,
      avgResponseTime: 1200,
      successRate: 0.95,
    };
  }

  /**
   * Call HuggingFace model
   */
  private async callModel(
    modelId: string,
    prompt: string,
    retries = 2
  ): Promise<ModelResponse> {
    const startTime = Date.now();

    for (let attempt = 0; attempt <= retries; attempt++) {
      try {
        const response = await this.makeModelRequest(modelId, prompt);
        const result = await this.handleModelResponse(
          response,
          modelId,
          attempt,
          retries
        );

        if (!result) continue; // Retry needed

        return {
          content: result.text,
          metadata: {
            model: modelId,
            provider: 'huggingface',
            tokens: Math.round(result.text.split(/\s+/).length * 1.3), // Rough estimate
            cost: 0, // HuggingFace free tier
            responseTime: Date.now() - startTime,
          },
        };
      } catch (error) {
        if (attempt === retries) throw error;
        await new Promise(resolve => setTimeout(resolve, 2000 * (attempt + 1)));
      }
    }

    throw new AIServiceError(
      'Failed to get response from model',
      'MODEL_ERROR',
      'huggingface'
    );
  }

  /**
   * Make HTTP request to model API
   */
  private makeModelRequest(modelId: string, prompt: string): Promise<Response> {
    return fetch(`${this.baseUrl}/${modelId}`, {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${this.apiKey}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        inputs: prompt,
        parameters: {
          max_new_tokens: 250,
          temperature: 0.7,
          top_p: 0.9,
          do_sample: true,
        },
        options: {
          wait_for_model: true,
        },
      }),
    });
  }

  /**
   * Handle model API response
   */
  private async handleModelResponse(
    response: Response,
    modelId: string,
    attempt: number,
    maxRetries: number
  ): Promise<{ text: string } | null> {
    if (response.ok) {
      const result = await response.json();
      const text = Array.isArray(result)
        ? result[0]?.generated_text || ''
        : result.generated_text || result;
      return { text };
    }

    // Handle model loading retry
    if (response.status === 503 && attempt < maxRetries) {
      await new Promise(resolve => setTimeout(resolve, 5000));
      return null; // Signal retry needed
    }

    // Handle quota exceeded
    if (response.status === 429) {
      throw new QuotaExceededError('huggingface');
    }

    // Extract error details and throw
    const errorMessage = await this.extractErrorMessage(response);
    logger.error(`Model ${modelId} error:`, {
      status: response.status,
      error: errorMessage,
    });
    throw new ModelUnavailableError('huggingface', modelId);
  }

  /**
   * Extract error message from response
   */
  private async extractErrorMessage(response: Response): Promise<string> {
    try {
      const errorData = await response.json();
      return errorData.error || response.statusText;
    } catch {
      return response.statusText;
    }
  }

  /**
   * Calculate confidence score
   */
  private calculateConfidence(
    baseScore: number,
    response: ModelResponse
  ): number {
    let confidence = baseScore;

    // Adjust based on processing time (faster = more confident)
    if (response.metadata.responseTime < 1000) confidence += 5;
    if (response.metadata.responseTime > 3000) confidence -= 10;

    // Adjust based on response length
    const words = response.content.split(/\s+/).length;
    if (words > 50 && words < 200) confidence += 5;

    return Math.max(0, Math.min(100, confidence)) / 100;
  }

  /**
   * Generate bio improvement suggestions
   */
  private generateBioSuggestions(
    original: string,
    _enhanced: string
  ): string[] {
    const suggestions: string[] = [];

    if (original.length < 50) {
      suggestions.push('Consider adding more details about your expertise');
    }
    if (!original.match(/\d+/)) {
      suggestions.push('Include specific metrics or years of experience');
    }
    if (!original.match(/\b(?:led|managed|developed|created)\b/i)) {
      suggestions.push('Use action verbs to highlight your achievements');
    }

    return suggestions;
  }

  /**
   * Score template match based on profile
   */
  private scoreTemplateMatch(template: string, profile: UserProfile): number {
    const industryMatches: Record<string, string[]> = {
      developer: ['technology', 'software', 'engineering', 'it'],
      designer: ['design', 'ux', 'ui', 'creative', 'art'],
      consultant: ['consulting', 'business', 'strategy', 'management'],
      business: ['business', 'finance', 'marketing', 'sales'],
      creative: ['creative', 'media', 'content', 'photography'],
      minimal: ['any'], // Works for any industry
      educator: ['education', 'teaching', 'academic', 'training'],
      modern: ['startup', 'tech', 'innovation'],
    };

    let score = 50; // Base score

    // Industry match
    const industries = industryMatches[template] || [];
    const profileIndustry = profile.industry?.toLowerCase() || '';
    if (
      industries.includes('any') ||
      (profileIndustry && industries.some(ind => profileIndustry.includes(ind)))
    ) {
      score += 30;
    }

    // Experience level match
    if (template === 'minimal' && profile.experienceLevel === 'entry') {
      score += 20;
    } else if (
      template === 'business' &&
      profile.experienceLevel === 'senior'
    ) {
      score += 20;
    }

    // Style preferences
    if (
      profile.skills.some(skill => skill.toLowerCase().includes('design')) &&
      template === 'designer'
    ) {
      score += 20;
    }

    return Math.min(100, score);
  }

  /**
   * Get template reasoning
   */
  private getTemplateReasoning(
    template: string,
    _profile: UserProfile
  ): string {
    const reasons: Record<string, string> = {
      developer: 'Perfect for showcasing technical projects and code samples',
      designer: 'Ideal for visual portfolios with strong design emphasis',
      consultant: 'Professional layout for business and consulting services',
      business: 'Corporate style suitable for business professionals',
      creative: 'Expressive design for creative professionals',
      minimal: 'Clean and simple, letting your content speak for itself',
      educator: 'Academic-friendly layout for educators and researchers',
      modern: 'Contemporary design for innovative professionals',
    };

    return reasons[template] || 'Suitable for your professional profile';
  }

  /**
   * Handle errors appropriately
   */
  private handleError(error: unknown, operation: string): Error {
    if (error instanceof AIServiceError) {
      return error;
    }

    const message = error instanceof Error ? error.message : 'Unknown error';
    logger.error(`HuggingFace ${operation} failed:`, { error: message });

    return new AIServiceError(
      `Failed to complete ${operation}: ${message}`,
      'OPERATION_FAILED',
      'huggingface'
    );
  }
}

