import { NextResponse } from 'next/server';
import { ZodError } from 'zod';

import { apiError } from '@/lib/api/versioning';
import { logger } from '@/lib/utils/logger';

/**
 * Centralized API Error Handler
 * Provides consistent error handling and logging across all API routes
 */

export interface ApiError extends Error {
  statusCode?: number;
  code?: string;
  retryable?: boolean;
  metadata?: Record<string, unknown>;
}

/**
 * Error type mapping for consistent error responses
 */
const ERROR_TYPES = {
  VALIDATION_ERROR: { statusCode: 400, code: 'VALIDATION_ERROR' },
  AUTHENTICATION_ERROR: { statusCode: 401, code: 'AUTHENTICATION_ERROR' },
  AUTHORIZATION_ERROR: { statusCode: 403, code: 'AUTHORIZATION_ERROR' },
  NOT_FOUND: { statusCode: 404, code: 'NOT_FOUND' },
  RATE_LIMIT_ERROR: { statusCode: 429, code: 'RATE_LIMIT_ERROR' },
  INTERNAL_ERROR: { statusCode: 500, code: 'INTERNAL_ERROR' },
  SERVICE_UNAVAILABLE: { statusCode: 503, code: 'SERVICE_UNAVAILABLE' },
} as const;

/**
 * Creates a standardized API error
 */
export function createApiError(
  message: string,
  type: keyof typeof ERROR_TYPES = 'INTERNAL_ERROR',
  metadata?: Record<string, unknown>
): ApiError {
  const error = new Error(message) as ApiError;
  const errorConfig = ERROR_TYPES[type];

  error.statusCode = errorConfig.statusCode;
  error.code = errorConfig.code;
  error.metadata = metadata;

  return error;
}

/**
 * Global error handler for API routes
 * Transforms various error types into consistent API responses
 */
export function handleApiError(
  error: unknown,
  context?: {
    operation?: string;
    userId?: string;
    path?: string;
    [key: string]: any;
  }
): NextResponse {
  // Log the error with context
  logger.error('API Error', {
    error: error instanceof Error ? error : new Error(String(error)),
    context,
  });

  // Handle Zod validation errors
  if (error instanceof ZodError) {
    return apiError('Validation failed', {
      status: 400,
      data: {
        code: 'VALIDATION_ERROR',
        errors: error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message,
        })),
      },
    });
  }

  // Handle custom API errors
  if (error instanceof Error && 'statusCode' in error) {
    const apiErr = error as ApiError;
    return apiError(apiErr.message, {
      status: apiErr.statusCode || 500,
      data: {
        code: apiErr.code,
        ...apiErr.metadata,
      },
    });
  }

  // Handle Supabase/Database errors
  if (error instanceof Error && 'code' in error) {
    const dbError = error as { code?: string };

    // Common database error mappings
    switch (dbError.code) {
      case '23505': // Unique constraint violation
        return apiError('Resource already exists', {
          status: 409,
          data: { code: 'DUPLICATE_ERROR' },
        });
      case '23503': // Foreign key violation
        return apiError('Related resource not found', {
          status: 404,
          data: { code: 'REFERENCE_ERROR' },
        });
      case 'PGRST116': // No rows found
        return apiError('Resource not found', {
          status: 404,
          data: { code: 'NOT_FOUND' },
        });
      default:
        // Log unknown database errors for debugging
        logger.error('Unknown database error', { code: dbError.code, error });
    }
  }

  // Handle standard JavaScript errors
  if (error instanceof TypeError) {
    return apiError('Invalid request format', {
      status: 400,
      data: { code: 'TYPE_ERROR' },
    });
  }

  if (error instanceof SyntaxError) {
    return apiError('Invalid JSON in request', {
      status: 400,
      data: { code: 'SYNTAX_ERROR' },
    });
  }

  // Default error response
  const message =
    error instanceof Error ? error.message : 'An unexpected error occurred';
  return apiError(message, {
    status: 500,
    data: { code: 'INTERNAL_ERROR' },
  });
}

/**
 * Async error wrapper for route handlers
 * Automatically catches and handles errors
 */
export function withErrorHandler<T extends (...args: unknown[]) => any>(
  handler: T,
  context?: Record<string, unknown>
): T {
  return (async (...args: Parameters<T>) => {
    try {
      return await handler(...args);
    } catch (error) {
      return handleApiError(error, context);
    }
  }) as T;
}

/**
 * Type guard for API errors
 */
export function isApiError(error: unknown): error is ApiError {
  return (
    error instanceof Error &&
    'statusCode' in error &&
    typeof (error as { statusCode?: unknown }).statusCode === 'number'
  );
}

/**
 * Extract error details for logging
 */
export function extractErrorDetails(error: unknown): {
  message: string;
  stack?: string;
  code?: string;
  statusCode?: number;
  [key: string]: any;
} {
  if (error instanceof Error) {
    const details: any = {
      message: error.message,
      stack: error.stack,
    };

    if (isApiError(error)) {
      details.code = error.code;
      details.statusCode = error.statusCode;
      details.metadata = error.metadata;
    }

    return details;
  }

  return {
    message: String(error),
  };
}

/**
 * Encryption middleware for API routes
 * Automatically encrypts/decrypts sensitive fields in requests and responses
 */

import { NextRequest, NextResponse } from 'next/server';
import {
  encryptUserData,
  decryptUserData,
  encryptOAuthTokens,
  decryptOAuthTokens,
  encryptPortfolioContact,
  decryptPortfolioContact,
  encryptAILogs,
  decryptAILogs,
  hashIPAddress,
} from '@/lib/services/encryption-service';
import { logger } from '@/lib/utils/logger';
import type { EncryptedUser } from '@/lib/services/encryption-service';

export type EncryptionConfig = {
  encryptRequest?: boolean;
  decryptResponse?: boolean;
  fields?: string[];
  type?: 'user' | 'oauth' | 'portfolio' | 'ai' | 'analytics';
};

/**
 * Middleware to handle automatic encryption/decryption
 */
export function withEncryption(
  handler: (req: NextRequest) => Promise<NextResponse>,
  config: EncryptionConfig = {}
) {
  return async (req: NextRequest): Promise<NextResponse> => {
    try {
      let processedReq = req;

      // Encrypt request data if needed
      if (config.encryptRequest && req.method !== 'GET') {
        const body = await req.json();
        const encryptedBody = await encryptRequestData(body, config);

        // Create new request with encrypted body
        processedReq = new NextRequest(req.url, {
          method: req.method,
          headers: req.headers,
          body: JSON.stringify(encryptedBody),
        });
      }

      // Call the handler
      const response = await handler(processedReq);

      // Decrypt response data if needed
      if (config.decryptResponse && response.status === 200) {
        try {
          const data = await response.json();
          const decryptedData = await decryptResponseData(data, config);

          return NextResponse.json(decryptedData, {
            status: response.status,
            headers: response.headers,
          });
        } catch (_error) {
          // If response is not JSON, return as is
          return response;
        }
      }

      return response;
    } catch (error) {
      logger.error('Encryption middleware error', { error });
      return NextResponse.json(
        { error: 'Internal server error' },
        { status: 500 }
      );
    }
  };
}

/**
 * Encrypt request data based on type
 */
async function encryptRequestData(
  data: unknown,
  config: EncryptionConfig
): Promise<unknown> {
  if (!data || typeof data !== 'object') return data;

  switch (config.type) {
    case 'user':
      return Array.isArray(data)
        ? data.map(item => encryptUserData(item))
        : encryptUserData(data);

    case 'oauth':
      return encryptOAuthTokens(data);

    case 'portfolio':
      return Array.isArray(data)
        ? data.map(item => encryptPortfolioContact(item))
        : encryptPortfolioContact(data);

    case 'ai':
      return encryptAILogs(data);

    case 'analytics':
      // Hash IP addresses in analytics data
      if (typeof data === 'object' && data !== null && 'ip_address' in data) {
        const analyticsData = data as {
          ip_address?: string;
          ip_address_hash?: string;
          [key: string]: unknown;
        };
        if (analyticsData.ip_address) {
          analyticsData.ip_address_hash = hashIPAddress(
            analyticsData.ip_address
          );
          delete analyticsData.ip_address;
        }
        return analyticsData;
      }
      return data;

    default:
      return data;
  }
}

/**
 * Decrypt response data based on type
 */
async function decryptResponseData(
  data: unknown,
  config: EncryptionConfig
): Promise<unknown> {
  if (!data || typeof data !== 'object') return data;

  switch (config.type) {
    case 'user':
      if (Array.isArray(data)) {
        return data.map(item => decryptUserData(item));
      } else if (typeof data === 'object' && 'users' in data) {
        // Handle paginated responses
        const paginatedData = data as {
          users: unknown[];
          [key: string]: unknown;
        };
        return {
          ...paginatedData,
          users: paginatedData.users.map((user: unknown) =>
            decryptUserData(user as Partial<EncryptedUser>)
          ),
        };
      } else {
        return decryptUserData(data);
      }

    case 'oauth':
      return decryptOAuthTokens(data);

    case 'portfolio':
      if (Array.isArray(data)) {
        return data.map(item => decryptPortfolioContact(item));
      } else if (typeof data === 'object' && 'portfolios' in data) {
        // Handle paginated responses
        const paginatedData = data as {
          portfolios: unknown[];
          [key: string]: unknown;
        };
        return {
          ...paginatedData,
          portfolios: paginatedData.portfolios.map((portfolio: unknown) =>
            decryptPortfolioContact(portfolio)
          ),
        };
      } else {
        return decryptPortfolioContact(data);
      }

    case 'ai':
      if (Array.isArray(data)) {
        return data.map(item => decryptAILogs(item));
      } else if (typeof data === 'object' && 'logs' in data) {
        const paginatedData = data as {
          logs: unknown[];
          [key: string]: unknown;
        };
        return {
          ...paginatedData,
          logs: paginatedData.logs.map((log: unknown) => decryptAILogs(log)),
        };
      } else {
        return decryptAILogs(data);
      }

    default:
      return data;
  }
}

