/**
 * Get encryption config for a route
 */
export function getEncryptionConfig(pathname: string): EncryptionConfig | null {
  // Check exact match first
  if (encryptionConfigs[pathname]) {
    return encryptionConfigs[pathname];
  }

  // Check prefix matches for dynamic routes
  for (const [route, config] of Object.entries(encryptionConfigs)) {
    if (pathname.startsWith(route)) {
      return config;
    }
  }

  return null;
}

/**
 * Observability Middleware
 *
 * Combines PostHog analytics and SigNoz APM for API routes
 */

import { NextRequest, NextResponse } from 'next/server';
import { withAPMTracking } from '@/lib/monitoring/unified';
import { getCurrentTraceId, addSpanAttributes } from '@/lib/monitoring/signoz';
import { captureServerEvent } from '@/lib/analytics/posthog/server';
import { recordPerformanceMetric } from '@/lib/monitoring/signoz/metrics';

/**
 * Observability middleware configuration
 */
export interface ObservabilityConfig {
  trackAnalytics?: boolean;
  trackPerformance?: boolean;
  sensitiveFields?: string[];
  customAttributes?: Record<string, unknown>;
}

/**
 * Apply observability to API routes
 */
export function withObservability<T extends (...args: unknown[]) => any>(
  handler: T,
  config: ObservabilityConfig = {}
): T {
  const {
    trackAnalytics = true,
    trackPerformance = true,
    customAttributes = {},
  } = config;

  return withAPMTracking(async (...args: unknown[]) => {
    const req = args[0] as NextRequest;
    const startTime = performance.now();
    const method = req.method;
    const pathname = req.nextUrl.pathname;
    const traceId = getCurrentTraceId();

    // Extract request metadata
    const metadata = {
      method,
      path: pathname,
      trace_id: traceId,
      user_agent: req.headers.get('user-agent'),
      referer: req.headers.get('referer'),
      ...customAttributes,
    };

    // Add span attributes
    addSpanAttributes({
      'http.method': method,
      'http.path': pathname,
      'http.user_agent': req.headers.get('user-agent') || 'unknown',
      ...customAttributes,
    });

    try {
      // Execute the handler
      const response = await handler(req, ...args);
      const duration = performance.now() - startTime;

      // Track success metrics
      if (trackPerformance) {
        recordPerformanceMetric('apiResponseTime', duration, {
          method,
          path: pathname,
          status: 'success',
          status_code: response.status,
        });
      }

      // Track analytics event
      if (trackAnalytics && process.env.NODE_ENV === 'production') {
        await captureServerEvent(
          req.headers.get('x-user-id') || 'anonymous',
          'api_request',
          {
            ...metadata,
            duration_ms: duration,
            status_code: response.status,
            success: true,
          }
        );
      }

      // Add trace ID to response headers
      if (traceId) {
        response.headers.set('x-trace-id', traceId);
      }

      return response;
    } catch (error) {
      const duration = performance.now() - startTime;
      const errorMessage =
        error instanceof Error ? error.message : 'Unknown error';
      const statusCode = (error as { statusCode?: number })?.statusCode || 500;

      // Track error metrics
      if (trackPerformance) {
        recordPerformanceMetric('apiErrors', 1, {
          method,
          path: pathname,
          error_type:
            error instanceof Error ? error.constructor.name : 'unknown',
          status_code: statusCode,
        });

        recordPerformanceMetric('apiResponseTime', duration, {
          method,
          path: pathname,
          status: 'error',
          status_code: statusCode,
        });
      }

      // Track analytics event
      if (trackAnalytics && process.env.NODE_ENV === 'production') {
        await captureServerEvent(
          req.headers.get('x-user-id') || 'anonymous',
          'api_error',
          {
            ...metadata,
            duration_ms: duration,
            status_code: statusCode,
            error: errorMessage,
            error_type:
              error instanceof Error ? error.constructor.name : 'unknown',
          }
        );
      }

      // Create error response
      const errorResponse = NextResponse.json(
        {
          error:
            process.env.NODE_ENV === 'production'
              ? 'Internal server error'
              : errorMessage,
          trace_id: traceId,
        },
        { status: statusCode }
      );

      // Add trace ID to error response
      if (traceId) {
        errorResponse.headers.set('x-trace-id', traceId);
      }

      return errorResponse;
    }
  }) as T;
}

/**
 * Extract user context from request
 */
export const extractUserContext = (
  req: NextRequest
): {
  userId?: string;
  sessionId?: string;
  ip?: string;
} => {
  return {
    userId: req.headers.get('x-user-id') || undefined,
    sessionId: req.cookies.get('session_id')?.value,
    ip:
      req.headers.get('x-forwarded-for') ||
      req.headers.get('x-real-ip') ||
      undefined,
  };
};

/**
 * Sanitize request/response data
 */
export const sanitizeData = (
  data: unknown,
  sensitiveFields: string[] = ['password', 'token', 'secret', 'key']
): any => {
  if (!data || typeof data !== 'object') return data;

  const sanitized = { ...data } as Record<string, unknown>;

  for (const field of sensitiveFields) {
    if (field in sanitized) {
      sanitized[field] = '[REDACTED]';
    }
  }

  // Recursively sanitize nested objects
  for (const key in sanitized) {
    if (typeof sanitized[key] === 'object' && sanitized[key] !== null) {
      sanitized[key] = sanitizeData(sanitized[key], sensitiveFields);
    }
  }

  return sanitized;
};

/**
 * Create observability context for manual tracking
 */
export class ObservabilityContext {
  private startTime: number;
  private metadata: Record<string, unknown>;

  constructor(
    private operation: string,
    metadata: Record<string, unknown> = {}
  ) {
    this.startTime = performance.now();
    this.metadata = {
      operation,
      trace_id: getCurrentTraceId(),
      timestamp: new Date().toISOString(),
      ...metadata,
    };
  }

  /**
   * Add additional metadata
   */
  addMetadata(key: string, value: unknown): void {
    this.metadata[key] = value;
  }

  /**
   * Track success
   */
  async success(result?: unknown): Promise<void> {
    const duration = performance.now() - this.startTime;

    // Record metrics
    recordPerformanceMetric('apiResponseTime', duration, {
      operation: this.operation,
      status: 'success',
    });

    // Track analytics
    if (process.env.NODE_ENV === 'production') {
      await captureServerEvent(
        String(this.metadata.userId || 'system'),
        `operation_${this.operation}_success`,
        {
          ...this.metadata,
          duration_ms: duration,
          success: true,
        }
      );
    }
  }

  /**
   * Track failure
   */
  async failure(error: Error): Promise<void> {
    const duration = performance.now() - this.startTime;

    // Record metrics
    recordPerformanceMetric('apiErrors', 1, {
      operation: this.operation,
      error_type: error.constructor.name,
    });

    // Track analytics
    if (process.env.NODE_ENV === 'production') {
      await captureServerEvent(
        String(this.metadata.userId || 'system'),
        `operation_${this.operation}_failed`,
        {
          ...this.metadata,
          duration_ms: duration,
          error: error.message,
          error_type: error.constructor.name,
        }
      );
    }
  }
}

/**
 * Export utilities
 */
export const observability = {
  middleware: withObservability,
  context: ObservabilityContext,
  extractUser: extractUserContext,
  sanitize: sanitizeData,
};

/**
 * Simple rate limiting middleware - delegates to Redis rate limiter
 */


export { withRateLimit } from './redis-rate-limiter';

/**
 * Redis-based Rate Limiter for Production Scalability
 * Replaces in-memory rate limiting with distributed Redis storage
 */

import { NextRequest, NextResponse } from 'next/server';
import { redis, isRedisAvailable } from '@/lib/cache/redis-client';
import { logger } from '@/lib/utils/logger';

export interface RateLimitConfig {
  windowMs: number; // Time window in milliseconds
  max: number; // Max requests per window
  message?: string; // Custom error message
  skipFailedRequests?: boolean;
  skipSuccessfulRequests?: boolean;
  keyGenerator?: (req: NextRequest) => string;
}

export interface RateLimitResult {
  success: boolean;
  limit: number;
  current: number;
  remaining: number;
  resetTime: Date;
  retryAfter?: number;
}

// Default configurations for different endpoint types
export const RATE_LIMIT_CONFIGS = {
  api: {
    windowMs: 60 * 1000, // 1 minute
    max: 100, // 100 requests per minute
    message: 'Too many requests, please try again later.',
  },
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 5, // 5 attempts per 15 minutes
    message: 'Too many authentication attempts, please try again later.',
  },
  ai: {
    windowMs: 60 * 1000, // 1 minute
    max: 10, // 10 AI requests per minute
    message: 'AI rate limit exceeded, please try again later.',
  },
  stripe: {
    windowMs: 60 * 1000, // 1 minute
    max: 20, // 20 payment requests per minute
    message: 'Payment rate limit exceeded, please try again later.',
  },
  upload: {
    windowMs: 60 * 1000, // 1 minute
    max: 30, // 30 uploads per minute
    message: 'Upload rate limit exceeded, please try again later.',
  },
  admin: {
    windowMs: 60 * 1000, // 1 minute
    max: 200, // Higher limit for admin operations
    message: 'Admin rate limit exceeded, please try again later.',
  },
} as const satisfies Record<string, RateLimitConfig>;

/**
 * Redis Rate Limiter Implementation
 */
class RedisRateLimiter {
  private fallbackStore = new Map<
    string,
    { count: number; resetTime: number }
  >();

  /**
   * Check rate limit using Redis or fallback to in-memory
   */
  async checkRateLimit(
    key: string,
    config: RateLimitConfig
  ): Promise<RateLimitResult> {
    if (isRedisAvailable()) {
      return this.checkRedisRateLimit(key, config);
    } else {
      logger.warn('Redis unavailable, falling back to in-memory rate limiting');
      return this.checkMemoryRateLimit(key, config);
    }
  }

  /**
   * Redis-based rate limiting using sliding window
   */
  private async checkRedisRateLimit(
    key: string,
    config: RateLimitConfig
  ): Promise<RateLimitResult> {
    const now = Date.now();
    const window = Math.floor(now / config.windowMs);
    const redisKey = `rate_limit:${key}:${window}`;
    const resetTime = new Date((window + 1) * config.windowMs);

    try {
      // Use Redis pipeline for atomic operations
      const pipeline = redis.pipeline();

      // Increment counter and set expiration
      pipeline.incr(redisKey);
      pipeline.expire(redisKey, Math.ceil(config.windowMs / 1000));

      const results = await pipeline.exec();
      const current = (results?.[0]?.[1] as number) || 0;

      const remaining = Math.max(0, config.max - current);
      const success = current <= config.max;

      if (!success) {
        // Log rate limit violation
        logger.warn('Redis rate limit exceeded', {
          key,
          current,
          max: config.max,
          window,
        });
      }

      return {
        success,
        limit: config.max,
        current,
        remaining,
        resetTime,
        retryAfter: success
          ? undefined
          : Math.ceil((resetTime.getTime() - now) / 1000),
      };
    } catch (error) {
      logger.error('Redis rate limit error, falling back to memory', { error });
      return this.checkMemoryRateLimit(key, config);
    }
  }

  /**
   * In-memory fallback rate limiting
   */
  private checkMemoryRateLimit(
    key: string,
    config: RateLimitConfig
  ): Promise<RateLimitResult> {
    const now = Date.now();
    const window = Math.floor(now / config.windowMs);
    const windowKey = `${key}:${window}`;
    const resetTime = new Date((window + 1) * config.windowMs);

    // Cleanup old entries (run occasionally)
    if (Math.random() < 0.01) {
      this.cleanupMemoryStore(now);
    }

    // Get or create rate limit data
    let rateLimitData = this.fallbackStore.get(windowKey);

    if (!rateLimitData) {
      rateLimitData = { count: 0, resetTime: resetTime.getTime() };
      this.fallbackStore.set(windowKey, rateLimitData);
    }

    // Increment counter
    rateLimitData.count++;

    const remaining = Math.max(0, config.max - rateLimitData.count);
    const success = rateLimitData.count <= config.max;

    if (!success) {
      logger.warn('Memory rate limit exceeded', {
        key: windowKey,
        current: rateLimitData.count,
        max: config.max,
      });
    }

    return Promise.resolve({
      success,
      limit: config.max,
      current: rateLimitData.count,
      remaining,
      resetTime,
      retryAfter: success
        ? undefined
        : Math.ceil((resetTime.getTime() - now) / 1000),
    });
  }

  /**
   * Cleanup expired entries from memory store
   */
  private cleanupMemoryStore(now: number): void {
    const maxSize = 10000; // Prevent unbounded growth

    // Clean up expired entries
    for (const [key, value] of this.fallbackStore.entries()) {
      if (value.resetTime < now) {
        this.fallbackStore.delete(key);
      }
    }

    // If still too large, remove oldest entries
    if (this.fallbackStore.size > maxSize) {
      const entries = Array.from(this.fallbackStore.entries()).sort(
        (a, b) => a[1].resetTime - b[1].resetTime
      );

      const toRemove = entries.slice(0, entries.length - maxSize);
      toRemove.forEach(([key]) => this.fallbackStore.delete(key));
    }
  }

  /**
   * Get client identifier from request
   */
  getClientId(request: NextRequest): string {
    // Try to get real IP from various headers
    const forwardedFor = request.headers.get('x-forwarded-for');
    const realIp = request.headers.get('x-real-ip');
    const cfConnectingIp = request.headers.get('cf-connecting-ip');

    // Use the first available IP
    const ip =
      forwardedFor?.split(',')[0]?.trim() ||
      realIp ||
      cfConnectingIp ||
      'unknown';

    // Include the path in the key to have separate limits per endpoint
    const path = new URL(request.url).pathname;
    return `${ip}:${path}`;
  }

  /**
   * Get authenticated user ID for user-specific rate limiting
   */
  getUserId(request: NextRequest): string | null {
    // Extract user ID from JWT token or session
    const authHeader = request.headers.get('authorization');
    if (authHeader?.startsWith('Bearer ')) {
      try {
        // In a real implementation, decode and verify the JWT
        // For now, return a placeholder
        return 'user_from_token';
      } catch (error) {
        logger.debug('Failed to extract user from token', { error });
      }
    }
    return null;
  }

  /**
   * Determine rate limit config based on path and user
   */
  getConfigForPath(
    path: string,
    isAuthenticated: boolean = false
  ): RateLimitConfig {
    // Admin routes get higher limits
    if (path.startsWith('/api/admin/')) {
      return RATE_LIMIT_CONFIGS.admin;
    }

    // Authentication routes
    if (path.startsWith('/api/auth/') || path.includes('/auth/')) {
      return RATE_LIMIT_CONFIGS.auth;
    }

    // AI routes
    if (
      path.startsWith('/api/ai/') ||
      path.includes('/enhance') ||
      path.includes('/optimize')
    ) {
      return RATE_LIMIT_CONFIGS.ai;
    }

    // Payment routes
    if (path.startsWith('/api/stripe/') || path.includes('/payment')) {
      return RATE_LIMIT_CONFIGS.stripe;
    }

    // Upload routes
    if (path.includes('/upload') || path.includes('/import')) {
      return RATE_LIMIT_CONFIGS.upload;
    }

    // General API routes
    if (path.startsWith('/api/')) {
      const config: RateLimitConfig = {
        windowMs: RATE_LIMIT_CONFIGS.api.windowMs,
        max: RATE_LIMIT_CONFIGS.api.max,
        message: RATE_LIMIT_CONFIGS.api.message,
      };

      // Authenticated users get higher limits
      if (isAuthenticated) {
        config.max = Math.floor(config.max * 1.5);
      }

      return config;
    }

    // No rate limiting for non-API routes
    return { windowMs: 0, max: Infinity };
  }
}

// Singleton instance
const rateLimiter = new RedisRateLimiter();

/**
 * Rate limiting middleware function
 */
export async function withRateLimit<T extends unknown[]>(
  handler: (...args: T) => Promise<NextResponse>,
  type: keyof typeof RATE_LIMIT_CONFIGS | RateLimitConfig = 'api',
  customLimit?: number
): Promise<(...args: T) => Promise<NextResponse>> {
  return async (...args: T): Promise<NextResponse> => {
    // Extract request from arguments (assuming first arg is NextRequest)
    const request = args[0] as NextRequest;

    if (!request || typeof request.url !== 'string') {
      // If we can't get the request, proceed without rate limiting
      return handler(...args);
    }

    try {
      const path = new URL(request.url).pathname;

      // Skip rate limiting for non-API routes
      if (!path.startsWith('/api/')) {
        return handler(...args);
      }

      // Get configuration
      const config =
        typeof type === 'string'
          ? { ...RATE_LIMIT_CONFIGS[type] }
          : { ...type };

      // Apply custom limit if provided
      if (customLimit !== undefined) {
        config.max = customLimit;
      }

      // Skip if no rate limiting for this config
      if (config.max === Infinity) {
        return handler(...args);
      }

      // Generate rate limit key
      const clientId = rateLimiter.getClientId(request);
      const userId = rateLimiter.getUserId(request);
      const key = userId ? `user:${userId}` : `ip:${clientId}`;

      // Check rate limit
      const result = await rateLimiter.checkRateLimit(key, config);

      if (!result.success) {
        // Rate limit exceeded
        return NextResponse.json(
          {
            error: config.message || 'Too many requests',
            retryAfter: result.retryAfter,
          },
          {
            status: 429,
            headers: {
              'Retry-After': result.retryAfter?.toString() || '60',
              'X-RateLimit-Limit': result.limit.toString(),
              'X-RateLimit-Remaining': result.remaining.toString(),
              'X-RateLimit-Reset': result.resetTime.toISOString(),
            },
          }
        );
      }

      // Execute handler
      const response = await handler(...args);

      // Add rate limit headers to successful responses
      response.headers.set('X-RateLimit-Limit', result.limit.toString());
      response.headers.set(
        'X-RateLimit-Remaining',
        result.remaining.toString()
      );
      response.headers.set('X-RateLimit-Reset', result.resetTime.toISOString());

      return response;
    } catch (error) {
      logger.error('Rate limiting error, proceeding without limit', { error });
      return handler(...args);
    }
  };
}

/**
 * Edge middleware compatible rate limiter
 */
export async function redisRateLimitMiddleware(
  request: NextRequest,
  customConfig?: Partial<RateLimitConfig>
): Promise<NextResponse | null> {
  try {
    const path = new URL(request.url).pathname;
    const userId = rateLimiter.getUserId(request);
    const baseConfig = rateLimiter.getConfigForPath(path, !!userId);
    const config = { ...baseConfig, ...customConfig };

    // Skip if no rate limiting for this path
    if (config.max === Infinity) {
      return null;
    }

    // Generate rate limit key
    const clientId = rateLimiter.getClientId(request);
    const key = userId ? `user:${userId}` : `ip:${clientId}`;

    // Check rate limit
    const result = await rateLimiter.checkRateLimit(key, config);

    if (!result.success) {
      // Rate limit exceeded
      return NextResponse.json(
        {
          error: config.message || 'Too many requests',
          retryAfter: result.retryAfter,
        },
        {
          status: 429,
          headers: {
            'Retry-After': result.retryAfter?.toString() || '60',
            'X-RateLimit-Limit': result.limit.toString(),
            'X-RateLimit-Remaining': result.remaining.toString(),
            'X-RateLimit-Reset': result.resetTime.toISOString(),
          },
        }
      );
    }

    // Add rate limit headers
    const response = NextResponse.next();
    response.headers.set('X-RateLimit-Limit', result.limit.toString());
    response.headers.set('X-RateLimit-Remaining', result.remaining.toString());
    response.headers.set('X-RateLimit-Reset', result.resetTime.toISOString());

    return null; // Continue to next middleware
  } catch (error) {
    logger.error('Redis rate limit middleware error', { error });
    return null; // Continue without rate limiting on error
  }
}

