import { NextRequest, NextResponse } from 'next/server';
import { createHash, randomBytes } from 'crypto';

import { logger } from '@/lib/utils/logger';

/**
 * @fileoverview Security middleware for API routes
 * Provides CSRF protection, rate limiting, and other security measures
 */

/**
 * CSRF Token Configuration
 */
const CSRF_TOKEN_HEADER = 'X-CSRF-Token';
const CSRF_COOKIE_NAME = '_csrf_token';
const CSRF_TOKEN_LENGTH = 32;

/**
 * Generate a cryptographically secure CSRF token
 */
export function generateCSRFToken(): string {
  return randomBytes(CSRF_TOKEN_LENGTH).toString('hex');
}

/**
 * Validate CSRF token from request
 */
export function validateCSRFToken(request: NextRequest): boolean {
  // Skip CSRF validation for GET, HEAD, OPTIONS requests
  if (['GET', 'HEAD', 'OPTIONS'].includes(request.method)) {
    return true;
  }

  const tokenFromHeader = request.headers.get(CSRF_TOKEN_HEADER);
  const tokenFromCookie = request.cookies.get(CSRF_COOKIE_NAME)?.value;

  if (!tokenFromHeader || !tokenFromCookie) {
    logger.warn('CSRF validation failed: missing tokens', {
      hasHeader: !!tokenFromHeader,
      hasCookie: !!tokenFromCookie,
      method: request.method,
      path: request.nextUrl.pathname,
    });
    return false;
  }

  // Use timing-safe comparison
  try {
    if (!tokenFromHeader || !tokenFromCookie) {
      return false;
    }

    const headerBuffer = Buffer.from(tokenFromHeader, 'hex');
    const cookieBuffer = Buffer.from(tokenFromCookie, 'hex');

    if (headerBuffer.length !== cookieBuffer.length) {
      return false;
    }

    let result = 0;
    for (let i = 0; i < headerBuffer.length; i++) {
      result |= (headerBuffer[i] ?? 0) ^ (cookieBuffer[i] ?? 0);
    }

    return result === 0;
  } catch (error) {
    logger.error('CSRF token validation error:', error as Error);
    return false;
  }
}

/**
 * Extract client IP address from request
 */
function getClientIP(request: NextRequest): string {
  // Try multiple headers to get the real client IP
  const xForwardedFor = request.headers.get('x-forwarded-for');
  const xRealIP = request.headers.get('x-real-ip');
  const cfConnectingIP = request.headers.get('cf-connecting-ip');

  if (xForwardedFor) {
    return xForwardedFor.split(',')[0]?.trim() || 'unknown';
  }

  if (xRealIP) {
    return xRealIP;
  }

  if (cfConnectingIP) {
    return cfConnectingIP;
  }

  // Fallback to connection remote address if available
  return 'unknown';
}

/**
 * Request fingerprinting for additional security
 */
export function generateRequestFingerprint(request: NextRequest): string {
  const components = [
    request.headers.get('user-agent') || '',
    request.headers.get('accept-language') || '',
    request.headers.get('accept-encoding') || '',
    getClientIP(request),
  ];

  return createHash('sha256').update(components.join('|')).digest('hex');
}

/**
 * Check for suspicious request patterns
 */
export function detectSuspiciousActivity(request: NextRequest): {
  isSuspicious: boolean;
  reasons: string[];
} {
  const reasons: string[] = [];

  // Check for suspicious user agents
  const userAgent = request.headers.get('user-agent') || '';
  const suspiciousUAPatterns = [
    /bot/i,
    /crawler/i,
    /spider/i,
    /scraper/i,
    /^$/,
  ];

  if (suspiciousUAPatterns.some(pattern => pattern.test(userAgent))) {
    reasons.push('suspicious_user_agent');
  }

  // Check for missing expected headers
  if (!request.headers.get('accept')) {
    reasons.push('missing_accept_header');
  }

  // Check for suspicious referer patterns
  const referer = request.headers.get('referer');
  if (referer) {
    try {
      const refererUrl = new URL(referer);
      const currentHost = request.nextUrl.host;

      // Allow common staging/development patterns
      const allowedHosts = [
        'localhost',
        'prisma.madfam.io',
        '*.vercel.app',
        '*.netlify.app',
      ];

      const isAllowed = allowedHosts.some(pattern => {
        if (pattern.startsWith('*')) {
          return refererUrl.hostname.endsWith(pattern.slice(1));
        }
        return (
          refererUrl.hostname === pattern || refererUrl.hostname === currentHost
        );
      });

      if (!isAllowed) {
        reasons.push('suspicious_referer');
      }
    } catch (_error) {
      // Invalid referer URL
      reasons.push('invalid_referer');
    }
  }

  // Check for rapid sequential requests (basic rate limiting check)
  // This would ideally use Redis, but for now we'll just flag it

  const forwardedFor = request.headers.get('x-forwarded-for');
  if (forwardedFor) {
    const forwardedIPs = forwardedFor.split(',');
    if (forwardedIPs.length > 5) {
      reasons.push('multiple_proxies');
    }
  }

  return {
    isSuspicious: reasons.length > 0,
    reasons,
  };
}

/**
 * Security middleware wrapper for API routes
 */
export function withSecurity<T extends (...args: unknown[]) => any>(
  handler: T,
  options: {
    requireCSRF?: boolean;
    allowedMethods?: string[];
    checkSuspiciousActivity?: boolean;
  } = {}
): T {
  const {
    requireCSRF = true,
    allowedMethods = ['GET', 'POST', 'PUT', 'DELETE', 'PATCH'],
    checkSuspiciousActivity = true,
  } = options;

  return (async (request: NextRequest, ...args: unknown[]) => {
    try {
      // Method validation
      if (!allowedMethods.includes(request.method)) {
        logger.warn('Method not allowed', {
          method: request.method,
          path: request.nextUrl.pathname,
          ip: getClientIP(request),
        });

        return NextResponse.json(
          { error: 'Method not allowed' },
          {
            status: 405,
            headers: {
              Allow: allowedMethods.join(', '),
            },
          }
        );
      }

      // CSRF protection
      if (requireCSRF && !validateCSRFToken(request)) {
        logger.warn('CSRF validation failed', {
          method: request.method,
          path: request.nextUrl.pathname,
          ip: getClientIP(request),
        });

        return NextResponse.json(
          { error: 'Invalid CSRF token' },
          { status: 403 }
        );
      }

      // Suspicious activity detection
      if (checkSuspiciousActivity) {
        const { isSuspicious, reasons } = detectSuspiciousActivity(request);

        if (isSuspicious) {
          logger.warn('Suspicious activity detected', {
            reasons,
            method: request.method,
            path: request.nextUrl.pathname,
            ip: getClientIP(request),
            userAgent: request.headers.get('user-agent'),
          });

          // For now, just log it. In production, might want to rate limit or block
        }
      }

      // Add security headers to response
      const response = await handler(request, ...args);

      if (response instanceof NextResponse) {
        // Add CSRF token for future requests
        if (!request.cookies.get(CSRF_COOKIE_NAME)) {
          const csrfToken = generateCSRFToken();
          response.cookies.set(CSRF_COOKIE_NAME, csrfToken, {
            httpOnly: true,
            secure: process.env.NODE_ENV === 'production',
            sameSite: 'strict',
            maxAge: 60 * 60 * 24, // 24 hours
          });

          response.headers.set('X-CSRF-Token', csrfToken);
        }

        // Add additional security headers
        response.headers.set('X-Content-Type-Options', 'nosniff');
        response.headers.set('X-Frame-Options', 'DENY');
        response.headers.set(
          'Referrer-Policy',
          'strict-origin-when-cross-origin'
        );
      }

      return response;
    } catch (error) {
      logger.error('Security middleware error:', error as Error);
      return NextResponse.json(
        { error: 'Internal server error' },
        { status: 500 }
      );
    }
  }) as T;
}

/**
 * Content validation for API requests
 */
export function validateRequestContent(request: NextRequest): {
  isValid: boolean;
  error?: string;
} {
  const contentType = request.headers.get('content-type');

  // For POST/PUT/PATCH requests, require proper content type
  if (['POST', 'PUT', 'PATCH'].includes(request.method)) {
    if (!contentType) {
      return { isValid: false, error: 'Content-Type header required' };
    }

    if (
      !contentType.includes('application/json') &&
      !contentType.includes('multipart/form-data') &&
      !contentType.includes('application/x-www-form-urlencoded')
    ) {
      return { isValid: false, error: 'Unsupported content type' };
    }
  }

  // Check for reasonable content length
  const contentLength = request.headers.get('content-length');
  if (contentLength) {
    const length = parseInt(contentLength, 10);
    if (length > 10 * 1024 * 1024) {
      // 10MB limit
      return { isValid: false, error: 'Request too large' };
    }
  }

  return { isValid: true };
}

/**
 * Security-hardened error response
 */
export function secureErrorResponse(
  message: string = 'An error occurred',
  status: number = 500,
  details?: Record<string, unknown>
): NextResponse {
  // In production, don't leak sensitive information
  const response =
    process.env.NODE_ENV === 'production'
      ? { error: message }
      : { error: message, details };

  return NextResponse.json(response, { status });
}

import DOMPurify from 'isomorphic-dompurify';
import { NextRequest, NextResponse } from 'next/server';
import { z, ZodError, ZodSchema } from 'zod';

import { logger } from '@/lib/utils/logger';

/**
 * Input validation middleware for API routes
 * Provides sanitization and validation for request data
 */

interface ValidationOptions {
  body?: ZodSchema;
  query?: ZodSchema;
  params?: ZodSchema;
  headers?: ZodSchema;
  sanitize?: boolean;
}

interface ValidatedRequest extends NextRequest {
  validated?: {
    body?: unknown;
    query?: unknown;
    params?: unknown;
    headers?: unknown;
  };
}

/**
 * Sanitize string input to prevent XSS
 */
function sanitizeString(input: string): string {
  return DOMPurify.sanitize(input, {
    ALLOWED_TAGS: [],
    ALLOWED_ATTR: [],
  });
}

/**
 * Recursively sanitize all string values in an object
 */
function sanitizeObject(obj: unknown): unknown {
  if (typeof obj === 'string') {
    return sanitizeString(obj);
  }

  if (Array.isArray(obj)) {
    return obj.map(sanitizeObject);
  }

  if (obj !== null && typeof obj === 'object') {
    const sanitized: any = {};
    for (const [key, value] of Object.entries(obj)) {
      sanitized[key] = sanitizeObject(value);
    }
    return sanitized;
  }

  return obj;
}

/**
 * Parse and validate request body
 */
async function parseBody(request: NextRequest): Promise<unknown> {
  const contentType = request.headers.get('content-type') || '';

  try {
    if (contentType.includes('application/json')) {
      return await request.json();
    }

    if (contentType.includes('multipart/form-data')) {
      const formData = await request.formData();
      const body: Record<string, unknown> = {};

      formData.forEach((value, key) => {
        if (value instanceof File) {
          // Handle file uploads
          body[key] = {
            name: value.name,
            type: value.type,
            size: value.size,
            // Don't include file content in validation
          };
        } else {
          body[key] = value.toString();
        }
      });

      return body;
    }

    if (contentType.includes('application/x-www-form-urlencoded')) {
      const text = await request.text();
      const params = new URLSearchParams(text);
      const body: Record<string, unknown> = {};

      params.forEach((value, key) => {
        body[key] = value;
      });

      return body;
    }

    // Default to empty object for other content types
    return {};
  } catch (error) {
    logger.error('Failed to parse request body', error as Error);
    return {};
  }
}

/**
 * Validation middleware factory
 */
export function validateRequest(options: ValidationOptions) {
  return async function middleware(
    request: ValidatedRequest,
    context?: unknown
  ): Promise<NextResponse | null> {
    try {
      const validated: any = {};

      // Validate body
      if (options.body && ['POST', 'PUT', 'PATCH'].includes(request.method)) {
        const body = await parseBody(request);
        const sanitizedBody =
          options.sanitize !== false ? sanitizeObject(body) : body;
        validated.body = options.body.parse(sanitizedBody);
      }

      // Validate query parameters
      if (options.query) {
        const { searchParams } = new URL(request.url);
        const query: Record<string, unknown> = {};

        searchParams.forEach((value, key) => {
          query[key] = value;
        });

        const sanitizedQuery =
          options.sanitize !== false ? sanitizeObject(query) : query;
        validated.query = options.query.parse(sanitizedQuery);
      }

      // Validate route params
      const contextWithParams = context as { params?: unknown };
      if (options.params && contextWithParams?.params) {
        const sanitizedParams =
          options.sanitize !== false
            ? sanitizeObject(contextWithParams.params)
            : contextWithParams.params;
        validated.params = options.params.parse(sanitizedParams);
      }

      // Validate headers
      if (options.headers) {
        const headers: Record<string, unknown> = {};

        request.headers.forEach((value, key) => {
          headers[key] = value;
        });

        validated.headers = options.headers.parse(headers);
      }

      // Attach validated data to request
      request.validated = validated;

      // Continue to next middleware
      return null;
    } catch (error) {
      if (error instanceof ZodError) {
        logger.warn('Validation error', {
          errors: error.errors,
          url: request.url,
          method: request.method,
        });

        return NextResponse.json(
          {
            error: 'Validation failed',
            details: error.errors.map(err => ({
              path: err.path.join('.'),
              message: err.message,
            })),
          },
          { status: 400 }
        );
      }

      logger.error('Unexpected validation error', error as Error);

      return NextResponse.json(
        { error: 'Internal server error' },
        { status: 500 }
      );
    }
  };
}

/**
 * Common validation schemas
 */
const commonSchemas = {
  // ID validation
  id: z.string().uuid('Invalid ID format'),

  // Email validation
  email: z.string().email('Invalid email format').toLowerCase(),

  // Password validation
  password: z
    .string()
    .min(12, 'Password must be at least 12 characters')
    .regex(/[A-Z]/, 'Password must contain at least one uppercase letter')
    .regex(/[a-z]/, 'Password must contain at least one lowercase letter')
    .regex(/[0-9]/, 'Password must contain at least one number')
    .regex(
      /[^A-Za-z0-9]/,
      'Password must contain at least one special character'
    ),

  // URL validation
  url: z.string().url('Invalid URL format'),

  // Pagination
  pagination: z.object({
    page: z.coerce.number().int().min(1).default(1),
    limit: z.coerce.number().int().min(1).max(100).default(20),
    sortBy: z.string().optional(),
    sortOrder: z.enum(['asc', 'desc']).default('desc'),
  }),

  // Date range
  dateRange: z
    .object({
      startDate: z.string().datetime().optional(),
      endDate: z.string().datetime().optional(),
    })
    .refine(
      data => {
        if (data.startDate && data.endDate) {
          return new Date(data.startDate) <= new Date(data.endDate);
        }
        return true;
      },
      { message: 'Start date must be before end date' }
    ),

  // File upload
  fileUpload: z.object({
    name: z.string().max(255),
    type: z.string(),
    size: z.number().max(10 * 1024 * 1024, 'File size must be less than 10MB'),
  }),
};

/**
 * Helper function to create validated API route handler
 */
function createValidatedHandler<T extends ValidationOptions>(
  validation: T,
  handler: (
    request: NextRequest & { validated: unknown },
    context?: unknown
  ) => Promise<Response>
) {
  return async function (
    request: NextRequest,
    context?: unknown
  ): Promise<Response> {
    const validationResult = await validateRequest(validation)(
      request,
      context
    );

    if (validationResult) {
      return validationResult;
    }

    return handler(request as NextRequest & { validated: unknown }, context);
  };
}

/**
 * Alias for createValidatedHandler to match expected import
 */
export const withValidation = createValidatedHandler;

export { createValidatedHandler, commonSchemas };

import { createClient } from '@supabase/supabase-js';

import type {
  AuthResponse,
  User,
  Session,
  SupabaseClient,
  AuthError,
} from '@supabase/supabase-js';

// Create Supabase client factory function for better testability
function createSupabaseClient(): SupabaseClient | null {
  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
  const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

  if (!supabaseUrl || !supabaseAnonKey) {
    return null;
  }

  return createClient(supabaseUrl, supabaseAnonKey);
}

// Default client instance
let supabaseInstance: SupabaseClient | null = null;
let supabaseInitialized = false;

function getSupabaseClient(): SupabaseClient | null {
  if (!supabaseInitialized) {
    supabaseInstance = createSupabaseClient();
    supabaseInitialized = true;
  }
  return supabaseInstance;
}

// Helper function to ensure Supabase is configured
function requireSupabaseClient(): SupabaseClient {
  const client = getSupabaseClient();
  if (!client) {
    throw new Error(
      'Authentication service not configured. Please set up Supabase environment variables.'
    );
  }
  return client;
}

// For testing: allow setting a mock client
export function setSupabaseClient(client: SupabaseClient): void {
  supabaseInstance = client;
  supabaseInitialized = true;
}

// For testing: reset to default client
export function resetSupabaseClient(): void {
  supabaseInstance = null;
  supabaseInitialized = false;
}

// Types for authentication
export interface SignUpCredentials {
  email: string;
  password: string;
  fullName?: string;
}

export interface SignInCredentials {
  email: string;
  password: string;
}

export type OAuthProvider = 'google' | 'github' | 'linkedin_oidc';

// Validation helpers
function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

function isValidPassword(password: string): boolean {
  // Minimum 12 characters for strong security
  if (password.length < 12) return false;

  // Require at least one of each character type
  const hasUppercase = /[A-Z]/.test(password);
  const hasLowercase = /[a-z]/.test(password);
  const hasNumbers = /\d/.test(password);
  const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);

  // Check for common weak patterns
  const commonPatterns = [
    /123456/,
    /password/i,
    /qwerty/i,
    /(.)\1{2,}/, // Repeated characters (3+ times)
  ];

  const hasWeakPattern = commonPatterns.some(pattern => pattern.test(password));

  return (
    hasUppercase &&
    hasLowercase &&
    hasNumbers &&
    hasSpecialChar &&
    !hasWeakPattern
  );
}

/**
 * Get password strength rating
 */
export function getPasswordStrength(password: string): 'weak' | 'medium' | 'strong' {
  if (password.length < 8) return 'weak';
  if (password.length < 12) return 'medium';
  if (!isValidPassword(password)) return 'medium';
  return 'strong';
}

/**
 * Sign up a new user
 */
export async function signUp(
  email: string,
  password: string,
  fullName?: string
): Promise<AuthResponse> {
  const supabase = requireSupabaseClient();

  // Client-side validation
  if (!isValidEmail(email)) {
    throw new Error('Invalid email format');
  }

  if (!isValidPassword(password)) {
    throw new Error(
      'Password must be at least 12 characters with uppercase, lowercase, numbers, and special characters'
    );
  }

  const signUpData: {
    email: string;
    password: string;
    options?: {
      data: {
        full_name: string;
      };
    };
  } = {
    email,
    password,
  };

  if (fullName) {
    signUpData.options = {
      data: {
        full_name: fullName,
      },
    };
  }

  const response = await supabase.auth.signUp(signUpData);
  return response;
}

/**
 * Sign in an existing user
 */
export async function signIn(
  email: string,
  password: string
): Promise<AuthResponse> {
  const supabase = requireSupabaseClient();
  const response = await supabase.auth.signInWithPassword({
    email,
    password,
  });

  return response;
}

/**
 * Sign in with OAuth provider
 */
export async function signInWithOAuth(
  provider: OAuthProvider,
  redirectTo?: string
): Promise<{ data: { url: string | null } | null; error: AuthError | null }> {
  const supabase = requireSupabaseClient();

  const defaultRedirectTo =
    typeof window !== 'undefined'
      ? `${window.location.origin}/auth/callback`
      : `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/auth/callback`;
  const response = await supabase.auth.signInWithOAuth({
    provider,
    options: {
      redirectTo: redirectTo || defaultRedirectTo,
    },
  });

  return response;
}

/**
 * Sign out the current user
 */
export async function signOut(): Promise<{ error: AuthError | null }> {
  const supabase = requireSupabaseClient();
  const response = await supabase.auth.signOut();
  return response;
}

/**
 * Get the current authenticated user
 */
export async function getCurrentUser(): Promise<{
  data: { user: User | null };
  error: AuthError | null;
}> {
  const supabase = requireSupabaseClient();
  const response = await supabase.auth.getUser();
  return response;
}

/**
 * Get the current session
 */
export async function getCurrentSession(): Promise<{
  data: { session: Session | null };
  error: AuthError | null;
}> {
  const supabase = requireSupabaseClient();
  const response = await supabase.auth.getSession();
  return response;
}

/**
 * Listen to authentication state changes
 */
export function onAuthStateChange(
  callback: (event: string, session: Session | null) => void
) {
  const supabase = requireSupabaseClient();
  return supabase.auth.onAuthStateChange(callback);
}

/**
 * Refresh the current session
 */
export async function refreshSession(): Promise<AuthResponse> {
  const supabase = requireSupabaseClient();
  const response = await supabase.auth.refreshSession();
  return response;
}

/**
 * Send a password reset email
 */
export async function resetPassword(
  email: string
): Promise<{ error: AuthError | null }> {
  if (!isValidEmail(email)) {
    throw new Error('Invalid email format');
  }

  const defaultRedirectTo =
    typeof window !== 'undefined'
      ? `${window.location.origin}/auth/reset-password`
      : `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/auth/reset-password`;

  const supabase = requireSupabaseClient();
  const response = await supabase.auth.resetPasswordForEmail(email, {
    redirectTo: defaultRedirectTo,
  });

  return response;
}

/**
 * Update user password
 */
export async function updatePassword(
  password: string
): Promise<{ error: AuthError | null }> {
  if (!isValidPassword(password)) {
    throw new Error(
      'Password must be at least 12 characters with uppercase, lowercase, numbers, and special characters'
    );
  }

  const supabase = requireSupabaseClient();
  const response = await supabase.auth.updateUser({ password });
  return response;
}

/**
 * Update user metadata
 */
export async function updateUserMetadata(
  metadata: Record<string, unknown>
): Promise<{ error: AuthError | null }> {
  const supabase = requireSupabaseClient();
  const response = await supabase.auth.updateUser({ data: metadata });
  return response;
}

import {
  User,
  AdminPermission,
  AdminRole,
  SubscriptionPlan,
  PermissionContext,
  ADMIN_PERMISSIONS,
  PLAN_FEATURES,
} from '@/types/auth';

// Re-export for convenience
export { PLAN_FEATURES } from '@/types/auth';

/**
 * Role-based Access Control (RBAC) utilities for PRISMA
 * Handles permission checking, role management, and admin mode switching
 */

/**
 * Check if user has specific admin permission
 */
export function hasPermission(
  user: User,
  permission: AdminPermission
): boolean {
  if (user.accountType !== 'admin' || !user.adminProfile) {
    return false;
  }

  // Only check permissions if user is in admin mode
  if (!user.adminProfile.isInAdminMode) {
    return false;
  }

  return user.adminProfile.permissions.includes(permission);
}

/**
 * Check if user can access a feature based on subscription plan
 */
export function canAccessFeature(
  user: User,
  feature: keyof typeof PLAN_FEATURES.free
): boolean {
  if (user.accountType === 'admin') {
    return true; // Admins have access to all features
  }

  if (!user.customerProfile) {
    return false;
  }

  const planFeatures = PLAN_FEATURES[user.customerProfile.subscriptionPlan];
  return Boolean(planFeatures[feature]);
}

/**
 * Check if user has reached their plan limits
 */
function hasReachedLimit(
  user: User,
  limitType: 'portfolios' | 'aiEnhancements',
  currentUsage: number
): boolean {
  if (user.accountType === 'admin') {
    return false; // Admins have no limits
  }

  if (!user.customerProfile) {
    return true; // No profile means no access
  }

  const planFeatures = PLAN_FEATURES[user.customerProfile.subscriptionPlan];

  switch (limitType) {
    case 'portfolios':
      return (
        planFeatures.maxPortfolios !== -1 &&
        currentUsage >= planFeatures.maxPortfolios
      );
    case 'aiEnhancements':
      return (
        planFeatures.maxAiEnhancements !== -1 &&
        currentUsage >= planFeatures.maxAiEnhancements
      );
    default:
      return false;
  }
}

/**
 * Get user's permission level description
 */
export function getPermissionLevel(user: User): string {
  if (user.accountType === 'admin' && user.adminProfile) {
    const roleDescriptions: Record<AdminRole, string> = {
      admin_viewer: 'View-Only Admin',
      admin_support: 'Support Admin',
      admin_moderator: 'Content Moderator',
      admin_manager: 'Admin Manager',
      admin_developer: 'Developer Admin',
      admin_architect: 'System Architect',
    };
    return roleDescriptions[user.adminProfile.adminRole];
  }

  if (user.customerProfile) {
    const planDescriptions: Record<SubscriptionPlan, string> = {
      free: 'Free User',
      pro: 'Pro Subscriber',
      business: 'Business Subscriber',
      enterprise: 'Enterprise Client',
    };
    return planDescriptions[user.customerProfile.subscriptionPlan];
  }

  return 'Unknown';
}

/**
 * Get all permissions for an admin role
 */
function getRolePermissions(role: AdminRole): AdminPermission[] {
  return ADMIN_PERMISSIONS[role] || [];
}

/**
 * Check if admin role can perform specific actions
 */
function canPerformAction(role: AdminRole, action: AdminPermission): boolean {
  return ADMIN_PERMISSIONS[role]?.includes(action) || false;
}

/**
 * Validate if user can switch to admin mode
 */
export function canSwitchToAdminMode(user: User): boolean {
  return (
    user.accountType === 'admin' &&
    user.status === 'active' &&
    user.adminProfile !== undefined
  );
}

/**
 * Validate if user can impersonate other users
 */
function canImpersonateUsers(user: User): boolean {
  return hasPermission(user, 'impersonation:users');
}

/**
 * Get subscription plan display name
 */
function getPlanDisplayName(plan: SubscriptionPlan): string {
  const displayNames: Record<SubscriptionPlan, string> = {
    free: 'PRISMA Free',
    pro: 'PRISMA Pro',
    business: 'PRISMA Business',
    enterprise: 'PRISMA Enterprise',
  };
  return displayNames[plan];
}

/**
 * Get plan pricing information
 */
function getPlanPricing(
  plan: SubscriptionPlan,
  currency: 'USD' | 'MXN' | 'EUR'
) {
  const pricing = {
    free: { USD: 0, MXN: 0, EUR: 0 },
    pro: { USD: 29, MXN: 149, EUR: 25 },
    business: { USD: 79, MXN: 399, EUR: 69 },
    enterprise: { USD: 299, MXN: 1499, EUR: 249 },
  };

  return {
    amount: pricing[plan][currency],
    currency,
    period: plan === 'free' ? null : 'month',
  };
}

/**
 * Check if subscription is active and not expired
 */
export function isSubscriptionActive(user: User): boolean {
  if (user.accountType === 'admin') {
    return true; // Admins always have access
  }

  if (!user.customerProfile) {
    return false;
  }

  const { subscriptionStatus, subscriptionEndDate } = user.customerProfile;

  // Free plan is always considered active
  if (user.customerProfile.subscriptionPlan === 'free') {
    return subscriptionStatus === 'active';
  }

  // Check if subscription is active and not expired
  const isActive =
    subscriptionStatus === 'active' || subscriptionStatus === 'trialing';
  const notExpired = !subscriptionEndDate || new Date() < subscriptionEndDate;

  return isActive && notExpired;
}

/**
 * Get days until subscription expires
 */
export function getDaysUntilExpiration(user: User): number | null {
  if (user.accountType === 'admin' || !user.customerProfile) {
    return null;
  }

  const { subscriptionEndDate, trialEndDate } = user.customerProfile;
  const expirationDate = subscriptionEndDate || trialEndDate;

  if (!expirationDate) {
    return null;
  }

  const now = new Date();
  const diffTime = expirationDate.getTime() - now.getTime();
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  return diffDays > 0 ? diffDays : 0;
}

/**
 * Generate permission context for the current user
 */
function createPermissionContext(user: User): PermissionContext {
  const isAdmin = user.accountType === 'admin';
  const isInAdminMode = (isAdmin && user.adminProfile?.isInAdminMode) || false;
  const permissions =
    isAdmin && user.adminProfile ? user.adminProfile.permissions : [];

  return {
    user,
    isAdmin,
    isInAdminMode,
    permissions,
    subscriptionPlan: user.customerProfile?.subscriptionPlan,
    canAccess: (permission: AdminPermission) => hasPermission(user, permission),
    canAccessFeature: (feature: string) =>
      canAccessFeature(user, feature as keyof typeof PLAN_FEATURES.free),
    switchToAdminMode: () => {
      // This would be implemented in the auth service
      throw new Error('switchToAdminMode must be implemented by auth service');
    },
    switchToUserMode: () => {
      // This would be implemented in the auth service
      throw new Error('switchToUserMode must be implemented by auth service');
    },
  };
}

/**
 * Admin role hierarchy for permission inheritance
 */
export const ADMIN_ROLE_HIERARCHY: Record<AdminRole, number> = {
  admin_viewer: 1,
  admin_support: 2,
  admin_moderator: 3,
  admin_manager: 4,
  admin_developer: 5,
  admin_architect: 6,
};

/**
 * Check if one admin role is higher than another
 */
function isHigherRole(role1: AdminRole, role2: AdminRole): boolean {
  return ADMIN_ROLE_HIERARCHY[role1] > ADMIN_ROLE_HIERARCHY[role2];
}

/**
 * Get required role for specific permission
 */
function getRequiredRoleForPermission(
  permission: AdminPermission
): AdminRole | null {
  for (const [role, permissions] of Object.entries(ADMIN_PERMISSIONS)) {
    if (permissions.includes(permission)) {
      return role as AdminRole;
    }
  }
  return null;
}

