/**
 * Onboarding Hook
 *
 * Provides easy access to onboarding functionality throughout the app
 */

import { useEffect, useCallback } from 'react';
import { useOnboardingStore } from '@/lib/store/onboarding-store';
import { useAuthStore } from '@/lib/store/auth-store';
import { track } from '@/lib/monitoring/unified/events';
import { useRouter } from 'next/navigation';

export interface UseOnboardingOptions {
  autoStart?: boolean;
  skipIfCompleted?: boolean;
}

export function useOnboarding(options: UseOnboardingOptions = {}) {
  const { autoStart = true, skipIfCompleted = true } = options;

  const router = useRouter();
  const { user } = useAuthStore();
  const {
    isOnboarding,
    currentFlow,
    completedFlows,
    tourActive,
    showChecklist,
    preferences,
    startOnboarding,
    completeStep,
    skipStep,
    getCurrentStep,
    getProgress,
    startTour,
    toggleChecklist,
    updatePreferences,
  } = useOnboardingStore();

  // Auto-start onboarding for new users
  useEffect(() => {
    if (!autoStart || !user) return;

    const isNewUser =
      user.created_at &&
      new Date(user.created_at).getTime() > Date.now() - 5 * 60 * 1000;

    if (isNewUser && !completedFlows.includes('new-user-flow')) {
      startOnboarding('new');
    }
  }, [user, autoStart, completedFlows, startOnboarding]);

  // Complete current step with tracking
  const completeCurrentStep = useCallback(
    async (metadata?: Record<string, unknown>) => {
      const currentStep = getCurrentStep();
      if (!currentStep) return;

      await track.user.action(
        'onboarding_step_completed',
        user?.id || 'anonymous',
        async () => completeStep(currentStep.id, metadata),
        {
          step_id: currentStep.id,
          step_title: currentStep.title,
          flow_id: currentFlow?.id,
          ...metadata,
        }
      );
    },
    [completeStep, getCurrentStep, currentFlow, user]
  );

  // Skip current step with tracking
  const skipCurrentStep = useCallback(
    async (reason?: string) => {
      const currentStep = getCurrentStep();
      if (!currentStep) return;

      await track.user.action(
        'onboarding_step_skipped',
        user?.id || 'anonymous',
        async () => skipStep(currentStep.id),
        {
          step_id: currentStep.id,
          step_title: currentStep.title,
          flow_id: currentFlow?.id,
          reason,
        }
      );
    },
    [skipStep, getCurrentStep, currentFlow, user]
  );

  // Start product tour for specific page
  const startPageTour = useCallback(
    (page: 'dashboard' | 'editor' | 'portfolio') => {
      startTour(page);
      track.user.action(
        'product_tour_started',
        user?.id || 'anonymous',
        async () => {},
        { page }
      );
    },
    [startTour, user]
  );

  // Check if user should see onboarding
  const shouldShowOnboarding = useCallback(() => {
    if (!user) return false;

    // New user check
    const isNewUser =
      user.created_at &&
      new Date(user.created_at).getTime() > Date.now() - 24 * 60 * 60 * 1000; // 24 hours

    // Has completed onboarding check
    const hasCompletedOnboarding = completedFlows.includes('new-user-flow');

    return isNewUser && !hasCompletedOnboarding;
  }, [user, completedFlows]);

  // Navigate to next recommended action
  const goToNextAction = useCallback(() => {
    const currentStep = getCurrentStep();
    if (!currentStep) {
      router.push('/dashboard');
      return;
    }

    // Route based on step
    switch (currentStep.id) {
      case 'profile-setup':
        router.push('/settings/profile');
        break;
      case 'first-portfolio':
      case 'enhance-imported':
        router.push('/editor/new');
        break;
      case 'ai-enhancement':
      case 'customize-design':
        router.push('/editor');
        break;
      case 'publish':
        router.push('/dashboard');
        break;
      default:
        router.push('/dashboard');
    }
  }, [getCurrentStep, router]);

  // Get onboarding status
  const getStatus = useCallback(() => {
    return {
      isActive: isOnboarding,
      currentStep: getCurrentStep(),
      progress: getProgress(),
      flowType: currentFlow?.userType,
      isComplete: currentFlow
        ? currentFlow.steps.every(s => s.completed || s.skipped)
        : false,
    };
  }, [isOnboarding, getCurrentStep, getProgress, currentFlow]);

  return {
    // State
    isOnboarding,
    currentFlow,
    currentStep: getCurrentStep(),
    progress: getProgress(),
    tourActive,
    showChecklist,
    preferences,

    // Actions
    startOnboarding,
    completeCurrentStep,
    skipCurrentStep,
    startPageTour,
    toggleChecklist,
    updatePreferences,

    // Utilities
    shouldShowOnboarding,
    goToNextAction,
    getStatus,
  };
}

// Hook for specific onboarding flows
export function useOnboardingFlow(flowType: 'new' | 'returning' | 'imported') {
  const { startOnboarding, currentFlow } = useOnboardingStore();

  const start = useCallback(() => {
    startOnboarding(flowType);
  }, [startOnboarding, flowType]);

  const isActive = currentFlow?.userType === flowType;

  return { start, isActive };
}

// Hook for tracking onboarding events
export function useOnboardingTracking() {
  const { user } = useAuthStore();

  const trackEvent = useCallback(
    async (event: string, properties?: Record<string, unknown>) => {
      await track.user.action(
        `onboarding_${event}`,
        user?.id || 'anonymous',
        async () => {},
        properties
      );
    },
    [user]
  );

  return { trackEvent };
}

/**
 * Error tracking and monitoring utilities
 * Provides centralized error handling with optional Sentry integration
 */

import { NextRequest } from 'next/server';

export interface ErrorReport {
  message: string;
  stack?: string;
  context?: Record<string, unknown>;
  userId?: string;
  sessionId?: string;
  timestamp: number;
  level: 'error' | 'warning' | 'info';
  category: string;
  fingerprint?: string[];
}

export interface PerformanceMetric {
  name: string;
  value: number;
  unit: 'ms' | 'bytes' | 'count';
  tags?: Record<string, string>;
  timestamp: number;
}

/**
 * Error tracking service
 */
class ErrorTracker {
  private isProduction = process.env.NODE_ENV === 'production';
  private sentryDsn = process.env.SENTRY_DSN;
  private enableConsoleLogging = process.env.ENABLE_ERROR_LOGGING !== 'false';

  /**
   * Initialize error tracking
   */
  init(): void {
    if (this.isProduction && this.sentryDsn) {
      // Initialize Sentry in production
      this.initSentry();
    }

    // Global error handlers
    if (typeof window !== 'undefined') {
      window.addEventListener('error', event => {
        this.captureError(new Error(event.message), {
          category: 'javascript',
          context: {
            filename: event.filename,
            lineno: event.lineno,
            colno: event.colno,
          },
        });
      });

      window.addEventListener('unhandledrejection', event => {
        this.captureError(new Error(event.reason), {
          category: 'unhandled-promise',
          context: {
            reason: event.reason,
          },
        });
      });
    }
  }

  /**
   * Initialize Sentry (placeholder for actual implementation)
   */
  private initSentry(): void {
    // This would integrate with @sentry/nextjs
    // For now, we'll use our internal tracking
    // console.log('Sentry would be initialized here in production');
  }

  /**
   * Capture and report errors
   */
  captureError(error: Error, context: Partial<ErrorReport> = {}): void {
    const report: ErrorReport = {
      message: error.message,
      stack: error.stack,
      timestamp: Date.now(),
      level: 'error',
      category: context.category || 'general',
      context: {
        userAgent:
          typeof window !== 'undefined'
            ? window.navigator?.userAgent
            : undefined,
        url: typeof window !== 'undefined' ? window.location?.href : undefined,
        ...context.context,
      },
      userId: context.userId,
      sessionId: context.sessionId,
      fingerprint: context.fingerprint || [error.message],
    };

    // Log to console in development or if enabled
    if (!this.isProduction || this.enableConsoleLogging) {
      console.error('Error captured:', {
        message: report.message,
        category: report.category,
        context: report.context,
        stack: report.stack,
      });
    }

    // Send to external service in production
    if (this.isProduction) {
      this.sendToExternalService(report);
    }

    // Store locally for development
    this.storeLocally(report);
  }

  /**
   * Capture performance metrics
   */
  capturePerformance(metric: PerformanceMetric): void {
    if (!this.isProduction || this.enableConsoleLogging) {
      console.log('Performance metric:', metric);
    }

    if (this.isProduction) {
      this.sendMetricToExternalService(metric);
    }
  }

  /**
   * Send error to external monitoring service
   */
  private async sendToExternalService(report: ErrorReport): Promise<void> {
    try {
      // This would send to Sentry, LogRocket, or other service
      // For now, we'll use a webhook or API endpoint
      const endpoint = process.env.ERROR_TRACKING_ENDPOINT;

      if (endpoint) {
        await fetch(endpoint, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(report),
        });
      }
    } catch (error) {
      console.error('Failed to send error report:', error);
    }
  }

  /**
   * Send performance metric to external service
   */
  private async sendMetricToExternalService(
    metric: PerformanceMetric
  ): Promise<void> {
    try {
      const endpoint = process.env.METRICS_ENDPOINT;

      if (endpoint) {
        await fetch(endpoint, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(metric),
        });
      }
    } catch (error) {
      console.error('Failed to send performance metric:', error);
    }
  }

  /**
   * Store error locally for debugging
   */
  private storeLocally(report: ErrorReport): void {
    try {
      if (typeof window !== 'undefined' && window.localStorage) {
        const errors = JSON.parse(
          localStorage.getItem('error-reports') || '[]'
        );
        errors.push(report);

        // Keep only last 50 errors
        if (errors.length > 50) {
          errors.splice(0, errors.length - 50);
        }

        localStorage.setItem('error-reports', JSON.stringify(errors));
      }
    } catch (_error) {
      // Ignore localStorage errors
    }
  }

  /**
   * Get stored error reports for debugging
   */
  getStoredReports(): ErrorReport[] {
    try {
      if (typeof window !== 'undefined' && window.localStorage) {
        return JSON.parse(localStorage.getItem('error-reports') || '[]');
      }
    } catch (_error) {
      // Ignore localStorage errors
    }
    return [];
  }

  /**
   * Clear stored error reports
   */
  clearStoredReports(): void {
    try {
      if (typeof window !== 'undefined' && window.localStorage) {
        localStorage.removeItem('error-reports');
      }
    } catch (_error) {
      // Ignore localStorage errors
    }
  }
}

// Global error tracker instance
export const errorTracker = new ErrorTracker();

/**
 * Higher-order function to wrap API handlers with error tracking
 */
export function withErrorTracking<T extends (...args: unknown[]) => any>(
  handler: T,
  category: string = 'api'
): T {
  return (async (...args: unknown[]) => {
    try {
      return await handler(...args);
    } catch (error) {
      const request = args[0] as NextRequest;

      errorTracker.captureError(error as Error, {
        category,
        context: {
          method: request?.method,
          url: request?.url,
          headers: Object.fromEntries(request?.headers?.entries() || []),
          userAgent: request?.headers?.get('user-agent'),
        },
      });

      throw error; // Re-throw to maintain normal error handling
    }
  }) as T;
}

/**
 * React error boundary hook
 */
export function useErrorBoundary() {
  return {
    captureError: (error: Error, errorInfo?: unknown) => {
      const errorInfoObj = errorInfo as { componentStack?: string } | undefined;
      errorTracker.captureError(error, {
        category: 'react',
        context: {
          componentStack: errorInfoObj?.componentStack,
          errorBoundary: true,
        },
      });
    },
  };
}

/**
 * Performance monitoring utilities
 */
export const performanceMonitor = {
  /**
   * Measure and track operation duration
   */
  measureOperation: <T>(
    operationName: string,
    operation: () => Promise<T> | T,
    tags?: Record<string, string>
  ): Promise<T> | T => {
    const startTime = performance.now();

    const finish = (result: T) => {
      const duration = performance.now() - startTime;

      errorTracker.capturePerformance({
        name: operationName,
        value: duration,
        unit: 'ms',
        tags,
        timestamp: Date.now(),
      });

      return result;
    };

    try {
      const result = operation();

      if (result instanceof Promise) {
        return result.then(finish).catch(error => {
          const duration = performance.now() - startTime;

          errorTracker.capturePerformance({
            name: `${operationName}_error`,
            value: duration,
            unit: 'ms',
            tags: { ...tags, status: 'error' },
            timestamp: Date.now(),
          });

          throw error;
        });
      }

      return finish(result);
    } catch (error) {
      const duration = performance.now() - startTime;

      errorTracker.capturePerformance({
        name: `${operationName}_error`,
        value: duration,
        unit: 'ms',
        tags: { ...tags, status: 'error' },
        timestamp: Date.now(),
      });

      throw error;
    }
  },

  /**
   * Track Core Web Vitals
   */
  trackWebVitals: () => {
    if (typeof window === 'undefined') return;

    // Largest Contentful Paint
    new PerformanceObserver(list => {
      const entries = list.getEntries();
      const lastEntry = entries[entries.length - 1];
      if (lastEntry) {
        errorTracker.capturePerformance({
          name: 'largest_contentful_paint',
          value: lastEntry.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        });
      }
    }).observe({ entryTypes: ['largest-contentful-paint'] });

    // First Input Delay
    new PerformanceObserver(list => {
      const entries = list.getEntries();
      entries.forEach(entry => {
        const fidEntry = entry as PerformanceEventTiming;
        if (fidEntry.processingStart && fidEntry.startTime) {
          errorTracker.capturePerformance({
            name: 'first_input_delay',
            value: fidEntry.processingStart - fidEntry.startTime,
            unit: 'ms',
            timestamp: Date.now(),
          });
        }
      });
    }).observe({ entryTypes: ['first-input'] });

    // Cumulative Layout Shift
    let cumulativeScore = 0;
    new PerformanceObserver(list => {
      const entries = list.getEntries();
      entries.forEach(entry => {
        const clsEntry = entry as { hadRecentInput?: boolean; value?: number };
        if (!clsEntry.hadRecentInput && clsEntry.value !== undefined) {
          cumulativeScore += clsEntry.value;
        }
      });

      errorTracker.capturePerformance({
        name: 'cumulative_layout_shift',
        value: cumulativeScore,
        unit: 'count',
        timestamp: Date.now(),
      });
    }).observe({ entryTypes: ['layout-shift'] });
  },

  /**
   * Track API response times
   */
  trackAPIResponse: (
    endpoint: string,
    duration: number,
    statusCode: number
  ) => {
    errorTracker.capturePerformance({
      name: 'api_response_time',
      value: duration,
      unit: 'ms',
      tags: {
        endpoint,
        status: statusCode.toString(),
        success: statusCode < 400 ? 'true' : 'false',
      },
      timestamp: Date.now(),
    });
  },
};

/**
 * Initialize monitoring on application start
 */
export function initializeMonitoring(): void {
  errorTracker.init();

  if (typeof window !== 'undefined') {
    performanceMonitor.trackWebVitals();
  }
}

/**
 * Health check and system monitoring utilities
 * Provides comprehensive system health monitoring
 */

export interface HealthCheck {
  name: string;
  status: 'healthy' | 'degraded' | 'unhealthy';
  message?: string;
  lastCheck: number;
  responseTime?: number;
  metadata?: Record<string, unknown>;
}

export interface SystemHealth {
  overall: 'healthy' | 'degraded' | 'unhealthy';
  checks: HealthCheck[];
  timestamp: number;
  uptime: number;
  version: string;
}

/**
 * Health monitoring service
 */
class HealthMonitor {
  private checks: Map<string, () => Promise<HealthCheck>> = new Map();
  private cache: Map<string, HealthCheck> = new Map();
  private cacheTimeout = 30000; // 30 seconds
  private startTime = Date.now();

  constructor() {
    this.registerDefaultChecks();
  }

  /**
   * Register a health check
   */
  registerCheck(name: string, checkFn: () => Promise<HealthCheck>): void {
    this.checks.set(name, checkFn);
  }

  /**
   * Run all health checks
   */
  async runAllChecks(): Promise<SystemHealth> {
    const checkPromises = Array.from(this.checks.entries()).map(
      async ([name, checkFn]) => {
        try {
          const startTime = performance.now();
          const cached = this.cache.get(name);

          // Use cache if still valid
          if (cached && Date.now() - cached.lastCheck < this.cacheTimeout) {
            return cached;
          }

          const result = await Promise.race([
            checkFn(),
            this.timeoutPromise<HealthCheck>(5000, {
              name,
              status: 'unhealthy',
              message: 'Health check timeout',
              lastCheck: Date.now(),
              responseTime: 5000,
            }),
          ]);

          result.responseTime = performance.now() - startTime;
          result.lastCheck = Date.now();

          this.cache.set(name, result);
          return result;
        } catch (error) {
          const failedCheck: HealthCheck = {
            name,
            status: 'unhealthy',
            message: error instanceof Error ? error.message : 'Unknown error',
            lastCheck: Date.now(),
          };
          this.cache.set(name, failedCheck);
          return failedCheck;
        }
      }
    );

    const checks = await Promise.all(checkPromises);
    const overall = this.calculateOverallHealth(checks);

    return {
      overall,
      checks,
      timestamp: Date.now(),
      uptime: Date.now() - this.startTime,
      version: process.env.npm_package_version || '0.3.0-beta',
    };
  }

  /**
   * Run a specific health check
   */
  async runCheck(name: string): Promise<HealthCheck | null> {
    const checkFn = this.checks.get(name);
    if (!checkFn) return null;

    try {
      const startTime = performance.now();
      const result = await checkFn();
      result.responseTime = performance.now() - startTime;
      result.lastCheck = Date.now();

      this.cache.set(name, result);
      return result;
    } catch (error) {
      const failedCheck: HealthCheck = {
        name,
        status: 'unhealthy',
        message: error instanceof Error ? error.message : 'Unknown error',
        lastCheck: Date.now(),
      };
      this.cache.set(name, failedCheck);
      return failedCheck;
    }
  }

  /**
   * Register default system health checks
   */
  private registerDefaultChecks(): void {
    // Database connectivity check
    this.registerCheck('database', async () => {
      try {
        // This would check actual database connection
        // For now, we'll simulate the check
        const isConnected = await this.checkDatabaseConnection();

        return {
          name: 'database',
          status: isConnected ? 'healthy' : 'unhealthy',
          message: isConnected
            ? 'Database connection successful'
            : 'Database connection failed',
          lastCheck: Date.now(),
        };
      } catch (error) {
        return {
          name: 'database',
          status: 'unhealthy',
          message:
            error instanceof Error ? error.message : 'Database check failed',
          lastCheck: Date.now(),
        };
      }
    });

    // Redis cache check
    this.registerCheck('redis', async () => {
      try {
        const isConnected = await this.checkRedisConnection();

        return {
          name: 'redis',
          status: isConnected ? 'healthy' : 'degraded',
          message: isConnected
            ? 'Redis connection successful'
            : 'Redis unavailable, using memory cache',
          lastCheck: Date.now(),
        };
      } catch (_error) {
        return {
          name: 'redis',
          status: 'degraded',
          message: 'Redis check failed, using memory cache',
          lastCheck: Date.now(),
        };
      }
    });

    // External API dependencies
    this.registerCheck('huggingface', async () => {
      try {
        const isAvailable = await this.checkExternalAPI(
          'https://huggingface.co'
        );

        return {
          name: 'huggingface',
          status: isAvailable ? 'healthy' : 'degraded',
          message: isAvailable
            ? 'HuggingFace API accessible'
            : 'HuggingFace API unavailable',
          lastCheck: Date.now(),
        };
      } catch (_error) {
        return {
          name: 'huggingface',
          status: 'degraded',
          message: 'HuggingFace API check failed',
          lastCheck: Date.now(),
        };
      }
    });

    this.registerCheck('stripe', async () => {
      try {
        const isAvailable = await this.checkExternalAPI(
          'https://api.stripe.com'
        );

        return {
          name: 'stripe',
          status: isAvailable ? 'healthy' : 'degraded',
          message: isAvailable
            ? 'Stripe API accessible'
            : 'Stripe API unavailable',
          lastCheck: Date.now(),
        };
      } catch (_error) {
        return {
          name: 'stripe',
          status: 'degraded',
          message: 'Stripe API check failed',
          lastCheck: Date.now(),
        };
      }
    });

    // System resources check
    this.registerCheck('system', async () => {
      try {
        const memoryUsage = process.memoryUsage();
        const cpuUsage = process.cpuUsage();

        const memoryMB = memoryUsage.heapUsed / 1024 / 1024;
        const isHealthy = memoryMB < 512; // Alert if using more than 512MB

        return {
          name: 'system',
          status: isHealthy ? 'healthy' : 'degraded',
          message: `Memory usage: ${memoryMB.toFixed(2)}MB`,
          lastCheck: Date.now(),
          metadata: {
            memory: memoryUsage,
            cpu: cpuUsage,
          },
        };
      } catch (_error) {
        return {
          name: 'system',
          status: 'unhealthy',
          message: 'System resources check failed',
          lastCheck: Date.now(),
        };
      }
    });

    // Application-specific checks
    this.registerCheck('portfolio_generation', async () => {
      try {
        // Test portfolio generation pipeline
        const isWorking = await this.testPortfolioGeneration();

        return {
          name: 'portfolio_generation',
          status: isWorking ? 'healthy' : 'unhealthy',
          message: isWorking
            ? 'Portfolio generation working'
            : 'Portfolio generation failed',
          lastCheck: Date.now(),
        };
      } catch (_error) {
        return {
          name: 'portfolio_generation',
          status: 'unhealthy',
          message: 'Portfolio generation test failed',
          lastCheck: Date.now(),
        };
      }
    });
  }

  /**
   * Calculate overall system health based on individual checks
   */
  private calculateOverallHealth(
    checks: HealthCheck[]
  ): 'healthy' | 'degraded' | 'unhealthy' {
    const unhealthyCount = checks.filter(c => c.status === 'unhealthy').length;
    const degradedCount = checks.filter(c => c.status === 'degraded').length;

    if (unhealthyCount > 0) {
      return 'unhealthy';
    }

    if (degradedCount > 0) {
      return 'degraded';
    }

    return 'healthy';
  }

  /**
   * Create a promise that rejects after a timeout
   */
  private timeoutPromise<T>(ms: number, fallback: T): Promise<T> {
    return new Promise(resolve => {
      setTimeout(() => resolve(fallback), ms);
    });
  }

  /**
   * Check database connection (placeholder)
   */
  private async checkDatabaseConnection(): Promise<boolean> {
    try {
      // This would use actual database connection
      // For now, we'll simulate success
      await Promise.resolve();
      return true;
    } catch {
      return false;
    }
  }

  /**
   * Check Redis connection (placeholder)
   */
  private async checkRedisConnection(): Promise<boolean> {
    try {
      // This would use actual Redis connection
      // For now, we'll simulate success
      await Promise.resolve();
      return true;
    } catch {
      return false;
    }
  }

  /**
   * Check external API availability
   */
  private async checkExternalAPI(url: string): Promise<boolean> {
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 3000);

      const response = await fetch(url, {
        signal: controller.signal,
        method: 'HEAD',
      });

      clearTimeout(timeoutId);
      return response.ok;
    } catch {
      return false;
    }
  }

  /**
   * Test portfolio generation functionality
   */
  private async testPortfolioGeneration(): Promise<boolean> {
    try {
      // This would test the actual portfolio generation pipeline
      // For now, we'll simulate success
      await Promise.resolve();
      return true;
    } catch {
      return false;
    }
  }
}

// Global health monitor instance
export const healthMonitor = new HealthMonitor();

/**
 * Express/Next.js health check endpoint handler
 */
export async function handleHealthCheck(): Promise<Response> {
  try {
    const health = await healthMonitor.runAllChecks();

    const statusCode = {
      healthy: 200,
      degraded: 200, // Still operational
      unhealthy: 503, // Service unavailable
    }[health.overall];

    return new Response(JSON.stringify(health, null, 2), {
      status: statusCode,
      headers: {
        'Content-Type': 'application/json',
        'Cache-Control': 'no-cache, no-store, must-revalidate',
      },
    });
  } catch (_error) {
    return new Response(
      JSON.stringify({
        overall: 'unhealthy',
        message: 'Health check system failure',
        timestamp: Date.now(),
      }),
      {
        status: 503,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }
}

/**
 * Readiness check (for Kubernetes/Docker)
 */
export async function handleReadinessCheck(): Promise<Response> {
  try {
    // Check only critical services for readiness
    const criticalChecks = ['database', 'system'];
    const results = await Promise.all(
      criticalChecks.map(name => healthMonitor.runCheck(name))
    );

    const isReady = results.every(
      check =>
        check && (check.status === 'healthy' || check.status === 'degraded')
    );

    return new Response(
      JSON.stringify({
        ready: isReady,
        checks: results,
        timestamp: Date.now(),
      }),
      {
        status: isReady ? 200 : 503,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  } catch (_error) {
    return new Response(
      JSON.stringify({
        ready: false,
        message: 'Readiness check failed',
        timestamp: Date.now(),
      }),
      {
        status: 503,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }
}

/**
 * Liveness check (for Kubernetes/Docker)
 */
export function handleLivenessCheck(): Response {
  // Simple liveness check - just verify the process is running
  return new Response(
    JSON.stringify({
      alive: true,
      timestamp: Date.now(),
      uptime: Date.now() - ((global as any).__startup_time || 0),
    }),
    {
      status: 200,
      headers: {
        'Content-Type': 'application/json',
      },
    }
  );
}

/**
 * Initialize health monitoring
 */
export function initializeHealthMonitoring(): void {
  // Set startup time for uptime calculation
  (global as any).__startup_time = Date.now();

  // Run periodic health checks
  if (process.env.NODE_ENV === 'production') {
    setInterval(async () => {
      const health = await healthMonitor.runAllChecks();

      if (health.overall === 'unhealthy') {
        console.error('System health is unhealthy:', health);
      } else if (health.overall === 'degraded') {
        console.warn('System health is degraded:', health);
      }
    }, 60000); // Check every minute
  }
}

