/**
 * Preload components for better UX
 */
export function preloadComponent(importFn: () => Promise<any>): void {
  if (typeof window !== 'undefined') {
    // Preload after initial page load
    const preload = () => {
      importFn().catch(() => {
        // Silently fail - component will be loaded when needed
      });
    };

    if (document.readyState === 'complete') {
      setTimeout(preload, 1000); // 1 second delay
    } else {
      window.addEventListener('load', () => {
        setTimeout(preload, 1000);
      });
    }
  }
}

/**
 * Dynamic import with retry logic
 */
export async function dynamicImportWithRetry<T>(
  importFn: () => Promise<T>,
  retries: number = 3,
  delay: number = 1000
): Promise<T> {
  for (let i = 0; i < retries; i++) {
    try {
      return await importFn();
    } catch (error) {
      if (i === retries - 1) {
        throw error;
      }

      // Wait before retrying
      await new Promise(resolve => setTimeout(resolve, delay * (i + 1)));
    }
  }

  throw new Error('Dynamic import failed after retries');
}

/**
 * Critical resource hints for performance
 */
export function addResourceHints(): void {
  if (typeof window === 'undefined') return;

  // Preconnect to external domains
  const preconnectDomains = [
    'https://fonts.googleapis.com',
    'https://fonts.gstatic.com',
    'https://api.stripe.com',
    'https://js.stripe.com',
  ];

  preconnectDomains.forEach(domain => {
    const link = document.createElement('link');
    link.rel = 'preconnect';
    link.href = domain;
    document.head.appendChild(link);
  });

  // DNS prefetch for optional resources
  const dnsPrefetchDomains = [
    'https://huggingface.co',
    'https://api.github.com',
    'https://ipapi.co',
  ];

  dnsPrefetchDomains.forEach(domain => {
    const link = document.createElement('link');
    link.rel = 'dns-prefetch';
    link.href = domain;
    document.head.appendChild(link);
  });
}

/**
 * Intersection Observer for lazy loading
 */
export function createIntersectionObserver(
  callback: (entries: IntersectionObserverEntry[]) => void,
  options: IntersectionObserverInit = {}
): IntersectionObserver | null {
  if (typeof window === 'undefined' || !('IntersectionObserver' in window)) {
    return null;
  }

  return new IntersectionObserver(callback, {
    rootMargin: '50px',
    threshold: 0.1,
    ...options,
  });
}

/**
 * Image optimization utilities
 */
export const imageOptimization = {
  // Generate srcSet for responsive images
  generateSrcSet: (
    baseUrl: string,
    sizes: number[] = [320, 640, 1280, 1920]
  ) => {
    return sizes.map(size => `${baseUrl}?w=${size} ${size}w`).join(', ');
  },

  // Lazy load images with intersection observer
  lazyLoadImage: (img: HTMLImageElement, src: string) => {
    const observer = createIntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const target = entry.target as HTMLImageElement;
          target.src = src;
          target.classList.remove('lazy');
          observer?.unobserve(target);
        }
      });
    });

    if (observer) {
      img.classList.add('lazy');
      observer.observe(img);
    } else {
      // Fallback for browsers without IntersectionObserver
      img.src = src;
    }
  },
};

/**
 * Service Worker utilities for caching
 */
export const serviceWorkerUtils = {
  register: async (): Promise<ServiceWorkerRegistration | undefined> => {
    if ('serviceWorker' in navigator && process.env.NODE_ENV === 'production') {
      try {
        const registration = await navigator.serviceWorker.register('/sw.js');
        console.log('Service Worker registered:', registration);
        return registration;
      } catch (error) {
        console.error('Service Worker registration failed:', error);
        return undefined;
      }
    }
    return undefined;
  },

  unregister: async (): Promise<void> => {
    if ('serviceWorker' in navigator) {
      const registrations = await navigator.serviceWorker.getRegistrations();
      for (const registration of registrations) {
        await registration.unregister();
      }
    }
  },
};

/**
 * Performance monitoring utilities
 */
export const performanceMonitoring = {
  // Measure Core Web Vitals
  measureWebVitals: () => {
    if (typeof window === 'undefined') return;

    // Largest Contentful Paint
    new PerformanceObserver(list => {
      const entries = list.getEntries();
      const lastEntry = entries[entries.length - 1];
      if (lastEntry) {
        console.log('LCP:', lastEntry.startTime);
      }
    }).observe({ entryTypes: ['largest-contentful-paint'] });

    // First Input Delay
    new PerformanceObserver(list => {
      const entries = list.getEntries();
      entries.forEach(entry => {
        const fidEntry = entry as PerformanceEventTiming;
        if (fidEntry.processingStart && fidEntry.startTime) {
          console.log('FID:', fidEntry.processingStart - fidEntry.startTime);
        }
      });
    }).observe({ entryTypes: ['first-input'] });

    // Cumulative Layout Shift
    let cumulativeScore = 0;
    new PerformanceObserver(list => {
      const entries = list.getEntries();
      entries.forEach(entry => {
        const clsEntry = entry as any; // Layout shift entry type
        if (!clsEntry.hadRecentInput) {
          cumulativeScore += clsEntry.value;
        }
      });
      console.log('CLS:', cumulativeScore);
    }).observe({ entryTypes: ['layout-shift'] });
  },

  // Report navigation timing
  reportNavigationTiming: () => {
    if (typeof window === 'undefined') return;

    window.addEventListener('load', () => {
      const timing = performance.getEntriesByType(
        'navigation'
      )[0] as PerformanceNavigationTiming;

      const metrics = {
        dns: timing.domainLookupEnd - timing.domainLookupStart,
        tcp: timing.connectEnd - timing.connectStart,
        ttfb: timing.responseStart - timing.requestStart,
        download: timing.responseEnd - timing.responseStart,
        domParsing: timing.domContentLoadedEventStart - timing.responseEnd,
        resourceLoading:
          timing.loadEventStart - timing.domContentLoadedEventStart,
        total: timing.loadEventEnd - timing.startTime,
      };

      console.log('Navigation Timing:', metrics);
    });
  },
};

/**
 * @fileoverview Mobile Performance Optimization
 *
 * Specialized optimizations for mobile devices to ensure excellent
 * performance on smartphones and tablets with limited resources.
 *
 * @author PRISMA Development Team
 * @version 1.0.0
 */

export interface MobileOptimizationConfig {
  enableTouchOptimizations: boolean;
  enableReducedMotion: boolean;
  enableDataSaver: boolean;
  maxMobileImageSize: number;
  prefersReducedData: boolean;
  enableOfflineSupport: boolean;
}

export interface DeviceCapabilities {
  isMobile: boolean;
  isTablet: boolean;
  isLowEndDevice: boolean;
  hasSlowConnection: boolean;
  prefersReducedMotion: boolean;
  prefersReducedData: boolean;
  screenSize: { width: number; height: number };
  devicePixelRatio: number;
  memoryLimit?: number;
}

/**
 * Device detection and capability assessment
 */
export class DeviceDetector {
  private capabilities: DeviceCapabilities;

  constructor() {
    this.capabilities = this.detectCapabilities();
  }

  /**
   * Detect device capabilities and limitations
   */
  private detectCapabilities(): DeviceCapabilities {
    const userAgent =
      typeof navigator !== 'undefined' ? navigator.userAgent : '';
    const isMobile =
      /Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
        userAgent
      );
    const isTablet =
      /iPad|Android(?=.*\bMobile\b)(?=.*\bSafari\b)|KFAPWI/i.test(userAgent);

    // Detect low-end devices
    const isLowEndDevice = this.isLowEndDevice();

    // Detect connection speed
    const hasSlowConnection = this.hasSlowConnection();

    // Get user preferences
    const prefersReducedMotion =
      typeof window !== 'undefined' && window.matchMedia
        ? window.matchMedia('(prefers-reduced-motion: reduce)').matches
        : false;

    const prefersReducedData =
      typeof navigator !== 'undefined' && 'connection' in navigator
        ? (navigator as any).connection?.saveData
        : false;

    // Screen information
    const screenSize =
      typeof window !== 'undefined'
        ? { width: window.innerWidth, height: window.innerHeight }
        : { width: 1024, height: 768 };

    const devicePixelRatio =
      typeof window !== 'undefined' ? window.devicePixelRatio : 1;

    // Memory information (if available)
    const memoryLimit =
      typeof navigator !== 'undefined' && 'deviceMemory' in navigator
        ? (navigator as any).deviceMemory
        : undefined;

    return {
      isMobile,
      isTablet,
      isLowEndDevice,
      hasSlowConnection,
      prefersReducedMotion,
      prefersReducedData,
      screenSize,
      devicePixelRatio,
      memoryLimit,
    };
  }

  /**
   * Detect if device is low-end based on various heuristics
   */
  private isLowEndDevice(): boolean {
    if (typeof navigator === 'undefined') return false;

    // Check device memory
    if ('deviceMemory' in navigator) {
      const memory = (navigator as any).deviceMemory;
      if (memory && memory <= 2) return true;
    }

    // Check hardware concurrency (CPU cores)
    if ('hardwareConcurrency' in navigator) {
      const cores = navigator.hardwareConcurrency;
      if (cores && cores <= 2) return true;
    }

    // Check user agent for known low-end devices
    const userAgent = navigator.userAgent.toLowerCase();
    const lowEndIndicators = [
      'android 4',
      'android 5',
      'iphone os 10',
      'iphone os 11',
      'windows phone',
    ];

    return lowEndIndicators.some(indicator => userAgent.includes(indicator));
  }

  /**
   * Detect slow connection
   */
  private hasSlowConnection(): boolean {
    if (typeof navigator === 'undefined' || !('connection' in navigator))
      return false;

    const connection = (navigator as any).connection;
    if (!connection) return false;

    // Check effective connection type
    const slowTypes = ['slow-2g', '2g', '3g'];
    if (
      connection.effectiveType &&
      slowTypes.includes(connection.effectiveType)
    ) {
      return true;
    }

    // Check downlink speed (Mbps)
    if (connection.downlink && connection.downlink < 1.5) {
      return true;
    }

    return false;
  }

  /**
   * Get device capabilities
   */
  getCapabilities(): DeviceCapabilities {
    return this.capabilities;
  }

  /**
   * Check if device needs aggressive optimizations
   */
  needsAggressiveOptimization(): boolean {
    return (
      this.capabilities.isLowEndDevice ||
      this.capabilities.hasSlowConnection ||
      this.capabilities.prefersReducedData
    );
  }
}

/**
 * Mobile-specific image optimization
 */
export class MobileImageOptimizer {
  private config: MobileOptimizationConfig;
  private deviceDetector: DeviceDetector;

  constructor(
    config: MobileOptimizationConfig,
    deviceDetector: DeviceDetector
  ) {
    this.config = config;
    this.deviceDetector = deviceDetector;
  }

  /**
   * Optimize images for mobile devices
   */
  optimizeForMobile(imageUrl: string): {
    src: string;
    srcSet: string;
    sizes: string;
    loading: 'lazy' | 'eager';
    quality: number;
  } {
    const capabilities = this.deviceDetector.getCapabilities();
    const isLowEnd = this.deviceDetector.needsAggressiveOptimization();

    // Determine quality based on device capabilities
    const quality = this.getOptimalQuality(capabilities, isLowEnd);

    // Generate responsive image sizes
    const sizes = this.generateMobileSizes(capabilities);

    // Create srcSet for different screen densities
    const srcSet = this.generateSrcSet(imageUrl, capabilities, quality);

    // Determine loading strategy
    const loading = this.getLoadingStrategy(capabilities);

    return {
      src: `${imageUrl}?w=${sizes.default}&q=${quality}`,
      srcSet,
      sizes: this.generateSizesAttribute(capabilities),
      loading,
      quality,
    };
  }

  /**
   * Get optimal image quality for device
   */
  private getOptimalQuality(
    capabilities: DeviceCapabilities,
    isLowEnd: boolean
  ): number {
    if (capabilities.prefersReducedData || isLowEnd) {
      return 60; // Lower quality for data saving
    }

    if (capabilities.hasSlowConnection) {
      return 70; // Moderate quality for slow connections
    }

    if (capabilities.isMobile) {
      return 80; // Good quality for mobile
    }

    return 85; // High quality for desktop
  }

  /**
   * Generate mobile-appropriate image sizes
   */
  private generateMobileSizes(capabilities: DeviceCapabilities): {
    small: number;
    medium: number;
    large: number;
    default: number;
  } {
    const baseWidth = capabilities.screenSize.width;
    const pixelRatio = capabilities.devicePixelRatio;

    return {
      small: Math.round(baseWidth * 0.5 * pixelRatio),
      medium: Math.round(baseWidth * 0.75 * pixelRatio),
      large: Math.round(baseWidth * 1.0 * pixelRatio),
      default: Math.round(baseWidth * 0.75 * pixelRatio),
    };
  }

  /**
   * Generate srcSet for responsive images
   */
  private generateSrcSet(
    imageUrl: string,
    capabilities: DeviceCapabilities,
    quality: number
  ): string {
    const sizes = this.generateMobileSizes(capabilities);

    return [
      `${imageUrl}?w=${sizes.small}&q=${quality} ${sizes.small}w`,
      `${imageUrl}?w=${sizes.medium}&q=${quality} ${sizes.medium}w`,
      `${imageUrl}?w=${sizes.large}&q=${quality} ${sizes.large}w`,
    ].join(', ');
  }

  /**
   * Generate sizes attribute for responsive images
   */
  private generateSizesAttribute(capabilities: DeviceCapabilities): string {
    if (capabilities.isMobile) {
      return '(max-width: 640px) 100vw, (max-width: 768px) 75vw, 50vw';
    }

    if (capabilities.isTablet) {
      return '(max-width: 768px) 100vw, (max-width: 1024px) 75vw, 50vw';
    }

    return '(max-width: 1024px) 75vw, 50vw';
  }

  /**
   * Determine loading strategy based on device capabilities
   */
  private getLoadingStrategy(
    capabilities: DeviceCapabilities
  ): 'lazy' | 'eager' {
    // Use eager loading for above-the-fold content on fast devices
    if (!capabilities.hasSlowConnection && !capabilities.prefersReducedData) {
      return 'eager';
    }

    // Default to lazy loading for better performance
    return 'lazy';
  }
}

/**
 * Mobile viewport and interaction optimizations
 */
export class MobileViewportOptimizer {
  private config: MobileOptimizationConfig;
  private deviceDetector: DeviceDetector;

  constructor(
    config: MobileOptimizationConfig,
    deviceDetector: DeviceDetector
  ) {
    this.config = config;
    this.deviceDetector = deviceDetector;
  }

  /**
   * Optimize viewport settings for mobile
   */
  optimizeViewport(): void {
    if (typeof document === 'undefined') return;

    const capabilities = this.deviceDetector.getCapabilities();

    // Set optimal viewport meta tag
    this.setViewportMeta(capabilities);

    // Apply mobile-specific CSS optimizations
    this.applyMobileCSS(capabilities);

    // Configure touch interactions
    if (this.config.enableTouchOptimizations) {
      this.optimizeTouchInteractions();
    }
  }

  /**
   * Set optimal viewport meta tag
   */
  private setViewportMeta(capabilities: DeviceCapabilities): void {
    let viewportMeta = document.querySelector(
      'meta[name="viewport"]'
    ) as HTMLMetaElement;

    if (!viewportMeta) {
      viewportMeta = document.createElement('meta');
      viewportMeta.name = 'viewport';
      document.head.appendChild(viewportMeta);
    }

    // Base viewport settings
    let content = 'width=device-width, initial-scale=1';

    // Prevent zoom on low-end devices for better performance
    if (capabilities.isLowEndDevice) {
      content += ', user-scalable=no, maximum-scale=1';
    }

    // Optimize for specific device types
    if (capabilities.isMobile) {
      content += ', shrink-to-fit=no';
    }

    viewportMeta.content = content;
  }

  /**
   * Apply mobile-specific CSS optimizations
   */
  private applyMobileCSS(capabilities: DeviceCapabilities): void {
    const style = document.createElement('style');
    style.id = 'mobile-optimizations';

    let css = `
      /* Base mobile optimizations */
      * {
        -webkit-tap-highlight-color: transparent;
        -webkit-touch-callout: none;
      }

      body {
        -webkit-text-size-adjust: 100%;
        -ms-text-size-adjust: 100%;
      }

      /* Touch target optimization */
      button, a, input, select, textarea {
        min-height: 44px;
        min-width: 44px;
      }

      /* Smooth scrolling for iOS */
      * {
        -webkit-overflow-scrolling: touch;
      }
    `;

    // Add reduced motion styles if preferred
    if (capabilities.prefersReducedMotion || this.config.enableReducedMotion) {
      css += `
        @media (prefers-reduced-motion: reduce) {
          *, *::before, *::after {
            animation-duration: 0.01ms !important;
            animation-iteration-count: 1 !important;
            transition-duration: 0.01ms !important;
          }
        }
      `;
    }

    // Add low-end device optimizations
    if (capabilities.isLowEndDevice) {
      css += `
        /* Disable expensive CSS features on low-end devices */
        .disable-on-low-end {
          transform: none !important;
          filter: none !important;
          box-shadow: none !important;
        }

        /* Simplify animations */
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
      `;
    }

    style.textContent = css;
    document.head.appendChild(style);
  }

  /**
   * Optimize touch interactions
   */
  private optimizeTouchInteractions(): void {
    if (typeof document === 'undefined') return;

    // Add touch-friendly classes to interactive elements
    const interactiveElements = document.querySelectorAll(
      'button, a, input, select, textarea'
    );

    interactiveElements.forEach(element => {
      element.classList.add('touch-optimized');

      // Add touch event listeners for better responsiveness
      element.addEventListener(
        'touchstart',
        this.handleTouchStart as EventListener,
        { passive: true }
      );
      element.addEventListener(
        'touchend',
        this.handleTouchEnd as EventListener,
        { passive: true }
      );
    });
  }

  /**
   * Handle touch start for visual feedback
   */
  private handleTouchStart = (event: TouchEvent): void => {
    const target = event.target as HTMLElement;
    target.classList.add('touch-active');
  };

  /**
   * Handle touch end to remove visual feedback
   */
  private handleTouchEnd = (event: TouchEvent): void => {
    const target = event.target as HTMLElement;
    setTimeout(() => {
      target.classList.remove('touch-active');
    }, 150);
  };
}

/**
 * Mobile data usage optimization
 */
export class MobileDataOptimizer {
  private config: MobileOptimizationConfig;
  private deviceDetector: DeviceDetector;

  constructor(
    config: MobileOptimizationConfig,
    deviceDetector: DeviceDetector
  ) {
    this.config = config;
    this.deviceDetector = deviceDetector;
  }

  /**
   * Optimize data usage for mobile connections
   */
  optimizeDataUsage(): {
    enablePreloading: boolean;
    imageQuality: number;
    enableAnimations: boolean;
    enableBackgroundSync: boolean;
  } {
    const capabilities = this.deviceDetector.getCapabilities();
    const needsDataSaving =
      capabilities.prefersReducedData ||
      capabilities.hasSlowConnection ||
      this.config.enableDataSaver;

    return {
      enablePreloading: !needsDataSaving,
      imageQuality: needsDataSaving ? 60 : 80,
      enableAnimations: !needsDataSaving && !capabilities.prefersReducedMotion,
      enableBackgroundSync: !needsDataSaving,
    };
  }

  /**
   * Get optimized fetch configuration for mobile
   */
  getOptimizedFetchConfig(): RequestInit {
    const capabilities = this.deviceDetector.getCapabilities();
    const needsCompression =
      capabilities.hasSlowConnection || capabilities.prefersReducedData;

    return {
      headers: {
        'Accept-Encoding': needsCompression ? 'gzip, deflate, br' : 'identity',
        'Save-Data': capabilities.prefersReducedData ? 'on' : 'off',
      },
      // Use shorter timeout for slow connections
      signal: capabilities.hasSlowConnection
        ? AbortSignal.timeout(10000)
        : AbortSignal.timeout(30000),
    };
  }
}

/**
 * Complete mobile optimization system
 */
export class MobileOptimizationSystem {
  private config: MobileOptimizationConfig;
  private deviceDetector: DeviceDetector;
  private imageOptimizer: MobileImageOptimizer;
  private viewportOptimizer: MobileViewportOptimizer;
  private dataOptimizer: MobileDataOptimizer;

  constructor(config: MobileOptimizationConfig = DEFAULT_MOBILE_CONFIG) {
    this.config = config;
    this.deviceDetector = new DeviceDetector();
    this.imageOptimizer = new MobileImageOptimizer(config, this.deviceDetector);
    this.viewportOptimizer = new MobileViewportOptimizer(
      config,
      this.deviceDetector
    );
    this.dataOptimizer = new MobileDataOptimizer(config, this.deviceDetector);
  }

  /**
   * Initialize mobile optimizations
   */
  initialize(): void {
    this.viewportOptimizer.optimizeViewport();

    // Apply data optimizations
    const dataConfig = this.dataOptimizer.optimizeDataUsage();
    this.applyDataOptimizations(dataConfig);
  }

  /**
   * Get optimized image configuration
   */
  optimizeImage(imageUrl: string) {
    return this.imageOptimizer.optimizeForMobile(imageUrl);
  }

  /**
   * Get device capabilities
   */
  getDeviceCapabilities(): DeviceCapabilities {
    return this.deviceDetector.getCapabilities();
  }

  /**
   * Check if mobile optimizations are needed
   */
  isMobileDevice(): boolean {
    const capabilities = this.deviceDetector.getCapabilities();
    return capabilities.isMobile || capabilities.isTablet;
  }

  /**
   * Apply data usage optimizations
   */
  private applyDataOptimizations(config: any): void {
    if (typeof document === 'undefined') return;

    // Disable autoplay videos on slow connections
    if (!config.enableAnimations) {
      const videos = document.querySelectorAll('video[autoplay]');
      videos.forEach(video => {
        (video as HTMLVideoElement).removeAttribute('autoplay');
      });
    }

    // Reduce image preloading
    if (!config.enablePreloading) {
      const preloadLinks = document.querySelectorAll('link[rel="preload"]');
      preloadLinks.forEach(link => {
        if ((link as HTMLLinkElement).as === 'image') {
          link.remove();
        }
      });
    }
  }
}

/**
 * Default mobile optimization configuration
 */
export const DEFAULT_MOBILE_CONFIG: MobileOptimizationConfig = {
  enableTouchOptimizations: true,
  enableReducedMotion: false,
  enableDataSaver: false,
  maxMobileImageSize: 2 * 1024 * 1024, // 2MB
  prefersReducedData: false,
  enableOfflineSupport: true,
};

/**
 * Create mobile optimization system
 */
export function createMobileOptimizer(
  config: MobileOptimizationConfig = DEFAULT_MOBILE_CONFIG
) {
  return new MobileOptimizationSystem(config);
}

/**
 * @fileoverview Performance Optimization Utilities
 *
 * Comprehensive performance optimization system for portfolio generation,
 * ensuring sub-30-second creation times and optimal user experience.
 *
 * @author PRISMA Development Team
 * @version 1.0.0
 */

import React from 'react';

export interface PerformanceMetrics {
  portfolioCreation: number;
  templateRendering: number;
  aiProcessing: number;
  imageOptimization: number;
  apiResponseTime: number;
  totalTime: number;
}

export interface OptimizationConfig {
  enableImageOptimization: boolean;
  enableCodeSplitting: boolean;
  enablePreloading: boolean;
  enableCompression: boolean;
  maxImageSize: number;
  compressionLevel: number;
}

/**
 * Performance monitor for tracking critical user journey metrics
 */
export class PerformanceMonitor {
  private metrics: Map<string, number> = new Map();
  private startTimes: Map<string, number> = new Map();

  /**
   * Start timing a performance operation
   */
  startTimer(operation: string): void {
    this.startTimes.set(operation, performance.now());
  }

  /**
   * End timing and record the duration
   */
  endTimer(operation: string): number {
    const startTime = this.startTimes.get(operation);
    if (!startTime) {
      throw new Error(`Timer for ${operation} was not started`);
    }

    const duration = performance.now() - startTime;
    this.metrics.set(operation, duration);
    this.startTimes.delete(operation);

    return duration;
  }

  /**
   * Get performance metrics for analysis
   */
  getMetrics(): PerformanceMetrics {
    return {
      portfolioCreation: this.metrics.get('portfolioCreation') || 0,
      templateRendering: this.metrics.get('templateRendering') || 0,
      aiProcessing: this.metrics.get('aiProcessing') || 0,
      imageOptimization: this.metrics.get('imageOptimization') || 0,
      apiResponseTime: this.metrics.get('apiResponseTime') || 0,
      totalTime: Array.from(this.metrics.values()).reduce(
        (sum, time) => sum + time,
        0
      ),
    };
  }

  /**
   * Check if performance meets sub-30-second target
   */
  meetsPerformanceTarget(): boolean {
    const totalTime = this.getMetrics().totalTime;
    return totalTime < 30000; // 30 seconds in milliseconds
  }

  /**
   * Reset all metrics
   */
  reset(): void {
    this.metrics.clear();
    this.startTimes.clear();
  }
}

/**
 * Image optimization utilities
 */
export class ImageOptimizer {
  private config: OptimizationConfig;

  constructor(config: OptimizationConfig) {
    this.config = config;
  }

  /**
   * Optimize image for web delivery
   */
  async optimizeImage(imageData: File | string): Promise<string> {
    if (!this.config.enableImageOptimization) {
      return typeof imageData === 'string'
        ? imageData
        : URL.createObjectURL(imageData);
    }

    const monitor = new PerformanceMonitor();
    monitor.startTimer('imageOptimization');

    try {
      if (typeof imageData === 'string') {
        // Already optimized URL
        return imageData;
      }

      // Check file size
      if (imageData.size > this.config.maxImageSize) {
        throw new Error(
          `Image size ${imageData.size} exceeds maximum ${this.config.maxImageSize}`
        );
      }

      // Create optimized blob URL
      const optimizedUrl = URL.createObjectURL(imageData);

      return optimizedUrl;
    } finally {
      monitor.endTimer('imageOptimization');
    }
  }

  /**
   * Generate responsive image sizes
   */
  generateResponsiveSizes(baseUrl: string): {
    sm: string;
    md: string;
    lg: string;
  } {
    const sizes = {
      sm: `${baseUrl}?w=400&q=${this.config.compressionLevel}`,
      md: `${baseUrl}?w=800&q=${this.config.compressionLevel}`,
      lg: `${baseUrl}?w=1200&q=${this.config.compressionLevel}`,
    };

    return sizes;
  }
}

/**
 * API response optimization
 */
export class APIOptimizer {
  private cache = new Map<
    string,
    { data: any; timestamp: number; ttl: number }
  >();

  /**
   * Cache API response with TTL
   */
  cacheResponse(key: string, data: any, ttlMs: number = 300000): void {
    this.cache.set(key, {
      data,
      timestamp: Date.now(),
      ttl: ttlMs,
    });
  }

  /**
   * Get cached response if valid
   */
  getCachedResponse(key: string): any | null {
    const cached = this.cache.get(key);
    if (!cached) return null;

    const isExpired = Date.now() - cached.timestamp > cached.ttl;
    if (isExpired) {
      this.cache.delete(key);
      return null;
    }

    return cached.data;
  }

  /**
   * Optimize API request with caching and compression
   */
  async optimizedFetch(
    url: string,
    options: RequestInit = {}
  ): Promise<Response> {
    const monitor = new PerformanceMonitor();
    monitor.startTimer('apiResponseTime');

    try {
      // Check cache first for GET requests
      if (!options.method || options.method === 'GET') {
        const cached = this.getCachedResponse(url);
        if (cached) {
          return new Response(JSON.stringify(cached), {
            status: 200,
            headers: { 'Content-Type': 'application/json' },
          });
        }
      }

      // Add compression headers
      const optimizedOptions: RequestInit = {
        ...options,
        headers: {
          ...options.headers,
          'Accept-Encoding': 'gzip, deflate, br',
          'Content-Type': 'application/json',
        },
      };

      const response = await fetch(url, optimizedOptions);

      // Cache successful GET responses
      if (response.ok && (!options.method || options.method === 'GET')) {
        const data = await response.clone().json();
        this.cacheResponse(url, data);
      }

      return response;
    } finally {
      monitor.endTimer('apiResponseTime');
    }
  }
}

/**
 * Bundle size optimization utilities
 */
export class BundleOptimizer {
  /**
   * Lazy load component with error boundary
   */
  static lazyLoadComponent<T extends React.ComponentType<any>>(
    importFn: () => Promise<{ default: T }>
  ): React.LazyExoticComponent<T> {
    return React.lazy(async () => {
      try {
        const component = await importFn();
        return component;
      } catch (error) {
        console.error('Failed to load component:', error);
        // Return fallback component
        return {
          default: (() =>
            React.createElement(
              'div',
              { className: 'p-4 text-center' },
              'Component failed to load'
            )) as unknown as T,
        };
      }
    });
  }

  /**
   * Preload critical resources
   */
  static preloadResources(resources: string[]): void {
    resources.forEach(resource => {
      const link = document.createElement('link');
      link.rel = 'preload';
      link.href = resource;

      if (resource.endsWith('.js')) {
        link.as = 'script';
      } else if (resource.endsWith('.css')) {
        link.as = 'style';
      } else if (resource.match(/\.(jpg|jpeg|png|webp)$/)) {
        link.as = 'image';
      }

      document.head.appendChild(link);
    });
  }

  /**
   * Check if resource should be preloaded
   */
  static shouldPreload(resource: string): boolean {
    // Preload critical template assets
    const criticalResources = [
      '/templates/modern.css',
      '/templates/minimal.css',
      '/js/template-renderer.js',
      '/fonts/inter.woff2',
    ];

    return criticalResources.some(critical => resource.includes(critical));
  }
}

/**
 * Portfolio generation performance optimizer
 */
export class PortfolioPerformanceOptimizer {
  private monitor: PerformanceMonitor;
  private imageOptimizer: ImageOptimizer;
  private apiOptimizer: APIOptimizer;

  constructor(config: OptimizationConfig) {
    this.monitor = new PerformanceMonitor();
    this.imageOptimizer = new ImageOptimizer(config);
    this.apiOptimizer = new APIOptimizer();
  }

  /**
   * Optimize portfolio creation workflow
   */
  async optimizePortfolioCreation(portfolioData: any): Promise<{
    optimizedData: any;
    metrics: PerformanceMetrics;
    success: boolean;
  }> {
    this.monitor.startTimer('portfolioCreation');

    try {
      // Parallel optimization tasks
      const optimizationTasks = [
        this.optimizeImages(portfolioData),
        this.optimizeApiCalls(portfolioData),
        this.preloadTemplateAssets(portfolioData.template),
      ];

      const [optimizedImages, optimizedApi] =
        await Promise.all(optimizationTasks);

      const optimizedData = {
        ...portfolioData,
        ...optimizedImages,
        ...optimizedApi,
      };

      this.monitor.endTimer('portfolioCreation');
      const metrics = this.monitor.getMetrics();

      return {
        optimizedData,
        metrics,
        success: this.monitor.meetsPerformanceTarget(),
      };
    } catch (error) {
      console.error('Portfolio optimization failed:', error);
      this.monitor.endTimer('portfolioCreation');

      return {
        optimizedData: portfolioData,
        metrics: this.monitor.getMetrics(),
        success: false,
      };
    }
  }

  /**
   * Optimize images in portfolio data
   */
  private async optimizeImages(portfolioData: any): Promise<any> {
    this.monitor.startTimer('imageOptimization');

    try {
      const optimizedData = { ...portfolioData };

      // Optimize avatar
      if (portfolioData.avatarUrl) {
        optimizedData.avatarUrl = await this.imageOptimizer.optimizeImage(
          portfolioData.avatarUrl
        );
        optimizedData.avatarSizes = this.imageOptimizer.generateResponsiveSizes(
          optimizedData.avatarUrl
        );
      }

      // Optimize project images
      if (portfolioData.projects) {
        optimizedData.projects = await Promise.all(
          portfolioData.projects.map(async (project: any) => {
            if (project.imageUrl) {
              const optimizedUrl = await this.imageOptimizer.optimizeImage(
                project.imageUrl
              );
              return {
                ...project,
                imageUrl: optimizedUrl,
                imageSizes:
                  this.imageOptimizer.generateResponsiveSizes(optimizedUrl),
              };
            }
            return project;
          })
        );
      }

      return optimizedData;
    } finally {
      this.monitor.endTimer('imageOptimization');
    }
  }

  /**
   * Optimize API calls
   */
  private async optimizeApiCalls(portfolioData: any): Promise<any> {
    this.monitor.startTimer('apiResponseTime');

    try {
      // Pre-cache template data
      if (portfolioData.template) {
        const templateUrl = `/api/v1/templates/${portfolioData.template}`;
        await this.apiOptimizer.optimizedFetch(templateUrl);
      }

      // Pre-cache user preferences
      if (portfolioData.userId) {
        const preferencesUrl = `/api/v1/users/${portfolioData.userId}/preferences`;
        await this.apiOptimizer.optimizedFetch(preferencesUrl);
      }

      return portfolioData;
    } finally {
      this.monitor.endTimer('apiResponseTime');
    }
  }

  /**
   * Preload template assets
   */
  private async preloadTemplateAssets(template: string): Promise<void> {
    const templateAssets = [
      `/templates/${template}.css`,
      `/templates/${template}.js`,
      `/fonts/template-${template}.woff2`,
    ].filter(asset => BundleOptimizer.shouldPreload(asset));

    BundleOptimizer.preloadResources(templateAssets);
  }

  /**
   * Get optimization recommendations
   */
  getOptimizationRecommendations(): {
    performance: string[];
    images: string[];
    api: string[];
    bundle: string[];
  } {
    const metrics = this.monitor.getMetrics();
    const recommendations: {
      performance: string[];
      images: string[];
      api: string[];
      bundle: string[];
    } = {
      performance: [],
      images: [],
      api: [],
      bundle: [],
    };

    // Performance recommendations
    if (metrics.totalTime > 30000) {
      recommendations.performance.push(
        'Total generation time exceeds 30-second target'
      );
    }

    if (metrics.templateRendering > 5000) {
      recommendations.performance.push(
        'Template rendering is slow - consider simplifying template'
      );
    }

    // Image optimization recommendations
    if (metrics.imageOptimization > 3000) {
      recommendations.images.push(
        'Image optimization is slow - consider reducing image sizes'
      );
      recommendations.images.push('Enable WebP format for better compression');
    }

    // API optimization recommendations
    if (metrics.apiResponseTime > 2000) {
      recommendations.api.push('API responses are slow - implement caching');
      recommendations.api.push('Consider API request batching');
    }

    // Bundle optimization recommendations
    if (metrics.totalTime > 20000) {
      recommendations.bundle.push(
        'Enable code splitting for better performance'
      );
      recommendations.bundle.push('Implement resource preloading');
    }

    return recommendations;
  }
}

