import { logger } from '@/lib/utils/logger';

/**
 * Database Query Optimizer
 * Provides utilities for optimizing database queries and preventing N+1 problems
 */

export interface QueryOptions {
  page?: number;
  limit?: number;
  orderBy?: string;
  orderDirection?: 'asc' | 'desc';
  include?: string[];
  select?: string[];
}

export interface PaginationResult<T> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
}

/**
 * Default query limits to prevent unbounded queries
 */
export const QUERY_LIMITS = {
  DEFAULT_PAGE_SIZE: 10,
  MAX_PAGE_SIZE: 100,
  DEFAULT_PAGE: 1,
} as const;

/**
 * Build pagination parameters for database queries
 */
export function buildPaginationParams(options: QueryOptions = {}) {
  const page = Math.max(1, options.page || QUERY_LIMITS.DEFAULT_PAGE);
  const limit = Math.min(
    options.limit || QUERY_LIMITS.DEFAULT_PAGE_SIZE,
    QUERY_LIMITS.MAX_PAGE_SIZE
  );

  const offset = (page - 1) * limit;

  return {
    page,
    limit,
    offset,
    from: offset,
    to: offset + limit - 1,
  };
}

/**
 * Create pagination response metadata
 */
export function createPaginationMeta(
  total: number,
  page: number,
  limit: number
): PaginationResult<any>['pagination'] {
  const totalPages = Math.ceil(total / limit);

  return {
    page,
    limit,
    total,
    totalPages,
    hasNext: page < totalPages,
    hasPrev: page > 1,
  };
}

/**
 * Optimize SELECT fields to reduce data transfer
 */
export function optimizeSelectFields(
  requestedFields?: string[],
  allowedFields?: string[]
): string {
  if (!requestedFields || requestedFields.length === 0) {
    return '*';
  }

  // If allowed fields are specified, filter requested fields
  if (allowedFields) {
    const validFields = requestedFields.filter(field =>
      allowedFields.includes(field)
    );

    if (validFields.length === 0) {
      logger.warn('No valid fields requested, returning default set', {
        requested: requestedFields,
        allowed: allowedFields,
      });
      return allowedFields.join(',');
    }

    return validFields.join(',');
  }

  return requestedFields.join(',');
}

/**
 * Build optimized query with relations to avoid N+1 queries
 */
export function buildOptimizedQuery(
  baseTable: string,
  options: QueryOptions & {
    joins?: Array<{ table: string; on: string; type?: 'inner' | 'left' }>;
  } = {}
): {
  select: string;
  from: string;
  orderBy: string;
  limit: string;
  joins: string;
} {
  const { limit, offset } = buildPaginationParams(options);

  // Build SELECT clause
  const selectClause = options.select
    ? `SELECT ${optimizeSelectFields(options.select)}`
    : 'SELECT *';

  // Build FROM clause
  const fromClause = `FROM ${baseTable}`;

  // Build JOIN clauses
  const joinClauses = options.joins
    ? options.joins
        .map(
          join =>
            `${join.type?.toUpperCase() || 'LEFT'} JOIN ${join.table} ON ${join.on}`
        )
        .join(' ')
    : '';

  // Build ORDER BY clause
  const orderByClause = options.orderBy
    ? `ORDER BY ${options.orderBy} ${options.orderDirection || 'asc'}`
    : '';

  // Build LIMIT clause
  const limitClause = `LIMIT ${limit} OFFSET ${offset}`;

  return {
    select: selectClause,
    from: fromClause,
    joins: joinClauses,
    orderBy: orderByClause,
    limit: limitClause,
  };
}

/**
 * Cache key generator for query results
 */
export function generateQueryCacheKey(
  table: string,
  options: QueryOptions,
  userId?: string
): string {
  const parts = [
    'query',
    table,
    userId || 'public',
    `p${options.page || 1}`,
    `l${options.limit || QUERY_LIMITS.DEFAULT_PAGE_SIZE}`,
  ];

  if (options.orderBy) {
    parts.push(`o${options.orderBy}_${options.orderDirection || 'asc'}`);
  }

  if (options.select) {
    parts.push(`s${options.select.sort().join('_')}`);
  }

  return parts.join(':');
}

/**
 * Batch loader to prevent N+1 queries
 */
export class BatchLoader<K, V> {
  private batch: Map<K, Array<(value: V | null) => void>> = new Map();
  private scheduled = false;

  constructor(
    private loader: (keys: K[]) => Promise<Map<K, V>>,
    private options: { maxBatchSize?: number; delay?: number } = {}
  ) {}

  load(key: K): Promise<V | null> {
    return new Promise(resolve => {
      // Add to batch
      const callbacks = this.batch.get(key) || [];
      callbacks.push(resolve);
      this.batch.set(key, callbacks);

      // Schedule batch execution
      if (!this.scheduled) {
        this.scheduled = true;
        setTimeout(() => this.executeBatch(), this.options.delay || 0);
      }
    });
  }

  private async executeBatch() {
    const currentBatch = this.batch;
    this.batch = new Map();
    this.scheduled = false;

    if (currentBatch.size === 0) return;

    try {
      const keys = Array.from(currentBatch.keys());
      const results = await this.loader(keys);

      // Resolve all callbacks
      currentBatch.forEach((callbacks, key) => {
        const value = results.get(key) || null;
        callbacks.forEach(callback => callback(value));
      });
    } catch (error) {
      // Reject all callbacks
      currentBatch.forEach(callbacks => {
        callbacks.forEach(callback => callback(null));
      });

      logger.error('Batch loader error', error as Error);
    }
  }
}

/**
 * Content Security Policy configuration
 * Protects against XSS and other injection attacks
 */

export interface CSPDirectives {
  'default-src': string[];
  'script-src': string[];
  'style-src': string[];
  'img-src': string[];
  'font-src': string[];
  'connect-src': string[];
  'media-src': string[];
  'object-src': string[];
  'frame-src': string[];
  'frame-ancestors': string[];
  'base-uri': string[];
  'form-action': string[];
  'manifest-src': string[];
  'worker-src': string[];
}

/**
 * Get CSP directives based on environment
 */
export function getCSPDirectives(): CSPDirectives {
  const isDevelopment = process.env.NODE_ENV === 'development';

  const directives: CSPDirectives = {
    'default-src': ["'self'"],
    'script-src': [
      "'self'",
      "'unsafe-eval'", // Required for Next.js in development
      isDevelopment ? "'unsafe-inline'" : '', // Only in development
      'https://www.googletagmanager.com',
      'https://www.google-analytics.com',
      'https://app.posthog.com',
      'https://cdn.jsdelivr.net',
    ].filter(Boolean),
    'style-src': [
      "'self'",
      "'unsafe-inline'", // Required for styled-jsx and inline styles
      'https://fonts.googleapis.com',
    ],
    'img-src': [
      "'self'",
      'data:',
      'blob:',
      'https://*.supabase.co',
      'https://*.supabase.in',
      'https://avatars.githubusercontent.com',
      'https://lh3.googleusercontent.com',
      'https://platform-lookaside.fbsbx.com',
    ],
    'font-src': ["'self'", 'https://fonts.gstatic.com'],
    'connect-src': [
      "'self'",
      'https://*.supabase.co',
      'https://*.supabase.in',
      'https://api.github.com',
      'https://www.google-analytics.com',
      'https://app.posthog.com',
      'https://api-inference.huggingface.co',
      'wss://*.supabase.co',
      'wss://*.supabase.in',
      isDevelopment ? 'ws://localhost:*' : '',
      isDevelopment ? 'http://localhost:*' : '',
    ].filter(Boolean),
    'media-src': ["'self'"],
    'object-src': ["'none'"],
    'frame-src': [
      "'self'",
      'https://www.youtube.com',
      'https://player.vimeo.com',
    ],
    'frame-ancestors': ["'self'"],
    'base-uri': ["'self'"],
    'form-action': ["'self'"],
    'manifest-src': ["'self'"],
    'worker-src': ["'self'", 'blob:'],
  };

  return directives;
}

/**
 * Format CSP directives as a header string
 */
export function formatCSPHeader(directives: CSPDirectives): string {
  return Object.entries(directives)
    .map(([key, values]) => {
      const filteredValues = values.filter(Boolean);
      if (filteredValues.length === 0) return '';
      return `${key} ${filteredValues.join(' ')}`;
    })
    .filter(Boolean)
    .join('; ');
}

/**
 * Generate CSP header string from directives
 */
export function generateCSPHeader(directives: CSPDirectives): string {
  return Object.entries(directives)
    .map(([key, values]) => {
      const validValues = values.filter((v: string) => v !== '');
      return validValues.length > 0 ? `${key} ${validValues.join(' ')}` : '';
    })
    .filter(directive => directive !== '')
    .join('; ');
}

/**
 * Generate nonce for inline scripts
 */
export function generateNonce(): string {
  if (typeof crypto !== 'undefined' && 'randomBytes' in crypto) {
    return (crypto as any).randomBytes(16).toString('base64');
  }
  // Fallback for environments without crypto
  return Buffer.from(Math.random().toString()).toString('base64');
}

import { Portfolio, Project, Experience, Education } from '@/types/portfolio';

/**
 * Structured Data Generation for SEO
 * Generates Schema.org JSON-LD structured data for portfolios
 */

/**
 * Generate complete structured data for a portfolio
 */
export function generatePortfolioStructuredData(portfolio: Portfolio): string {
  const structuredData = {
    '@context': 'https://schema.org',
    '@graph': [
      generatePersonSchema(portfolio),
      generateWebPageSchema(portfolio),
      ...generateProjectSchemas(portfolio.projects),
      ...generateEmploymentSchemas(portfolio.experience),
      ...generateEducationSchemas(portfolio.education),
    ].filter(Boolean),
  };

  return JSON.stringify(structuredData, null, 2);
}

/**
 * Generate Person schema
 */
function generatePersonSchema(portfolio: Portfolio): any {
  const person: any = {
    '@type': 'Person',
    '@id': `#person-${portfolio.id}`,
    name: portfolio.name,
    jobTitle: portfolio.title,
    description: portfolio.bio,
  };

  // Add contact information
  if (portfolio.contact.email) {
    person.email = portfolio.contact.email;
  }
  if (portfolio.contact.phone) {
    person.telephone = portfolio.contact.phone;
  }
  if (portfolio.contact.location) {
    person.address = {
      '@type': 'PostalAddress',
      addressLocality: portfolio.contact.location,
    };
  }

  // Add social profiles
  const sameAs: string[] = [];
  if (portfolio.social.linkedin) sameAs.push(portfolio.social.linkedin);
  if (portfolio.social.github) sameAs.push(portfolio.social.github);
  if (portfolio.social.twitter) sameAs.push(portfolio.social.twitter);
  if (portfolio.social.website) sameAs.push(portfolio.social.website);

  if (sameAs.length > 0) {
    person.sameAs = sameAs;
  }

  // Add skills
  if (portfolio.skills && portfolio.skills.length > 0) {
    person.knowsAbout = portfolio.skills.map(skill => skill.name);
  }

  // Add current employment
  if (portfolio.experience && portfolio.experience.length > 0) {
    const currentJob = portfolio.experience.find(exp => exp.current);
    if (currentJob) {
      person.worksFor = {
        '@type': 'Organization',
        name: currentJob.company,
      };
    }
  }

  // Add education
  if (portfolio.education && portfolio.education.length > 0) {
    person.alumniOf = portfolio.education.map(edu => ({
      '@type': 'EducationalOrganization',
      name: edu.institution,
    }));
  }

  // Add image if available
  if (portfolio.avatarUrl) {
    person.image = portfolio.avatarUrl;
  }

  return person;
}

/**
 * Generate WebPage schema
 */
function generateWebPageSchema(portfolio: Portfolio): any {
  const webpage: any = {
    '@type': 'ProfilePage',
    '@id': `#webpage-${portfolio.id}`,
    name: `${portfolio.name} - ${portfolio.title}`,
    description: portfolio.bio,
    url:
      portfolio.customDomain ||
      `https://prisma.madfam.io/${portfolio.subdomain}`,
    about: { '@id': `#person-${portfolio.id}` },
    dateModified: portfolio.updatedAt,
    datePublished: portfolio.publishedAt || portfolio.createdAt,
  };

  // Add breadcrumb
  webpage.breadcrumb = {
    '@type': 'BreadcrumbList',
    itemListElement: [
      {
        '@type': 'ListItem',
        position: 1,
        name: 'Home',
        item: 'https://prisma.madfam.io',
      },
      {
        '@type': 'ListItem',
        position: 2,
        name: 'Portfolios',
        item: 'https://prisma.madfam.io/portfolios',
      },
      {
        '@type': 'ListItem',
        position: 3,
        name: portfolio.name,
      },
    ],
  };

  return webpage;
}

/**
 * Generate Project schemas
 */
function generateProjectSchemas(projects: Project[]): any[] {
  if (!projects || projects.length === 0) return [];

  return projects.map((project, index) => ({
    '@type': 'CreativeWork',
    '@id': `#project-${project.id}`,
    name: project.title,
    description: project.description,
    url: project.liveUrl || project.projectUrl,
    creator: { '@id': '#person' },
    position: index + 1,
    keywords: project.technologies.join(', '),
    ...(project.imageUrl && { image: project.imageUrl }),
    ...(project.githubUrl && {
      codeRepository: project.githubUrl,
    }),
  }));
}

/**
 * Generate Employment schemas
 */
function generateEmploymentSchemas(experiences: Experience[]): any[] {
  if (!experiences || experiences.length === 0) return [];

  return experiences.map(exp => ({
    '@type': 'EmployeeRole',
    '@id': `#employment-${exp.id}`,
    startDate: exp.startDate,
    endDate: exp.endDate || undefined,
    roleName: exp.position,
    worksFor: {
      '@type': 'Organization',
      name: exp.company,
    },
    description: exp.description,
    skills: exp.technologies?.join(', '),
  }));
}

/**
 * Generate Education schemas
 */
function generateEducationSchemas(education: Education[]): any[] {
  if (!education || education.length === 0) return [];

  return education.map(edu => ({
    '@type': 'EducationalOccupationalCredential',
    '@id': `#education-${edu.id}`,
    name: `${edu.degree} in ${edu.field}`,
    educationalLevel: edu.degree,
    competencyRequired: edu.field,
    credentialCategory: 'degree',
    dateCreated: edu.endDate,
    recognizedBy: {
      '@type': 'EducationalOrganization',
      name: edu.institution,
    },
  }));
}

/**
 * Generate Article schema for blog posts
 */
export function generateArticleSchema(article: {
  title: string;
  description: string;
  content: string;
  author: string;
  datePublished: Date;
  dateModified?: Date;
  image?: string;
  keywords?: string[];
}): string {
  const schema = {
    '@context': 'https://schema.org',
    '@type': 'Article',
    headline: article.title,
    description: article.description,
    articleBody: article.content,
    author: {
      '@type': 'Person',
      name: article.author,
    },
    datePublished: article.datePublished.toISOString(),
    dateModified: (article.dateModified || article.datePublished).toISOString(),
    ...(article.image && { image: article.image }),
    ...(article.keywords && { keywords: article.keywords.join(', ') }),
  };

  return JSON.stringify(schema, null, 2);
}

/**
 * Generate Service schema for freelance services
 */
export function generateServiceSchema(service: {
  name: string;
  description: string;
  provider: string;
  category: string;
  price?: {
    min: number;
    max: number;
    currency: string;
  };
  areaServed?: string[];
}): string {
  const schema: any = {
    '@context': 'https://schema.org',
    '@type': 'Service',
    name: service.name,
    description: service.description,
    provider: {
      '@type': 'Person',
      name: service.provider,
    },
    serviceType: service.category,
  };

  if (service.price) {
    schema.offers = {
      '@type': 'Offer',
      priceSpecification: {
        '@type': 'PriceSpecification',
        minPrice: service.price.min,
        maxPrice: service.price.max,
        priceCurrency: service.price.currency,
      },
    };
  }

  if (service.areaServed) {
    schema.areaServed = service.areaServed;
  }

  return JSON.stringify(schema, null, 2);
}

/**
 * Generate FAQ schema
 */
export function generateFAQSchema(
  faqs: Array<{
    question: string;
    answer: string;
  }>
): string {
  const schema = {
    '@context': 'https://schema.org',
    '@type': 'FAQPage',
    mainEntity: faqs.map(faq => ({
      '@type': 'Question',
      name: faq.question,
      acceptedAnswer: {
        '@type': 'Answer',
        text: faq.answer,
      },
    })),
  };

  return JSON.stringify(schema, null, 2);
}

/**
 * Generate How-To schema for tutorials
 */
export function generateHowToSchema(howTo: {
  name: string;
  description: string;
  totalTime?: string;
  estimatedCost?: {
    value: number;
    currency: string;
  };
  supplies?: string[];
  tools?: string[];
  steps: Array<{
    name: string;
    text: string;
    image?: string;
  }>;
}): string {
  const schema: any = {
    '@context': 'https://schema.org',
    '@type': 'HowTo',
    name: howTo.name,
    description: howTo.description,
    step: howTo.steps.map((step, index) => ({
      '@type': 'HowToStep',
      position: index + 1,
      name: step.name,
      text: step.text,
      ...(step.image && { image: step.image }),
    })),
  };

  if (howTo.totalTime) {
    schema.totalTime = howTo.totalTime;
  }

  if (howTo.estimatedCost) {
    schema.estimatedCost = {
      '@type': 'MonetaryAmount',
      currency: howTo.estimatedCost.currency,
      value: howTo.estimatedCost.value,
    };
  }

  if (howTo.supplies) {
    schema.supply = howTo.supplies.map(supply => ({
      '@type': 'HowToSupply',
      name: supply,
    }));
  }

  if (howTo.tools) {
    schema.tool = howTo.tools.map(tool => ({
      '@type': 'HowToTool',
      name: tool,
    }));
  }

  return JSON.stringify(schema, null, 2);
}

/**
 * Generate LocalBusiness schema
 */
export function generateLocalBusinessSchema(business: {
  name: string;
  description: string;
  address: {
    street: string;
    city: string;
    region: string;
    postalCode: string;
    country: string;
  };
  telephone?: string;
  email?: string;
  openingHours?: string[];
  priceRange?: string;
  image?: string;
}): string {
  const schema: any = {
    '@context': 'https://schema.org',
    '@type': 'LocalBusiness',
    name: business.name,
    description: business.description,
    address: {
      '@type': 'PostalAddress',
      streetAddress: business.address.street,
      addressLocality: business.address.city,
      addressRegion: business.address.region,
      postalCode: business.address.postalCode,
      addressCountry: business.address.country,
    },
  };

  if (business.telephone) {
    schema.telephone = business.telephone;
  }

  if (business.email) {
    schema.email = business.email;
  }

  if (business.openingHours) {
    schema.openingHoursSpecification = business.openingHours;
  }

  if (business.priceRange) {
