  }

  if (business.image) {
    schema.image = business.image;
  }

  return JSON.stringify(schema, null, 2);
}

import { GitHubAnalyticsClient } from '@/lib/analytics/github/client';
import { createClient } from '@/lib/supabase/server';
import { logger } from '@/lib/utils/logger';

import type { Repository, RepositoryAnalytics } from '@/types/analytics';

/**
 * Repository Analytics Service
 *
 * Handles repository-specific analytics operations including
 * syncing, fetching, and processing repository data.
 */
export class RepositoryAnalyticsService {
  private githubClient: GitHubAnalyticsClient;
  private userId: string;

  constructor(userId: string, githubClient: GitHubAnalyticsClient) {
    this.userId = userId;
    this.githubClient = githubClient;
  }

  /**
   * Sync repositories from GitHub
   */
  async syncRepositories(): Promise<Repository[]> {
    const supabase = await createClient();

    if (!supabase) {
      throw new Error('Database connection not available');
    }

    try {
      // Get GitHub integration
      const { data: integration } = await supabase
        .from('github_integrations')
        .select('id')
        .eq('user_id', this.userId)
        .single();

      if (!integration) {
        throw new Error('No GitHub integration found');
      }

      // Fetch repositories from GitHub
      const githubRepos = await this.githubClient.fetchRepositories();

      // Upsert repositories to database in parallel
      const repositoryPromises = githubRepos.map(async githubRepo => {
        const repoData = GitHubAnalyticsClient.transformRepository(
          githubRepo,
          this.userId,
          integration.id
        );

        const { data: repo, error } = await supabase
          .from('repositories')
          .upsert({
            ...repoData,
            last_synced_at: new Date().toISOString(),
          })
          .select()
          .single();

        if (error) {
          logger.error('Failed to upsert repository', { error, repoData });
          return null;
        }

        return repo as Repository;
      });

      const repositoryResults = await Promise.all(repositoryPromises);
      const repositories = repositoryResults.filter(
        (repo): repo is Repository => repo !== null
      );

      // Update integration last synced time
      await supabase
        .from('github_integrations')
        .update({ last_synced_at: new Date().toISOString() })
        .eq('id', integration.id);

      return repositories;
    } catch (error) {
      logger.error('Failed to sync repositories', { error });
      throw error;
    }
  }

  /**
   * Get user's repositories
   */
  async getRepositories(): Promise<Repository[]> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const { data, error } = await supabase
      .from('repositories')
      .select('*')
      .eq('user_id', this.userId)
      .eq('is_active', true)
      .order('updated_at', { ascending: false });

    if (error) {
      logger.error('Failed to fetch repositories', { error });
      throw error;
    }

    return data as Repository[];
  }

  /**
   * Get repository by ID
   */
  async getRepository(repositoryId: string): Promise<Repository | null> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const { data, error } = await supabase
      .from('repositories')
      .select('*')
      .eq('id', repositoryId)
      .eq('user_id', this.userId)
      .single();

    if (error) {
      if (error.code === 'PGRST116') return null; // Not found
      logger.error('Failed to fetch repository', { error });
      throw error;
    }

    return data as Repository;
  }

  /**
   * Get repository analytics
   */
  async getRepositoryAnalytics(
    repositoryId: string
  ): Promise<RepositoryAnalytics | null> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const repository = await this.getRepository(repositoryId);
    if (!repository) return null;

    try {
      // Fetch current and historical metrics
      const { data: metricsData } = await supabase
        .from('code_metrics')
        .select('*')
        .eq('repository_id', repositoryId)
        .order('metric_date', { ascending: false })
        .limit(30);

      const metrics = metricsData || [];
      const currentMetrics = metrics[0];

      // Fetch contributors
      const { data: contributorData } = await supabase
        .from('repository_contributors')
        .select(
          `
          *,
          contributor:contributors(*)
        `
        )
        .eq('repository_id', repositoryId)
        .order('commit_count', { ascending: false });

      // Fetch pull requests
      const { data: prData } = await supabase
        .from('pull_requests')
        .select('*')
        .eq('repository_id', repositoryId)
        .order('created_at', { ascending: false })
        .limit(20);

      const pullRequests = prData || [];

      // Calculate PR stats
      const mergedPRs = pullRequests.filter((pr: any) => pr.state === 'merged');
      const avgCycleTime =
        mergedPRs.length > 0
          ? mergedPRs.reduce(
              (sum: number, pr: any) => sum + (pr.cycleTimeHours || 0),
              0
            ) / mergedPRs.length
          : 0;
      const avgLeadTime =
        mergedPRs.length > 0
          ? mergedPRs.reduce(
              (sum: number, pr: any) => sum + (pr.leadTimeHours || 0),
              0
            ) / mergedPRs.length
          : 0;
      const mergeRate =
        pullRequests.length > 0
          ? (mergedPRs.length / pullRequests.length) * 100
          : 0;

      // Fetch commit analytics
      const { data: commitData } = await supabase
        .from('commit_analytics')
        .select('*')
        .eq('repository_id', repositoryId)
        .order('commit_date', { ascending: false })
        .limit(30);

      const commitAnalytics = commitData || [];

      // Calculate commit patterns
      const commitsByHour = new Array(24).fill(0);
      commitAnalytics.forEach((ca: any) => {
        if (ca.peakHour !== null && ca.peakHour !== undefined) {
          commitsByHour[ca.peakHour] += ca.commitCount;
        }
      });

      // Calculate languages breakdown
      const languages = currentMetrics?.locByLanguage || {};
      const totalLines = Object.values(languages).reduce(
        (sum: number, lines: any) => sum + lines,
        0
      );
      const languageBreakdown = Object.entries(languages).map(
        ([language, lines]: [string, any]) => ({
          language,
          lines,
          percentage: totalLines > 0 ? (lines / totalLines) * 100 : 0,
        })
      );

      return {
        repository,
        metrics: {
          current: currentMetrics || null,
          history: metrics,
        },
        contributors:
          contributorData?.map((rc: any) => ({
            contributor: rc.contributor,
            stats: rc,
          })) || [],
        pullRequests: {
          recent: pullRequests,
          stats: {
            averageCycleTime: avgCycleTime,
            averageLeadTime: avgLeadTime,
            mergeRate,
            averageTimeToFirstReview: 0, // TODO: Calculate this
          },
        },
        commits: {
          byDay: commitAnalytics,
          byHour: commitsByHour.map((count, hour) => ({ hour, count })),
          byAuthor: [], // TODO: Implement author aggregation
        },
        languages: languageBreakdown,
      };
    } catch (error) {
      logger.error('Failed to get repository analytics', {
        repositoryId,
        error,
      });
      throw error;
    }
  }
}

import { SupabaseClient } from '@supabase/supabase-js';
import { startOfMonth, endOfMonth, subMonths, format } from 'date-fns';
import { z } from 'zod';

// Revenue metrics types
export interface RevenueMetrics {
  mrr: number; // Monthly Recurring Revenue
  arr: number; // Annual Recurring Revenue
  newMrr: number; // New MRR this month
  expansionMrr: number; // Expansion MRR (upgrades)
  contractionMrr: number; // Contraction MRR (downgrades)
  churnedMrr: number; // Churned MRR
  netNewMrr: number; // Net new MRR
  customerCount: number;
  newCustomers: number;
  churnedCustomers: number;
  churnRate: number; // Monthly churn rate percentage
  arpu: number; // Average Revenue Per User
  ltv: number; // Customer Lifetime Value
}

export interface RevenueByPlan {
  plan: string;
  customerCount: number;
  mrr: number;
  percentage: number;
}

export interface RevenueTrend {
  month: string;
  mrr: number;
  customerCount: number;
  churnRate: number;
}

export interface CustomerMetrics {
  totalCustomers: number;
  payingCustomers: number;
  trialCustomers: number;
  conversionRate: number;
}


export class RevenueMetricsService {
  constructor(private supabase: SupabaseClient) {}

  /**
   * Calculate comprehensive revenue metrics
   */
  async calculateMetrics(date: Date = new Date()): Promise<RevenueMetrics> {
    const startDate = startOfMonth(date);
    const endDate = endOfMonth(date);
    const previousStartDate = startOfMonth(subMonths(date, 1));

    // Get current active subscriptions
    const { data: currentSubscriptions, error: currentError } =
      await this.supabase
        .from('subscriptions')
        .select('*')
        .in('status', ['active', 'trialing'])
        .lte('created_at', endDate.toISOString());

    if (currentError) throw currentError;

    // Get previous month subscriptions for comparison
    const { data: previousSubscriptions, error: previousError } =
      await this.supabase
        .from('subscriptions')
        .select('*')
        .in('status', ['active', 'trialing'])
        .lte('created_at', previousStartDate.toISOString());

    if (previousError) throw previousError;

    // Calculate MRR
    const currentMrr = this.calculateMrrFromSubscriptions(
      currentSubscriptions || []
    );
    const previousMrr = this.calculateMrrFromSubscriptions(
      previousSubscriptions || []
    );

    // Get new subscriptions this month
    const { data: newSubscriptions, error: newError } = await this.supabase
      .from('subscriptions')
      .select('*')
      .gte('created_at', startDate.toISOString())
      .lte('created_at', endDate.toISOString())
      .in('status', ['active', 'trialing']);

    if (newError) throw newError;

    // Get churned subscriptions this month
    const { data: churnedSubscriptions, error: churnError } =
      await this.supabase
        .from('subscriptions')
        .select('*')
        .eq('status', 'canceled')
        .gte('updated_at', startDate.toISOString())
        .lte('updated_at', endDate.toISOString());

    if (churnError) throw churnError;

    // Calculate metrics
    const newMrr = this.calculateMrrFromSubscriptions(newSubscriptions || []);
    const churnedMrr = this.calculateMrrFromSubscriptions(
      churnedSubscriptions || []
    );
    const netNewMrr = currentMrr - previousMrr;
    const expansionMrr = Math.max(0, netNewMrr - newMrr + churnedMrr);
    const contractionMrr = Math.max(0, -netNewMrr + newMrr - churnedMrr);

    const customerCount =
      currentSubscriptions?.filter(s => s.status === 'active').length || 0;
    const newCustomers = newSubscriptions?.length || 0;
    const churnedCustomers = churnedSubscriptions?.length || 0;
    const churnRate =
      customerCount > 0 ? (churnedCustomers / customerCount) * 100 : 0;
    const arpu = customerCount > 0 ? currentMrr / customerCount : 0;
    const ltv = churnRate > 0 ? arpu / (churnRate / 100) : arpu * 24; // 24 months if no churn

    return {
      mrr: currentMrr,
      arr: currentMrr * 12,
      newMrr,
      expansionMrr,
      contractionMrr,
      churnedMrr,
      netNewMrr,
      customerCount,
      newCustomers,
      churnedCustomers,
      churnRate: Math.round(churnRate * 100) / 100,
      arpu: Math.round(arpu * 100) / 100,
      ltv: Math.round(ltv * 100) / 100,
    };
  }

  /**
   * Get revenue breakdown by plan
   */
  async getRevenueByPlan(): Promise<RevenueByPlan[]> {
    const { data: subscriptions, error } = await this.supabase
      .from('subscriptions')
      .select('*')
      .in('status', ['active', 'trialing']);

    if (error) throw error;

    const planMap = new Map<string, { count: number; mrr: number }>();
    let totalMrr = 0;

    subscriptions?.forEach(sub => {
      const mrr = this.getSubscriptionMrr(sub);
      totalMrr += mrr;

      const current = planMap.get(sub.plan) || { count: 0, mrr: 0 };
      planMap.set(sub.plan, {
        count: current.count + 1,
        mrr: current.mrr + mrr,
      });
    });

    return Array.from(planMap.entries())
      .map(([plan, data]) => ({
        plan,
        customerCount: data.count,
        mrr: data.mrr,
        percentage:
          totalMrr > 0 ? Math.round((data.mrr / totalMrr) * 10000) / 100 : 0,
      }))
      .sort((a, b) => b.mrr - a.mrr);
  }

  /**
   * Get revenue trends for the last N months
   */
  async getRevenueTrends(months: number = 12): Promise<RevenueTrend[]> {
    const trends: RevenueTrend[] = [];
    const now = new Date();

    for (let i = months - 1; i >= 0; i--) {
      const date = subMonths(now, i);
      const metrics = await this.calculateMetrics(date);

      trends.push({
        month: format(date, 'MMM yyyy'),
        mrr: metrics.mrr,
        customerCount: metrics.customerCount,
        churnRate: metrics.churnRate,
      });
    }

    return trends;
  }

  /**
   * Get customer metrics
   */
  async getCustomerMetrics(): Promise<CustomerMetrics> {
    const { data: allUsers, error: usersError } = await this.supabase
      .from('users')
      .select('id');

    if (usersError) throw usersError;

    const { data: subscriptions, error: subsError } = await this.supabase
      .from('subscriptions')
      .select('*')
      .in('status', ['active', 'trialing', 'past_due']);

    if (subsError) throw subsError;

    const totalCustomers = allUsers?.length || 0;
    const payingCustomers =
      subscriptions?.filter(s => s.status === 'active' && s.plan !== 'free')
        .length || 0;
    const trialCustomers =
      subscriptions?.filter(s => s.status === 'trialing').length || 0;
    const conversionRate =
      totalCustomers > 0 ? (payingCustomers / totalCustomers) * 100 : 0;

    return {
      totalCustomers,
      payingCustomers,
      trialCustomers,
      conversionRate: Math.round(conversionRate * 100) / 100,
    };
  }

  /**
   * Get top paying customers
   */
  async getTopCustomers(limit: number = 10) {
    const { data, error } = await this.supabase
      .from('subscriptions')
      .select(
        `
        *,
        users!inner(
          id,
          email,
          full_name
        )
      `
      )
      .eq('status', 'active')
      .neq('plan', 'free')
      .order('amount', { ascending: false })
      .limit(limit);

    if (error) throw error;

    return data?.map(sub => ({
      userId: sub.user_id,
      email: sub.users.email,
      name: sub.users.full_name,
      plan: sub.plan,
      mrr: this.getSubscriptionMrr(sub),
      subscribedAt: sub.created_at,
    }));
  }

  /**
   * Calculate MRR from a list of subscriptions
   */
  private calculateMrrFromSubscriptions(subscriptions: any[]): number {
    return subscriptions.reduce((total, sub) => {
      return total + this.getSubscriptionMrr(sub);
    }, 0);
  }

  /**
   * Get MRR for a single subscription
   */
  private getSubscriptionMrr(subscription: any): number {
    // Skip free plans
    if (subscription.plan === 'free') return 0;

    // Use amount from subscription if available
    if (subscription.amount) {
      return subscription.amount;
    }

    // Otherwise use default pricing
    const pricing: Record<string, number> = {
      pro: 24,
      business: 39,
      enterprise: 79,
    };

    return pricing[subscription.plan] || 0;
  }

  /**
   * Track revenue event (for real-time updates)
   */
  async trackRevenueEvent(event: {
    type:
      | 'new_subscription'
      | 'upgrade'
      | 'downgrade'
      | 'churn'
      | 'reactivation';
    userId: string;
    oldPlan?: string;
    newPlan?: string;
    amount?: number;
  }) {
    const { error } = await this.supabase.from('revenue_events').insert({
      type: event.type,
      user_id: event.userId,
      old_plan: event.oldPlan,
      new_plan: event.newPlan,
      amount: event.amount,
      created_at: new Date().toISOString(),
    });

    if (error) {
      console.error('Failed to track revenue event:', error);
    }
  }
}

import { GitHubAnalyticsClient } from '@/lib/integrations/github/analytics-client';
import { createClient } from '@/lib/supabase/server';
import { logger } from '@/lib/utils/logger';

import type { Repository, PullRequest, Contributor } from '@/types/analytics';

interface DashboardAnalytics {
  repositories: Repository[];
  metrics: CodeMetrics[];
  pullRequests: PullRequest[];
  contributors: Contributor[];
}

interface CodeMetrics {
  id: string;
  user_id: string;
  repository_id: string;
  metric_date: string;
  commits: number;
  additions: number;
  deletions: number;
  files_changed: number;
  created_at: string;
  updated_at: string;
}

/**
 * Optimized Analytics Service
 * Fixes N+1 query problems and improves performance
 */

export class OptimizedAnalyticsService {
  private userId: string;
  private githubClient: GitHubAnalyticsClient | null = null;

  constructor(userId: string) {
    this.userId = userId;
  }

  /**
   * Initialize GitHub client lazily
   */
  private async getGitHubClient(): Promise<GitHubAnalyticsClient> {
    if (!this.githubClient) {
      this.githubClient = await GitHubAnalyticsClient.fromUserId(this.userId);
    }
    return this.githubClient;
  }

  /**
   * Sync repositories with batch operations
   */
  async syncRepositories(): Promise<Repository[]> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    try {
      // Get GitHub integration
      const { data: integration } = await supabase
        .from('github_integrations')
        .select('id')
        .eq('user_id', this.userId)
        .single();

      if (!integration) {
        throw new Error('No GitHub integration found');
      }

      // Fetch repositories from GitHub
      const githubClient = await this.getGitHubClient();
      const githubRepos = await githubClient.fetchRepositories();

      // Transform all repositories at once
      const repoData = githubRepos.map(githubRepo => ({
        githubId: githubRepo.id,
        name: githubRepo.name,
        fullName: githubRepo.full_name,
        owner: githubRepo.full_name.split('/')[0],
        description: githubRepo.description,
        visibility: githubRepo.private
          ? ('private' as const)
          : ('public' as const),
        githubCreatedAt: new Date(githubRepo.created_at),
        githubUpdatedAt: new Date(githubRepo.updated_at),
        sizeKb: githubRepo.size,
        stargazersCount: githubRepo.stargazers_count,
        watchersCount: githubRepo.watchers_count,
        language: githubRepo.language,
        forksCount: githubRepo.forks_count,
        openIssuesCount: githubRepo.open_issues_count,
        defaultBranch: githubRepo.default_branch,
        userId: this.userId,
        githubIntegrationId: integration.id,
        lastSyncedAt: new Date(),
        isActive: true,
        syncEnabled: true,
        metadata: {},
        topics: [],
      }));

      // Batch upsert all repositories in a single query
      const { data: repositories, error } = await supabase
        .from('repositories')
        .upsert(repoData)
        .select();

      if (error) {
        logger.error('Failed to batch upsert repositories', { error });
        throw error;
      }

      // Update integration last synced time
      await supabase
        .from('github_integrations')
        .update({ last_synced_at: new Date().toISOString() })
        .eq('id', integration.id);

      return repositories as Repository[];
    } catch (error) {
      logger.error('Failed to sync repositories', { error });
      throw error;
    }
  }

  /**
   * Sync pull requests with batch operations
   */
  async syncPullRequests(repositoryId: string): Promise<PullRequest[]> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    // Get repository details
    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }

    try {
      // Fetch all pull requests from GitHub
      const githubClient = await this.getGitHubClient();
      const [owner, repo] = repository.fullName.split('/');
      if (!owner || !repo) {
        throw new Error(
          `Invalid repository fullName format: ${repository.fullName}`
        );
      }
      const githubPRs = await githubClient.fetchPullRequests(owner, repo);

      // Transform all PRs at once
      const prData = githubPRs.map(pr => ({
        repositoryId: repositoryId,
        githubPrId: pr.id,
        number: pr.number,
        title: pr.title,
        body: '', // Not provided by GitHub API in list response
        state:
          pr.state === 'open'
            ? ('open' as const)
            : pr.merged_at
              ? ('merged' as const)
              : ('closed' as const),
        draft: false, // Default value
        authorLogin: pr.user.login,
        authorAvatarUrl: pr.user.avatar_url,
        additions: pr.additions,
        deletions: pr.deletions,
        changedFiles: pr.changed_files,
        commits: 0, // Would need additional API call
        reviewComments: 0, // Would need additional API call
        createdAt: new Date(pr.created_at),
        updatedAt: new Date(pr.updated_at),
        closedAt: pr.closed_at ? new Date(pr.closed_at) : undefined,
        mergedAt: pr.merged_at ? new Date(pr.merged_at) : undefined,
        labels: [],
        metadata: {},
      }));

      // Batch upsert all pull requests
      const { data: pullRequests, error } = await supabase
        .from('pull_requests')
        .upsert(prData)
        .select();

      if (error) {
        logger.error('Failed to batch upsert pull requests', { error });
        throw error;
      }

      return pullRequests as PullRequest[];
    } catch (error) {
      logger.error('Failed to sync pull requests', { repositoryId, error });
      throw error;
    }
  }

  /**
   * Sync contributors with batch operations
   */
  async syncContributors(repositoryId: string): Promise<void> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }

    try {
      // Fetch contributors from GitHub
      const githubClient = await this.getGitHubClient();
      const [owner, repo] = repository.fullName.split('/');
      if (!owner || !repo) {
        throw new Error(
          `Invalid repository fullName format: ${repository.fullName}`
        );
      }
      const githubContributors = await githubClient.fetchContributors(
        owner,
        repo
      );

      // Prepare contributor data
      const contributorData = githubContributors.map(contributor => ({
        githubId: contributor.id,
        login: contributor.login,
        avatarUrl: contributor.avatar_url,
        lastSeenAt: new Date(),
        createdAt: new Date(),
        updatedAt: new Date(),
      }));

      // Batch upsert all contributors
      const { data: contributors, error: contributorError } = await supabase
        .from('contributors')
        .upsert(contributorData)
        .select();

      if (contributorError) {
        logger.error('Failed to batch upsert contributors', {
          contributorError,
        });
        throw contributorError;
      }

      // Prepare repository-contributor links
      const linkData =
        contributors?.map((contributor, index) => ({
          repositoryId: repositoryId,
          contributorId: contributor.id,
          commitCount: githubContributors[index]?.contributions || 0,
          additions: 0,
          deletions: 0,
          isActive: true,
          commitsLast30Days: 0,
          createdAt: new Date(),
          updatedAt: new Date(),
        })) || [];

      // Batch upsert all links
      const { error: linkError } = await supabase
        .from('repository_contributors')
        .upsert(linkData);

      if (linkError) {
        logger.error('Failed to link contributors', { linkError });
        throw linkError;
      }
    } catch (error) {
      logger.error('Failed to sync contributors', { repositoryId, error });
      throw error;
    }
  }

  /**
   * Get dashboard analytics with optimized queries
   */
  async getDashboardAnalytics(): Promise<DashboardAnalytics> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    try {
      // Use a database function to aggregate all dashboard data in one query
      const { data, error } = await supabase.rpc('get_dashboard_analytics', {
        p_user_id: this.userId,
      });

      if (error) {
        logger.error('Failed to fetch dashboard analytics', { error });
        throw error;
      }

      return data;
    } catch (_error) {
      // Fallback to multiple queries if RPC doesn't exist
      logger.warn(
        'Dashboard RPC not available, falling back to multiple queries'
      );

      // Fetch all data in parallel
      const [repositories, metrics, pullRequests, contributors] =
        await Promise.all([
          this.syncRepositories(),
          this.getAggregatedMetrics(),
          this.getRecentPullRequests(),
          this.getTopContributors(),
        ]);

      return {
        repositories,
        metrics,
        pullRequests,
        contributors,
      };
    }
  }

  /**
   * Get aggregated metrics with single query
   */
  async getAggregatedMetrics(): Promise<CodeMetrics[]> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const { data, error } = await supabase
      .from('code_metrics')
      .select('*')
      .eq('user_id', this.userId)
      .order('metric_date', { ascending: false })
      .limit(30);

    if (error) {
      logger.error('Failed to fetch metrics', { error });
      throw error;
    }

    return data;
  }

  /**
   * Get recent pull requests with single query
   */
  async getRecentPullRequests(): Promise<PullRequest[]> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const { data, error } = await supabase
      .from('pull_requests')
      .select('*, repositories!inner(user_id)')
      .eq('repositories.user_id', this.userId)
      .order('created_at', { ascending: false })
      .limit(20);

    if (error) {
      logger.error('Failed to fetch pull requests', { error });
      throw error;
    }

    return data as PullRequest[];
  }

  /**
   * Get top contributors across all user repositories
   */
  async getTopContributors(repositoryIds?: string[]): Promise<Contributor[]> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    // If no repository IDs provided, fetch all user repositories
    let repoIds = repositoryIds;
    if (!repoIds || repoIds.length === 0) {
      const { data: repos } = await supabase
        .from('repositories')
        .select('id')
        .eq('user_id', this.userId);

      repoIds = repos?.map(r => r.id) || [];
    }

    if (repoIds.length === 0) {
      return [];
    }

    const { data, error } = (await supabase
      .from('repository_contributors')
      .select(
        `
        commitCount,
        contributorId,
        contributors (
          id,
          githubId,
          login,
          avatarUrl,
          name,
          email,
          company,
          location,
          bio,
          publicRepos,
          followers,
          following,
          githubCreatedAt,
          lastSeenAt,
          createdAt,
          updatedAt
        )
      `
      )
      .in('repositoryId', repoIds)
      .order('commitCount', { ascending: false })
      .limit(10)) as { data: unknown[] | null; error: any };

    if (error) {
      logger.error('Failed to fetch contributors', { error });
      throw error;
    }

    // Define the structure of the joined data
    interface ContributorWithJoin {
      commitCount: number;
      contributorId: string;
      contributors: {
        id: string;
        githubId: number;
        login: string;
        avatarUrl?: string;
        name?: string;
        email?: string;
        company?: string;
        location?: string;
        bio?: string;
        publicRepos?: number;
        followers?: number;
        following?: number;
        githubCreatedAt?: string;
        lastSeenAt?: string;
        createdAt: string;
        updatedAt: string;
      };
    }

    // Type guard to validate ContributorWithJoin structure
    function isContributorWithJoin(item: unknown): item is ContributorWithJoin {
      return (
        typeof item === 'object' &&
        item !== null &&
        'commitCount' in item &&
        'contributorId' in item &&
        'contributors' in item &&
        typeof item.contributors === 'object'
      );
    }

    // Transform to proper Contributor type
    const validData = (data || []).filter(isContributorWithJoin);
    return validData.map(item => ({
      id: item.contributors?.id || '',
      githubId: item.contributors?.githubId || 0,
      login: item.contributors?.login || '',
      name: item.contributors?.name,
      email: item.contributors?.email,
      avatarUrl: item.contributors?.avatarUrl,
      company: item.contributors?.company,
      location: item.contributors?.location,
      bio: item.contributors?.bio,
      publicRepos: item.contributors?.publicRepos,
      followers: item.contributors?.followers,
      following: item.contributors?.following,
      githubCreatedAt: item.contributors?.githubCreatedAt
        ? new Date(item.contributors.githubCreatedAt)
        : undefined,
      lastSeenAt: item.contributors?.lastSeenAt
        ? new Date(item.contributors.lastSeenAt)
        : undefined,
      createdAt: new Date(item.contributors?.createdAt || new Date()),
      updatedAt: new Date(item.contributors?.updatedAt || new Date()),
    }));
  }

  /**
   * Helper method to get repository
   */
  private async getRepository(
    repositoryId: string
  ): Promise<Repository | null> {
    const supabase = await createClient();
    if (!supabase) {
      throw new Error('Database connection not available');
    }

    const { data, error } = await supabase
      .from('repositories')
      .select('*')
      .eq('id', repositoryId)
      .eq('user_id', this.userId)
      .single();

    if (error) {
      if (error.code === 'PGRST116') return null;
      throw error;
    }

    return data as Repository;
  }
}

import { GitHubAnalyticsClient } from '@/lib/analytics/github/client';
import { logger } from '@/lib/utils/logger';

import { DashboardAnalyticsService } from './analytics/DashboardAnalyticsService';
import { MetricsCalculationService } from './analytics/MetricsCalculationService';
import { RepositoryAnalyticsService } from './analytics/RepositoryAnalyticsService';

import type {
  Repository,
  CodeMetrics,
  PullRequest,
  AnalyticsDashboardData,
  RepositoryAnalytics,
} from '@/types/analytics';

/**
 * @fileoverview Analytics Service
 *
 * Main service layer for GitHub analytics operations.
 * Coordinates between specialized services for different analytics domains.
 *
 * @author PRISMA Development Team
 * @version 0.0.1-alpha
 */

/**
 * Analytics Service
 *
 * Provides high-level methods for analytics operations.
 * Delegates to specialized services for repository, metrics, and dashboard operations.
 */
export class AnalyticsService {
  private githubClient: GitHubAnalyticsClient;
  private userId: string;
  private repositoryService: RepositoryAnalyticsService;
  private metricsService: MetricsCalculationService;
  private dashboardService: DashboardAnalyticsService;

  constructor(userId: string) {
    this.userId = userId;
    this.githubClient = new GitHubAnalyticsClient();
    this.repositoryService = new RepositoryAnalyticsService(
      userId,
      this.githubClient
    );
    this.metricsService = new MetricsCalculationService(
      userId,
      this.githubClient
    );
    this.dashboardService = new DashboardAnalyticsService(userId);
  }

  /**
   * Initialize the service
   */
  async initialize(): Promise<void> {
    await this.githubClient.initialize(this.userId);
  }

  /**
   * Sync repositories from GitHub
   */
  syncRepositories(): Promise<Repository[]> {
    return this.repositoryService.syncRepositories();
  }

  /**
   * Get user's repositories
   */
  getRepositories(): Promise<Repository[]> {
    return this.repositoryService.getRepositories();
  }

  /**
   * Get repository by ID
   */
  getRepository(repositoryId: string): Promise<Repository | null> {
    return this.repositoryService.getRepository(repositoryId);
  }

  /**
   * Sync repository metrics
   */
  async syncRepositoryMetrics(repositoryId: string): Promise<CodeMetrics> {
    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }
    return this.metricsService.syncRepositoryMetrics(repository);
  }

  /**
   * Sync pull requests for a repository
   */
  async syncPullRequests(repositoryId: string): Promise<PullRequest[]> {
    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }
    return this.metricsService.syncPullRequests(repository);
  }

  /**
   * Sync contributors for a repository
   */
  async syncContributors(repositoryId: string): Promise<void> {
    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }
    await this.metricsService.syncContributors(repository);
  }

  /**
   * Sync commit analytics for a repository
   */
  async syncCommitAnalytics(repositoryId: string, days = 30): Promise<void> {
    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }
    await this.metricsService.syncCommitAnalytics(repository, days);
  }

  /**
   * Get analytics dashboard data
   */
  async getDashboardData(): Promise<AnalyticsDashboardData> {
    try {
      const repositories = await this.getRepositories();
      return this.dashboardService.getDashboardData(repositories);
    } catch (error) {
      logger.error('Failed to get dashboard data', { error });
      throw error;
    }
  }

  /**
   * Get repository analytics
   */
  getRepositoryAnalytics(
    repositoryId: string
  ): Promise<RepositoryAnalytics | null> {
    return this.repositoryService.getRepositoryAnalytics(repositoryId);
  }

  /**
   * Sync all data for a repository
   */
  async syncRepositoryData(
    repositoryId: string,
    options: {
      syncMetrics?: boolean;
      syncPullRequests?: boolean;
      syncContributors?: boolean;
      syncCommits?: boolean;
    } = {}
  ): Promise<void> {
    const {
      syncMetrics = true,
      syncPullRequests = true,
      syncContributors = true,
      syncCommits = true,
    } = options;

    const repository = await this.getRepository(repositoryId);
    if (!repository) {
      throw new Error('Repository not found');
    }

    const syncPromises: Promise<any>[] = [];

    if (syncMetrics) {
      syncPromises.push(this.metricsService.syncRepositoryMetrics(repository));
    }

    if (syncPullRequests) {
      syncPromises.push(this.metricsService.syncPullRequests(repository));
    }

    if (syncContributors) {
      syncPromises.push(this.metricsService.syncContributors(repository));
    }

    if (syncCommits) {
      syncPromises.push(this.metricsService.syncCommitAnalytics(repository));
    }

    await Promise.all(syncPromises);
  }
}

/**
 * Audit Logging Service for Compliance
 * Tracks all user actions, data access, and system events for SOC 2, GDPR compliance
 */

import { logger } from '@/lib/utils/logger';
import { redis, isRedisAvailable } from '@/lib/cache/redis-client';
import { createClient } from '@/lib/supabase/client';

export interface AuditEvent {
  id: string;
  timestamp: Date;
  userId?: string;
  userEmail?: string;
  userIp: string;
  userAgent?: string;
  action: AuditAction;
  resource: AuditResource;
  resourceId?: string;
  details: Record<string, any>;
  outcome: 'success' | 'failure' | 'error';
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  sessionId?: string;
  correlationId?: string;
}

export type AuditAction =
  // Authentication events
  | 'auth.login'
  | 'auth.logout'
  | 'auth.signup'
  | 'auth.password_reset'
  | 'auth.password_change'
  | 'auth.mfa_enable'
  | 'auth.mfa_disable'
  | 'auth.mfa_verify'
  | 'auth.session_expired'
  | 'auth.account_locked'

  // Data access events
  | 'data.read'
  | 'data.create'
  | 'data.update'
  | 'data.delete'
  | 'data.export'
  | 'data.import'
  | 'data.backup'

  // Portfolio events
  | 'portfolio.create'
  | 'portfolio.update'
  | 'portfolio.delete'
  | 'portfolio.publish'
  | 'portfolio.unpublish'
  | 'portfolio.view'
  | 'portfolio.export'
  | 'portfolio.share'

  // AI events
  | 'ai.enhance_bio'
  | 'ai.optimize_project'
  | 'ai.recommend_template'
  | 'ai.quota_exceeded'

  // Payment events
  | 'payment.subscription_create'
  | 'payment.subscription_update'
  | 'payment.subscription_cancel'
  | 'payment.invoice_paid'
  | 'payment.invoice_failed'
  | 'payment.refund'

  // Admin events
  | 'admin.user_view'
  | 'admin.user_update'
  | 'admin.user_delete'
  | 'admin.user_impersonate'
  | 'admin.system_config'
  | 'admin.data_export'

  // Security events
  | 'security.suspicious_activity'
  | 'security.rate_limit_exceeded'
  | 'security.unauthorized_access'
  | 'security.data_breach_detected'

  // GDPR events
  | 'gdpr.data_request'
  | 'gdpr.data_export'
  | 'gdpr.data_deletion'
  | 'gdpr.consent_given'
  | 'gdpr.consent_withdrawn'

  // System events
  | 'system.error'
  | 'system.maintenance'
  | 'system.backup'
  | 'system.restore';

export type AuditResource =
  | 'user'
  | 'portfolio'
  | 'ai_request'
  | 'payment'
  | 'subscription'
  | 'admin'
  | 'system'
  | 'session'
  | 'file'
  | 'api_key';

/**
 * Audit Logger Service
 */
class AuditLogger {
  private supabase = createClient();
  private buffer: AuditEvent[] = [];
  private maxBufferSize = 100;
  private flushInterval = 10000; // 10 seconds

  constructor() {
    // Start periodic buffer flush
    setInterval(() => {
      this.flushBuffer();
    }, this.flushInterval);

    // Flush buffer on process exit
    process.on('beforeExit', () => {
      this.flushBuffer();
    });
  }

  /**
   * Log an audit event
   */
  async log(event: Omit<AuditEvent, 'id' | 'timestamp'>): Promise<void> {
    const auditEvent: AuditEvent = {
      id: this.generateEventId(),
      timestamp: new Date(),
      ...event,
    };

    try {
      // Add to buffer
      this.buffer.push(auditEvent);

      // Log to application logger as well
      logger.info('Audit event', { ...auditEvent });

      // Store in Redis for immediate access if available
      if (isRedisAvailable()) {
        await this.storeInRedis(auditEvent);
      }

      // Flush buffer if full
      if (this.buffer.length >= this.maxBufferSize) {
        await this.flushBuffer();
      }

      // Handle critical events immediately
      if (auditEvent.riskLevel === 'critical') {
        await this.handleCriticalEvent(auditEvent);
      }
    } catch (error) {
      logger.error('Failed to log audit event', { error, event: auditEvent });
    }
  }

  /**
   * Log authentication events
   */
  async logAuth(
    action: Extract<AuditAction, `auth.${string}`>,
    details: {
      userId?: string;
      userEmail?: string;
      userIp: string;
      userAgent?: string;
      outcome: 'success' | 'failure' | 'error';
      sessionId?: string;
      details?: Record<string, any>;
    }
  ): Promise<void> {
    await this.log({
      action,
      resource: 'user',
      resourceId: details.userId,
      userId: details.userId,
      userEmail: details.userEmail,
      userIp: details.userIp,
      userAgent: details.userAgent,
      outcome: details.outcome,
      riskLevel: this.getRiskLevel(action, details.outcome),
      sessionId: details.sessionId,
      details: details.details || {},
    });
  }

  /**
   * Log data access events
   */
  async logDataAccess(
    action: Extract<AuditAction, `data.${string}`>,
    details: {
      userId: string;
      userEmail?: string;
      userIp: string;
      resource: AuditResource;
      resourceId?: string;
      outcome: 'success' | 'failure' | 'error';
      details?: Record<string, any>;
    }
  ): Promise<void> {
    await this.log({
      action,
      resource: details.resource,
      resourceId: details.resourceId,
      userId: details.userId,
      userEmail: details.userEmail,
      userIp: details.userIp,
      outcome: details.outcome,
      riskLevel: this.getDataAccessRiskLevel(action),
      details: details.details || {},
    });
  }

  /**
   * Log security events
   */
  async logSecurity(
    action: Extract<AuditAction, `security.${string}`>,
    details: {
      userId?: string;
      userEmail?: string;
      userIp: string;
      userAgent?: string;
      resource?: AuditResource;
      resourceId?: string;
      details: Record<string, any>;
    }
  ): Promise<void> {
    await this.log({
      action,
      resource: details.resource || 'system',
      resourceId: details.resourceId,
      userId: details.userId,
      userEmail: details.userEmail,
      userIp: details.userIp,
      userAgent: details.userAgent,
      outcome: 'error',
      riskLevel: 'critical',
      details: details.details,
    });
  }

  /**
   * Log GDPR compliance events
   */
  async logGDPR(
    action: Extract<AuditAction, `gdpr.${string}`>,
    details: {
      userId: string;
      userEmail?: string;
      userIp: string;
      outcome: 'success' | 'failure' | 'error';
      details: Record<string, any>;
    }
  ): Promise<void> {
    await this.log({
      action,
      resource: 'user',
      resourceId: details.userId,
      userId: details.userId,
      userEmail: details.userEmail,
      userIp: details.userIp,
      outcome: details.outcome,
      riskLevel: 'high', // GDPR events are always high risk
      details: details.details,
    });
  }

  /**
   * Search audit logs
   */
  async search(criteria: {
    userId?: string;
    action?: AuditAction;
    resource?: AuditResource;
    startDate?: Date;
    endDate?: Date;
    riskLevel?: AuditEvent['riskLevel'];
    limit?: number;
    offset?: number;
  }): Promise<{ events: AuditEvent[]; total: number }> {
    try {
      if (!this.supabase) {
        throw new Error('Database not available');
      }

      // Build query
      let query = this.supabase
        .from('audit_logs')
        .select('*', { count: 'exact' })
        .order('timestamp', { ascending: false });

      if (criteria.userId) {
        query = query.eq('user_id', criteria.userId);
      }
      if (criteria.action) {
        query = query.eq('action', criteria.action);
      }
      if (criteria.resource) {
        query = query.eq('resource', criteria.resource);
      }
      if (criteria.startDate) {
        query = query.gte('timestamp', criteria.startDate.toISOString());
      }
      if (criteria.endDate) {
        query = query.lte('timestamp', criteria.endDate.toISOString());
      }
      if (criteria.riskLevel) {
        query = query.eq('risk_level', criteria.riskLevel);
      }

      if (criteria.limit) {
        query = query.limit(criteria.limit);
      }
      if (criteria.offset) {
        query = query.range(
          criteria.offset,
          criteria.offset + (criteria.limit || 50) - 1
        );
      }

      const { data, error, count } = await query;

      if (error) {
        logger.error('Failed to search audit logs', { error, criteria });
        return { events: [], total: 0 };
      }

      // Convert from database format
      const events: AuditEvent[] = data?.map(this.fromDatabaseFormat) || [];

      return { events, total: count || 0 };
    } catch (error) {
      logger.error('Audit log search failed', { error, criteria });
      return { events: [], total: 0 };
    }
  }

  /**
   * Generate compliance report
   */
  async generateComplianceReport(
    startDate: Date,
    endDate: Date
  ): Promise<{
    totalEvents: number;
    eventsByAction: Record<string, number>;
    eventsByResource: Record<string, number>;
    eventsByRiskLevel: Record<string, number>;
    failedEvents: number;
    criticalEvents: AuditEvent[];
  }> {
    try {
      const { events } = await this.search({
        startDate,
        endDate,
        limit: 10000, // Large limit for report
      });

      const eventsByAction: Record<string, number> = {};
      const eventsByResource: Record<string, number> = {};
      const eventsByRiskLevel: Record<string, number> = {};
      const criticalEvents: AuditEvent[] = [];
      let failedEvents = 0;

      for (const event of events) {
        // Count by action
        eventsByAction[event.action] = (eventsByAction[event.action] || 0) + 1;

        // Count by resource
        eventsByResource[event.resource] =
          (eventsByResource[event.resource] || 0) + 1;

        // Count by risk level
        eventsByRiskLevel[event.riskLevel] =
          (eventsByRiskLevel[event.riskLevel] || 0) + 1;

        // Count failed events
        if (event.outcome === 'failure' || event.outcome === 'error') {
          failedEvents++;
        }

        // Collect critical events
        if (event.riskLevel === 'critical') {
          criticalEvents.push(event);
        }
      }

      return {
        totalEvents: events.length,
        eventsByAction,
        eventsByResource,
        eventsByRiskLevel,
        failedEvents,
        criticalEvents,
      };
    } catch (error) {
      logger.error('Failed to generate compliance report', { error });
      throw error;
    }
  }

  /**
   * Store event in Redis for immediate access
   */
  private async storeInRedis(event: AuditEvent): Promise<void> {
    try {
      const key = `audit:${event.id}`;
      await redis.setex(key, 3600, JSON.stringify(event)); // Store for 1 hour

      // Add to user-specific timeline
      if (event.userId) {
        const timelineKey = `audit:user:${event.userId}`;
        await redis.lpush(timelineKey, event.id);
        await redis.ltrim(timelineKey, 0, 99); // Keep last 100 events
        await redis.expire(timelineKey, 86400); // 24 hours
      }
    } catch (error) {
      logger.error('Failed to store audit event in Redis', { error });
    }
  }

  /**
   * Flush buffer to persistent storage
   */
  private async flushBuffer(): Promise<void> {
    if (this.buffer.length === 0) return;

    const events = [...this.buffer];
    this.buffer = [];

    try {
      if (!this.supabase) {
        logger.warn('Cannot flush audit buffer - database not available');
        return;
      }

      // Convert to database format
      const dbEvents = events.map(this.toDatabaseFormat);

      const { error } = await this.supabase.from('audit_logs').insert(dbEvents);

      if (error) {
        logger.error('Failed to flush audit buffer', { error });
        // Add events back to buffer for retry
        this.buffer.unshift(...events);
      }
    } catch (error) {
      logger.error('Audit buffer flush error', { error });
      // Add events back to buffer for retry
      this.buffer.unshift(...events);
    }
  }

  /**
   * Handle critical security events immediately
   */
  private async handleCriticalEvent(event: AuditEvent): Promise<void> {
    try {
      // Log to application logger with high priority
      logger.error('CRITICAL AUDIT EVENT', { ...event });

      // Store immediately in database
      if (this.supabase) {
        const { error } = await this.supabase
          .from('audit_logs')
          .insert([this.toDatabaseFormat(event)]);

        if (error) {
          logger.error('Failed to store critical audit event', { error });
        }
      }

      // TODO: Send alerts to security team
      // TODO: Trigger automated security responses
    } catch (error) {
      logger.error('Failed to handle critical audit event', { error });
    }
  }

  /**
   * Generate unique event ID
   */
  private generateEventId(): string {
    return `audit_${Date.now()}_${Math.random().toString(36).substring(2)}`;
  }

  /**
   * Determine risk level based on action and outcome
   */
  private getRiskLevel(
    action: AuditAction,
    outcome: string
  ): AuditEvent['riskLevel'] {
    // Critical security events
    if (action.startsWith('security.') || action === 'auth.account_locked') {
      return 'critical';
    }

    // High risk events
    if (
      action.startsWith('admin.') ||
      action.startsWith('gdpr.') ||
      action === 'auth.password_reset' ||
      action === 'data.delete'
    ) {
      return 'high';
    }

    // Medium risk events
    if (
      action.startsWith('auth.') ||
      action.startsWith('payment.') ||
      outcome === 'failure'
    ) {
      return 'medium';
    }

    return 'low';
  }

  /**
   * Determine risk level for data access events
   */
  private getDataAccessRiskLevel(
    action: Extract<AuditAction, `data.${string}`>
  ): AuditEvent['riskLevel'] {
    switch (action) {
      case 'data.delete':
      case 'data.export':
        return 'high';
      case 'data.update':
      case 'data.create':
        return 'medium';
      default:
        return 'low';
    }
  }

  /**
   * Convert to database format
   */
  private toDatabaseFormat(event: AuditEvent): any {
    return {
      id: event.id,
      timestamp: event.timestamp.toISOString(),
      user_id: event.userId,
      user_email: event.userEmail,
      user_ip: event.userIp,
      user_agent: event.userAgent,
      action: event.action,
      resource: event.resource,
      resource_id: event.resourceId,
      details: event.details,
      outcome: event.outcome,
      risk_level: event.riskLevel,
      session_id: event.sessionId,
      correlation_id: event.correlationId,
    };
  }

  /**
   * Convert from database format
   */
  private fromDatabaseFormat(data: any): AuditEvent {
    return {
      id: data.id,
      timestamp: new Date(data.timestamp),
      userId: data.user_id,
      userEmail: data.user_email,
      userIp: data.user_ip,
      userAgent: data.user_agent,
      action: data.action,
      resource: data.resource,
      resourceId: data.resource_id,
      details: data.details || {},
      outcome: data.outcome,
      riskLevel: data.risk_level,
      sessionId: data.session_id,
      correlationId: data.correlation_id,
    };
  }
}

