export interface QuickStartOptions {
  userId?: string;
  demoId: string;
  customizations?: {
    name?: string;
    title?: string;
    colors?: {
      primary?: string;
      secondary?: string;
    };
  };
  aiEnhance?: boolean;
}

export class DemoPortfolioService {
  /**
   * Get all available demo portfolios
   */
  static getAvailableDemos(industry?: string): DemoPortfolio[] {
    let demos = [...DEMO_PORTFOLIOS];

    if (industry) {
      demos = demos.filter(
        demo => demo.industry.toLowerCase() === industry.toLowerCase()
      );
    }

    // Sort by popularity
    return demos.sort((a, b) => b.popularity - a.popularity);
  }

  /**
   * Get a specific demo portfolio
   */
  static getDemo(demoId: string): DemoPortfolio | null {
    return DEMO_PORTFOLIOS.find(demo => demo.id === demoId) || null;
  }

  /**
   * Create a portfolio from a demo template
   */
  static async createFromDemo(options: QuickStartOptions): Promise<{
    portfolioId: string;
    portfolio: any;
    isTemporary: boolean;
  }> {
    const demo = this.getDemo(options.demoId);
    if (!demo) {
      throw new Error('Demo portfolio not found');
    }

    // Track demo selection
    await track.portfolio.create('demo-' + uuidv4(), async () => {}, {
      template: demo.template,
      ai_assisted: options.aiEnhance,
    });

    // Create portfolio content from demo
    const portfolioContent = {
      ...demo.sampleData,
      // Apply customizations
      personal: {
        ...demo.sampleData.personal,
        name: options.customizations?.name || demo.sampleData.personal.name,
        title: options.customizations?.title || demo.sampleData.personal.title,
      },
    };

    // Create portfolio settings
    const portfolioSettings = {
      template: demo.template,
      theme: {
        mode: 'light',
        colors: {
          primary:
            TEMPLATE_CONFIGS[demo.template]?.colorScheme?.primary || '#3B82F6',
          secondary:
            TEMPLATE_CONFIGS[demo.template]?.colorScheme?.secondary ||
            '#1E40AF',
          accent:
            TEMPLATE_CONFIGS[demo.template]?.colorScheme?.accent || '#06B6D4',
          background:
            TEMPLATE_CONFIGS[demo.template]?.colorScheme?.background ||
            '#F8FAFC',
          text: TEMPLATE_CONFIGS[demo.template]?.colorScheme?.text || '#1E293B',
          ...(options.customizations?.colors || {}),
        },
        font: {
          heading: 'Inter',
          body: 'Inter',
        },
      },
      seo: {
        title: `${portfolioContent.personal.name} - ${portfolioContent.personal.title}`,
        description: portfolioContent.personal.bio,
        keywords: portfolioContent.skills.technical.concat(
          portfolioContent.skills.soft
        ),
      },
      customization: {
        showProjects: true,
        showExperience: true,
        showEducation: true,
        showSkills: true,
        showTestimonials: false,
        projectsFirst: demo.template === 'developer',
      },
    };

    // If user is logged in, save to database
    if (options.userId) {
      const supabase = createClient();
      if (!supabase) {
        throw new Error('Supabase client not available');
      }

      const { data: portfolio, error } = await supabase
        .from('portfolios')
        .insert({
          user_id: options.userId,
          name: `${demo.name} Portfolio`,
          slug: `demo-${demo.id}-${Date.now()}`,
          content: portfolioContent,
          settings: portfolioSettings,
          is_published: false,
          is_demo: true,
          demo_id: demo.id,
        })
        .select()
        .single();

      if (error) throw error;

      return {
        portfolioId: portfolio.id,
        portfolio,
        isTemporary: false,
      };
    }

    // For non-logged in users, return temporary portfolio
    const temporaryId = `temp-${uuidv4()}`;
    const temporaryPortfolio = {
      id: temporaryId,
      name: `${demo.name} Portfolio`,
      content: portfolioContent,
      settings: portfolioSettings,
      is_published: false,
      is_demo: true,
      demo_id: demo.id,
      created_at: new Date().toISOString(),
    };

    // Store in session storage
    if (typeof window !== 'undefined') {
      sessionStorage.setItem(
        `demo-portfolio-${temporaryId}`,
        JSON.stringify(temporaryPortfolio)
      );
    }

    return {
      portfolioId: temporaryId,
      portfolio: temporaryPortfolio,
      isTemporary: true,
    };
  }

  /**
   * Get recommended demos based on user profile
   */
  static getRecommendedDemos(userProfile?: {
    industry?: string;
    experience?: string;
    goals?: string[];
  }): DemoPortfolio[] {
    let recommendations = [...DEMO_PORTFOLIOS];

    // Filter by industry if provided
    if (userProfile?.industry) {
      recommendations = recommendations.filter(
        demo =>
          demo.industry.toLowerCase() === userProfile.industry?.toLowerCase() ||
          demo.industry === 'General'
      );
    }

    // Sort by relevance
    recommendations.sort((a, b) => {
      let scoreA = a.popularity;
      let scoreB = b.popularity;

      // Boost score for matching industry
      if (userProfile?.industry) {
        if (a.industry === userProfile.industry) scoreA += 10;
        if (b.industry === userProfile.industry) scoreB += 10;
      }

      // Boost score based on experience level
      if (userProfile?.experience === 'senior' && a.template === 'business') {
        scoreA += 5;
      }
      if (userProfile?.experience === 'junior' && a.template === 'minimal') {
        scoreA += 5;
      }

      return scoreB - scoreA;
    });

    return recommendations.slice(0, 3);
  }

  /**
   * Clone a demo portfolio for customization
   */
  static async cloneDemo(
    demoId: string,
    userId: string,
    customName?: string
  ): Promise<string> {
    const demo = this.getDemo(demoId);
    if (!demo) {
      throw new Error('Demo portfolio not found');
    }

    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }

    // Check if user already has this demo
    const { data: existing } = await supabase
      .from('portfolios')
      .select('id')
      .eq('user_id', userId)
      .eq('demo_id', demoId)
      .single();

    if (existing) {
      return existing.id;
    }

    // Create new portfolio from demo
    const result = await this.createFromDemo({
      userId,
      demoId,
      customizations: {
        name: customName,
      },
    });

    return result.portfolioId;
  }

  /**
   * Get user's demo portfolios
   */
  static async getUserDemoPortfolios(userId: string): Promise<any[]> {
    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }

    const { data, error } = await supabase
      .from('portfolios')
      .select('*')
      .eq('user_id', userId)
      .eq('is_demo', true)
      .order('created_at', { ascending: false });

    if (error) throw error;

    return data || [];
  }

  /**
   * Convert temporary demo to permanent
   */
  static async convertToPermanent(
    temporaryId: string,
    userId: string
  ): Promise<string> {
    // Get temporary portfolio from session
    const stored = sessionStorage.getItem(`demo-portfolio-${temporaryId}`);
    if (!stored) {
      throw new Error('Temporary portfolio not found');
    }

    const temporaryPortfolio = JSON.parse(stored);
    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }

    // Save to database
    const { data: portfolio, error } = await supabase
      .from('portfolios')
      .insert({
        user_id: userId,
        name: temporaryPortfolio.name,
        slug: `portfolio-${Date.now()}`,
        content: temporaryPortfolio.content,
        settings: temporaryPortfolio.settings,
        is_published: false,
        is_demo: true,
        demo_id: temporaryPortfolio.demo_id,
      })
      .select()
      .single();

    if (error) throw error;

    // Clean up session storage
    sessionStorage.removeItem(`demo-portfolio-${temporaryId}`);

    // Track conversion
    await track.user.action(
      'demo_portfolio_converted',
      userId,
      async () => {},
      {
        demo_id: temporaryPortfolio.demo_id,
        temporary_id: temporaryId,
        permanent_id: portfolio.id,
      }
    );

    return portfolio.id;
  }
}

import { createClient } from '@/lib/supabase/client';

interface DetailedDomainAnalytics {
  totalViews: number;
  uniqueVisitors: number;
  uniqueSessions: number;
  averageSessionDuration: number;
  bounceRate: number;
  popularPages: Array<{ path: string; views: number }>;
  topReferrers: Array<{ referrer: string; count: number }>;
  dailyViews: Array<{ date: string; views: number }>;
  deviceTypes: Record<string, number>;
  countries: Array<{ country: string; count: number }>;
}

export class DomainAnalyticsService {
  /**
   * Track a page view for a custom domain
   */
  static async trackPageView(
    domainId: string,
    path: string,
    referrer?: string,
    userAgent?: string
  ): Promise<void> {
    try {
      const supabase = createClient();
      if (!supabase) {
        throw new Error('Supabase client not available');
      }
      await supabase.from('domain_analytics').insert({
        domain_id: domainId,
        event_type: 'page_view',
        path,
        referrer,
        user_agent: userAgent,
        visitor_id: this.generateVisitorId(),
        session_id: this.generateSessionId(),
      });
    } catch (error) {
      console.error('Failed to track page view:', error);
    }
  }

  /**
   * Get analytics for a domain
   */
  static async getDomainAnalytics(
    domainId: string,
    dateRange: { from: Date; to: Date }
  ): Promise<DetailedDomainAnalytics> {
    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }
    const { data: events, error } = await supabase
      .from('domain_analytics')
      .select('*')
      .eq('domain_id', domainId)
      .gte('created_at', dateRange.from.toISOString())
      .lte('created_at', dateRange.to.toISOString());

    if (error) {
      throw error;
    }

    const pageViews = events?.filter(e => e.event_type === 'page_view') || [];
    const uniqueVisitors = new Set(pageViews.map(e => e.visitor_id)).size;
    const uniqueSessions = new Set(pageViews.map(e => e.session_id)).size;

    // Calculate page popularity
    const pageCounts = pageViews.reduce(
      (acc, event) => {
        acc[event.path] = (acc[event.path] || 0) + 1;
        return acc;
      },
      {} as Record<string, number>
    );

    const popularPages = Object.entries(pageCounts)
      .sort(([, a], [, b]) => (b as number) - (a as number))
      .slice(0, 10)
      .map(([path, views]) => ({ path, views: views as number }));

    // Calculate referrer stats
    const referrerCounts = pageViews
      .filter(e => e.referrer)
      .reduce(
        (acc, event) => {
          const referrerDomain = this.extractDomain(event.referrer);
          acc[referrerDomain] = (acc[referrerDomain] || 0) + 1;
          return acc;
        },
        {} as Record<string, number>
      );

    const topReferrers = Object.entries(referrerCounts)
      .sort(([, a], [, b]) => (b as number) - (a as number))
      .slice(0, 10)
      .map(([referrer, count]) => ({ referrer, count: count as number }));

    // Calculate daily views
    const dailyViews = this.calculateDailyViews(pageViews, dateRange);

    return {
      totalViews: pageViews.length,
      uniqueVisitors,
      uniqueSessions,
      averageSessionDuration: this.calculateAverageSessionDuration(
        events || []
      ),
      bounceRate: this.calculateBounceRate(events || []),
      popularPages,
      topReferrers,
      dailyViews,
      deviceTypes: this.calculateDeviceTypes(pageViews),
      countries: [], // Would need IP geolocation service
    };
  }

  /**
   * Get aggregated analytics for multiple domains
   */
  static async getAggregatedAnalytics(
    domainIds: string[],
    dateRange: { from: Date; to: Date }
  ): Promise<{
    totalViews: number;
    totalVisitors: number;
    domainPerformance: Array<{
      domainId: string;
      views: number;
      visitors: number;
    }>;
  }> {
    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }
    const { data: events, error } = await supabase
      .from('domain_analytics')
      .select('*')
      .in('domain_id', domainIds)
      .eq('event_type', 'page_view')
      .gte('created_at', dateRange.from.toISOString())
      .lte('created_at', dateRange.to.toISOString());

    if (error) {
      throw error;
    }

    const allVisitors = new Set(events?.map(e => e.visitor_id) || []);

    const domainPerformance = domainIds.map(domainId => {
      const domainEvents = events?.filter(e => e.domain_id === domainId) || [];
      const domainVisitors = new Set(domainEvents.map(e => e.visitor_id));

      return {
        domainId,
        views: domainEvents.length,
        visitors: domainVisitors.size,
      };
    });

    return {
      totalViews: events?.length || 0,
      totalVisitors: allVisitors.size,
      domainPerformance,
    };
  }

  /**
   * Record SSL certificate events
   */
  static async recordSSLEvent(
    domainId: string,
    eventType: 'provisioned' | 'renewed' | 'expired' | 'failed',
    details?: any
  ): Promise<void> {
    try {
      const supabase = createClient();
      if (!supabase) {
        throw new Error('Supabase client not available');
      }
      await supabase.from('domain_analytics').insert({
        domain_id: domainId,
        event_type: `ssl_${eventType}`,
        metadata: details,
      });
    } catch (error) {
      console.error('Failed to record SSL event:', error);
    }
  }

  /**
   * Helper methods
   */
  private static generateVisitorId(): string {
    // In production, this would use a more sophisticated method
    // like fingerprinting or cookie-based tracking
    return `visitor_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private static generateSessionId(): string {
    // In production, this would track actual user sessions
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private static extractDomain(url: string): string {
    try {
      const urlObj = new URL(url);
      return urlObj.hostname;
    } catch {
      return 'direct';
    }
  }

  private static calculateDailyViews(
    events: any[],
    dateRange: { from: Date; to: Date }
  ): Array<{ date: string; views: number }> {
    const dailyCounts: Record<string, number> = {};

    events.forEach(event => {
      if (event.created_at) {
        const date = new Date(event.created_at).toISOString().split('T')[0];
        if (date) {
          dailyCounts[date] = (dailyCounts[date] || 0) + 1;
        }
      }
    });

    // Fill in missing days with 0
    const days: Array<{ date: string; views: number }> = [];
    const current = new Date(dateRange.from);

    while (current <= dateRange.to) {
      const dateStr = current.toISOString().split('T')[0]!;
      days.push({
        date: dateStr,
        views: dailyCounts[dateStr] || 0,
      });
      current.setDate(current.getDate() + 1);
    }

    return days;
  }

  private static calculateAverageSessionDuration(events: any[]): number {
    // Simplified calculation - in production would track actual session times
    const sessions = new Map<string, { start: Date; end: Date }>();

    events.forEach(event => {
      const sessionId = event.session_id;
      const eventTime = new Date(event.created_at);

      if (!sessions.has(sessionId)) {
        sessions.set(sessionId, { start: eventTime, end: eventTime });
      } else {
        const session = sessions.get(sessionId)!;
        if (eventTime < session.start) session.start = eventTime;
        if (eventTime > session.end) session.end = eventTime;
      }
    });

    let totalDuration = 0;
    sessions.forEach(session => {
      totalDuration += session.end.getTime() - session.start.getTime();
    });

    return sessions.size > 0 ? totalDuration / sessions.size / 1000 : 0; // in seconds
  }

  private static calculateBounceRate(events: any[]): number {
    // Count sessions with only one page view
    const sessionPageCounts = new Map<string, number>();

    events
      .filter(e => e.event_type === 'page_view')
      .forEach(event => {
        const count = sessionPageCounts.get(event.session_id) || 0;
        sessionPageCounts.set(event.session_id, count + 1);
      });

    const bouncedSessions = Array.from(sessionPageCounts.values()).filter(
      count => count === 1
    ).length;

    return sessionPageCounts.size > 0
      ? (bouncedSessions / sessionPageCounts.size) * 100
      : 0;
  }

  private static calculateDeviceTypes(events: any[]): Record<string, number> {
    const deviceCounts: Record<string, number> = {
      desktop: 0,
      mobile: 0,
      tablet: 0,
    };

    events.forEach(event => {
      if (!event.user_agent) return;

      const ua = event.user_agent.toLowerCase();
      if (/mobile|android|iphone/i.test(ua) && !/ipad/i.test(ua)) {
        deviceCounts.mobile = (deviceCounts.mobile || 0) + 1;
      } else if (/ipad|tablet/i.test(ua)) {
        deviceCounts.tablet = (deviceCounts.tablet || 0) + 1;
      } else {
        deviceCounts.desktop = (deviceCounts.desktop || 0) + 1;
      }
    });

    return deviceCounts;
  }
}

/**
 * Custom Domain Service
 *
 * Handles custom domain configuration, verification, and management
 */

import { createClient } from '@/lib/supabase/client';
import { track } from '@/lib/monitoring/unified/events';
import { logger } from '@/lib/utils/logger';
import type {
  CustomDomain,
  DomainCheckResult,
  DomainVerificationResult,
  DNSRecord,
  DomainSetupInstructions,
  SSLCertificateInfo,
  DomainRedirect,
} from '@/types/domains';

// DNS providers we support with specific instructions
const DNS_PROVIDERS = {
  cloudflare: 'Cloudflare',
  namecheap: 'Namecheap',
  godaddy: 'GoDaddy',
  route53: 'AWS Route 53',
  googledomains: 'Google Domains',
  generic: 'Generic DNS Provider',
};

export class DomainService {
  /**
   * Check if a domain is available for use
   */
  static async checkDomainAvailability(
    domain: string
  ): Promise<DomainCheckResult> {
    // Validate domain format
    const domainRegex =
      /^[a-zA-Z0-9][a-zA-Z0-9-]{0,61}[a-zA-Z0-9]?\.[a-zA-Z]{2,}$/;
    if (!domainRegex.test(domain)) {
      return {
        isAvailable: false,
        reason: 'invalid',
      };
    }

    // Check if domain is in our blacklist
    const blacklistedDomains = [
      'prisma.madfam.io',
      'app.prisma.madfam.io',
      'api.prisma.madfam.io',
      'localhost',
      '127.0.0.1',
    ];

    if (blacklistedDomains.some(blocked => domain.includes(blocked))) {
      return {
        isAvailable: false,
        reason: 'blacklisted',
      };
    }

    // Check if domain is already taken
    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }

    const { data } = await supabase.rpc('check_domain_availability', {
      p_domain: domain,
    });

    if (!data) {
      // Generate suggestions if domain is taken
      const suggestions = this.generateDomainSuggestions(domain);
      return {
        isAvailable: false,
        reason: 'taken',
        suggestions,
      };
    }

    return { isAvailable: true };
  }

  /**
   * Add a custom domain to a portfolio
   */
  static async addCustomDomain(
    userId: string,
    portfolioId: string,
    domain: string
  ): Promise<CustomDomain> {
    // Check availability first
    const availability = await this.checkDomainAvailability(domain);
    if (!availability.isAvailable) {
      throw new Error(`Domain is not available: ${availability.reason}`);
    }

    const supabase = createClient();
    if (!supabase) {
      throw new Error('Supabase client not available');
    }

    // Generate verification token
    const verificationToken = this.generateVerificationToken();

    // Create domain record
    const { data, error } = await supabase
      .from('custom_domains')
      .insert({
        user_id: userId,
        portfolio_id: portfolioId,
        domain,
        verification_token: verificationToken,
        dns_txt_record: `prisma-verify=${verificationToken}`,
        dns_cname_record: 'portfolios.prisma.madfam.io',
      })
      .select()
      .single();

    if (error) throw error;

    // Track domain addition
    await track.domain.add({
      user_id: userId,
      portfolio_id: portfolioId,
      domain,
    });

    return this.transformDomain(data);
  }

