  private async incrementCampaignSendCount(
    userId: string,
    campaignId: string
  ): Promise<void> {
    if (!isRedisAvailable()) return;

    await redis.incr(`retention:count:${userId}:${campaignId}`);
    await redis.expire(
      `retention:count:${userId}:${campaignId}`,
      90 * 24 * 60 * 60
    ); // 90 days
  }
}

