/**
 * Migrate existing data to encrypted format
 */
export const migrateTableToEncryption = async (
  tableName: string,
  batchSize: number = 100
): Promise<{ success: boolean; error?: string }> => {
  const supabase = await createClient();
  if (!supabase) {
    throw new Error('Supabase client not available');
  }

  try {
    // Update migration status
    await supabase
      .from('encryption_migration_status')
      .update({ status: 'in_progress' })
      .eq('table_name', tableName);

    let offset = 0;
    let hasMore = true;
    let totalEncrypted = 0;

    while (hasMore) {
      // Fetch batch
      const { data: records, error: fetchError } = await supabase
        .from(tableName)
        .select('*')
        .range(offset, offset + batchSize - 1);

      if (fetchError) throw fetchError;

      if (!records || records.length === 0) {
        hasMore = false;
        break;
      }

      // Encrypt records based on table
      const encryptedRecords = records.map(record => {
        switch (tableName) {
          case 'users':
            return encryptUserData(record);
          case 'linkedin_connections':
          case 'github_integrations':
            return encryptOAuthTokens(record);
          case 'portfolios':
            return encryptPortfolioContact(record);
          default:
            return record;
        }
      });

      // Update records
      for (const record of encryptedRecords) {
        const { error: updateError } = await supabase
          .from(tableName)
          .update(record)
          .eq('id', record.id);

        if (updateError) {
          logger.error('Failed to encrypt record', {
            tableName,
            recordId: record.id,
            error: updateError,
          });
        } else {
          totalEncrypted++;
        }
      }

      // Update progress
      await supabase
        .from('encryption_migration_status')
        .update({ encrypted_records: totalEncrypted })
        .eq('table_name', tableName);

      offset += batchSize;
    }

    // Mark as completed
    await supabase
      .from('encryption_migration_status')
      .update({
        status: 'completed',
        migration_completed_at: new Date().toISOString(),
        encrypted_records: totalEncrypted,
      })
      .eq('table_name', tableName);

    logger.info('Encryption migration completed', {
      tableName,
      totalEncrypted,
    });

    return { success: true };
  } catch (error) {
    const errorMessage =
      error instanceof Error ? error.message : 'Unknown error';

    await supabase
      .from('encryption_migration_status')
      .update({
        status: 'failed',
        error_message: errorMessage,
      })
      .eq('table_name', tableName);

    logger.error('Encryption migration failed', {
      tableName,
      error,
    });

    return { success: false, error: errorMessage };
  }
};

