/**
 * API Error Handler
 * Provides standardized error handling for API routes
 */

import { NextRequest, NextResponse } from 'next/server';
import { AppError, isAppError, getErrorMessage } from '@/types/errors';
import { errorLogger, ErrorContext } from './error-logger';

export interface ApiErrorResponse {
  error: {
    message: string;
    code: string;
    statusCode: number;
    requestId?: string;
    details?: Record<string, unknown>;
  };
}

/**
 * Generate a unique request ID
 */
function generateRequestId(): string {
  return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * Extract error context from request
 */
function extractErrorContext(req: NextRequest): ErrorContext {
  const requestId = req.headers.get('x-request-id') || generateRequestId();

  return {
    requestId,
    url: req.url,
    method: req.method,
    metadata: {
      userAgent: req.headers.get('user-agent') || undefined,
      referer: req.headers.get('referer') || undefined,
      ip:
        req.headers.get('x-forwarded-for') ||
        req.headers.get('x-real-ip') ||
        undefined,
    },
  };
}

/**
 * Handle API errors and return standardized error response
 */
export function handleApiError(
  error: unknown,
  req?: NextRequest,
  additionalContext?: Partial<ErrorContext>
): NextResponse<ApiErrorResponse> {
  const context: ErrorContext = {
    ...(req ? extractErrorContext(req) : {}),
    ...additionalContext,
  };

  // Log the error
  errorLogger.logError(error, context);

  // Prepare error response
  let statusCode = 500;
  let code = 'INTERNAL_SERVER_ERROR';
  let message = 'An unexpected error occurred';
  let details: Record<string, unknown> | undefined;

  if (isAppError(error)) {
    statusCode = error.statusCode;
    code = error.code;
    message = error.message;
    details = error.details;
  } else if (error instanceof Error) {
    // For non-AppError instances, use generic message in production
    message =
      process.env.NODE_ENV === 'development'
        ? error.message
        : 'An unexpected error occurred';
  }

  const response: ApiErrorResponse = {
    error: {
      message,
      code,
      statusCode,
      requestId: context.requestId,
      ...(process.env.NODE_ENV === 'development' && details ? { details } : {}),
    },
  };

  return NextResponse.json(response, { status: statusCode });
}

/**
 * Async error wrapper for API route handlers
 */
export function withErrorHandler<T extends any[], R>(
  handler: (...args: T) => Promise<R>
) {
  return async (...args: T): Promise<R | NextResponse<ApiErrorResponse>> => {
    try {
      return await handler(...args);
    } catch (error) {
      // Try to extract request from args
      const req = args.find(arg => arg instanceof NextRequest) as
        | NextRequest
        | undefined;
      return handleApiError(error, req) as R;
    }
  };
}

/**
 * Type-safe API route handler wrapper
 */
export function createApiHandler<TParams = any, TResponse = any>(
  handler: (
    req: NextRequest,
    context: { params: TParams }
  ) => Promise<NextResponse<TResponse>>
) {
  return withErrorHandler(handler);
}

/**
 * Validate request method
 */
export function validateMethod(
  req: NextRequest,
  allowedMethods: string[]
): void {
  if (!allowedMethods.includes(req.method)) {
    throw new AppError(
      `Method ${req.method} not allowed`,
      'METHOD_NOT_ALLOWED',
      405,
      { allowed: allowedMethods }
    );
  }
}

/**
 * Parse JSON body with error handling
 */
export async function parseJsonBody<T = any>(req: NextRequest): Promise<T> {
  try {
    const body = await req.json();
    return body as T;
  } catch (error) {
    throw new AppError('Invalid JSON in request body', 'INVALID_JSON', 400, {
      originalError: getErrorMessage(error),
    });
  }
}

/**
 * Centralized error logging service
 * Handles error logging with proper context and formatting
 */

import { isAppError, getErrorMessage, getErrorCode } from '@/types/errors';

export interface ErrorContext {
  userId?: string;
  sessionId?: string;
  requestId?: string;
  url?: string;
  method?: string;
  component?: string;
  action?: string;
  metadata?: Record<string, unknown>;
}

export interface ErrorLogEntry {
  timestamp: string;
  level: 'error' | 'warn' | 'info';
  message: string;
  code: string;
  statusCode?: number;
  stack?: string;
  context?: ErrorContext;
  details?: Record<string, unknown>;
}

export class ErrorLogger {
  private static instance: ErrorLogger;
  private isDevelopment = process.env.NODE_ENV === 'development';
  private isTest = process.env.NODE_ENV === 'test';

  private constructor() {}

  static getInstance(): ErrorLogger {
    if (!ErrorLogger.instance) {
      ErrorLogger.instance = new ErrorLogger();
    }
    return ErrorLogger.instance;
  }

  /**
   * Log an error with full context
   */
  logError(error: unknown, context?: ErrorContext): void {
    const entry = this.createErrorLogEntry(error, 'error', context);
    this.writeLog(entry);
  }

  /**
   * Log a warning
   */
  logWarning(message: string, context?: ErrorContext): void {
    const entry: ErrorLogEntry = {
      timestamp: new Date().toISOString(),
      level: 'warn',
      message,
      code: 'WARNING',
      context,
    };
    this.writeLog(entry);
  }

  /**
   * Log info message
   */
  logInfo(message: string, context?: ErrorContext): void {
    const entry: ErrorLogEntry = {
      timestamp: new Date().toISOString(),
      level: 'info',
      message,
      code: 'INFO',
      context,
    };
    this.writeLog(entry);
  }

  /**
   * Create a structured error log entry
   */
  private createErrorLogEntry(
    error: unknown,
    level: 'error' | 'warn' | 'info',
    context?: ErrorContext
  ): ErrorLogEntry {
    const entry: ErrorLogEntry = {
      timestamp: new Date().toISOString(),
      level,
      message: getErrorMessage(error),
      code: getErrorCode(error),
      context,
    };

    if (isAppError(error)) {
      entry.statusCode = error.statusCode;
      entry.details = error.details;
    }

    if (error instanceof Error) {
      entry.stack = error.stack;
    }

    return entry;
  }

  /**
   * Write log entry to appropriate destination
   */
  private writeLog(entry: ErrorLogEntry): void {
    // In test environment, suppress logs unless explicitly enabled
    if (this.isTest && !process.env.ENABLE_TEST_LOGS) {
      return;
    }

    // Format the log entry
    const formattedLog = this.formatLogEntry(entry);

    // In development, use console with color coding
    if (this.isDevelopment) {
      this.writeToConsole(entry, formattedLog);
    } else {
      // In production, write structured JSON logs
      // console.error(JSON.stringify(entry));
    }

    // TODO: In production, send to external logging service (e.g., Sentry, LogRocket)
    // this.sendToExternalService(entry);
  }

  /**
   * Format log entry for readable output
   */
  private formatLogEntry(entry: ErrorLogEntry): string {
    const parts = [
      `[${entry.timestamp}]`,
      `[${entry.level.toUpperCase()}]`,
      `[${entry.code}]`,
      entry.message,
    ];

    if (entry.context?.component) {
      parts.push(`Component: ${entry.context.component}`);
    }

    if (entry.context?.action) {
      parts.push(`Action: ${entry.context.action}`);
    }

    if (entry.context?.url) {
      parts.push(`URL: ${entry.context.url}`);
    }

    return parts.join(' | ');
  }

  /**
   * Write formatted log to console with color coding
   */
  private writeToConsole(entry: ErrorLogEntry, formattedLog: string): void {
    const colorMap = {
      error: '\x1b[31m', // Red
      warn: '\x1b[33m', // Yellow
      info: '\x1b[36m', // Cyan
    };
    const reset = '\x1b[0m';
    const color = colorMap[entry.level];

    console.error(`${color}${formattedLog}${reset}`);

    if (entry.stack && this.isDevelopment) {
      console.error(`${color}Stack trace:${reset}`);
      console.error(entry.stack);
    }

    if (entry.details && Object.keys(entry.details).length > 0) {
      console.error(`${color}Details:${reset}`, entry.details);
    }

    if (
      entry.context?.metadata &&
      Object.keys(entry.context.metadata).length > 0
    ) {
      console.error(`${color}Metadata:${reset}`, entry.context.metadata);
    }
  }

  /**
   * Send error to external monitoring service
   */
  public sendToExternalService(entry: ErrorLogEntry): void {
    // TODO: Implement integration with error monitoring service
    // Example: Sentry, LogRocket, Datadog, etc.
    // This would be configured based on environment variables
    console.debug('Would send error to external service:', entry.timestamp);
  }
}

// Export singleton instance
export const errorLogger = ErrorLogger.getInstance();

/**
 * Retry Handler
 * Implements retry logic with exponential backoff for transient failures
 */

import { AppError, ExternalServiceError } from '@/types/errors';
import { errorLogger } from './error-logger';

export interface RetryOptions {
  maxAttempts?: number;
  initialDelay?: number;
  maxDelay?: number;
  backoffMultiplier?: number;
  retryCondition?: (error: unknown) => boolean;
  onRetry?: (error: unknown, attempt: number) => void;
}

const DEFAULT_RETRY_OPTIONS: Required<RetryOptions> = {
  maxAttempts: 3,
  initialDelay: 1000,
  maxDelay: 10000,
  backoffMultiplier: 2,
  retryCondition: error => {
    // Retry on network errors and 5xx status codes
    if (error instanceof ExternalServiceError) {
      return true;
    }
    if (error instanceof AppError) {
      return error.statusCode >= 500 && error.statusCode < 600;
    }
    if (error instanceof Error) {
      return (
        error.message.includes('network') ||
        error.message.includes('timeout') ||
        error.message.includes('ECONNREFUSED')
      );
    }
    return false;
  },
  onRetry: () => {},
};

/**
 * Retry a function with exponential backoff
 */
export async function retry<T>(
  fn: () => Promise<T>,
  options: RetryOptions = {}
): Promise<T> {
  const opts = { ...DEFAULT_RETRY_OPTIONS, ...options };
  let lastError: unknown;

  for (let attempt = 1; attempt <= opts.maxAttempts; attempt++) {
    try {
      return await fn();
    } catch (error) {
      lastError = error;

      // Check if we should retry
      if (attempt === opts.maxAttempts || !opts.retryCondition(error)) {
        throw error;
      }

      // Calculate delay with exponential backoff
      const delay = Math.min(
        opts.initialDelay * Math.pow(opts.backoffMultiplier, attempt - 1),
        opts.maxDelay
      );

      // Log retry attempt
      errorLogger.logWarning(`Retry attempt ${attempt}/${opts.maxAttempts}`, {
        action: 'retry',
        metadata: {
          attempt,
          delay,
          error: error instanceof Error ? error.message : String(error),
        },
      });

      // Call retry callback
      opts.onRetry(error, attempt);

      // Wait before retrying
      await sleep(delay);
    }
  }

  throw lastError;
}

/**
 * Retry with circuit breaker pattern
 */
export class CircuitBreaker {
  private failureCount = 0;
  private lastFailureTime = 0;
  private state: 'closed' | 'open' | 'half-open' = 'closed';

  constructor(
    private readonly threshold: number = 5,
    public readonly timeout: number = 60000, // 1 minute
    private readonly retryTimeout: number = 30000 // 30 seconds
  ) {}

  async execute<T>(
    fn: () => Promise<T>,
    fallback?: () => T | Promise<T>
  ): Promise<T> {
    // Check if circuit is open
    if (this.state === 'open') {
      const now = Date.now();
      if (now - this.lastFailureTime > this.retryTimeout) {
        this.state = 'half-open';
      } else if (fallback) {
        return fallback();
      } else {
        throw new AppError(
          'Service temporarily unavailable',
          'CIRCUIT_BREAKER_OPEN',
          503
        );
      }
    }

    try {
      const result = await fn();

      // Reset on success
      if (this.state === 'half-open') {
        this.reset();
      }

      return result;
    } catch (error) {
      this.recordFailure();

      if (this.failureCount >= this.threshold) {
        this.trip();
      }

      // Use fallback if available when circuit trips to open state
      if (fallback && this.failureCount >= this.threshold) {
        return fallback();
      }

      throw error;
    }
  }

  private recordFailure(): void {
    this.failureCount++;
    this.lastFailureTime = Date.now();
  }

  private trip(): void {
    this.state = 'open';
    errorLogger.logWarning('Circuit breaker tripped', {
      component: 'CircuitBreaker',
      metadata: {
        failureCount: this.failureCount,
        threshold: this.threshold,
      },
    });
  }

  private reset(): void {
    this.failureCount = 0;
    this.lastFailureTime = 0;
    this.state = 'closed';
    errorLogger.logInfo('Circuit breaker reset');
  }

  getState(): 'closed' | 'open' | 'half-open' {
    return this.state;
  }
}

/**
 * Debounced retry for user-triggered actions
 */
export function createDebouncedRetry<
  T extends (...args: any[]) => Promise<any>,
>(fn: T, delay: number = 1000): T {
  let timeoutId: NodeJS.Timeout | null = null;
  let lastArgs: any[] | null = null;

  return ((...args: Parameters<T>) => {
    lastArgs = args;

    if (timeoutId) {
      clearTimeout(timeoutId);
    }

    return new Promise((resolve, reject) => {
      timeoutId = setTimeout(async () => {
        try {
          const result = await fn(...(lastArgs as Parameters<T>));
          resolve(result);
        } catch (error) {
          reject(error);
        } finally {
          timeoutId = null;
          lastArgs = null;
        }
      }, delay);
    });
  }) as T;
}

/**
 * Utility sleep function
 */
function sleep(ms: number): Promise<void> {
  return new Promise(resolve => setTimeout(resolve, ms));
}

/**
 * Timeout wrapper for promises
 */
export function withTimeout<T>(
  promise: Promise<T>,
  timeoutMs: number,
  timeoutError?: Error
): Promise<T> {
  const timeout = new Promise<never>((_, reject) => {
    setTimeout(() => {
      reject(
        timeoutError ||
          new AppError(
            `Operation timed out after ${timeoutMs}ms`,
            'TIMEOUT',
            408
          )
      );
    }, timeoutMs);
  });

  return Promise.race([promise, timeout]);
}

import crypto from 'crypto';

import { cookies } from 'next/headers';

import { createClient } from '@/lib/supabase/server';
import { logger } from '@/lib/utils/logger';

import type {
  GetActiveExperimentResponse,
  VisitorAssignment,
  TargetAudience,
} from '@/types/experiments';

/**
 * @fileoverview Feature Flag Service for A/B Testing
 *
 * Handles experiment assignment, variant selection, and traffic splitting
 * for landing page A/B tests. Uses cookies for persistent assignment.
 */

/**
 * Cookie name for storing experiment assignments
 */
const EXPERIMENT_COOKIE_NAME = 'prisma_experiments';
const VISITOR_ID_COOKIE_NAME = 'prisma_visitor_id';

/**
 * Cookie expiration time (90 days)
 */
const COOKIE_MAX_AGE = 90 * 24 * 60 * 60; // 90 days in seconds

/**
 * Feature flag service for managing A/B test assignments
 */
export class FeatureFlagService {
  /**
   * Get or create a visitor ID
   */
  private static async getOrCreateVisitorId(): Promise<string> {
    const cookieStore = await cookies();
    let visitorId = cookieStore.get(VISITOR_ID_COOKIE_NAME)?.value;

    if (!visitorId) {
      visitorId = crypto.randomUUID();
      cookieStore.set(VISITOR_ID_COOKIE_NAME, visitorId, {
        httpOnly: true,
        secure: process.env.NODE_ENV === 'production',
        sameSite: 'lax',
        maxAge: COOKIE_MAX_AGE,
        path: '/',
      });
    }

    return visitorId;
  }

  /**
   * Get stored experiment assignments from cookies
   */
  private static async getStoredAssignments(): Promise<
    Record<string, VisitorAssignment>
  > {
    const cookieStore = await cookies();
    const assignmentsCookie = cookieStore.get(EXPERIMENT_COOKIE_NAME)?.value;

    if (!assignmentsCookie) {
      return {};
    }

    try {
      return JSON.parse(decodeURIComponent(assignmentsCookie));
    } catch (error) {
      logger.error(
        'Failed to parse experiment assignments cookie',
        error as Error
      );
      return {};
    }
  }

  /**
   * Store experiment assignment in cookies
   */
  private static async storeAssignment(
    experimentId: string,
    variantId: string
  ): Promise<void> {
    const cookieStore = await cookies();
    const assignments = await this.getStoredAssignments();

    assignments[experimentId] = {
      experimentId,
      variantId,
      assignedAt: new Date(),
    };

    cookieStore.set(
      EXPERIMENT_COOKIE_NAME,
      encodeURIComponent(JSON.stringify(assignments)),
      {
        httpOnly: true,
        secure: process.env.NODE_ENV === 'production',
        sameSite: 'lax',
        maxAge: COOKIE_MAX_AGE,
        path: '/',
      }
    );
  }

  /**
   * Check if visitor matches targeting criteria
   */
  private static matchesTargeting(
    targetAudience: TargetAudience,
    visitorContext: {
      country?: string;
      device?: string;
      language?: string;
      referrer?: string;
      utmSource?: string;
    }
  ): boolean {
    // If no targeting specified, include all visitors
    if (!targetAudience || Object.keys(targetAudience).length === 0) {
      return true;
    }

    // Check geo targeting
    if (targetAudience.geo && visitorContext.country) {
      if (!targetAudience.geo.includes(visitorContext.country)) {
        return false;
      }
    }

    // Check device targeting
    if (targetAudience.device && visitorContext.device) {
      if (!targetAudience.device.includes(visitorContext.device)) {
        return false;
      }
    }

    // Check language targeting
    if (targetAudience.language && visitorContext.language) {
      if (!targetAudience.language.includes(visitorContext.language)) {
        return false;
      }
    }

    // Check referrer targeting
    if (targetAudience.referrer && visitorContext.referrer) {
      const referrerMatch = targetAudience.referrer.some(ref =>
        visitorContext.referrer?.includes(ref)
      );
      if (!referrerMatch) {
        return false;
      }
    }

    // Check UTM source targeting
    if (targetAudience.utm_source && visitorContext.utmSource) {
      if (!targetAudience.utm_source.includes(visitorContext.utmSource)) {
        return false;
      }
    }

    return true;
  }

  /**
   * Hash a string to a number between 0 and 99
   * Used for consistent traffic splitting
   */
  private static hashToPercentage(str: string): number {
    const hash = crypto.createHash('md5').update(str).digest('hex');
    const num = parseInt(hash.substring(0, 8), 16);
    return num % 100;
  }

  /**
   * Process a single experiment for visitor
   */
  private static processExperiment(data: {
    experiment: any;
    visitorId: string;
    visitorContext: any;
    storedAssignments: any;
    supabase: any;
  }): Promise<GetActiveExperimentResponse | null> {
    const {
      experiment,
      visitorId,
      visitorContext,
      storedAssignments,
      supabase,
    } = data;
    // Check if visitor is already assigned to this experiment
    const existingAssignment = storedAssignments[experiment.id];
    if (existingAssignment) {
      const result = this.processExistingAssignment(
        experiment,
        existingAssignment
      );
      if (result) return Promise.resolve(result);
    }

    // Check targeting criteria
    if (
      !this.matchesTargeting(experiment.target_audience, visitorContext || {})
    ) {
      return Promise.resolve(null);
    }

    // Check traffic percentage for experiment
    const experimentHash = this.hashToPercentage(
      `${visitorId}-${experiment.id}`
    );
    if (experimentHash >= experiment.traffic_percentage) {
      return Promise.resolve(null);
    }

    // Assign variant based on traffic allocation
    const variantHash = this.hashToPercentage(
      `${visitorId}-${experiment.id}-variant`
    );
    let cumulativePercentage = 0;

    for (const variant of experiment.variants) {
      cumulativePercentage += variant.traffic_percentage;

      if (variantHash < cumulativePercentage) {
        return this.assignAndRecordVariant({
          experiment,
          variant,
          visitorId,
          visitorContext,
          supabase,
        });
      }
    }

    return Promise.resolve(null);
  }

  /**
   * Process existing variant assignment
   */
  private static processExistingAssignment(
    experiment: any,
    existingAssignment: any
  ): GetActiveExperimentResponse | null {
    const variant = experiment.variants.find(
      (v: any) => v.id === existingAssignment.variantId
    );

    if (variant) {
      return {
        experimentId: experiment.id,
        variantId: variant.id,
        variantName: variant.name,
        components: variant.components,
        themeOverrides: variant.theme_overrides,
      };
    }
    return null;
  }

  /**
   * Assign and record variant for visitor
   */
  private static async assignAndRecordVariant(data: {
    experiment: any;
    variant: any;
    visitorId: string;
    visitorContext: any;
    supabase: any;
  }): Promise<GetActiveExperimentResponse> {
    const { experiment, variant, visitorId, visitorContext, supabase } = data;
    // Store assignment
    await this.storeAssignment(experiment.id, variant.id);

    // Record visitor in analytics
    if (supabase) {
      await supabase.rpc('record_landing_page_event', {
        p_session_id: visitorId,
        p_experiment_id: experiment.id,
        p_variant_id: variant.id,
        p_event_type: 'assignment',
        p_event_data: { visitorContext },
      });
    }

    return {
      experimentId: experiment.id,
      variantId: variant.id,
      variantName: variant.name,
      components: variant.components,
      themeOverrides: variant.theme_overrides,
    };
  }

  /**
   * Get active experiment and variant for the current visitor
   */
  static async getActiveExperiment(visitorContext?: {
    country?: string;
    device?: string;
    language?: string;
    referrer?: string;
    utmSource?: string;
  }): Promise<GetActiveExperimentResponse | null> {
    try {
      const supabase = await createClient();
      const visitorId = await this.getOrCreateVisitorId();
      const storedAssignments = await this.getStoredAssignments();

      // Get active experiments
      if (!supabase) {
        logger.error('Failed to create Supabase client');
        return Promise.resolve(null);
      }

      const { data: experiments, error: experimentsError } = await supabase
        .from('landing_page_experiments')
        .select(
          `
          *,
          variants:landing_page_variants(*)
        `
        )
        .eq('status', 'active')
        .or('start_date.is.null,start_date.lte.now()')
        .or('end_date.is.null,end_date.gte.now()')
        .order('created_at', { ascending: false });

      if (experimentsError || !experiments || experiments.length === 0) {
        return Promise.resolve(null);
      }

      // Find the first matching experiment
      for (const experiment of experiments) {
        const result = this.processExperiment({
          experiment,
          visitorId,
          visitorContext,
          storedAssignments,
          supabase,
        });
        if (result) return result;
      }

      return Promise.resolve(null);
    } catch (error) {
      logger.error('Failed to get active experiment', error as Error);
      return Promise.resolve(null);
    }
  }

  /**
   * Record a conversion event
   */
  static async recordConversion(
    experimentId: string,
    variantId: string,
    conversionData?: Record<string, any>
  ): Promise<void> {
    try {
      const supabase = await createClient();
      const visitorId = await this.getOrCreateVisitorId();

      if (!supabase) {
        logger.error(
          'Failed to create Supabase client for conversion tracking'
        );
        return;
      }

      await supabase.rpc('record_landing_page_event', {
        p_session_id: visitorId,
        p_experiment_id: experimentId,
        p_variant_id: variantId,
        p_event_type: 'conversion',
        p_event_data: conversionData || {},
      });
    } catch (error) {
      logger.error('Failed to record conversion', error as Error);
    }
  }

  /**
   * Record a click event
   */
  static async recordClick(
    experimentId: string,
    variantId: string,
    element: string,
    additionalData?: Record<string, any>
  ): Promise<void> {
    try {
      const supabase = await createClient();
      const visitorId = await this.getOrCreateVisitorId();

      if (!supabase) {
        logger.error('Failed to create Supabase client for click tracking');
        return;
      }

      await supabase.rpc('record_landing_page_event', {
        p_session_id: visitorId,
        p_experiment_id: experimentId,
        p_variant_id: variantId,
        p_event_type: 'click',
        p_event_data: {
          element,
          timestamp: new Date().toISOString(),
          ...additionalData,
        },
      });
    } catch (error) {
      logger.error('Failed to record click', error as Error);
    }
  }

  /**
   * Get all active assignments for the current visitor
   */
  static getAssignments(): Promise<Record<string, VisitorAssignment>> {
    return this.getStoredAssignments();
  }

  /**
   * Clear all experiment assignments (useful for testing)
   */
  static async clearAssignments(): Promise<void> {
    const cookieStore = await cookies();
    cookieStore.delete(EXPERIMENT_COOKIE_NAME);
  }

  /**
   * Check if a specific feature/variant is enabled
   */
  static async isFeatureEnabled(
    componentType: string,
    variantName: string
  ): Promise<boolean> {
    const experiment = await this.getActiveExperiment();

    if (!experiment) {
      return false;
    }

    const component = experiment.components.find(
      c => c.type === componentType && c.variant === variantName
    );

    return component?.visible || false;
  }
}

'use client';

import { usePathname } from 'next/navigation';
import { useEffect, useState, useCallback } from 'react';

import { logger } from '@/lib/utils/logger';

import type {
  GetActiveExperimentResponse,
  ComponentConfig,
} from '@/types/experiments';

/**
 * @fileoverview Client-side hook for A/B testing experiments
 *
 * Provides React hooks for accessing experiment data and tracking
 * events in landing page A/B tests.
 */

/**
 * Experiment context data
 */
interface ExperimentContext {
  experimentId?: string;
  variantId?: string;
  variantName?: string;
  components: ComponentConfig[];
  themeOverrides: Record<string, string | number | boolean>;
  isLoading: boolean;
  error?: Error;
}

/**
 * Hook for accessing current experiment data
 */
export function useExperiment(): ExperimentContext {
  const [context, setContext] = useState<ExperimentContext>({
    components: [],
    themeOverrides: {},
    isLoading: true,
  });
  const pathname = usePathname();

  useEffect(() => {
    // Only run on landing page
    if (pathname !== '/') {
      setContext(prev => ({ ...prev, isLoading: false }));
      return;
    }

    const fetchExperiment = async (): Promise<void> => {
      try {
        const response = await fetch('/api/v1/experiments/active', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch experiment');
        }

        const data: GetActiveExperimentResponse | null = await response.json();

        if (data) {
          setContext({
            experimentId: data.experimentId,
            variantId: data.variantId,
            variantName: data.variantName,
            components: data.components,
            themeOverrides: data.themeOverrides as Record<
              string,
              string | number | boolean
            >,
            isLoading: false,
          });
        } else {
          setContext({
            components: [],
            themeOverrides: {},
            isLoading: false,
          });
        }
      } catch (error) {
        logger.error('Failed to load experiment', error as Error);
        setContext({
          components: [],
          themeOverrides: {},
          isLoading: false,
          error: error as Error,
        });
      }
    };

    fetchExperiment();
  }, [pathname]);

  return context;
}

/**
 * Hook for tracking experiment events
 */
export function useExperimentTracking(): {
  trackClick: (
    element: string,
    additionalData?: Record<string, string | number | boolean>
  ) => Promise<void>;
  trackConversion: (
    conversionType: string,
    value?: number,
    metadata?: Record<string, string | number | boolean>
  ) => Promise<void>;
  trackEngagement: (metrics: Record<string, number>) => Promise<void>;
} {
  const { experimentId, variantId } = useExperiment();

  /**
   * Track a click event
   */
  const trackClick = useCallback(
    async (
      element: string,
      additionalData?: Record<string, string | number | boolean>
    ) => {
      if (!experimentId || !variantId) return;

      try {
        await fetch('/api/v1/experiments/track', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            experimentId,
            variantId,
            eventType: 'click',
            eventData: {
              element,
              ...additionalData,
            },
          }),
        });
      } catch (error) {
        logger.error('Failed to track click', error as Error);
      }
    },
    [experimentId, variantId]
  );

  /**
   * Track a conversion event
   */
  const trackConversion = useCallback(
    async (
      conversionType: string,
      value?: number,
      metadata?: Record<string, string | number | boolean>
    ) => {
      if (!experimentId || !variantId) return;

      try {
        await fetch('/api/v1/experiments/track', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            experimentId,
            variantId,
            eventType: 'conversion',
            eventData: {
              type: conversionType,
              value,
              ...metadata,
            },
          }),
        });
      } catch (error) {
        logger.error('Failed to track conversion', error as Error);
      }
    },
    [experimentId, variantId]
  );

  /**
   * Track engagement metrics (scroll depth, time on page)
   */
  const trackEngagement = useCallback(
    async (metrics: { scrollDepth?: number; timeOnPage?: number }) => {
      if (!experimentId || !variantId) return;

      try {
        await fetch('/api/v1/experiments/track', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            experimentId,
            variantId,
            eventType: 'engagement',
            eventData: metrics,
          }),
        });
      } catch (error) {
        logger.error('Failed to track engagement', error as Error);
      }
    },
    [experimentId, variantId]
  );

  return {
    trackClick,
    trackConversion,
    trackEngagement,
  };
}

/**
 * Hook to check if a specific component variant is active
 */
function useComponentVariant(componentType: string): {
  variant: string | null;
  props: Record<string, string | number | boolean | Record<string, unknown>>;
  isVisible: boolean;
} {
  const { components } = useExperiment();

  const component = components.find(c => c.type === componentType);

  return {
    variant: component?.variant || null,
    props: component?.props || {},
    isVisible: component?.visible ?? true,
  };
}

/**
 * Hook to get theme overrides from experiment
 */
export function useExperimentTheme(): Record<
  string,
  string | number | boolean
> {
  const { themeOverrides } = useExperiment();

  useEffect(() => {
    // Apply theme overrides to CSS variables
    if (themeOverrides && Object.keys(themeOverrides).length > 0) {
      const root = document.documentElement;

      Object.entries(themeOverrides).forEach(([key, value]) => {
        if (key === 'primaryColor') {
          root.style.setProperty('--color-primary', value as string);
        } else if (key === 'secondaryColor') {
          root.style.setProperty('--color-secondary', value as string);
        } else if (key === 'fontFamily') {
          root.style.setProperty('--font-family', value as string);
        } else if (key === 'borderRadius') {
          root.style.setProperty('--border-radius', value as string);
        } else if (key === 'spacing') {
          const spacingMap = {
            compact: '0.75',
            normal: '1',
            relaxed: '1.25',
          };
          root.style.setProperty(
            '--spacing-multiplier',
            spacingMap[value as keyof typeof spacingMap]
          );
        }
      });

      // Cleanup function to reset styles
      return () => {
        root.style.removeProperty('--color-primary');
        root.style.removeProperty('--color-secondary');
        root.style.removeProperty('--font-family');
        root.style.removeProperty('--border-radius');
        root.style.removeProperty('--spacing-multiplier');
      };
    }

    // Return undefined when no theme overrides
    return undefined;
  }, [themeOverrides]);

  return themeOverrides;
}

/**
 * Get component configuration by type
 */
function getComponentConfig(
  components: ComponentConfig[],
  type: string
): ComponentConfig | undefined {
  return components.find(c => c.type === type);
}

/**
 * Sort components by order
 */
export function sortComponentsByOrder(
  components: ComponentConfig[]
): ComponentConfig[] {
  return [...components].sort((a, b) => a.order - b.order);
}

/**
 * GDPR Compliance Service
 * Handles data protection requests, consent management, and privacy controls
 */

import { createClient } from '@/lib/supabase/client';
import { logger } from '@/lib/utils/logger';
import { auditLogger } from '@/lib/services/audit/audit-logger';
import { AppError } from '@/types/errors';
import { redis, isRedisAvailable } from '@/lib/cache/redis-client';

export interface ConsentRecord {
  userId: string;
  consentType: ConsentType;
  granted: boolean;
  timestamp: Date;
  ipAddress: string;
  userAgent?: string;
  purpose: string;
  legalBasis: LegalBasis;
  expiryDate?: Date;
}

export type ConsentType =
  | 'marketing'
  | 'analytics'
  | 'essential'
  | 'performance'
  | 'targeting'
  | 'ai_processing'
  | 'data_sharing';

export type LegalBasis =
  | 'consent'
  | 'contract'
  | 'legal_obligation'
  | 'vital_interests'
  | 'public_task'
  | 'legitimate_interests';

export interface DataExportRequest {
  id: string;
  userId: string;
  userEmail: string;
  requestDate: Date;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  completedDate?: Date;
  downloadUrl?: string;
  expiryDate?: Date;
  requestIp: string;
}

export interface DataDeletionRequest {
  id: string;
  userId: string;
  userEmail: string;
  requestDate: Date;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  completedDate?: Date;
  deletionType: 'soft' | 'hard';
  retentionPeriod?: number; // days
  requestIp: string;
  reason?: string;
}

export interface PrivacySettings {
  userId: string;
  profileVisibility: 'public' | 'private' | 'unlisted';
  analyticsEnabled: boolean;
  marketingEnabled: boolean;
  dataProcessingEnabled: boolean;
  thirdPartySharing: boolean;
  dataRetentionPeriod: number; // days
  updatedAt: Date;
}

/**
 * GDPR Compliance Service
 */
class GDPRService {
  private supabase = createClient();

  /**
   * Record user consent
   */
  async recordConsent(
    consent: Omit<ConsentRecord, 'timestamp'>
  ): Promise<void> {
    try {
      const consentRecord: ConsentRecord = {
        ...consent,
        timestamp: new Date(),
      };

      // Store in database
      if (this.supabase) {
        const { error } = await this.supabase.from('user_consent').upsert({
          user_id: consent.userId,
          consent_type: consent.consentType,
          granted: consent.granted,
          timestamp: consentRecord.timestamp.toISOString(),
          ip_address: consent.ipAddress,
          user_agent: consent.userAgent,
          purpose: consent.purpose,
          legal_basis: consent.legalBasis,
          expiry_date: consent.expiryDate?.toISOString(),
        });

        if (error) {
          logger.error('Failed to record consent', { error, consent });
          throw new AppError(
            'CONSENT_RECORD_FAILED',
            'Failed to record consent',
            500
          );
        }
      }

      // Cache in Redis for quick access
      if (isRedisAvailable()) {
        const cacheKey = `consent:${consent.userId}:${consent.consentType}`;
        await redis.setex(cacheKey, 86400, JSON.stringify(consentRecord)); // 24 hours
      }

      // Log audit event
      await auditLogger.logGDPR('gdpr.consent_given', {
        userId: consent.userId,
        userIp: consent.ipAddress,
        outcome: 'success',
        details: {
          consentType: consent.consentType,
          granted: consent.granted,
          purpose: consent.purpose,
          legalBasis: consent.legalBasis,
        },
      });

      logger.info('Consent recorded successfully', {
        userId: consent.userId,
        consentType: consent.consentType,
        granted: consent.granted,
      });
    } catch (error) {
      if (error instanceof AppError) throw error;
      logger.error('Failed to record consent', { error, consent });
      throw new AppError(
        'CONSENT_RECORD_FAILED',
        'Failed to record consent',
        500
      );
    }
  }

  /**
   * Get user consent status
   */
  async getUserConsent(
    userId: string
  ): Promise<Record<ConsentType, ConsentRecord | null>> {
    try {
      // Try Redis first
      if (isRedisAvailable()) {
        const cachedConsent = await this.getCachedConsent(userId);
        if (cachedConsent) {
          return cachedConsent;
        }
      }

      // Fallback to database
      if (!this.supabase) {
        return this.getDefaultConsent();
      }

      const { data, error } = await this.supabase
        .from('user_consent')
        .select('*')
        .eq('user_id', userId);

      if (error) {
        logger.error('Failed to get user consent', { error, userId });
        return this.getDefaultConsent();
      }

      const consentMap: Record<ConsentType, ConsentRecord | null> =
        this.getDefaultConsent();

      data?.forEach((record: any) => {
        consentMap[record.consent_type as ConsentType] = {
          userId: record.user_id,
          consentType: record.consent_type,
          granted: record.granted,
          timestamp: new Date(record.timestamp),
          ipAddress: record.ip_address,
          userAgent: record.user_agent,
          purpose: record.purpose,
          legalBasis: record.legal_basis,
          expiryDate: record.expiry_date
            ? new Date(record.expiry_date)
            : undefined,
        };
      });

      // Cache the result
      if (isRedisAvailable()) {
        await this.cacheConsent(userId, consentMap);
      }

      return consentMap;
    } catch (error) {
      logger.error('Failed to get user consent', { error, userId });
      return this.getDefaultConsent();
    }
  }

  /**
   * Request data export (Right of Access)
   */
  async requestDataExport(
    userId: string,
    userEmail: string,
    requestIp: string
  ): Promise<DataExportRequest> {
    try {
      const request: DataExportRequest = {
        id: `export_${Date.now()}_${Math.random().toString(36).substring(2)}`,
        userId,
        userEmail,
        requestDate: new Date(),
        status: 'pending',
        requestIp,
        expiryDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days
      };

      // Store request in database
      if (this.supabase) {
        const { error } = await this.supabase
          .from('data_export_requests')
          .insert({
            id: request.id,
            user_id: request.userId,
            user_email: request.userEmail,
            request_date: request.requestDate.toISOString(),
            status: request.status,
            request_ip: request.requestIp,
            expiry_date: request.expiryDate?.toISOString(),
          });

        if (error) {
          logger.error('Failed to create data export request', {
            error,
            request,
          });
          throw new AppError(
            'EXPORT_REQUEST_FAILED',
            'Failed to create data export request',
            500
          );
        }
      }

      // Log audit event
      await auditLogger.logGDPR('gdpr.data_request', {
        userId,
        userEmail,
        userIp: requestIp,
        outcome: 'success',
        details: {
          requestId: request.id,
          type: 'export',
        },
      });

      // Queue processing (in production, use a proper job queue)
      this.processDataExportAsync(request);

      logger.info('Data export request created', {
        requestId: request.id,
        userId,
      });
      return request;
    } catch (error) {
      if (error instanceof AppError) throw error;
      logger.error('Failed to request data export', { error, userId });
      throw new AppError(
        'EXPORT_REQUEST_FAILED',
        'Failed to request data export',
        500
      );
    }
  }

  /**
   * Request data deletion (Right to be Forgotten)
   */
  async requestDataDeletion(
    userId: string,
    userEmail: string,
    requestIp: string,
    deletionType: 'soft' | 'hard' = 'soft',
    reason?: string
  ): Promise<DataDeletionRequest> {
    try {
      const request: DataDeletionRequest = {
        id: `deletion_${Date.now()}_${Math.random().toString(36).substring(2)}`,
        userId,
        userEmail,
        requestDate: new Date(),
        status: 'pending',
        deletionType,
        retentionPeriod: deletionType === 'soft' ? 90 : 0, // 90 days for soft delete
        requestIp,
        reason,
      };

      // Store request in database
      if (this.supabase) {
        const { error } = await this.supabase
          .from('data_deletion_requests')
          .insert({
            id: request.id,
            user_id: request.userId,
            user_email: request.userEmail,
            request_date: request.requestDate.toISOString(),
            status: request.status,
            deletion_type: request.deletionType,
            retention_period: request.retentionPeriod,
            request_ip: request.requestIp,
            reason: request.reason,
          });

        if (error) {
          logger.error('Failed to create data deletion request', {
            error,
            request,
          });
          throw new AppError(
            'DELETION_REQUEST_FAILED',
            'Failed to create data deletion request',
            500
          );
        }
      }

      // Log audit event
      await auditLogger.logGDPR('gdpr.data_deletion', {
        userId,
        userEmail,
        userIp: requestIp,
        outcome: 'success',
        details: {
          requestId: request.id,
          deletionType,
          reason,
        },
      });

      // Queue processing
      this.processDataDeletionAsync(request);

      logger.info('Data deletion request created', {
        requestId: request.id,
        userId,
      });
      return request;
    } catch (error) {
      if (error instanceof AppError) throw error;
      logger.error('Failed to request data deletion', { error, userId });
      throw new AppError(
        'DELETION_REQUEST_FAILED',
        'Failed to request data deletion',
        500
      );
    }
  }

  /**
   * Get user privacy settings
   */
  async getPrivacySettings(userId: string): Promise<PrivacySettings> {
    try {
      if (!this.supabase) {
        return this.getDefaultPrivacySettings(userId);
      }

      const { data, error } = await this.supabase
        .from('user_privacy_settings')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error || !data) {
        return this.getDefaultPrivacySettings(userId);
      }

      return {
        userId: data.user_id,
        profileVisibility: data.profile_visibility,
        analyticsEnabled: data.analytics_enabled,
        marketingEnabled: data.marketing_enabled,
        dataProcessingEnabled: data.data_processing_enabled,
        thirdPartySharing: data.third_party_sharing,
        dataRetentionPeriod: data.data_retention_period,
        updatedAt: new Date(data.updated_at),
      };
    } catch (error) {
      logger.error('Failed to get privacy settings', { error, userId });
      return this.getDefaultPrivacySettings(userId);
    }
  }

  /**
   * Update user privacy settings
   */
  async updatePrivacySettings(
    userId: string,
    settings: Partial<Omit<PrivacySettings, 'userId' | 'updatedAt'>>,
    userIp: string
  ): Promise<PrivacySettings> {
    try {
      const currentSettings = await this.getPrivacySettings(userId);
      const updatedSettings: PrivacySettings = {
        ...currentSettings,
        ...settings,
        updatedAt: new Date(),
      };

      if (this.supabase) {
        const { error } = await this.supabase
          .from('user_privacy_settings')
          .upsert({
            user_id: userId,
            profile_visibility: updatedSettings.profileVisibility,
            analytics_enabled: updatedSettings.analyticsEnabled,
            marketing_enabled: updatedSettings.marketingEnabled,
            data_processing_enabled: updatedSettings.dataProcessingEnabled,
            third_party_sharing: updatedSettings.thirdPartySharing,
            data_retention_period: updatedSettings.dataRetentionPeriod,
            updated_at: updatedSettings.updatedAt.toISOString(),
          });

        if (error) {
          logger.error('Failed to update privacy settings', {
            error,
            userId,
            settings,
          });
          throw new AppError(
            'PRIVACY_UPDATE_FAILED',
            'Failed to update privacy settings',
            500
          );
        }
      }

      // Log audit event
      await auditLogger.logDataAccess('data.update', {
        userId,
        userIp,
        resource: 'user',
        resourceId: userId,
        outcome: 'success',
        details: {
          type: 'privacy_settings',
          changes: settings,
        },
      });

      logger.info('Privacy settings updated', { userId, settings });
      return updatedSettings;
    } catch (error) {
      if (error instanceof AppError) throw error;
      logger.error('Failed to update privacy settings', { error, userId });
      throw new AppError(
        'PRIVACY_UPDATE_FAILED',
        'Failed to update privacy settings',
        500
      );
    }
  }

  /**
   * Check if user can be contacted for marketing
   */
  async canContactForMarketing(userId: string): Promise<boolean> {
    const consent = await this.getUserConsent(userId);
    const marketingConsent = consent.marketing;

    if (!marketingConsent || !marketingConsent.granted) {
      return false;
    }

    // Check if consent has expired
    if (
      marketingConsent.expiryDate &&
      marketingConsent.expiryDate < new Date()
    ) {
      return false;
    }

    return true;
  }

  /**
   * Process data export asynchronously
   */
  private async processDataExportAsync(
    request: DataExportRequest
  ): Promise<void> {
    try {
      // Update status to processing
      await this.updateExportRequestStatus(request.id, 'processing');

      // Collect all user data
      const userData = await this.collectUserData(request.userId);

      // Generate export file (JSON format)
      const exportData = {
        userId: request.userId,
        exportDate: new Date().toISOString(),
        requestId: request.id,
        data: userData,
      };

      // In production, upload to secure storage and generate download URL
      const downloadUrl = await this.generateExportFile(exportData);

      // Update request with download URL
      await this.updateExportRequestStatus(
        request.id,
        'completed',
        downloadUrl
      );

      // Log completion
      await auditLogger.logGDPR('gdpr.data_export', {
        userId: request.userId,
        userEmail: request.userEmail,
        userIp: request.requestIp,
        outcome: 'success',
        details: {
          requestId: request.id,
          exportSize: JSON.stringify(exportData).length,
        },
      });

      logger.info('Data export completed', { requestId: request.id });
    } catch (error) {
      logger.error('Data export processing failed', {
        error,
        requestId: request.id,
      });
      await this.updateExportRequestStatus(request.id, 'failed');
    }
  }

  /**
   * Process data deletion asynchronously
   */
  private async processDataDeletionAsync(
    request: DataDeletionRequest
  ): Promise<void> {
    try {
      // Update status to processing
      await this.updateDeletionRequestStatus(request.id, 'processing');

      if (request.deletionType === 'hard') {
        await this.performHardDeletion(request.userId);
      } else {
        await this.performSoftDeletion(request.userId);
      }

      // Update request status
      await this.updateDeletionRequestStatus(request.id, 'completed');

      logger.info('Data deletion completed', {
        requestId: request.id,
        type: request.deletionType,
      });
    } catch (error) {
      logger.error('Data deletion processing failed', {
        error,
        requestId: request.id,
      });
      await this.updateDeletionRequestStatus(request.id, 'failed');
    }
  }

  /**
   * Collect all user data for export
   */
  private async collectUserData(userId: string): Promise<any> {
    if (!this.supabase) {
      return { error: 'Database not available' };
    }

    try {
      // Collect from all relevant tables
      const [
        { data: profile },
        { data: portfolios },
        { data: aiRequests },
        { data: subscriptions },
        { data: consent },
        { data: auditLogs },
      ] = await Promise.all([
        this.supabase.from('user_profiles').select('*').eq('user_id', userId),
        this.supabase.from('portfolios').select('*').eq('user_id', userId),
        this.supabase.from('ai_requests').select('*').eq('user_id', userId),
        this.supabase
          .from('user_subscriptions')
          .select('*')
          .eq('user_id', userId),
        this.supabase.from('user_consent').select('*').eq('user_id', userId),
        this.supabase
          .from('audit_logs')
          .select('*')
          .eq('user_id', userId)
          .limit(1000),
      ]);

      return {
        profile: profile || [],
        portfolios: portfolios || [],
        aiRequests: aiRequests || [],
        subscriptions: subscriptions || [],
        consent: consent || [],
        auditLogs: auditLogs || [],
      };
    } catch (error) {
      logger.error('Failed to collect user data', { error, userId });
      throw error;
    }
  }

  /**
   * Generate export file and return download URL
   */
  private async generateExportFile(data: any): Promise<string> {
    // In production, this would:
    // 1. Upload to secure cloud storage (S3, etc.)
    // 2. Generate a signed URL with expiration
    // 3. Return the download URL

    // For now, return a placeholder URL
    return `https://export.example.com/${data.requestId}.json`;
  }

  /**
   * Perform soft deletion (mark as deleted, retain for compliance)
   */
  private async performSoftDeletion(userId: string): Promise<void> {
    if (!this.supabase) return;

    const deletedAt = new Date().toISOString();

    // Soft delete from main tables
    await Promise.all([
      this.supabase
        .from('user_profiles')
        .update({ deleted_at: deletedAt, status: 'deleted' })
        .eq('user_id', userId),
      this.supabase
        .from('portfolios')
        .update({ deleted_at: deletedAt, status: 'deleted' })
        .eq('user_id', userId),
    ]);
  }

  /**
   * Perform hard deletion (permanent removal)
   */
  private async performHardDeletion(userId: string): Promise<void> {
    if (!this.supabase) return;

    // Hard delete from all tables (except audit logs for compliance)
    await Promise.all([
      this.supabase.from('user_profiles').delete().eq('user_id', userId),
      this.supabase.from('portfolios').delete().eq('user_id', userId),
      this.supabase.from('ai_requests').delete().eq('user_id', userId),
      this.supabase.from('user_consent').delete().eq('user_id', userId),
      this.supabase
        .from('user_privacy_settings')
        .delete()
        .eq('user_id', userId),
    ]);
  }

  // Helper methods
  private async getCachedConsent(
    userId: string
  ): Promise<Record<ConsentType, ConsentRecord | null> | null> {
    try {
      const cacheKey = `consent:${userId}:all`;
      const cached = await redis.get(cacheKey);
      return cached ? JSON.parse(cached) : null;
    } catch (_error) {
      return null;
    }
  }

  private async cacheConsent(
    userId: string,
    consent: Record<ConsentType, ConsentRecord | null>
  ): Promise<void> {
    try {
      const cacheKey = `consent:${userId}:all`;
      await redis.setex(cacheKey, 86400, JSON.stringify(consent));
    } catch (_error) {
      // Ignore cache errors
    }
  }

  private getDefaultConsent(): Record<ConsentType, ConsentRecord | null> {
    return {
      marketing: null,
      analytics: null,
      essential: null,
      performance: null,
      targeting: null,
      ai_processing: null,
      data_sharing: null,
    };
  }

  private getDefaultPrivacySettings(userId: string): PrivacySettings {
    return {
      userId,
      profileVisibility: 'public',
      analyticsEnabled: true,
      marketingEnabled: false,
      dataProcessingEnabled: true,
      thirdPartySharing: false,
      dataRetentionPeriod: 1095, // 3 years
      updatedAt: new Date(),
    };
  }

  private async updateExportRequestStatus(
    requestId: string,
    status: DataExportRequest['status'],
    downloadUrl?: string
  ): Promise<void> {
    if (!this.supabase) return;

    const updateData: any = { status };
    if (status === 'completed') {
      updateData.completed_date = new Date().toISOString();
      if (downloadUrl) {
        updateData.download_url = downloadUrl;
      }
    }

    await this.supabase
      .from('data_export_requests')
      .update(updateData)
      .eq('id', requestId);
  }

  private async updateDeletionRequestStatus(
    requestId: string,
    status: DataDeletionRequest['status']
  ): Promise<void> {
    if (!this.supabase) return;

    const updateData: any = { status };
    if (status === 'completed') {
      updateData.completed_date = new Date().toISOString();
    }

    await this.supabase
      .from('data_deletion_requests')
      .update(updateData)
      .eq('id', requestId);
  }
}

