import { createClient } from '@/lib/supabase/server';
import { logger } from '@/lib/utils/logger';
import {
  Portfolio,
  CreatePortfolioDTO,
  UpdatePortfolioDTO,
} from '@/types/portfolio';

import { getMockPortfolios } from './__mocks__/portfolio.mock';
import { PortfolioMapper } from './portfolio.mapper';

import type { QueryOptions } from '@/lib/services/base';

/**
 * Portfolio repository for data access layer
 * Implements BaseRepository interface for consistent data access patterns
 */
export class PortfolioRepository {
  private supabase: any;
  private useMockData: boolean;

  constructor() {
    this.useMockData =
      process.env.NODE_ENV === 'development' && !process.env.SUPABASE_URL;
  }

  private async getClient(): Promise<any> {
    if (!this.supabase) {
      this.supabase = await createClient();
    }
    return this.supabase;
  }

  /**
   * Get all portfolios (optionally filtered)
   */
  async findAll(options?: QueryOptions): Promise<Portfolio[]> {
    // For now, we'll use a default userId from options or return empty array
    // In a real implementation, this would use proper filtering
    const userId = options?.filters?.userId as string;
    if (!userId) {
      logger.warn('findAll called without userId filter');
      return [];
    }
    return await this.findByUserId(userId);
  }

  /**
   * Get all portfolios for a user
   */
  async findByUserId(userId: string): Promise<Portfolio[]> {
    try {
      if (this.useMockData) {
        return getMockPortfolios().filter(p => p.userId === userId);
      }

      const client = await this.getClient();
      const { data, error } = await client
        .from('portfolios')
        .select('*')
        .eq('user_id', userId)
        .order('updated_at', { ascending: false });

      if (error) {
        logger.error('Error fetching portfolios:', error as Error);
        throw error;
      }

      return data.map(PortfolioMapper.fromDatabase);
    } catch (error) {
      logger.error('Repository error in findByUserId:', error as Error);
      throw error;
    }
  }

  /**
   * Get a specific portfolio by ID
   */
  async findById(id: string): Promise<Portfolio | null> {
    try {
      if (this.useMockData) {
        return getMockPortfolios().find(p => p.id === id) || null;
      }

      const client = await this.getClient();
      const { data, error } = await client
        .from('portfolios')
        .select('*')
        .eq('id', id)
        .single();

      if (error) {
        if (error.code === 'PGRST116') return null; // Not found
        logger.error('Error fetching portfolio:', error as Error);
        throw error;
      }

      return data ? PortfolioMapper.fromDatabase(data) : null;
    } catch (error) {
      logger.error('Repository error in findById:', error as Error);
      throw error;
    }
  }

  /**
   * Create a new portfolio
   */
  async create(
    data: CreatePortfolioDTO & { userId: string }
  ): Promise<Portfolio> {
    try {
      if (this.useMockData) {
        const newPortfolio: Portfolio = {
          id: `portfolio-${Date.now()}`,
          userId: data.userId,
          name: data.name,
          title: data.title,
          bio: data.bio || '',
          contact: { email: '', location: '' },
          social: {},
          experience: [],
          education: [],
          projects: [],
          skills: [],
          certifications: [],
          template: data.template,
          customization: PortfolioMapper.fromDatabase({}).customization,
          aiSettings: PortfolioMapper.fromDatabase({}).aiSettings,
          status: 'draft',
          subdomain: this.generateSubdomain(data.name),
          views: 0,
          createdAt: new Date(),
          updatedAt: new Date(),
        };
        return newPortfolio;
      }

      const dbData = PortfolioMapper.toDatabase({
        ...data,
        subdomain: this.generateSubdomain(data.name),
        status: 'draft',
        views: 0,
        createdAt: new Date(),
        updatedAt: new Date(),
      });

      const client = await this.getClient();
      const { data: created, error } = await client
        .from('portfolios')
        .insert(dbData)
        .select()
        .single();

      if (error) {
        logger.error('Error creating portfolio:', error as Error);
        throw error;
      }

      return PortfolioMapper.fromDatabase(created);
    } catch (error) {
      logger.error('Repository error in create:', error as Error);
      throw error;
    }
  }

  /**
   * Update an existing portfolio
   */
  async update(
    id: string,
    data: UpdatePortfolioDTO
  ): Promise<Portfolio | null> {
    try {
      if (this.useMockData) {
        const portfolio = getMockPortfolios().find(p => p.id === id);
        if (!portfolio) return null;
        return { ...portfolio, ...data, updatedAt: new Date() };
      }

      const dbData = PortfolioMapper.toDatabase({
        ...data,
        updatedAt: new Date(),
      });

      const client = await this.getClient();
      const { data: updated, error } = await client
        .from('portfolios')
        .update(dbData)
        .eq('id', id)
        .select()
        .single();

      if (error) {
        if (error.code === 'PGRST116') return null; // Not found
        logger.error('Error updating portfolio:', error as Error);
        throw error;
      }

      return updated ? PortfolioMapper.fromDatabase(updated) : null;
    } catch (error) {
      logger.error('Repository error in update:', error as Error);
      throw error;
    }
  }

  /**
   * Delete a portfolio
   */
  async delete(id: string): Promise<boolean> {
    try {
      if (this.useMockData) {
        return true;
      }

      const client = await this.getClient();
      const { error } = await client.from('portfolios').delete().eq('id', id);

      if (error) {
        logger.error('Error deleting portfolio:', error as Error);
        throw error;
      }

      return true;
    } catch (error) {
      logger.error('Repository error in delete:', error as Error);
      throw error;
    }
  }

  /**
   * Find portfolio by subdomain
   */
  async findBySubdomain(subdomain: string): Promise<Portfolio | null> {
    try {
      if (this.useMockData) {
        return (
          getMockPortfolios().find(
            p => p.subdomain === subdomain && p.status === 'published'
          ) || null
        );
      }

      const client = await this.getClient();
      const { data, error } = await client
        .from('portfolios')
        .select('*')
        .eq('subdomain', subdomain)
        .eq('status', 'published')
        .single();

      if (error) {
        if (error.code === 'PGRST116') return null; // Not found
        logger.error('Error fetching portfolio by subdomain:', error as Error);
        throw error;
      }

      return data ? PortfolioMapper.fromDatabase(data) : null;
    } catch (error) {
      logger.error('Repository error in findBySubdomain:', error as Error);
      throw error;
    }
  }

  /**
   * Check subdomain availability
   */
  async checkSubdomainAvailability(subdomain: string): Promise<boolean> {
    return await this.isSubdomainAvailable(subdomain);
  }

  /**
   * Check subdomain availability
   */
  async isSubdomainAvailable(subdomain: string): Promise<boolean> {
    try {
      if (this.useMockData) {
        return !getMockPortfolios().some(p => p.subdomain === subdomain);
      }

      const client = await this.getClient();
      const { data, error } = await client
        .from('portfolios')
        .select('id')
        .eq('subdomain', subdomain)
        .single();

      if (error && error.code !== 'PGRST116') {
        logger.error('Error checking subdomain:', error as Error);
        throw error;
      }

      return !data;
    } catch (error) {
      logger.error('Repository error in isSubdomainAvailable:', error as Error);
      throw error;
    }
  }

  /**
   * Find published portfolios for a user
   */
  async findPublished(userId: string): Promise<Portfolio[]> {
    try {
      if (this.useMockData) {
        return getMockPortfolios().filter(
          p => p.userId === userId && p.publishedAt !== null
        );
      }

      const client = await this.getClient();
      const { data, error } = await client
        .from('portfolios')
        .select('*')
        .eq('user_id', userId)
        .eq('published', true)
        .order('updated_at', { ascending: false });

      if (error) {
        logger.error('Error fetching published portfolios:', error as Error);
        throw error;
      }

      return data ? data.map(PortfolioMapper.fromDatabase) : [];
    } catch (error) {
      logger.error('Repository error in findPublished:', error as Error);
      throw error;
    }
  }

  /**
   * Increment view count
   */
  async incrementViews(id: string): Promise<void> {
    try {
      if (this.useMockData) return;

      const client = await this.getClient();
      const { error } = await client.rpc('increment_portfolio_views', {
        portfolio_id: id,
      });

      if (error) {
        logger.error('Error incrementing views:', error);
        throw error;
      }
    } catch (error) {
      logger.error('Repository error in incrementViews:', error as Error);
      throw error;
    }
  }

  /**
   * Generate a unique subdomain from name
   */
  private generateSubdomain(name: string): string {
    const base = name
      .toLowerCase()
      .replace(/[^a-z0-9]/g, '')
      .substring(0, 20);

    const timestamp = Date.now().toString().slice(-4);
    return `${base}${timestamp}`;
  }
}

import {
  Portfolio,
  CreatePortfolioDTO,
  UpdatePortfolioDTO,
} from '@/types/portfolio';

import { ValidationResult } from './types';

/**
 * @fileoverview Portfolio validation utilities
 * @module services/portfolio/validation
 *
 * This module provides comprehensive validation for portfolio data
 * to ensure data integrity and consistency.
 */

/**
 * Validation rules configuration
 */
const VALIDATION_RULES = {
  name: {
    minLength: 2,
    maxLength: 100,
    pattern: /^[a-zA-Z-\s'-]+$/,
  },
  title: {
    minLength: 2,
    maxLength: 100,
  },
  bio: {
    minLength: 10,
    maxLength: 500,
  },
  tagline: {
    maxLength: 100,
  },
  url: {
    pattern: /^https?:\/\/([\w-]+\.)+[\w-]+(\/[\w-./?%&=]*)?$/,
  },
  email: {
    pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
  },
  phone: {
    pattern: /^[\d\s()+-]+$/,
  },
} as const;

/**
 * Validates a complete portfolio object
 *
 * @param portfolio - Portfolio to validate
 * @returns Validation result with errors and warnings
 */
export function validatePortfolio(portfolio: Portfolio): ValidationResult {
  const errors: ValidationResult['errors'] = [];
  const warnings: ValidationResult['warnings'] = [];

  // Validate required fields
  if (!portfolio.name) {
    errors.push({
      field: 'name',
      message: 'Name is required',
      code: 'REQUIRED_FIELD',
    });
  } else if (!validateName(portfolio.name)) {
    errors.push({
      field: 'name',
      message: 'Name contains invalid characters',
      code: 'INVALID_FORMAT',
    });
  }

  if (!portfolio.title) {
    errors.push({
      field: 'title',
      message: 'Title is required',
      code: 'REQUIRED_FIELD',
    });
  }

  // Validate optional fields
  if (portfolio.bio && !validateBio(portfolio.bio)) {
    errors.push({
      field: 'bio',
      message: `Bio must be between ${VALIDATION_RULES.bio.minLength} and ${VALIDATION_RULES.bio.maxLength} characters`,
      code: 'INVALID_LENGTH',
    });
  }

  if (portfolio.contact?.email && !validateEmail(portfolio.contact.email)) {
    errors.push({
      field: 'contact.email',
      message: 'Invalid email format',
      code: 'INVALID_FORMAT',
    });
  }

  // Validate URLs
  const urlFields = [
    { field: 'avatarUrl', value: portfolio.avatarUrl },
    { field: 'social.linkedin', value: portfolio.social?.linkedin },
    { field: 'social.github', value: portfolio.social?.github },
    { field: 'social.twitter', value: portfolio.social?.twitter },
  ];

  urlFields.forEach(({ field, value }) => {
    if (value && !validateUrl(value)) {
      errors.push({
        field,
        message: 'Invalid URL format',
        code: 'INVALID_URL',
      });
    }
  });

  // Add warnings for missing recommended fields
  if (!portfolio.bio) {
    warnings.push({
      field: 'bio',
      message: 'Adding a bio helps visitors understand your background',
      code: 'RECOMMENDED_FIELD',
    });
  }

  if (!portfolio.projects || portfolio.projects.length === 0) {
    warnings.push({
      field: 'projects',
      message: 'Adding projects showcases your work',
      code: 'RECOMMENDED_FIELD',
    });
  }

  return {
    isValid: errors.length === 0,
    errors,
    warnings,
  };
}

/**
 * Validates portfolio creation data
 *
 * @param data - Portfolio creation DTO
 * @returns Validation result
 */
export function validateCreatePortfolio(
  data: CreatePortfolioDTO
): ValidationResult {
  const errors: ValidationResult['errors'] = [];
  const warnings: ValidationResult['warnings'] = [];

  // Name is required for creation
  if (!data.name) {
    errors.push({
      field: 'name',
      message: 'Name is required',
      code: 'REQUIRED_FIELD',
    });
  }

  // Title is required for creation
  if (!data.title) {
    errors.push({
      field: 'title',
      message: 'Title is required',
      code: 'REQUIRED_FIELD',
    });
  }

  // Template must be specified
  if (!data.template) {
    errors.push({
      field: 'template',
      message: 'Template selection is required',
      code: 'REQUIRED_FIELD',
    });
  }

  return {
    isValid: errors.length === 0,
    errors,
    warnings,
  };
}

/**
 * Validates portfolio update data
 *
 * @param data - Portfolio update DTO
 * @returns Validation result
 */
export function validateUpdatePortfolio(
  data: UpdatePortfolioDTO
): ValidationResult {
  const errors: ValidationResult['errors'] = [];
  const warnings: ValidationResult['warnings'] = [];

  // Validate only provided fields
  if (data.name !== undefined && !validateName(data.name)) {
    errors.push({
      field: 'name',
      message: 'Name contains invalid characters',
      code: 'INVALID_FORMAT',
    });
  }

  if (data.bio !== undefined && !validateBio(data.bio)) {
    errors.push({
      field: 'bio',
      message: `Bio must be between ${VALIDATION_RULES.bio.minLength} and ${VALIDATION_RULES.bio.maxLength} characters`,
      code: 'INVALID_LENGTH',
    });
  }

  return {
    isValid: errors.length === 0,
    errors,
    warnings,
  };
}

/**
 * Individual field validators
 */

function validateName(name: string): boolean {
  return (
    name.length >= VALIDATION_RULES.name.minLength &&
    name.length <= VALIDATION_RULES.name.maxLength &&
    VALIDATION_RULES.name.pattern.test(name)
  );
}

function validateBio(bio: string): boolean {
  return (
    bio.length >= VALIDATION_RULES.bio.minLength &&
    bio.length <= VALIDATION_RULES.bio.maxLength
  );
}

function validateEmail(email: string): boolean {
  return VALIDATION_RULES.email.pattern.test(email);
}

function validateUrl(url: string): boolean {
  return VALIDATION_RULES.url.pattern.test(url);
}

/**
 * Sanitizes portfolio data before saving
 * Removes any potentially harmful content
 *
 * @param data - Portfolio data to sanitize
 * @returns Sanitized data
 */
export function sanitizePortfolioData<T extends Partial<Portfolio>>(
  data: T
): T {
  const sanitized = { ...data };

  // Trim string fields
  if (sanitized.name) sanitized.name = sanitized.name.trim();
  if (sanitized.title) sanitized.title = sanitized.title.trim();
  if (sanitized.bio) sanitized.bio = sanitized.bio.trim();
  if (sanitized.tagline) sanitized.tagline = sanitized.tagline.trim();

  // Sanitize URLs
  if (sanitized.avatarUrl) {
    sanitized.avatarUrl = sanitizeUrl(sanitized.avatarUrl);
  }

  // Sanitize social links
  if (sanitized.social) {
    // Sanitize string URL fields
    const urlFields: (keyof typeof sanitized.social)[] = [
      'linkedin',
      'github',
      'twitter',
      'instagram',
      'youtube',
      'website',
      'dribbble',
      'behance',
    ];

    urlFields.forEach(field => {
      const value = sanitized.social?.[field as keyof typeof sanitized.social];
      if (typeof value === 'string') {
        (sanitized.social as any)[field] = sanitizeUrl(value);
      }
    });

    // Handle custom array separately if it exists
    if (sanitized.social.custom && Array.isArray(sanitized.social.custom)) {
      sanitized.social.custom = sanitized.social.custom.map(link => ({
        ...link,
        url: sanitizeUrl(link.url),
      }));
    }
  }

  return sanitized;
}

/**
 * Sanitizes a URL by removing potentially harmful content
 */
function sanitizeUrl(url: string): string {
  // Remove javascript: and data: protocols
  if (url.startsWith('javascript:') || url.startsWith('data:')) {
    return '';
  }

  // Ensure URL starts with http:// or https://
  if (!url.startsWith('http://') && !url.startsWith('https://')) {
    return `https://${url}`;
  }

  return url.trim();
}

