/**
 * Helper function to format price with promotional display
 */
export function formatPriceWithPromotion(
  amount: number,
  promotionalAmount?: number,
  currency = 'usd'
): {
  original: string;
  promotional?: string;
  savings?: string;
} {
  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: currency.toUpperCase(),
  });

  const result: any = {
    original: formatter.format(amount / 100),
  };

  if (promotionalAmount !== undefined) {
    result.promotional = formatter.format(promotionalAmount / 100);
    result.savings = formatter.format((amount - promotionalAmount) / 100);
  }

  return result;
}

/**
 * Calculate promotional end date for a user
 */
export function calculatePromotionalEndDate(): Date {
  const endDate = new Date();
  endDate.setMonth(endDate.getMonth() + PROMOTIONAL_CONFIG.durationMonths);
  return endDate;
}

import { create } from 'zustand';
import { devtools, subscribeWithSelector, persist } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';

import { aiClient } from '@/lib/ai/client';

import { AIState, AIActions, AIEnhancement } from './types';

/**
 * AI Store
 * Manages AI model selection, enhancement history, and AI operations
 */

const initialState: AIState = {
  selectedModels: {
    bio: 'meta-llama/Meta-Llama-3.1-8B-Instruct',
    project: 'microsoft/Phi-3.5-mini-instruct',
    template: 'meta-llama/Meta-Llama-3.1-8B-Instruct',
  },
  availableModels: [],
  enhancementHistory: [],
  quotaUsed: 0,
  quotaLimit: 100,
  isProcessing: false,
  error: null,
};

export const useAIStore = create<AIState & AIActions>()(
  devtools(
    subscribeWithSelector(
      persist(
        immer((set, get) => ({
          ...initialState,

          // Model management
          setSelectedModel: (type, modelId) =>
            set(state => {
              state.selectedModels[type] = modelId;
            }),

          setAvailableModels: models =>
            set(state => {
              state.availableModels = models;
            }),

          // Enhancement history
          addEnhancement: enhancement =>
            set(state => {
              state.enhancementHistory.unshift(enhancement);
              // Keep only last 50 enhancements
              if (state.enhancementHistory.length > 50) {
                state.enhancementHistory = state.enhancementHistory.slice(
                  0,
                  50
                );
              }
            }),

          clearHistory: () =>
            set(state => {
              state.enhancementHistory = [];
            }),

          // Quota management
          setQuota: (used, limit) =>
            set(state => {
              state.quotaUsed = used;
              state.quotaLimit = limit;
            }),

          // State management
          setProcessing: isProcessing =>
            set(state => {
              state.isProcessing = isProcessing;
            }),

          setError: error =>
            set(state => {
              state.error = error;
            }),

          // AI operations
          enhanceBio: async text => {
            const { selectedModels, quotaUsed, quotaLimit } = get();

            if (quotaUsed >= quotaLimit) {
              throw new Error('AI enhancement quota exceeded');
            }

            set(state => {
              state.isProcessing = true;
              state.error = null;
            });

            try {
              // Update client preferences before enhancing
              aiClient.updateModelSelection('bio', selectedModels.bio);

              const enhanced = await aiClient.enhanceBio(text, {
                title: '',
                skills: [],
                experience: [],
                tone: 'professional',
                industry: 'tech',
                targetLength: 'concise',
              });

              const enhancement: AIEnhancement = {
                id: `enhancement-${Date.now()}`,
                type: 'bio',
                originalText: text,
                enhancedText: enhanced.content,
                model: selectedModels.bio,
                timestamp: new Date(),
                quality: enhanced.qualityScore || 0.8,
              };

              set(state => {
                state.enhancementHistory.unshift(enhancement);
                state.quotaUsed += 1;
                state.isProcessing = false;
              });

              return enhanced.content;
            } catch (error: unknown) {
              set(state => {
                state.error =
                  error instanceof Error ? error.message : 'Enhancement failed';
                state.isProcessing = false;
              });
              throw error;
            }
          },

          enhanceProject: async text => {
            const { selectedModels, quotaUsed, quotaLimit } = get();

            if (quotaUsed >= quotaLimit) {
              throw new Error('AI enhancement quota exceeded');
            }

            set(state => {
              state.isProcessing = true;
              state.error = null;
            });

            try {
              // Update client preferences before enhancing
              aiClient.updateModelSelection('project', selectedModels.project);

              const enhanced = await aiClient.optimizeProject(
                text,
                [],
                'Project'
              );

              const enhancement: AIEnhancement = {
                id: `enhancement-${Date.now()}`,
                type: 'project',
                originalText: text,
                enhancedText: enhanced.enhanced,
                model: selectedModels.project,
                timestamp: new Date(),
                quality: 0.8,
              };

              set(state => {
                state.enhancementHistory.unshift(enhancement);
                state.quotaUsed += 1;
                state.isProcessing = false;
              });

              return enhanced.enhanced;
            } catch (error: unknown) {
              set(state => {
                state.error =
                  error instanceof Error ? error.message : 'Enhancement failed';
                state.isProcessing = false;
              });
              throw error;
            }
          },

          recommendTemplate: async data => {
            const { selectedModels, quotaUsed, quotaLimit } = get();

            if (quotaUsed >= quotaLimit) {
              throw new Error('AI enhancement quota exceeded');
            }

            set(state => {
              state.isProcessing = true;
              state.error = null;
            });

            try {
              // Update client preferences before recommending
              aiClient.updateModelSelection(
                'template',
                selectedModels.template
              );

              const recommendation = await aiClient.recommendTemplate(
                data as any
              );

              set(state => {
                state.quotaUsed += 1;
                state.isProcessing = false;
              });

              return recommendation;
            } catch (error: unknown) {
              set(state => {
                state.error =
                  error instanceof Error
                    ? error.message
                    : 'Recommendation failed';
                state.isProcessing = false;
              });
              throw error;
            }
          },

          loadModels: async () => {
            set(state => {
              state.isProcessing = true;
              state.error = null;
            });

            try {
              // Fetch available models from API
              const response = await fetch('/api/ai/models');
              const models = await response.json();

              set(state => {
                state.availableModels = models;
                state.isProcessing = false;
              });
            } catch (error: unknown) {
              set(state => {
                state.error =
                  error instanceof Error
                    ? error.message
                    : 'Failed to load models';
                state.isProcessing = false;
              });
              throw error;
            }
          },
        })),
        {
          name: 'ai-store',
          // Persist model preferences and quota
          partialize: state => ({
            selectedModels: state.selectedModels,
            quotaUsed: state.quotaUsed,
            quotaLimit: state.quotaLimit,
          }),
        }
      )
    ),
    {
      name: 'ai-store',
    }
  )
);

import { create } from 'zustand';
import { devtools, subscribeWithSelector, persist } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';

import {
  authService,
  type SignUpMetadata,
} from '@/lib/services/auth/auth-service';
import { logger } from '@/lib/utils/logger';

import { AuthState, AuthActions } from './types';

/**
 * Auth Store
 * Manages authentication state, user sessions, and auth operations
 */

const initialState: AuthState = {
  user: null,
  session: null,
  isLoading: true,
  isAuthenticated: false,
  error: null,
};

export const useAuthStore = create<AuthState & AuthActions>()(
  devtools(
    persist(
      subscribeWithSelector(
        immer((set, get) => ({
          ...initialState,

          // Basic setters
          setUser: user =>
            set(state => {
              state.user = user;
              state.isAuthenticated = !!user;
            }),

          setSession: session =>
            set(state => {
              state.session = session;
            }),

          setLoading: isLoading =>
            set(state => {
              state.isLoading = isLoading;
            }),

          setError: error =>
            set(state => {
              state.error = error;
            }),

          // Auth operations
          signIn: async (email, password) => {
            set(state => {
              state.isLoading = true;
              state.error = null;
            });

            try {
              const { data, error } = await authService.signIn(email, password);

              if (error) {
                throw new Error(error.message);
              }

              if (!data) {
                throw new Error('Sign in failed');
              }

              set(state => {
                state.user = data.user;
                state.session = data.session;
                state.isAuthenticated = true;
                state.isLoading = false;
                state.error = null;
              });

              logger.info('User signed in', { userId: data.user.id });
            } catch (error: unknown) {
              const errorMessage =
                error instanceof Error ? error.message : 'Sign in failed';
              set(state => {
                state.error = errorMessage;
                state.isLoading = false;
              });
              logger.error('Sign in failed', { error: errorMessage });
              throw error;
            }
          },

          signUp: async (email, password, metadata = {}) => {
            set(state => {
              state.isLoading = true;
              state.error = null;
            });

            try {
              const { data, error } = await authService.signUp(
                email,
                password,
                metadata as SignUpMetadata
              );

              if (error) {
                throw new Error(error.message);
              }

              if (!data) {
                throw new Error('Sign up failed');
              }

              set(state => {
                state.user = data.user;
                state.session = data.session;
                state.isAuthenticated = !!data.user;
                state.isLoading = false;
                state.error = null;
              });

              logger.info('User signed up', { userId: data.user?.id });
            } catch (error: unknown) {
              const errorMessage =
                error instanceof Error ? error.message : 'Sign up failed';
              set(state => {
                state.error = errorMessage;
                state.isLoading = false;
              });
              logger.error('Sign up failed', { error: errorMessage });
              throw error;
            }
          },

          signOut: async () => {
            set(state => {
              state.isLoading = true;
              state.error = null;
            });

            try {
              const { error } = await authService.signOut();

              if (error) {
                throw new Error(error.message);
              }

              // Reset all auth state
              get().resetAuth();
              logger.info('User signed out');
            } catch (error: unknown) {
              const errorMessage =
                error instanceof Error ? error.message : 'Sign out failed';
              set(state => {
                state.error = errorMessage;
                state.isLoading = false;
              });
              logger.error('Sign out failed', { error: errorMessage });
              throw error;
            }
          },

          signInWithOAuth: async provider => {
            set(state => {
              state.isLoading = true;
              state.error = null;
            });

            try {
              const { data, error } =
                await authService.signInWithOAuth(provider);

              if (error) {
                throw new Error(error.message);
              }

              if (!data?.url) {
                throw new Error('OAuth sign in failed - no redirect URL');
              }

              // Redirect to OAuth provider
              window.location.href = data.url;
            } catch (error: unknown) {
              const errorMessage =
                error instanceof Error ? error.message : 'OAuth sign in failed';
              set(state => {
                state.error = errorMessage;
                state.isLoading = false;
              });
              logger.error('OAuth sign in failed', { error: errorMessage });
              throw error;
            }
          },

          resetAuth: () => set(() => initialState),

          // Initialize auth state from session
          initializeAuth: async () => {
            set(state => {
              state.isLoading = true;
            });

            try {
              const { data: sessionData } = await authService.getSession();
              const { data: userData } = await authService.getUser();

              set(state => {
                state.session = sessionData;
                state.user = userData;
                state.isAuthenticated = !!userData;
                state.isLoading = false;
              });

              if (userData) {
                logger.info('Auth initialized', { userId: userData.id });
              }
            } catch (error) {
              logger.error('Failed to initialize auth', error as Error);
              set(state => {
                state.isLoading = false;
              });
            }
          },
        }))
      ),
      {
        name: 'auth-store',
        partialize: state => ({
          user: state.user,
          isAuthenticated: state.isAuthenticated,
        }),
      }
    )
  )
);

import { create } from 'zustand';
import { devtools, subscribeWithSelector } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';
import type {
  EditorState,
  EditorBlock,
  EditorTheme,
  EditorPreferences,
  BlockValidation,
  EditorContextMenu,
} from '@/types/editor';

interface EditorStore extends EditorState {
  // Actions
  addBlock: (
    block: Omit<EditorBlock, 'id' | 'order'>,
    position?: number
  ) => void;
  updateBlock: (id: string, updates: Partial<EditorBlock>) => void;
  deleteBlock: (id: string) => void;
  reorderBlocks: (fromIndex: number, toIndex: number) => void;
  duplicateBlock: (id: string) => void;
  selectBlock: (id: string | null) => void;
  setTheme: (theme: EditorTheme) => void;
  setViewport: (viewport: 'desktop' | 'tablet' | 'mobile') => void;
  togglePreview: () => void;
  undo: () => void;
  redo: () => void;
  save: () => Promise<void>;
  load: (blocks: EditorBlock[], theme?: EditorTheme) => void;
  reset: () => void;

  // Drag & Drop
  startDragging: (blockId: string) => void;
  stopDragging: () => void;

  // Context Menu
  contextMenu: EditorContextMenu;
  showContextMenu: (
    position: { x: number; y: number },
    targetId: string
  ) => void;
  hideContextMenu: () => void;

  // Validation
  validateBlock: (id: string) => BlockValidation;
  validateAll: () => Record<string, BlockValidation>;

  // Preferences
  preferences: EditorPreferences;
  updatePreferences: (preferences: Partial<EditorPreferences>) => void;

  // Utility
  getBlockById: (id: string) => EditorBlock | undefined;
  getBlockIndex: (id: string) => number;
  canUndo: () => boolean;
  canRedo: () => boolean;
}

const defaultTheme: EditorTheme = {
  id: 'default',
  name: 'Default',
  colors: {
    primary: '#0f172a',
    secondary: '#64748b',
    accent: '#3b82f6',
    background: '#ffffff',
    surface: '#f8fafc',
    text: '#1e293b',
    textSecondary: '#64748b',
    border: '#e2e8f0',
  },
  fonts: {
    heading: 'Inter, sans-serif',
    body: 'Inter, sans-serif',
    code: 'Fira Code, monospace',
  },
  spacing: {
    xs: 4,
    sm: 8,
    md: 16,
    lg: 24,
    xl: 32,
  },
  borderRadius: {
    sm: 4,
    md: 8,
    lg: 12,
  },
  shadows: {
    sm: '0 1px 2px 0 rgb(0 0 0 / 0.05)',
    md: '0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1)',
    lg: '0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1)',
  },
};

const defaultPreferences: EditorPreferences = {
  autoSave: true,
  autoSaveInterval: 30000, // 30 seconds
  showGrid: false,
  snapToGrid: false,
  gridSize: 8,
  showRulers: false,
  showBlockOutlines: false,
  enableAnimations: true,
  keyboardShortcuts: {
    save: 'Cmd+S',
    undo: 'Cmd+Z',
    redo: 'Cmd+Shift+Z',
    copy: 'Cmd+C',
    paste: 'Cmd+V',
    delete: 'Delete',
    duplicate: 'Cmd+D',
  },
  theme: 'light',
};

const initialState: EditorState = {
  blocks: [],
  selectedBlockId: null,
  theme: defaultTheme,
  viewport: 'desktop',
  isPreviewMode: false,
  isDragging: false,
  history: {
    past: [],
    present: [],
    future: [],
  },
  settings: {
    showGrid: false,
    snapToGrid: false,
    gridSize: 8,
    showRulers: false,
    autoSave: true,
    autoSaveInterval: 30000,
  },
};

export const useEditorStore = create<EditorStore>()(
  devtools(
    subscribeWithSelector(
      immer((set, get) => ({
        ...initialState,
        contextMenu: {
          isOpen: false,
          position: { x: 0, y: 0 },
          targetId: null,
          items: [],
        },
        preferences: defaultPreferences,

        addBlock: (block, position) => {
          set(state => {
            const newBlock: EditorBlock = {
              ...block,
              id: `block-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
              order: position ?? state.blocks.length,
            };

            // Add to history
            state.history.past.push([...state.blocks]);
            state.history.future = [];

            // Insert at position or append
            if (position !== undefined && position < state.blocks.length) {
              state.blocks.splice(position, 0, newBlock);
              // Update order for subsequent blocks
              for (let i = position + 1; i < state.blocks.length; i++) {
                const block = state.blocks[i];
                if (block) {
                  block.order = i;
                }
              }
            } else {
              state.blocks.push(newBlock);
            }

            // Select the new block
            state.selectedBlockId = newBlock.id;
          });
        },

        updateBlock: (id, updates) => {
          set(state => {
            const blockIndex = state.blocks.findIndex(block => block.id === id);
            if (blockIndex !== -1) {
              // Add to history
              state.history.past.push([...state.blocks]);
              state.history.future = [];

              // Update block
              const block = state.blocks[blockIndex];
              if (block) {
                Object.assign(block, updates);
              }
            }
          });
        },

        deleteBlock: id => {
          set(state => {
            const blockIndex = state.blocks.findIndex(block => block.id === id);
            if (blockIndex !== -1) {
              // Add to history
              state.history.past.push([...state.blocks]);
              state.history.future = [];

              // Remove block
              state.blocks.splice(blockIndex, 1);

              // Update order for subsequent blocks
              for (let i = blockIndex; i < state.blocks.length; i++) {
                const block = state.blocks[i];
                if (block) {
                  block.order = i;
                }
              }

              // Clear selection if deleted block was selected
              if (state.selectedBlockId === id) {
                state.selectedBlockId = null;
              }
            }
          });
        },

        reorderBlocks: (fromIndex, toIndex) => {
          set(state => {
            if (fromIndex === toIndex) return;

            // Add to history
            state.history.past.push([...state.blocks]);
            state.history.future = [];

            // Move block
            const [movedBlock] = state.blocks.splice(fromIndex, 1);
            if (movedBlock) {
              state.blocks.splice(toIndex, 0, movedBlock);
            }

            // Update order for all blocks
            state.blocks.forEach((block, index) => {
              block.order = index;
            });
          });
        },

        duplicateBlock: id => {
          set(state => {
            const block = state.blocks.find(b => b.id === id);
            if (block) {
              const duplicatedBlock: EditorBlock = {
                ...JSON.parse(JSON.stringify(block)), // Deep clone
                id: `block-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
                order: block.order + 1,
              };

              // Add to history
              state.history.past.push([...state.blocks]);
              state.history.future = [];

              // Insert after original block
              const insertIndex = block.order + 1;
              state.blocks.splice(insertIndex, 0, duplicatedBlock);

              // Update order for subsequent blocks
              for (let i = insertIndex + 1; i < state.blocks.length; i++) {
                const block = state.blocks[i];
                if (block) {
                  block.order = i;
                }
              }

              // Select duplicated block
              state.selectedBlockId = duplicatedBlock.id;
            }
          });
        },

        selectBlock: id => {
          set(state => {
            state.selectedBlockId = id;
          });
        },

        setTheme: theme => {
          set(state => {
            state.theme = theme;
          });
        },

        setViewport: viewport => {
          set(state => {
            state.viewport = viewport;
          });
        },

        togglePreview: () => {
          set(state => {
            state.isPreviewMode = !state.isPreviewMode;
            if (state.isPreviewMode) {
              state.selectedBlockId = null;
            }
          });
        },

        undo: () => {
          set(state => {
            if (state.history.past.length > 0) {
              const previous = state.history.past.pop()!;
              state.history.future.unshift([...state.blocks]);
              state.blocks = previous;
              state.selectedBlockId = null;
            }
          });
        },

        redo: () => {
          set(state => {
            if (state.history.future.length > 0) {
              const next = state.history.future.shift()!;
              state.history.past.push([...state.blocks]);
              state.blocks = next;
              state.selectedBlockId = null;
            }
          });
        },

        save: async () => {
          const state = get();
          // Implementation would save to backend
          console.log('Saving editor state:', state.blocks);
        },

        load: (blocks, theme) => {
          set(state => {
            state.blocks = blocks;
            state.selectedBlockId = null;
            if (theme) {
              state.theme = theme;
            }
            // Reset history
            state.history = {
              past: [],
              present: [...blocks],
              future: [],
            };
          });
        },

        reset: () => {
          set(initialState);
        },

        startDragging: blockId => {
          set(state => {
            state.isDragging = true;
          });
        },

        stopDragging: () => {
          set(state => {
            state.isDragging = false;
          });
        },

        showContextMenu: (position, targetId) => {
          set(state => {
            state.contextMenu = {
              isOpen: true,
              position,
              targetId,
              items: [
                { label: 'Edit', action: 'edit', icon: 'edit' },
                {
                  label: 'Duplicate',
                  action: 'duplicate',
                  icon: 'copy',
                  shortcut: 'Cmd+D',
                },
                {
                  label: 'Delete',
                  action: 'delete',
                  icon: 'trash',
                  shortcut: 'Del',
                },
                { separator: true, label: '', action: '' },
                { label: 'Move Up', action: 'move-up', icon: 'arrow-up' },
                { label: 'Move Down', action: 'move-down', icon: 'arrow-down' },
              ],
            };
          });
        },

        hideContextMenu: () => {
          set(state => {
            state.contextMenu.isOpen = false;
          });
        },

        validateBlock: id => {
          const block = get().blocks.find(b => b.id === id);
          if (!block) {
            return {
              isValid: false,
              errors: [
                {
                  field: 'id',
                  message: 'Block not found',
                  severity: 'error' as const,
                },
              ],
            };
          }

          const errors: BlockValidation['errors'] = [];

          // Basic validation logic
          if (!block.data) {
            errors.push({
              field: 'data',
              message: 'Block data is required',
              severity: 'error',
            });
          }

          return {
            isValid: errors.length === 0,
            errors,
          };
        },

        validateAll: () => {
          const blocks = get().blocks;
          const results: Record<string, BlockValidation> = {};

          blocks.forEach(block => {
            results[block.id] = get().validateBlock(block.id);
          });

          return results;
        },

        updatePreferences: preferences => {
          set(state => {
            Object.assign(state.preferences, preferences);
          });
        },

        getBlockById: id => {
          return get().blocks.find(block => block.id === id);
        },

        getBlockIndex: id => {
          return get().blocks.findIndex(block => block.id === id);
        },

        canUndo: () => {
          return get().history.past.length > 0;
        },

        canRedo: () => {
          return get().history.future.length > 0;
        },
      }))
    ),
    { name: 'editor-store' }
  )
);

// Auto-save functionality
let autoSaveTimer: NodeJS.Timeout;

useEditorStore.subscribe(
  state => state.blocks,
  _blocks => {
    const { preferences, save } = useEditorStore.getState();

    if (preferences.autoSave) {
      clearTimeout(autoSaveTimer);
      autoSaveTimer = setTimeout(() => {
        save();
      }, preferences.autoSaveInterval);
    }
  }
);

import { useCallback, useEffect, useMemo } from 'react';

import { useAIStore } from './ai-store';
import { useAuthStore } from './auth-store';
import { usePortfolioStore } from './portfolio-store';
import { useUIStore } from './ui-store';
import type { AuthState, AuthActions } from './types';

/**
 * Store Hooks
 * Custom hooks for accessing and manipulating store state
 */

/**
 * Hook for authenticated user data with loading state
 */
function useUser() {
  const { user, isLoading, isAuthenticated } = useAuthStore(
    (state: AuthState & AuthActions) => ({
      user: state.user,
      isLoading: state.isLoading,
      isAuthenticated: state.isAuthenticated,
    })
  );

  return {
    user,
    isLoading,
    isAuthenticated,
    isGuest: !isAuthenticated,
  };
}

/**
 * Hook for current portfolio with auto-save
 */
function useCurrentPortfolio() {
  const {
    currentPortfolio,
    isEditing,
    isSaving,
    updatePortfolio,
    setCurrentPortfolio,
  } = usePortfolioStore(state => ({
    currentPortfolio: state.currentPortfolio,
    isEditing: state.isEditing,
    isSaving: state.isSaving,
    updatePortfolio: state.updatePortfolio,
    setCurrentPortfolio: state.setCurrentPortfolio,
  }));

  const updateField = useCallback(
    (field: string, value: unknown) => {
      if (!currentPortfolio) return;

      setCurrentPortfolio({
        ...currentPortfolio,
        [field]: value,
      });
    },
    [currentPortfolio, setCurrentPortfolio]
  );

  const updateNestedField = useCallback(
    (path: string, value: unknown) => {
      if (!currentPortfolio) return;

      const keys = path.split('.');
      const updated = { ...currentPortfolio };
      let current: any = updated;

      for (let i = 0; i < keys.length - 1; i++) {
        const key = keys[i];
        if (!key) continue;
        current[key] = { ...current[key] };
        current = current[key];
      }

      const lastKey = keys[keys.length - 1];
      if (lastKey) {
        current[lastKey] = value;
      }
      setCurrentPortfolio(updated);
    },
    [currentPortfolio, setCurrentPortfolio]
  );

  return {
    portfolio: currentPortfolio,
    isEditing,
    isSaving,
    updateField,
    updateNestedField,
    save: () =>
      currentPortfolio &&
      updatePortfolio(currentPortfolio.id, currentPortfolio),
  };
}

/**
 * Hook for theme management with system detection
 */
function useTheme() {
  const { theme, setTheme } = useUIStore(state => ({
    theme: state.theme,
    setTheme: state.setTheme,
  }));

  // Apply theme on mount and when it changes
  useEffect(() => {
    const root = document.documentElement;
    root.classList.remove('light', 'dark');

    if (theme === 'system') {
      const systemTheme = window.matchMedia('(prefers-color-scheme: dark)')
        .matches
        ? 'dark'
        : 'light';
      root.classList.add(systemTheme);
    } else {
      root.classList.add(theme);
    }
  }, [theme]);

  // Listen for system theme changes
  useEffect(() => {
    if (theme !== 'system') return;

    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
    const handleChange = (e: MediaQueryListEvent) => {
      const root = document.documentElement;
      root.classList.remove('light', 'dark');
      root.classList.add(e.matches ? 'dark' : 'light');
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, [theme]);

  const resolvedTheme = useMemo(() => {
    if (theme === 'system') {
      return window.matchMedia('(prefers-color-scheme: dark)').matches
        ? 'dark'
        : 'light';
    }
    return theme;
  }, [theme]);

  return {
    theme,
    resolvedTheme,
    setTheme,
    toggleTheme: () => setTheme(theme === 'light' ? 'dark' : 'light'),
  };
}

/**
 * Hook for AI model selection with quota tracking
 */
function useAIModels() {
  const {
    selectedModels,
    availableModels,
    quotaUsed,
    quotaLimit,
    setSelectedModel,
    loadModels,
  } = useAIStore(state => ({
    selectedModels: state.selectedModels,
    availableModels: state.availableModels,
    quotaUsed: state.quotaUsed,
    quotaLimit: state.quotaLimit,
    setSelectedModel: state.setSelectedModel,
    loadModels: state.loadModels,
  }));

  // Load models on mount
  useEffect(() => {
    if (availableModels.length === 0) {
      loadModels().catch(() => {
        // Error is handled by the store
      });
    }
  }, [availableModels.length, loadModels]);

  const quotaRemaining = quotaLimit - quotaUsed;
  const quotaPercentage = (quotaUsed / quotaLimit) * 100;
  const isQuotaExceeded = quotaUsed >= quotaLimit;

  return {
    selectedModels,
    availableModels,
    quota: {
      used: quotaUsed,
      limit: quotaLimit,
      remaining: quotaRemaining,
      percentage: quotaPercentage,
      isExceeded: isQuotaExceeded,
    },
    selectModel: setSelectedModel,
  };
}

/**
 * Hook for modal management
 */
function useModal(modalId: string) {
  const { modals, openModal, closeModal } = useUIStore(state => ({
    modals: state.modals,
    openModal: state.openModal,
    closeModal: state.closeModal,
  }));

  const isOpen = modals.some(m => m.id === modalId);

  const open = useCallback(
    (component: React.ComponentType<any>, props?: unknown) => {
      openModal({ id: modalId, component, props });
    },
    [modalId, openModal]
  );

  const close = useCallback(() => {
    closeModal(modalId);
  }, [modalId, closeModal]);

  return {
    isOpen,
    open,
    close,
  };
}

/**
 * Hook for toast notifications
 */
function useToasts() {
  const { toasts, showToast, removeToast, clearToasts } = useUIStore(state => ({
    toasts: state.toasts,
    showToast: state.showToast,
    removeToast: state.removeToast,
    clearToasts: state.clearToasts,
  }));

  return {
    toasts,
    show: showToast,
    remove: removeToast,
    clear: clearToasts,
    success: (title: string, description?: string) =>
      showToast({ title, description, type: 'success' }),
    error: (title: string, description?: string) =>
      showToast({ title, description, type: 'error' }),
    warning: (title: string, description?: string) =>
      showToast({ title, description, type: 'warning' }),
    info: (title: string, description?: string) =>
      showToast({ title, description, type: 'info' }),
  };
}

/**
 * Hook for global loading state
 */
function useGlobalLoading() {
  const { globalLoading, loadingMessage, setGlobalLoading } = useUIStore(
    state => ({
      globalLoading: state.globalLoading,
      loadingMessage: state.loadingMessage,
      setGlobalLoading: state.setGlobalLoading,
    })
  );

  const withLoading = useCallback(
    async <T>(action: () => Promise<T>, message?: string): Promise<T> => {
      setGlobalLoading(true, message);
      try {
        return await action();
      } finally {
        setGlobalLoading(false);
      }
    },
    [setGlobalLoading]
  );

  return {
    isLoading: globalLoading,
    message: loadingMessage,
    setLoading: setGlobalLoading,
    withLoading,
  };
}

/**
 * Hook for portfolio list with filtering and sorting
 */
function usePortfolios(options?: {
  filter?: (portfolio: unknown) => boolean;
  sort?: (a: unknown, b: unknown) => number;
}) {
  const { portfolios, isLoading, loadPortfolios } = usePortfolioStore(
    state => ({
      portfolios: state.portfolios,
      isLoading: state.isLoading,
      loadPortfolios: state.loadPortfolios,
    })
  );

  const { user } = useAuthStore(state => ({ user: state.user }));

  // Load portfolios on mount
  useEffect(() => {
    if (portfolios.length === 0 && !isLoading && user?.id) {
      loadPortfolios().catch(() => {
        // Error is handled by the store
      });
    }
  }, [portfolios.length, isLoading, loadPortfolios, user?.id]);

  const filteredAndSorted = useMemo(() => {
    let result = [...portfolios];

    if (options?.filter) {
      result = result.filter(options.filter);
    }

    if (options?.sort) {
      result.sort(options.sort);
    }

    return result;
  }, [portfolios, options?.filter, options?.sort]);

  return {
    portfolios: filteredAndSorted,
    isLoading,
    reload: loadPortfolios,
    count: filteredAndSorted.length,
    isEmpty: filteredAndSorted.length === 0,
  };
}

// Export all hooks
export {
  useUser,
  useCurrentPortfolio,
  useTheme,
  useAIModels,
  useModal,
  useToasts,
  useGlobalLoading,
  usePortfolios,
};

