/**
 * Onboarding Store
 *
 * Manages user onboarding state and progress tracking
 */

import { create } from 'zustand';
import { persist, createJSONStorage } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';

export interface OnboardingStep {
  id: string;
  title: string;
  description: string;
  completed: boolean;
  skipped: boolean;
  completedAt?: string;
  metadata?: Record<string, any>;
}

export interface OnboardingFlow {
  id: string;
  name: string;
  steps: OnboardingStep[];
  currentStepIndex: number;
  startedAt: string;
  completedAt?: string;
  userType: 'new' | 'returning' | 'imported';
}

export interface OnboardingState {
  // State
  isOnboarding: boolean;
  currentFlow: OnboardingFlow | null;
  completedFlows: string[];
  tourActive: boolean;
  currentTourStep: number;
  showChecklist: boolean;
  dismissedHints: string[];

  // User preferences
  preferences: {
    skipTours: boolean;
    emailReminders: boolean;
    showProgress: boolean;
  };

  // Actions
  startOnboarding: (userType: 'new' | 'returning' | 'imported') => void;
  completeStep: (stepId: string, metadata?: Record<string, any>) => void;
  skipStep: (stepId: string) => void;
  goToStep: (stepIndex: number) => void;
  completeOnboarding: () => void;
  resetOnboarding: () => void;

  // Tour actions
  startTour: (tourId: string) => void;
  nextTourStep: () => void;
  previousTourStep: () => void;
  endTour: () => void;

  // UI actions
  toggleChecklist: () => void;
  dismissHint: (hintId: string) => void;
  updatePreferences: (
    preferences: Partial<OnboardingState['preferences']>
  ) => void;

  // Getters
  getCurrentStep: () => OnboardingStep | null;
  getProgress: () => number;
  isStepCompleted: (stepId: string) => boolean;
  getNextIncompleteStep: () => OnboardingStep | null;
}

