export const useOnboardingStore = create<OnboardingState>()(
  persist(
    immer((set, get) => ({
      // Initial state
      isOnboarding: false,
      currentFlow: null,
      completedFlows: [],
      tourActive: false,
      currentTourStep: 0,
      showChecklist: true,
      dismissedHints: [],
      preferences: {
        skipTours: false,
        emailReminders: true,
        showProgress: true,
      },

      // Actions
      startOnboarding: userType => {
        const flow = {
          ...ONBOARDING_FLOWS[userType],
          currentStepIndex: 0,
          startedAt: new Date().toISOString(),
          userType,
        };

        set(state => {
          state.isOnboarding = true;
          state.currentFlow = flow;
          state.showChecklist = true;
        });
      },

      completeStep: (stepId, metadata) => {
        set(state => {
          if (!state.currentFlow) return;

          const stepIndex = state.currentFlow.steps.findIndex(
            s => s.id === stepId
          );
          if (stepIndex === -1) return;

          const step = state.currentFlow.steps[stepIndex];
          if (step) {
            step.completed = true;
            step.completedAt = new Date().toISOString();
            if (metadata) {
              step.metadata = metadata;
            }
          }

          // Auto-advance to next step
          const nextIncompleteIndex = state.currentFlow.steps.findIndex(
            (s, i) => i > stepIndex && !s.completed && !s.skipped
          );
          if (nextIncompleteIndex !== -1) {
            state.currentFlow.currentStepIndex = nextIncompleteIndex;
          }

          // Check if flow is complete
          const allCompleted = state.currentFlow.steps.every(
            s => s.completed || s.skipped
          );
          if (allCompleted) {
            state.currentFlow.completedAt = new Date().toISOString();
            state.completedFlows.push(state.currentFlow.id);
            state.isOnboarding = false;
          }
        });
      },

      skipStep: stepId => {
        set(state => {
          if (!state.currentFlow) return;

          const stepIndex = state.currentFlow.steps.findIndex(
            s => s.id === stepId
          );
          if (stepIndex === -1) return;

          const step = state.currentFlow.steps[stepIndex];
          if (step) {
            step.skipped = true;
          }

          // Auto-advance to next step
          const nextIncompleteIndex = state.currentFlow.steps.findIndex(
            (s, i) => i > stepIndex && !s.completed && !s.skipped
          );
          if (nextIncompleteIndex !== -1) {
            state.currentFlow.currentStepIndex = nextIncompleteIndex;
          }
        });
      },

      goToStep: stepIndex => {
        set(state => {
          if (!state.currentFlow) return;
          if (stepIndex < 0 || stepIndex >= state.currentFlow.steps.length)
            return;

          state.currentFlow.currentStepIndex = stepIndex;
        });
      },

      completeOnboarding: () => {
        set(state => {
          if (state.currentFlow) {
            state.currentFlow.completedAt = new Date().toISOString();
            state.completedFlows.push(state.currentFlow.id);
          }
          state.isOnboarding = false;
          state.currentFlow = null;
        });
      },

      resetOnboarding: () => {
        set(state => {
          state.isOnboarding = false;
          state.currentFlow = null;
          state.tourActive = false;
          state.currentTourStep = 0;
        });
      },

      // Tour actions
      startTour: _tourId => {
        set(state => {
          state.tourActive = true;
          state.currentTourStep = 0;
        });
      },

      nextTourStep: () => {
        set(state => {
          state.currentTourStep += 1;
        });
      },

      previousTourStep: () => {
        set(state => {
          if (state.currentTourStep > 0) {
            state.currentTourStep -= 1;
          }
        });
      },

      endTour: () => {
        set(state => {
          state.tourActive = false;
          state.currentTourStep = 0;
        });
      },

      // UI actions
      toggleChecklist: () => {
        set(state => {
          state.showChecklist = !state.showChecklist;
        });
      },

      dismissHint: hintId => {
        set(state => {
          if (!state.dismissedHints.includes(hintId)) {
            state.dismissedHints.push(hintId);
          }
        });
      },

      updatePreferences: preferences => {
        set(state => {
          state.preferences = { ...state.preferences, ...preferences };
        });
      },

      // Getters
      getCurrentStep: () => {
        const state = get();
        if (!state.currentFlow) return null;
        return (
          state.currentFlow.steps[state.currentFlow.currentStepIndex] || null
        );
      },

      getProgress: () => {
        const state = get();
        if (!state.currentFlow) return 0;

        const completed = state.currentFlow.steps.filter(
          s => s.completed || s.skipped
        ).length;
        return Math.round((completed / state.currentFlow.steps.length) * 100);
      },

      isStepCompleted: stepId => {
        const state = get();
        if (!state.currentFlow) return false;

        const step = state.currentFlow.steps.find(s => s.id === stepId);
        return step?.completed || false;
      },

      getNextIncompleteStep: () => {
        const state = get();
        if (!state.currentFlow) return null;

        return (
          state.currentFlow.steps.find(s => !s.completed && !s.skipped) || null
        );
      },
    })),
    {
      name: 'onboarding-storage',
      storage: createJSONStorage(() => localStorage),
      partialize: state => ({
        completedFlows: state.completedFlows,
        dismissedHints: state.dismissedHints,
        preferences: state.preferences,
      }),
    }
  )
);

import { create } from 'zustand';
import { devtools, subscribeWithSelector } from 'zustand/middleware';
import { immer } from 'zustand/middleware/immer';

import { logger } from '@/lib/utils/logger';
import { Portfolio } from '@/types/portfolio';

import { PortfolioState, PortfolioActions } from './types';

/**
 * Portfolio Store
 * Manages portfolio data, editing state, and portfolio operations
 */

const initialState: PortfolioState = {
  portfolios: [],
  currentPortfolio: null,
  currentPortfolioId: null,
  isEditing: false,
  isSaving: false,
  isLoading: false,
  error: null,
  lastSaved: null,
  history: [],
  historyIndex: -1,
  hasUnsavedChanges: false,
};

// Helper function to make API calls
async function apiCall<T>(url: string, options?: RequestInit): Promise<T> {
  const response = await fetch(url, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options?.headers,
    },
  });

  if (!response.ok) {
    const error = await response
      .json()
      .catch(() => ({ error: 'Unknown error' }));
    throw new Error(error.error || `API call failed: ${response.statusText}`);
  }

  const data = await response.json();
  return data.data || data;
}

export const usePortfolioStore = create<PortfolioState & PortfolioActions>()(
  devtools(
    subscribeWithSelector(
      immer((set, get) => ({
        ...initialState,

        // Basic setters
        setPortfolios: portfolios =>
          set(state => {
            state.portfolios = portfolios;
          }),

        setCurrentPortfolio: portfolio =>
          set(state => {
            state.currentPortfolio = portfolio;
            state.currentPortfolioId = portfolio?.id || null;
            state.hasUnsavedChanges = false;
          }),

        setEditing: isEditing =>
          set(state => {
            state.isEditing = isEditing;
          }),

        setSaving: isSaving =>
          set(state => {
            state.isSaving = isSaving;
          }),

        setLoading: isLoading =>
          set(state => {
            state.isLoading = isLoading;
          }),

        setError: error =>
          set(state => {
            state.error = error;
          }),

        // Portfolio operations
        loadPortfolios: async () => {
          set(state => {
            state.isLoading = true;
            state.error = null;
          });

          try {
            const { portfolios } = await apiCall<{ portfolios: Portfolio[] }>(
              '/api/v1/portfolios'
            );

            set(state => {
              state.portfolios = portfolios;
              state.isLoading = false;
            });

            return portfolios;
          } catch (error) {
            const errorMessage =
              error instanceof Error
                ? error.message
                : 'Failed to load portfolios';
            logger.error('Failed to load portfolios:', error as Error);

            set(state => {
              state.error = errorMessage;
              state.isLoading = false;
            });

            throw error;
          }
        },

        loadPortfolio: async (id: string) => {
          set(state => {
            state.isLoading = true;
            state.error = null;
          });

          try {
            const { portfolio } = await apiCall<{ portfolio: Portfolio }>(
              `/api/v1/portfolios/${id}`
            );

            set(state => {
              state.currentPortfolio = portfolio;
              state.currentPortfolioId = id;
              state.isLoading = false;
              state.hasUnsavedChanges = false;

              // Reset history when loading a new portfolio
              state.history = [];
              state.historyIndex = -1;
            });

            return portfolio;
          } catch (error) {
            const errorMessage =
              error instanceof Error
                ? error.message
                : 'Failed to load portfolio';
            logger.error('Failed to load portfolio:', error as Error);

            set(state => {
              state.error = errorMessage;
              state.isLoading = false;
            });

            throw error;
          }
        },

        createPortfolio: async data => {
          set(state => {
            state.isSaving = true;
            state.error = null;
          });

          try {
            const { portfolio } = await apiCall<{ portfolio: Portfolio }>(
              '/api/v1/portfolios',
              {
                method: 'POST',
                body: JSON.stringify(data),
              }
            );

            set(state => {
              state.portfolios.push(portfolio);
              state.currentPortfolio = portfolio;
              state.currentPortfolioId = portfolio.id;
              state.isSaving = false;
              state.hasUnsavedChanges = false;
            });

            return portfolio;
          } catch (error) {
            const errorMessage =
              error instanceof Error
                ? error.message
                : 'Failed to create portfolio';
            logger.error('Failed to create portfolio:', error as Error);

            set(state => {
              state.error = errorMessage;
              state.isSaving = false;
            });

            throw error;
          }
        },

        updatePortfolio: async (id, data) => {
          set(state => {
            state.isSaving = true;
            state.error = null;
          });

          try {
            const { portfolio } = await apiCall<{ portfolio: Portfolio }>(
              `/api/v1/portfolios/${id}`,
              {
                method: 'PUT',
                body: JSON.stringify(data),
              }
            );

            set(state => {
              const index = state.portfolios.findIndex(p => p.id === id);
              if (index !== -1) {
                state.portfolios[index] = portfolio;
              }
              if (state.currentPortfolio?.id === id) {
                state.currentPortfolio = portfolio;
              }
              state.isSaving = false;
              state.hasUnsavedChanges = false;
              state.lastSaved = new Date();
            });

            return portfolio;
          } catch (error) {
            const errorMessage =
              error instanceof Error
                ? error.message
                : 'Failed to update portfolio';
            logger.error('Failed to update portfolio:', error as Error);

            set(state => {
              state.error = errorMessage;
              state.isSaving = false;
            });

            throw error;
          }
        },

        deletePortfolio: async id => {
          set(state => {
            state.isLoading = true;
            state.error = null;
          });

          try {
            await apiCall(`/api/v1/portfolios/${id}`, {
              method: 'DELETE',
            });

            set(state => {
              state.portfolios = state.portfolios.filter(p => p.id !== id);
              if (state.currentPortfolio?.id === id) {
                state.currentPortfolio = null;
                state.currentPortfolioId = null;
              }
              state.isLoading = false;
            });
          } catch (error) {
            const errorMessage =
              error instanceof Error
                ? error.message
                : 'Failed to delete portfolio';
            logger.error('Failed to delete portfolio:', error as Error);

            set(state => {
              state.error = errorMessage;
              state.isLoading = false;
            });

            throw error;
          }
        },

        savePortfolio: async () => {
          const { currentPortfolio } = get();
          if (!currentPortfolio || !get().hasUnsavedChanges) return;

          try {
            await get().updatePortfolio(currentPortfolio.id, currentPortfolio);
          } catch (error) {
            // Error already handled in updatePortfolio
            throw error;
          }
        },

        updatePortfolioData: (field, value) => {
          set(state => {
            if (!state.currentPortfolio) return;

            // Save current state to history before updating
            if (state.hasUnsavedChanges === false) {
              const currentState = JSON.parse(
                JSON.stringify(state.currentPortfolio)
              );
              state.history = [
                ...state.history.slice(0, state.historyIndex + 1),
                currentState,
              ];
              state.historyIndex = state.history.length - 1;
            }

            // Handle nested data fields
            if (field.includes('.')) {
              const parts = field.split('.');
              let target: any = state.currentPortfolio;

              // Navigate to the parent of the field to update
              for (let i = 0; i < parts.length - 1; i++) {
                const part = parts[i];
                if (!part) continue;
                if (!target[part]) {
                  target[part] = {};
                }
                target = target[part];
              }

              // Update the final field
              const lastPart = parts[parts.length - 1];
              if (lastPart) {
                target[lastPart] = value;
              }
            } else {
              (state.currentPortfolio as any)[field] = value;
            }

            state.isEditing = true;
            state.hasUnsavedChanges = true;
          });
        },

        undo: () => {
          set(state => {
            if (state.historyIndex > 0 && state.currentPortfolio) {
              state.historyIndex--;
              const previousState = state.history[state.historyIndex];
              state.currentPortfolio = JSON.parse(
                JSON.stringify(previousState)
              );
              state.hasUnsavedChanges = true;
            }
          });
        },

        redo: () => {
          set(state => {
            if (
              state.historyIndex < state.history.length - 1 &&
              state.currentPortfolio
            ) {
              state.historyIndex++;
              const nextState = state.history[state.historyIndex];
              state.currentPortfolio = JSON.parse(JSON.stringify(nextState));
              state.hasUnsavedChanges = true;
            }
          });
        },

        get canUndo() {
          const state = get();
          return state.historyIndex > 0;
        },

        get canRedo() {
          const state = get();
          return state.historyIndex < state.history.length - 1;
        },

        resetPortfolios: () => set(() => initialState),
      }))
    ),
    {
      name: 'portfolio-store',
    }
  )
);

