import { NextRequest, NextResponse } from 'next/server';

/**
 * @fileoverview API Optimization Utilities
 *
 * Comprehensive suite of utilities for optimizing API performance, caching,
 * and improving response times across the PRISMA platform.
 *
 * Features:
 * - Response caching with multiple strategies
 * - Rate limiting and request throttling
 * - Database query optimization
 * - API response compression
 * - Request/response logging and monitoring
 * - Error handling and retry mechanisms
 *
 * @author PRISMA Development Team
 * @version 1.0.0
 */

// Cache configuration interfaces
export interface CacheConfig {
  strategy: 'memory' | 'redis' | 'database' | 'none';
  ttl: number; // Time to live in seconds
  key?: string;
  tags?: string[];
  revalidateOnStale?: boolean;
}

export interface RateLimitConfig {
  requests: number;
  window: number; // Window in seconds
  identifier?: (req: NextRequest) => string;
}

export interface APIMetrics {
  endpoint: string;
  method: string;
  statusCode: number;
  responseTime: number;
  timestamp: Date;
  userId?: string;
  userAgent?: string;
  ip?: string;
}

/**
 * In-Memory Cache Implementation
 *
 * A simple in-memory cache for development and small-scale deployments.
 * For production, consider Redis or other distributed caching solutions.
 */
class MemoryCache {
  private cache = new Map<
    string,
    { data: unknown; expiry: number; tags: string[] }
  >();
  private timers = new Map<string, NodeJS.Timeout>();

  set(key: string, data: unknown, ttl: number, tags: string[] = []): void {
    // Clear existing timer
    const existingTimer = this.timers.get(key);
    if (existingTimer) {
      clearTimeout(existingTimer);
    }

    const expiry = Date.now() + ttl * 1000;
    this.cache.set(key, { data, expiry, tags });

    // Set expiry timer
    const timer = setTimeout(() => {
      this.delete(key);
    }, ttl * 1000);
    this.timers.set(key, timer);
  }

  get(key: string): unknown | null {
    const entry = this.cache.get(key);
    if (!entry) return null;

    if (Date.now() > entry.expiry) {
      this.delete(key);
      return null;
    }

    return entry.data;
  }

  delete(key: string): boolean {
    const timer = this.timers.get(key);
    if (timer) {
      clearTimeout(timer);
      this.timers.delete(key);
    }
    return this.cache.delete(key);
  }

  invalidateByTag(tag: string): number {
    let count = 0;
    for (const [key, entry] of this.cache.entries()) {
      if (entry.tags.includes(tag)) {
        this.delete(key);
        count++;
      }
    }
    return count;
  }

  clear(): void {
    for (const timer of this.timers.values()) {
      clearTimeout(timer);
    }
    this.cache.clear();
    this.timers.clear();
  }

  size(): number {
    return this.cache.size;
  }

  stats(): { size: number; keys: string[] } {
    return {
      size: this.cache.size,
      keys: Array.from(this.cache.keys()),
    };
  }
}

// Global cache instance
const memoryCache = new MemoryCache();

/**
 * Rate Limiter Implementation
 *
 * Simple sliding window rate limiter for API endpoints.
 */
class RateLimiter {
  private requests = new Map<string, number[]>();

  isAllowed(identifier: string, config: RateLimitConfig): boolean {
    const now = Date.now();
    const windowStart = now - config.window * 1000;

    // Get existing requests for this identifier
    const userRequests = this.requests.get(identifier) || [];

    // Remove old requests outside the window
    const validRequests = userRequests.filter(
      timestamp => timestamp > windowStart
    );

    // Check if under limit
    if (validRequests.length >= config.requests) {
      return false;
    }

    // Add current request
    validRequests.push(now);
    this.requests.set(identifier, validRequests);

    return true;
  }

  getRemainingRequests(identifier: string, config: RateLimitConfig): number {
    const now = Date.now();
    const windowStart = now - config.window * 1000;
    const userRequests = this.requests.get(identifier) || [];
    const validRequests = userRequests.filter(
      timestamp => timestamp > windowStart
    );

    return Math.max(0, config.requests - validRequests.length);
  }

  getResetTime(identifier: string, config: RateLimitConfig): number {
    const userRequests = this.requests.get(identifier) || [];
    if (userRequests.length === 0) return 0;

    const oldestRequest = Math.min(...userRequests);
    return oldestRequest + config.window * 1000;
  }
}

const rateLimiter = new RateLimiter();

/**
 * API Metrics Collector
 */
class APIMetricsCollector {
  private metrics: APIMetrics[] = [];
  private maxMetrics = 1000; // Keep last 1000 requests

  record(metric: APIMetrics): void {
    this.metrics.push(metric);

    // Keep only the most recent metrics
    if (this.metrics.length > this.maxMetrics) {
      this.metrics = this.metrics.slice(-this.maxMetrics);
    }
  }

  getMetrics(options?: {
    endpoint?: string;
    timeRange?: { start: Date; end: Date };
    statusCode?: number;
  }): APIMetrics[] {
    let filtered = this.metrics;

    if (options?.endpoint) {
      filtered = filtered.filter(m => m.endpoint === options.endpoint);
    }

    if (options?.timeRange) {
      filtered = filtered.filter(
        m =>
          options.timeRange &&
          m.timestamp >= options.timeRange.start &&
          m.timestamp <= options.timeRange.end
      );
    }

    if (options?.statusCode) {
      filtered = filtered.filter(m => m.statusCode === options.statusCode);
    }

    return filtered;
  }

  getStats(): {
    totalRequests: number;
    averageResponseTime: number;
    errorRate: number;
    topEndpoints: Array<{ endpoint: string; count: number }>;
  } {
    const total = this.metrics.length;
    const avgResponseTime =
      total > 0
        ? this.metrics.reduce((sum, m) => sum + m.responseTime, 0) / total
        : 0;

    const errors = this.metrics.filter(m => m.statusCode >= 400).length;
    const errorRate = total > 0 ? (errors / total) * 100 : 0;

    // Count endpoint frequencies
    const endpointCounts = new Map<string, number>();
    this.metrics.forEach(m => {
      endpointCounts.set(m.endpoint, (endpointCounts.get(m.endpoint) || 0) + 1);
    });

    const topEndpoints = Array.from(endpointCounts.entries())
      .map(([endpoint, count]) => ({ endpoint, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 10);

    return {
      totalRequests: total,
      averageResponseTime: Math.round(avgResponseTime),
      errorRate: Math.round(errorRate * 100) / 100,
      topEndpoints,
    };
  }
}

const metricsCollector = new APIMetricsCollector();

/**
 * API Response Cache Middleware
 *
 * Caches API responses based on configuration.
 */
export function withCache(config: CacheConfig) {
  return function <T extends (...args: any[]) => Promise<NextResponse>>(
    handler: T
  ): T {
    return (async (req: NextRequest, ...args: any[]) => {
      // Skip caching for non-GET requests
      if (req.method !== 'GET' || config.strategy === 'none') {
        return handler(req, ...args);
      }

      // Generate cache key
      const cacheKey = config.key || `${req.url}_${req.method}`;

      // Try to get from cache
      const cached = memoryCache.get(cacheKey) as any;
      if (cached) {
        // Return cached response with cache headers
        const response = new NextResponse(cached.body, {
          status: cached.status,
          headers: {
            ...cached.headers,
            'X-Cache': 'HIT',
            'Cache-Control': `public, max-age=${config.ttl}`,
          },
        });
        return response;
      }

      // Execute handler
      const response = await handler(req, ...args);

      // Cache successful responses
      if (response.status >= 200 && response.status < 300) {
        const body = await response.text();
        const headers = Object.fromEntries(response.headers.entries());

        memoryCache.set(
          cacheKey,
          {
            body,
            status: response.status,
            headers,
          },
          config.ttl,
          config.tags || []
        );

        // Return response with cache headers
        return new NextResponse(body, {
          status: response.status,
          headers: {
            ...headers,
            'X-Cache': 'MISS',
            'Cache-Control': `public, max-age=${config.ttl}`,
          },
        });
      }

      return response;
    }) as T;
  };
}

/**
 * Rate Limiting Middleware
 *
 * Applies rate limiting to API endpoints.
 */
export function withRateLimit(config: RateLimitConfig) {
  return function <T extends (...args: any[]) => Promise<NextResponse>>(
    handler: T
  ): T {
    return (async (req: NextRequest, ...args: any[]) => {
      // Get identifier (IP address by default)
      const identifier = config.identifier
        ? config.identifier(req)
        : req.headers.get('x-forwarded-for') ||
          req.headers.get('x-real-ip') ||
          'anonymous';

      // Check rate limit
      if (!rateLimiter.isAllowed(identifier, config)) {
        const remaining = rateLimiter.getRemainingRequests(identifier, config);
        const resetTime = rateLimiter.getResetTime(identifier, config);

        return NextResponse.json(
          {
            error: 'Rate limit exceeded',
            retryAfter: Math.ceil((resetTime - Date.now()) / 1000),
          },
          {
            status: 429,
            headers: {
              'X-RateLimit-Limit': config.requests.toString(),
              'X-RateLimit-Remaining': remaining.toString(),
              'X-RateLimit-Reset': Math.ceil(resetTime / 1000).toString(),
              'Retry-After': Math.ceil(
                (resetTime - Date.now()) / 1000
              ).toString(),
            },
          }
        );
      }

      // Add rate limit headers to successful responses
      const response = await handler(req, ...args);
      const remaining = rateLimiter.getRemainingRequests(identifier, config);
      const resetTime = rateLimiter.getResetTime(identifier, config);

      response.headers.set('X-RateLimit-Limit', config.requests.toString());
      response.headers.set('X-RateLimit-Remaining', remaining.toString());
      response.headers.set(
        'X-RateLimit-Reset',
        Math.ceil(resetTime / 1000).toString()
      );

      return response;
    }) as T;
  };
}

/**
 * API Metrics Middleware
 *
 * Collects performance metrics for API endpoints.
 */
export function withMetrics() {
  return function <T extends (...args: any[]) => Promise<NextResponse>>(
    handler: T
  ): T {
    return (async (req: NextRequest, ...args: any[]) => {
      const startTime = Date.now();
      const endpoint = new URL(req.url).pathname;

      try {
        const response = await handler(req, ...args);
        const responseTime = Date.now() - startTime;

        // Record metrics
        metricsCollector.record({
          endpoint,
          method: req.method,
          statusCode: response.status,
          responseTime,
          timestamp: new Date(),
          userAgent: req.headers.get('user-agent') || undefined,
          ip:
            req.headers.get('x-forwarded-for') ||
            req.headers.get('x-real-ip') ||
            undefined,
        });

        // Add performance headers
        response.headers.set('X-Response-Time', `${responseTime}ms`);

        return response;
      } catch (error) {
        const responseTime = Date.now() - startTime;

        // Record error metrics
        metricsCollector.record({
          endpoint,
          method: req.method,
          statusCode: 500,
          responseTime,
          timestamp: new Date(),
          userAgent: req.headers.get('user-agent') || undefined,
          ip:
            req.headers.get('x-forwarded-for') ||
            req.headers.get('x-real-ip') ||
            undefined,
        });

        throw error;
      }
    }) as T;
  };
}

/**
 * Combined API Optimization Middleware
 *
 * Combines multiple optimization strategies into a single decorator.
 */
function withOptimization(options: {
  cache?: CacheConfig;
  rateLimit?: RateLimitConfig;
  metrics?: boolean;
}) {
  return function <T extends (...args: any[]) => Promise<NextResponse>>(
    handler: T
  ): T {
    let optimizedHandler = handler;

    // Apply metrics first (innermost)
    if (options.metrics !== false) {
      optimizedHandler = withMetrics()(optimizedHandler);
    }

    // Apply rate limiting
    if (options.rateLimit) {
      optimizedHandler = withRateLimit(options.rateLimit)(optimizedHandler);
    }

    // Apply caching (outermost)
    if (options.cache) {
      optimizedHandler = withCache(options.cache)(optimizedHandler);
    }

    return optimizedHandler;
  };
}

/**
 * Database Query Optimization Utilities
 */
class QueryOptimizer {
  /**
   * Batch multiple database queries
   */
  static batchQueries<T>(queries: (() => Promise<T>)[]): Promise<T[]> {
    return Promise.all(queries.map(query => query()));
  }

  /**
   * Paginate query results
   */
  static getPaginationParams(searchParams: URLSearchParams): {
    page: number;
    limit: number;
    offset: number;
  } {
    const page = Math.max(1, parseInt(searchParams.get('page') || '1'));
    const limit = Math.min(
      100,
      Math.max(1, parseInt(searchParams.get('limit') || '10'))
    );
    const offset = (page - 1) * limit;

    return { page, limit, offset };
  }

  /**
   * Build pagination response
   */
  static buildPaginatedResponse<T>(
    data: T[],
    paginationOptions: {
      total: number;
      page: number;
      limit: number;
      baseUrl?: string;
    }
  ) {
    const { total, page, limit, baseUrl } = paginationOptions;
    const totalPages = Math.ceil(total / limit);
    const hasNext = page < totalPages;
    const hasPrev = page > 1;

    return {
      data,
      pagination: {
        page,
        limit,
        total,
        totalPages,
        hasNext,
        hasPrev,
        ...(baseUrl && {
          nextUrl: hasNext
            ? `${baseUrl}?page=${page + 1}&limit=${limit}`
            : null,
          prevUrl: hasPrev
            ? `${baseUrl}?page=${page - 1}&limit=${limit}`
            : null,
        }),
      },
    };
  }
}

/**
 * Get API metrics and cache stats
 */
function getAPIStats() {
  return {
    metrics: metricsCollector.getStats(),
    cache: memoryCache.stats(),
  };
}

/**
 * Clear all caches
 */
function clearCaches() {
  memoryCache.clear();
}

/**
 * Invalidate cache by tag
 */
function invalidateCache(tag: string): number {
  return memoryCache.invalidateByTag(tag);
}

export { memoryCache, rateLimiter, metricsCollector };

import crypto from 'crypto';

import { logger } from '@/lib/utils/logger';

/**
 * Cryptographic utilities for secure data storage
 * Implements AES-256-GCM encryption for sensitive data like API tokens
 */

const algorithm = 'aes-256-gcm';

// Store generated keys in memory for consistency during runtime
let devEncryptionKey: Buffer | null = null;

/**
 * Get or generate the encryption key from environment
 * In production, this should be a stable key stored in environment variables
 */
function getEncryptionKey(): Buffer {
  const envKey = process.env.ENCRYPTION_KEY;

  if (!envKey) {
    // In production, this should never happen
    if (process.env.NODE_ENV === 'production') {
      logger.error(
        'CRITICAL: ENCRYPTION_KEY not set in production environment'
      );
      throw new Error('ENCRYPTION_KEY must be set in production environment');
    }

    // In development, generate a random key per instance but keep it consistent during runtime
    if (!devEncryptionKey) {
      logger.warn(
        'ENCRYPTION_KEY not found in environment variables. ' +
          'Generating a random key for development. ' +
          'Data will be lost on restart. Set ENCRYPTION_KEY for persistence!'
      );

      // Generate a cryptographically secure random key
      devEncryptionKey = crypto.randomBytes(32);

      // Also log the generated key so developers can set it if needed
      logger.info(
        `Generated development encryption key: ${devEncryptionKey.toString('hex')}\n` +
          'To persist data across restarts, add this to your .env.local:\n' +
          `ENCRYPTION_KEY=${devEncryptionKey.toString('hex')}`
      );
    }

    return devEncryptionKey;
  }

  // Validate key format and length
  if (!/^[0-9a-fA-F]{64}$/.test(envKey)) {
    throw new Error(
      'ENCRYPTION_KEY must be a 64-character hexadecimal string (32 bytes)'
    );
  }

  // Convert hex string to buffer
  return Buffer.from(envKey, 'hex');
}

/**
 * Encrypt a string using AES-256-GCM
 * @param text - The plain text to encrypt
 * @returns Object containing encrypted data, IV, and auth tag
 */
export function encrypt(text: string): {
  encrypted: string;
  iv: string;
  tag: string;
} {
  const key = getEncryptionKey();
  const iv = crypto.randomBytes(16);
  const cipher = crypto.createCipheriv(algorithm, key, iv);

  let encrypted = cipher.update(text, 'utf8', 'hex');
  encrypted += cipher.final('hex');

  const tag = cipher.getAuthTag();

  return {
    encrypted,
    iv: iv.toString('hex'),
    tag: tag.toString('hex'),
  };
}

/**
 * Decrypt data encrypted with the encrypt function
 * @param encryptedData - Object containing encrypted data, IV, and auth tag
 * @returns The decrypted plain text
 */
export function decrypt(encryptedData: {
  encrypted: string;
  iv: string;
  tag: string;
}): string {
  const key = getEncryptionKey();
  const decipher = crypto.createDecipheriv(
    algorithm,
    key,
    Buffer.from(encryptedData.iv, 'hex')
  );

  decipher.setAuthTag(Buffer.from(encryptedData.tag, 'hex'));

  let decrypted = decipher.update(encryptedData.encrypted, 'hex', 'utf8');
  decrypted += decipher.final('utf8');

  return decrypted;
}

/**
 * Generate a secure encryption key for production use
 * This should be run once during setup and the result stored securely
 */
function generateEncryptionKey(): string {
  return crypto.randomBytes(32).toString('hex');
}

/**
 * Rotate encryption key by re-encrypting data with a new key
 * This should be used periodically for security
 * @param oldKey - The current encryption key
 * @param newKey - The new encryption key to rotate to
 * @param encryptedData - The data to re-encrypt
 * @returns The re-encrypted data with the new key
 */
function rotateEncryptionKey(
  oldKey: Buffer,
  newKey: Buffer,
  encryptedData: { encrypted: string; iv: string; tag: string }
): { encrypted: string; iv: string; tag: string } {
  // Temporarily override the key getter to use the old key
  const originalKey = process.env.ENCRYPTION_KEY;
  process.env.ENCRYPTION_KEY = oldKey.toString('hex');

  try {
    // Decrypt with old key
    const plainText = decrypt(encryptedData);

    // Set new key
    process.env.ENCRYPTION_KEY = newKey.toString('hex');

    // Encrypt with new key
    return encrypt(plainText);
  } finally {
    // Restore original key
    if (originalKey) {
      process.env.ENCRYPTION_KEY = originalKey;
    } else {
      delete process.env.ENCRYPTION_KEY;
    }
  }
}

/**
 * Safely encrypt data with error handling
 * @param text - The text to encrypt
 * @returns Encrypted data or null if encryption fails
 */
function safeEncrypt(text: string): {
  encrypted: string;
  iv: string;
  tag: string;
} | null {
  try {
    return encrypt(text);
  } catch (error) {
    logger.error('Encryption failed', { error });
    return null;
  }
}

/**
 * Safely decrypt data with error handling
 * @param encryptedData - The encrypted data object
 * @returns Decrypted text or null if decryption fails
 */
function safeDecrypt(encryptedData: {
  encrypted: string;
  iv: string;
  tag: string;
}): string | null {
  try {
    return decrypt(encryptedData);
  } catch (error) {
    logger.error('Decryption failed', { error });
    return null;
  }
}

import { getCSRFToken } from '@/middleware/csrf-enhanced';

/**
 * Client-side CSRF token utilities
 */

/**
 * Fetch wrapper that automatically includes CSRF token
 */
function fetchWithCSRF(
  url: string,
  options: RequestInit = {}
): Promise<Response> {
  const csrfToken = getCSRFToken();

  // Add CSRF token to headers if it's a state-changing request
  const method = options.method?.toUpperCase() || 'GET';
  const requiresCSRF = ['POST', 'PUT', 'PATCH', 'DELETE'].includes(method);

  if (requiresCSRF && csrfToken) {
    options.headers = {
      ...options.headers,
      'x-csrf-token': csrfToken,
    };
  }

  return fetch(url, options);
}

/**
 * Hook to get CSRF token (for React components)
 */
function useCSRFToken(): string | null {
  if (typeof window === 'undefined') return null;
  return getCSRFToken();
}

/**
 * Add CSRF token to form data
 */
function addCSRFToFormData(formData: FormData): FormData {
  const csrfToken = getCSRFToken();
  if (csrfToken) {
    formData.append('_csrf', csrfToken);
  }
  return formData;
}

/**
 * Get headers with CSRF token included
 */
export function getCSRFHeaders(additionalHeaders?: HeadersInit): HeadersInit {
  const csrfToken = getCSRFToken();
  const headers: Record<string, string> = {};

  if (csrfToken) {
    headers['x-csrf-token'] = csrfToken;
  }

  // Merge with additional headers
  if (additionalHeaders) {
    if (additionalHeaders instanceof Headers) {
      additionalHeaders.forEach((value, key) => {
        headers[key] = value;
      });
    } else if (Array.isArray(additionalHeaders)) {
      additionalHeaders.forEach(([key, value]) => {
        headers[key] = value;
      });
    } else {
      Object.assign(headers, additionalHeaders);
    }
  }

  return headers;
}

/**
 * Date utility functions
 *
 * Lightweight date formatting utilities that replace date-fns
 * to reduce bundle size by ~38MB
 */

/**
 * Format distance to now (replacement for date-fns formatDistanceToNow)
 *
 * @param date - The date to format
 * @param options - Options for formatting
 * @returns Formatted string like "2 hours ago", "3 days ago", etc.
 */
export function formatDistanceToNow(
  date: Date | string | number,
  options?: { addSuffix?: boolean }
): string {
  const now = new Date();
  const then = new Date(date);
  const seconds = Math.floor((now.getTime() - then.getTime()) / 1000);

  const intervals = [
    { label: 'year', seconds: 31536000 },
    { label: 'month', seconds: 2592000 },
    { label: 'week', seconds: 604800 },
    { label: 'day', seconds: 86400 },
    { label: 'hour', seconds: 3600 },
    { label: 'minute', seconds: 60 },
    { label: 'second', seconds: 1 },
  ];

  for (const interval of intervals) {
    const count = Math.floor(seconds / interval.seconds);
    if (count >= 1) {
      const plural = count !== 1 ? 's' : '';
      const distance = `${count} ${interval.label}${plural}`;
      return options?.addSuffix ? `${distance} ago` : distance;
    }
  }

  return options?.addSuffix ? 'just now' : '0 seconds';
}

/**
 * Format date (replacement for date-fns format)
 *
 * @param date - The date to format
 * @param formatStr - Format string (supports common patterns)
 * @returns Formatted date string
 */
export function format(
  date: Date | string | number,
  formatStr: string
): string {
  const d = new Date(date);

  // Common format patterns
  const formatMap: Record<string, string> = {
    'MMM d, yyyy': d.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    }),
    'MM/dd/yyyy': d.toLocaleDateString('en-US', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
    }),
    'MMM d': d.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
    }),
    'MMMM yyyy': d.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
    }),
    'HH:mm': d.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: false,
    }),
  };

  return formatMap[formatStr] || d.toLocaleDateString();
}

/**
 * Parse ISO string to Date
 *
 * @param isoString - ISO date string
 * @returns Date object
 */
export function parseISO(isoString: string): Date {
  return new Date(isoString);
}

/**
 * Check if date is valid
 *
 * @param date - Date to check
 * @returns true if valid date
 */
export function isValid(date: any): boolean {
  return date instanceof Date && !isNaN(date.getTime());
}

/**
 * Add days to date
 *
 * @param date - Starting date
 * @param days - Number of days to add
 * @returns New date
 */
export function addDays(date: Date, days: number): Date {
  const result = new Date(date);
  result.setDate(result.getDate() + days);
  return result;
}

/**
 * Subtract days from date
 *
 * @param date - Starting date
 * @param days - Number of days to subtract
 * @returns New date
 */
export function subDays(date: Date, days: number): Date {
  return addDays(date, -days);
}

/**
 * Get start of day
 *
 * @param date - Input date
 * @returns Date at start of day (00:00:00)
 */
export function startOfDay(date: Date): Date {
  const result = new Date(date);
  result.setHours(0, 0, 0, 0);
  return result;
}

/**
 * Get end of day
 *
 * @param date - Input date
 * @returns Date at end of day (23:59:59.999)
 */
export function endOfDay(date: Date): Date {
  const result = new Date(date);
  result.setHours(23, 59, 59, 999);
  return result;
}

/**
 * Check if date is before another date
 *
 * @param date - Date to check
 * @param dateToCompare - Date to compare against
 * @returns true if date is before dateToCompare
 */
export function isBefore(date: Date, dateToCompare: Date): boolean {
  return date.getTime() < dateToCompare.getTime();
}

/**
 * Check if date is after another date
 *
 * @param date - Date to check
 * @param dateToCompare - Date to compare against
 * @returns true if date is after dateToCompare
 */
export function isAfter(date: Date, dateToCompare: Date): boolean {
  return date.getTime() > dateToCompare.getTime();
}

/**
 * Get current year
 *
 * @returns Current year as number
 */
export function getCurrentYear(): number {
  return new Date().getFullYear();
}

/**
 * Get promotional deadline (30 days from now)
 *
 * @returns Date string for promotional deadline
 */
export function getPromotionalDeadline(): string {
  const deadline = addDays(new Date(), 30);
  return format(deadline, 'MMMM yyyy');
}

/**
 * Get last updated date formatted
 *
 * @returns Formatted date string
 */
export function getLastUpdatedDate(): string {
  return format(new Date(), 'MMM d, yyyy');
}

/**
 * Get last updated date in Spanish
 *
 * @returns Formatted date string in Spanish
 */
export function getLastUpdatedDateSpanish(): string {
  const date = new Date();
  return date.toLocaleDateString('es-ES', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });
}

/**
 * Dynamic Import Utilities
 *
 * Helpers for optimizing bundle size through dynamic imports
 * and code splitting strategies
 */

import dynamic from 'next/dynamic';

import type { ComponentType, ReactNode } from 'react';

/**
 * Default loading function for heavy modules
 */
const defaultLoadingFn = () => null;

/**
 * Create a dynamically imported component with loading state
 *
 * @param importFn - Function that returns the component import
 * @param options - Dynamic import options
 * @returns Dynamically imported component
 */
export function createDynamicComponent<P = {}>(
  importFn: () => Promise<{ default: ComponentType<P> }>,
  options?: {
    loading?: () => ReactNode;
    ssr?: boolean;
  }
): ComponentType<P> {
  return dynamic(importFn, {
    loading: options?.loading || defaultLoadingFn,
    ssr: options?.ssr ?? true,
  });
}

/**
 * Preload a dynamic component
 *
 * Useful for preloading components that will be needed soon
 * but not immediately (e.g., on hover, on route change)
 *
 * @param importFn - Function that returns the component import
 */
export function preloadComponent(
  importFn: () => Promise<{ default: ComponentType<any> }>
): void {
  // Simply calling the import function will trigger the download
  importFn().catch(() => {
    // Silently fail if preload fails
  });
}

/**
 * Create a route-based dynamic component
 *
 * Automatically handles loading states and error boundaries
 * for route-level components
 */
export function createRouteComponent<P = {}>(
  importFn: () => Promise<{ default: ComponentType<P> }>,
  options?: {
    fallback?: () => ReactNode;
  }
): ComponentType<P> {
  return dynamic(importFn, {
    loading: options?.fallback || defaultLoadingFn,
    ssr: true,
  });
}

