/**
 * Language detection result interface
 */
export interface LanguageDetectionResult {
  /** Detected language code */
  language: 'es' | 'en';
  /** Country code (if detected) */
  countryCode?: string;
  /** Country flag emoji */
  flag: string;
  /** Detection method used */
  method: 'geolocation' | 'timezone' | 'browser' | 'fallback';
  /** Whether detection was successful */
  confident: boolean;
}

/**
 * Detects user's country from IP geolocation using a free service
 *
 * @returns Promise resolving to country code or null if detection fails
 */
export async function detectCountryFromIP(): Promise<string | null> {
  try {
    // Using ipapi.co for free IP geolocation (1000 requests/day limit)
    const response = await fetch('https://ipapi.co/country/', {
      method: 'GET',
      headers: {
        Accept: 'text/plain',
      },
    });

    if (response.ok) {
      const countryCode = (await response.text()).trim().toUpperCase();

      // Validate country code format (2 characters)
      if (countryCode.length === 2 && /^[A-Z]{2}$/.test(countryCode)) {
        return countryCode;
      }
    }
  } catch (error) {
    // IP geolocation failed - this is expected in many environments
    // Only log in development to avoid console noise
    if (process.env.NODE_ENV === 'development') {
      logger.debug('IP geolocation detection failed:', { error });
    }
  }

  return null;
}

/**
 * Detects user's likely country from timezone
 * Less accurate but works as fallback when IP detection fails
 *
 * @returns Estimated country code or null
 */
export function detectCountryFromTimezone(): string | null {
  try {
    const timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    // Common timezone to country mappings for our target markets
    const timezoneCountryMap: Record<string, string> = {
      // Spanish-speaking countries
      'America/Mexico_City': 'MX',
      'America/Cancun': 'MX',
      'America/Merida': 'MX',
      'America/Monterrey': 'MX',
      'America/Matamoros': 'MX',
      'America/Mazatlan': 'MX',
      'America/Chihuahua': 'MX',
      'America/Hermosillo': 'MX',
      'America/Tijuana': 'MX',
      'America/Buenos_Aires': 'AR',
      'America/Argentina/Buenos_Aires': 'AR',
      'America/Bogota': 'CO',
      'America/Lima': 'PE',
      'America/Santiago': 'CL',
      'America/Caracas': 'VE',
      'Europe/Madrid': 'ES',

      // English-speaking countries
      'America/New_York': 'US',
      'America/Chicago': 'US',
      'America/Denver': 'US',
      'America/Los_Angeles': 'US',
      'America/Toronto': 'CA',
      'America/Vancouver': 'CA',
      'Europe/London': 'GB',
      'Australia/Sydney': 'AU',
      'Australia/Melbourne': 'AU',
      'Pacific/Auckland': 'NZ',
    };

    return timezoneCountryMap[timezone] || null;
  } catch (_error) {
    // Timezone detection failed - this is expected in some environments
    return null;
  }
}

/**
 * Detects user's language from browser settings
 *
 * @returns Detected language code or null
 */
export function detectLanguageFromBrowser(): 'es' | 'en' | null {
  try {
    // Get browser language preferences
    const browserLanguages = [
      navigator.language,
      ...(navigator.languages || []),
    ];

    for (const lang of browserLanguages) {
      const langCode = lang.toLowerCase().split('-')[0];

      if (langCode === 'es' || langCode === 'spa') {
        return 'es';
      }
      if (langCode === 'en' || langCode === 'eng') {
        return 'en';
      }
    }

    return null;
  } catch (_error) {
    // Browser language detection failed - this is expected in some environments
    return null;
  }
}

/**
 * Determines appropriate language based on country code
 *
 * @param countryCode - ISO 3166-1 alpha-2 country code
 * @returns Language code and confidence level
 */
export function getLanguageFromCountry(countryCode: string): {
  language: 'es' | 'en';
  confident: boolean;
} {
  if (SPANISH_SPEAKING_COUNTRIES.includes(countryCode)) {
    return { language: 'es', confident: true };
  }

  if (ENGLISH_SPEAKING_COUNTRIES.includes(countryCode)) {
    return { language: 'en', confident: true };
  }

  // For unknown countries, fallback to Spanish (MADFAM's primary market)
  return { language: 'es', confident: false };
}

/**
 * Gets appropriate flag emoji for country or language
 *
 * @param countryCode - Country code (optional)
 * @param language - Language code
 * @returns Flag emoji
 */
export function getFlag(
  countryCode: string | undefined,
  language: 'es' | 'en'
): string {
  if (countryCode && COUNTRY_FLAGS[countryCode]) {
    return COUNTRY_FLAGS[countryCode];
  }

  // Default flag for language
  const defaultFlag =
    language === 'es'
      ? COUNTRY_FLAGS['ES_DEFAULT']
      : COUNTRY_FLAGS['EN_DEFAULT'];
  return defaultFlag || ''; // Fallback to Mexican flag if undefined
}

/**
 * Detects user's preferred language based on geographic location and browser settings
 *
 * Uses multiple detection methods in order of reliability:
 * 1. IP-based geolocation (most accurate for location)
 * 2. Timezone detection (fallback geolocation)
 * 3. Browser language preferences (user setting)
 * 4. Spanish fallback (MADFAM's primary market)
 *
 * @returns Promise resolving to language detection result
 */
async function detectUserLanguage(): Promise<LanguageDetectionResult> {
  // Method 1: Try IP-based geolocation first
  try {
    const countryCode = await detectCountryFromIP();
    if (countryCode) {
      const { language, confident } = getLanguageFromCountry(countryCode);

      return {
        language,
        countryCode,
        flag: getFlag(countryCode, language),
        method: 'geolocation',
        confident,
      };
    }
  } catch (_error) {
    // IP detection failed, trying timezone detection
  }

  // Method 2: Fallback to timezone detection
  const timezoneCountry = detectCountryFromTimezone();
  if (timezoneCountry) {
    const { language, confident } = getLanguageFromCountry(timezoneCountry);

    return {
      language,
      countryCode: timezoneCountry,
      flag: getFlag(timezoneCountry, language),
      method: 'timezone',
      confident,
    };
  }

  // Method 3: Try browser language detection
  const browserLanguage = detectLanguageFromBrowser();
  if (browserLanguage) {
    return {
      language: browserLanguage,
      flag: getFlag(undefined, browserLanguage),
      method: 'browser',
      confident: true,
    };
  }

  // Method 4: Final fallback to Spanish (MADFAM's primary market)
  return {
    language: 'es',
    flag: COUNTRY_FLAGS['ES_DEFAULT'] || '',
    method: 'fallback',
    confident: false,
  };
}

/**
 * Quick synchronous language detection for immediate UI rendering
 * Uses only browser settings and timezone (no async IP detection)
 *
 * @returns Language detection result (synchronous)
 */
function detectUserLanguageSync(): LanguageDetectionResult {
  // Try timezone detection first
  const timezoneCountry = detectCountryFromTimezone();
  if (timezoneCountry) {
    const { language, confident } = getLanguageFromCountry(timezoneCountry);

    return {
      language,
      countryCode: timezoneCountry,
      flag: getFlag(timezoneCountry, language),
      method: 'timezone',
      confident,
    };
  }

  // Try browser language detection
  const browserLanguage = detectLanguageFromBrowser();
  if (browserLanguage) {
    return {
      language: browserLanguage,
      flag: getFlag(undefined, browserLanguage),
      method: 'browser',
      confident: true,
    };
  }

  // Fallback to Spanish
  return {
    language: 'es',
    flag: COUNTRY_FLAGS['ES_DEFAULT'] || '',
    method: 'fallback',
    confident: false,
  };
}

/**
 * @fileoverview Structured logging utility for PRISMA
 *
 * Provides structured logging with context support, error serialization,
 * and production-ready features. In production, logs can be sent to
 * external services like Sentry, LogRocket, or DataDog.
 *
 * @author PRISMA Development Team
 * @version 1.0.0
 */

type LogLevel = 'debug' | 'info' | 'warn' | 'error';

interface LogContext {
  userId?: string;
  requestId?: string;
  sessionId?: string;
  feature?: string;
  action?: string;
  metadata?: Record<string, any>;
  [key: string]: unknown;
}

interface LogEntry {
  timestamp: string;
  level: LogLevel;
  message: string;
  context?: LogContext;
  error?: {
    message: string;
    stack?: string;
    code?: string;
  };
}

class Logger {
  private isDevelopment = process.env.NODE_ENV === 'development';
  private isTest = process.env.NODE_ENV === 'test';
  private debugEnabled =
    this.isDevelopment || process.env.ENABLE_DEBUG === 'true';
  private serviceName = 'prisma-portfolio-builder';

  /**
   * Format log entry for output
   */
  private formatLogEntry(entry: LogEntry): string {
    if (this.isDevelopment) {
      // Human-readable format for development
      const prefix = `[${entry.timestamp}] [${entry.level.toUpperCase()}] ${entry.message}`;
      const contextStr = entry.context
        ? ` | ${JSON.stringify(entry.context)}`
        : '';
      const errorStr = entry.error
        ? `\nError: ${entry.error.message}\n${entry.error.stack || ''}`
        : '';
      return `${prefix}${contextStr}${errorStr}`;
    } else {
      // JSON format for production (easier to parse by log aggregators)
      return JSON.stringify({
        ...entry,
        service: this.serviceName,
        environment: process.env.NODE_ENV,
      });
    }
  }

  /**
   * Send log to external service in production
   */
  private sendToExternalService(entry: LogEntry): void {
    // Silence unused parameter warning until implementation
    void entry;
    // In production, send to logging service
    // Example: Sentry, LogRocket, DataDog, etc.
    if (!this.isDevelopment && !this.isTest) {
      // External logging service integration would go here
      // Example with Sentry:
      // if (entry.level === 'error' && entry.error) {
      //   Sentry.captureException(new Error(entry.error.message), {
      //     contexts: { log: entry.context },
      //   });
      // }
    }
  }

  /**
   * Core logging method
   */
  private log(
    level: LogLevel,
    message: string,
    context?: LogContext | Error
  ): void {
    // Skip debug logs in production unless explicitly enabled
    if (level === 'debug' && !this.debugEnabled) {
      return;
    }

    // Skip all logs in test environment to keep test output clean
    if (this.isTest && process.env.SHOW_LOGS !== 'true') {
      return;
    }

    const entry: LogEntry = {
      timestamp: new Date().toISOString(),
      level,
      message,
    };

    // Handle Error objects
    if (context instanceof Error) {
      entry.error = {
        message: context.message,
        stack: context.stack,
        code: (context as any).code,
      };
    } else if (context) {
      entry.context = context;
    }

    // Format and output
    const formatted = this.formatLogEntry(entry);

    // Use appropriate console method
    switch (level) {
      case 'debug':
        console.debug(formatted);
        break;
      case 'info':
        console.info(formatted);
        break;
      case 'warn':
        console.warn(formatted);
        break;
      case 'error':
        console.error(formatted);
        break;
    }

    // Send to external service
    this.sendToExternalService(entry);
  }

  /**
   * Debug logging - only in development or when explicitly enabled
   */
  debug(message: string, context?: LogContext): void {
    this.log('debug', message, context);
  }

  /**
   * Info logging - development and production
   */
  info(message: string, context?: LogContext): void {
    this.log('info', message, context);
  }

  /**
   * Warning logging - always enabled
   */
  warn(message: string, context?: LogContext): void {
    this.log('warn', message, context);
  }

  /**
   * Error logging - always enabled
   */
  error(
    message: string,
    error?: Error | LogContext,
    context?: LogContext
  ): void {
    if (error instanceof Error) {
      this.log('error', message, error);
    } else {
      this.log('error', message, error || context);
    }
  }

  /**
   * Create a child logger with persistent context
   */
  child(context: LogContext): Logger {
    const parent = this;
    return {
      debug: (message: string, additionalContext?: LogContext) =>
        parent.debug(message, { ...context, ...additionalContext }),
      info: (message: string, additionalContext?: LogContext) =>
        parent.info(message, { ...context, ...additionalContext }),
      warn: (message: string, additionalContext?: LogContext) =>
        parent.warn(message, { ...context, ...additionalContext }),
      error: (
        message: string,
        error?: Error | LogContext,
        additionalContext?: LogContext
      ) => {
        if (error instanceof Error) {
          parent.error(message, error, { ...context, ...additionalContext });
        } else {
          parent.error(message, { ...context, ...error, ...additionalContext });
        }
      },
      child: (additionalContext: LogContext) =>
        parent.child({ ...context, ...additionalContext }),
    } as Logger;
  }
}

// Export singleton instance
export const logger = new Logger();

// Convenience exports
export const debug = logger.debug.bind(logger);
export const info = logger.info.bind(logger);
export const warn = logger.warn.bind(logger);
export const error = logger.error.bind(logger);

import React from 'react';

import { logger } from '@/lib/utils/logger';
/**
 * @fileoverview Performance Monitoring and Optimization Utilities
 *
 * A comprehensive suite of performance monitoring tools for the PRISMA platform.
 * Provides real-time metrics, performance profiling, and optimization suggestions.
 *
 * Features:
 * - Core Web Vitals monitoring (LCP, FID, CLS)
 * - Bundle size analysis and optimization recommendations
 * - Component render performance tracking
 * - Memory usage monitoring
 * - Network performance analysis
 * - User experience metrics
 *
 * @author PRISMA Development Team
 * @version 1.0.0
 */

// Performance metric interfaces
export interface PerformanceMetrics {
  /** Largest Contentful Paint - measures loading performance */
  lcp?: number;
  /** First Input Delay - measures interactivity */
  fid?: number;
  /** Cumulative Layout Shift - measures visual stability */
  cls?: number;
  /** Time to First Byte */
  ttfb?: number;
  /** First Contentful Paint */
  fcp?: number;
  /** Total Blocking Time */
  tbt?: number;
  /** Speed Index */
  si?: number;
}

export interface ComponentPerformance {
  componentName: string;
  renderTime: number;
  renderCount: number;
  averageRenderTime: number;
  lastRenderTimestamp: number;
  memoryUsage?: number;
}

export interface BundleAnalysis {
  totalSize: number;
  compressedSize: number;
  chunkSizes: Record<string, number>;
  largestChunks: Array<{ name: string; size: number }>;
  recommendations: string[];
}

/**
 * Performance Monitor Class
 *
 * Central hub for collecting and analyzing performance metrics.
 * Provides both real-time monitoring and historical analysis.
 */
export class PerformanceMonitor {
  private static instance: PerformanceMonitor;
  private metrics: PerformanceMetrics = {};
  private componentMetrics: Map<string, ComponentPerformance> = new Map();
  private observers: Map<string, PerformanceObserver> = new Map();
  private isEnabled: boolean;

  private constructor() {
    this.isEnabled =
      process.env.NODE_ENV === 'development' ||
      process.env.NEXT_PUBLIC_ENABLE_PERFORMANCE_MONITORING === 'true';

    if (this.isEnabled && typeof window !== 'undefined') {
      this.initializeObservers();
    }
  }

  /**
   * Get singleton instance of PerformanceMonitor
   */
  public static getInstance(): PerformanceMonitor {
    if (!PerformanceMonitor.instance) {
      PerformanceMonitor.instance = new PerformanceMonitor();
    }
    return PerformanceMonitor.instance;
  }

  /**
   * Initialize performance observers for Core Web Vitals
   */
  private initializeObservers(): void {
    if (!window.PerformanceObserver) return;

    // Largest Contentful Paint
    try {
      const lcpObserver = new PerformanceObserver(list => {
        const entries = list.getEntries();
        const lastEntry = entries[entries.length - 1] as PerformanceEntry & {
          startTime: number;
        };
        this.metrics.lcp = lastEntry.startTime;
        this.reportMetric('lcp', lastEntry.startTime);
      });
      lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });
      this.observers.set('lcp', lcpObserver);
    } catch (error) {
      logger.warn('LCP observer not supported:', { error });
    }

    // First Input Delay
    try {
      const fidObserver = new PerformanceObserver(list => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          this.metrics.fid = entry.processingStart - entry.startTime;
          this.reportMetric('fid', this.metrics.fid);
        });
      });
      fidObserver.observe({ entryTypes: ['first-input'] });
      this.observers.set('fid', fidObserver);
    } catch (error) {
      logger.warn('FID observer not supported:', { error });
    }

    // Cumulative Layout Shift
    try {
      let clsValue = 0;
      const clsObserver = new PerformanceObserver(list => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          if (!entry.hadRecentInput) {
            clsValue += entry.value;
            this.metrics.cls = clsValue;
            this.reportMetric('cls', clsValue);
          }
        });
      });
      clsObserver.observe({ entryTypes: ['layout-shift'] });
      this.observers.set('cls', clsObserver);
    } catch (error) {
      logger.warn('CLS observer not supported:', { error });
    }

    // Navigation timing
    this.observeNavigationTiming();
  }

  /**
   * Observe navigation timing metrics
   */
  private observeNavigationTiming(): void {
    if (!window.performance?.getEntriesByType) return;

    setTimeout(() => {
      const navigation = window.performance.getEntriesByType(
        'navigation'
      )[0] as PerformanceNavigationTiming;
      if (navigation) {
        this.metrics.ttfb = navigation.responseStart - navigation.requestStart;
        this.metrics.fcp = navigation.loadEventStart - navigation.fetchStart;

        this.reportMetric('ttfb', this.metrics.ttfb);
        this.reportMetric('fcp', this.metrics.fcp);
      }
    }, 0);
  }

  /**
   * Track component render performance
   */
  public trackComponentRender(componentName: string, renderTime: number): void {
    if (!this.isEnabled) return;

    const existing = this.componentMetrics.get(componentName);
    if (existing) {
      existing.renderCount++;
      existing.renderTime += renderTime;
      existing.averageRenderTime = existing.renderTime / existing.renderCount;
      existing.lastRenderTimestamp = Date.now();
    } else {
      this.componentMetrics.set(componentName, {
        componentName,
        renderTime,
        renderCount: 1,
        averageRenderTime: renderTime,
        lastRenderTimestamp: Date.now(),
      });
    }

    // Warn about slow components
    if (renderTime > 16) {
      // 60fps threshold
      if (process.env.NODE_ENV === 'development') {
        console.warn(
          `Slow component render: ${componentName} took ${renderTime.toFixed(2)}ms`
        );
      }
    }
  }

  /**
   * Get current performance metrics
   */
  public getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  /**
   * Get component performance data
   */
  public getComponentMetrics(): ComponentPerformance[] {
    return Array.from(this.componentMetrics.values());
  }

  /**
   * Get performance score based on Core Web Vitals
   */
  public getPerformanceScore(): number {
    const { lcp, fid, cls } = this.metrics;
    let score = 100;

    // LCP scoring (Good: <2.5s, Needs Improvement: 2.5-4s, Poor: >4s)
    if (lcp) {
      if (lcp > 4000) score -= 40;
      else if (lcp > 2500) score -= 20;
    }

    // FID scoring (Good: <100ms, Needs Improvement: 100-300ms, Poor: >300ms)
    if (fid) {
      if (fid > 300) score -= 30;
      else if (fid > 100) score -= 15;
    }

    // CLS scoring (Good: <0.1, Needs Improvement: 0.1-0.25, Poor: >0.25)
    if (cls) {
      if (cls > 0.25) score -= 30;
      else if (cls > 0.1) score -= 15;
    }

    return Math.max(0, score);
  }

  /**
   * Report metric to external services (development helper)
   */
  private reportMetric(name: string, value: number): void {
    if (process.env.NODE_ENV === 'development') {
      console.log(
        ` Performance Metric: ${name} = ${value.toFixed(2)}${this.getMetricUnit(name)}`
      );
    }

    // In production, send to analytics service
    // analytics.track('performance_metric', { name, value });
  }

  /**
   * Get unit for metric display
   */
  private getMetricUnit(metricName: string): string {
    switch (metricName) {
      case 'cls':
        return '';
      case 'lcp':
      case 'fid':
      case 'ttfb':
      case 'fcp':
        return 'ms';
      default:
        return '';
    }
  }

  /**
   * Clean up observers
   */
  public destroy(): void {
    this.observers.forEach(observer => observer.disconnect());
    this.observers.clear();
  }
}

/**
 * React Hook for Component Performance Tracking
 *
 * Automatically tracks render performance for React components.
 */
export function usePerformanceTracking(componentName: string) {
  const monitor = PerformanceMonitor.getInstance();

  React.useEffect(() => {
    const startTime = performance.now();

    return () => {
      const endTime = performance.now();
      const renderTime = endTime - startTime;
      monitor.trackComponentRender(componentName, renderTime);
    };
  });

  return {
    trackOperation: (
      operationName: string,
      operation: () => void | Promise<void>
    ) => {
      const startTime = performance.now();
      const result = operation();

      if (result instanceof Promise) {
        return result.finally(() => {
          const endTime = performance.now();
          monitor.trackComponentRender(
            `${componentName}.${operationName}`,
            endTime - startTime
          );
        });
      } else {
        const endTime = performance.now();
        monitor.trackComponentRender(
          `${componentName}.${operationName}`,
          endTime - startTime
        );
        return result;
      }
    },
  };
}

/**
 * Debounce utility for performance optimization
 */
function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number,
  immediate = false
): T {
  let timeout: NodeJS.Timeout | null = null;

  return ((...args: Parameters<T>) => {
    const later = (): void => {
      timeout = null;
      if (!immediate) func(...args);
    };

    const callNow = immediate && !timeout;

    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(later, wait);

    if (callNow) func(...args);
  }) as T;
}

/**
 * Throttle utility for performance optimization
 */
function throttle<T extends (...args: any[]) => any>(
  func: T,
  limit: number
): T {
  let inThrottle = false;

  return ((...args: Parameters<T>) => {
    if (!inThrottle) {
      func(...args);
      inThrottle = true;
      setTimeout(() => (inThrottle = false), limit);
    }
  }) as T;
}

/**
 * Memory usage monitoring utility
 */
export function getMemoryUsage(): {
  used: number;
  total: number;
  percentage: number;
} | null {
  if (typeof window === 'undefined' || !('memory' in window.performance)) {
    return null;
  }

  const memory = (
    window.performance as Performance & {
      memory: {
        usedJSHeapSize: number;
        totalJSHeapSize: number;
        jsHeapSizeLimit: number;
      };
    }
  ).memory;
  return {
    used: memory.usedJSHeapSize,
    total: memory.totalJSHeapSize,
    percentage: (memory.usedJSHeapSize / memory.totalJSHeapSize) * 100,
  };
}

/**
 * Bundle size analysis utility
 */
function analyzeBundleSize(): Promise<BundleAnalysis> {
  return new Promise(resolve => {
    // In a real implementation, this would analyze the actual bundle
    // For now, we'll provide a mock analysis based on the build output
    const analysis: BundleAnalysis = {
