import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { createServerClient, type CookieOptions } from '@supabase/ssr';

import { logger } from '@/lib/utils/logger';
import { apiVersionMiddleware } from './middleware/api-version';
import {
  securityMiddleware,
  applySecurityToResponse,
} from './middleware/security';

/**
 * Middleware for handling authentication, route protection, rate limiting, CSRF, and API versioning
 *
 * This middleware:
 * 1. Implements Redis-based rate limiting for all endpoints
 * 2. Implements CSRF protection for state-changing operations
 * 3. Implements API versioning with automatic redirection and deprecation warnings
 * 4. Creates a Supabase client for server-side auth
 * 5. Refreshes the session if needed
 * 6. Protects dashboard and editor routes
 * 7. Redirects unauthenticated users to sign in
 * 8. Redirects authenticated users away from auth pages
 */
export async function middleware(req: NextRequest): Promise<NextResponse> {
  const { pathname } = req.nextUrl;
  const hostname = req.headers.get('host') || '';

  // Check if this is a custom domain request
  const isCustomDomain =
    !hostname.includes('prisma.madfam.io') &&
    !hostname.includes('localhost') &&
    !hostname.includes('vercel.app') &&
    !hostname.includes('127.0.0.1');

  // Apply API versioning middleware first
  if (pathname.startsWith('/api/')) {
    const versionResponse = await apiVersionMiddleware(req);
    // If version middleware returns a response (redirect or error), return it
    if (
      versionResponse.status !== 200 ||
      versionResponse.headers.get('location')
    ) {
      return versionResponse;
    }
  }

  // Apply comprehensive security middleware
  const securityResponse = await securityMiddleware(req);
  if (securityResponse) {
    return securityResponse;
  }
  // Create a response that we can modify
  let response = NextResponse.next({
    request: {
      headers: req.headers,
    },
  });

  // Import environment config at the top of the function to avoid module initialization issues
  const { env, services } = await import('@/lib/config');

  // Handle custom domain routing
  if (isCustomDomain) {
    // Create a Supabase client for custom domain lookup
    const supabase = createServerClient(
      env.NEXT_PUBLIC_SUPABASE_URL!,
      env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
      {
        cookies: {
          get(name: string) {
            return req.cookies.get(name)?.value;
          },
          set() {},
          remove() {},
        },
      }
    );

    try {
      // Look up the domain in the database
      const { data: domain } = await supabase
        .from('custom_domains')
        .select('portfolio_id, status')
        .eq('domain', hostname)
        .eq('status', 'active')
        .single();

      if (domain && domain.portfolio_id) {
        // Get portfolio details
        const { data: portfolio } = await supabase
          .from('portfolios')
          .select('subdomain')
          .eq('id', domain.portfolio_id)
          .single();

        if (portfolio && portfolio.subdomain) {
          // Track page view analytics (async, don't block request)
          const userAgent = req.headers.get('user-agent') || '';
          const referrer = req.headers.get('referer') || '';

          // Fire and forget analytics tracking
          void supabase.from('domain_analytics').insert({
            domain_id: domain.portfolio_id,
            event_type: 'page_view',
            path: pathname,
            referrer: referrer || null,
            user_agent: userAgent,
            visitor_id:
              req.headers.get('x-forwarded-for') ||
              req.headers.get('x-real-ip') ||
              'unknown',
            session_id:
              req.headers.get('x-forwarded-for') ||
              req.headers.get('x-real-ip') ||
              'unknown',
          });

          // Rewrite to the portfolio route
          const url = req.nextUrl.clone();
          url.pathname = `/p/${portfolio.subdomain}${pathname}`;
          return NextResponse.rewrite(url);
        }
      }
    } catch (error) {
      logger.error('Custom domain lookup failed', { error, hostname });
    }

    // If domain not found or not active, show error page
    const url = req.nextUrl.clone();
    url.pathname = '/domain-not-found';
    return NextResponse.rewrite(url);
  }

  // If Supabase is not configured, skip authentication checks (development mode)
  if (!services.supabase) {
    logger.warn(
      'Supabase service not configured. Authentication middleware disabled.'
    );
    return response;
  }

  // Create a Supabase client configured to use cookies
  const supabase = createServerClient(
    env.NEXT_PUBLIC_SUPABASE_URL!,
    env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        get(name: string) {
          return req.cookies.get(name)?.value;
        },
        set(name: string, value: string, options: CookieOptions) {
          req.cookies.set({
            name,
            value,
            ...options,
          });
          response = NextResponse.next({
            request: {
              headers: req.headers,
            },
          });
          response.cookies.set({
            name,
            value,
            ...options,
          });
        },
        remove(name: string, options: CookieOptions) {
          req.cookies.set({
            name,
            value: '',
            ...options,
          });
          response = NextResponse.next({
            request: {
              headers: req.headers,
            },
          });
          response.cookies.set({
            name,
            value: '',
            ...options,
          });
        },
      },
    }
  );

  // Refresh session if expired - required for Server Components
  let session = null;
  try {
    const { data } = await supabase.auth.getSession();
    session = data.session;
  } catch (error) {
    logger.warn('Error getting session', { error: (error as Error).message });
    // Continue without session if there's an error
  }

  // Protected routes that require authentication
  const protectedRoutes = ['/dashboard', '/editor', '/profile'];

  // Auth routes that should redirect to dashboard if already authenticated
  const authRoutes = ['/auth/signin', '/auth/signup'];

  // Check if current path is a protected route
  const isProtectedRoute = protectedRoutes.some(route =>
    pathname.startsWith(route)
  );

  // Check if current path is an auth route
  const isAuthRoute = authRoutes.some(route => pathname.startsWith(route));

  // If user is not authenticated and trying to access protected route
  if (isProtectedRoute && !session) {
    const redirectUrl = new URL('/auth/signin', req.url);
    // Add the original URL as a redirect parameter
    redirectUrl.searchParams.set('redirectTo', pathname);
    return NextResponse.redirect(redirectUrl);
  }

  // If user is authenticated and trying to access auth routes
  if (isAuthRoute && session) {
    // Check if there's a redirectTo parameter
    const redirectTo = req.nextUrl.searchParams.get('redirectTo');
    if (
      redirectTo &&
      protectedRoutes.some(route => redirectTo.startsWith(route))
    ) {
      return NextResponse.redirect(new URL(redirectTo, req.url));
    }
    return NextResponse.redirect(new URL('/dashboard', req.url));
  }

  // Apply comprehensive security headers to response
  response = applySecurityToResponse(req, response);

  return response;
}

/**
 * Configuration for which routes should run this middleware
 *
 * This matcher ensures the middleware only runs on:
 * - Protected routes (/dashboard, /editor)
 * - Auth routes (/auth/signin, /auth/signup, /auth/callback, /auth/reset-password)
 *
 * It excludes:
 * - Static assets (_next/static, favicon.ico, etc.)
 * - API routes (already handled separately)
 * - Public routes (landing page, about, etc.)
 */
export const config = {
  matcher: [
    /*
     * Match all request paths except for the ones starting with:
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public folder
     */
    '/((?!_next/static|_next/image|favicon.ico|public/).*)',
  ],
};

import { NextRequest, NextResponse } from 'next/server';

export interface ApiVersionConfig {
  _currentVersion: string;
  supportedVersions: string[];
  deprecatedVersions: Map<
    string,
    { deprecatedAt: Date; sunsetDate: Date; message: string }
  >;
}

// API Version Configuration
export const API_VERSION_CONFIG: ApiVersionConfig = {
  _currentVersion: 'v1',
  supportedVersions: ['v1'],
  deprecatedVersions: new Map([
    // Example: ['v0', { deprecatedAt: new Date('2025-01-01'), sunsetDate: new Date('2025-06-01'), _message: 'Please upgrade to v1' }]
  ]),
};

/**
 * Extract API version from request URL
 */
export function extractApiVersion(pathname: string): string | null {
  const match = pathname.match(/^\/api\/(v\d+)\//);
  return match && match[1] ? match[1] : null;
}

/**
 * Validate if the API version is supported
 */
export function isVersionSupported(version: string): boolean {
  return API_VERSION_CONFIG.supportedVersions.includes(version);
}

/**
 * Check if API version is deprecated
 */
export function isVersionDeprecated(version: string): boolean {
  return API_VERSION_CONFIG.deprecatedVersions.has(version);
}

/**
 * Get deprecation info for a version
 */
export function getDeprecationInfo(version: string) {
  return API_VERSION_CONFIG.deprecatedVersions.get(version);
}

/**
 * API Version Middleware
 * Validates API version and adds appropriate headers
 */
export async function apiVersionMiddleware(
  request: NextRequest
): Promise<NextResponse> {
  const pathname = request.nextUrl.pathname;

  // Only process API routes
  if (!pathname.startsWith('/api/')) {
    return NextResponse.next();
  }

  // Extract version from URL
  const version = extractApiVersion(pathname);

  // If no version specified, redirect to current version
  const isApiRootPath = !version && pathname === '/api/';
  const isNonVersionedApiPath = pathname.match(/^\/api\/[^v]/) !== null;

  if (isApiRootPath || isNonVersionedApiPath) {
    const newPathname = pathname.replace(
      '/api/',
      `/api/${API_VERSION_CONFIG._currentVersion}/`
    );
    return NextResponse.redirect(new URL(newPathname, request.url));
  }

  // Create response that will be modified
  const response = NextResponse.next();

  // Add API version headers
  response.headers.set(
    'X-API-Version',
    version || API_VERSION_CONFIG._currentVersion
  );
  response.headers.set(
    'X-API-Current-Version',
    API_VERSION_CONFIG._currentVersion
  );
  response.headers.set(
    'X-API-Supported-Versions',
    API_VERSION_CONFIG.supportedVersions.join(', ')
  );

  // Check if version is supported
  if (version && !isVersionSupported(version)) {
    return NextResponse.json(
      {
        _error: 'Unsupported API Version',
        _message: `API version ${version} is not supported. Supported versions: ${API_VERSION_CONFIG.supportedVersions.join(', ')}`,
        _currentVersion: API_VERSION_CONFIG._currentVersion,
      },
      {
        status: 400,
        headers: response.headers,
      }
    );
  }

  // Check if version is deprecated
  if (version && isVersionDeprecated(version)) {
    const deprecationInfo = getDeprecationInfo(version);
    if (deprecationInfo !== null && deprecationInfo !== undefined) {
      response.headers.set(
        'X-API-Deprecation-Warning',
        deprecationInfo.message
      );
      response.headers.set(
        'X-API-Sunset-Date',
        deprecationInfo.sunsetDate.toISOString()
      );
      response.headers.set('Sunset', deprecationInfo.sunsetDate.toUTCString());
      response.headers.set(
        'Deprecation',
        `date="${deprecationInfo.deprecatedAt.toISOString()}"`
      );
    }
  }

  return response;
}

/**
 * Helper function to create versioned API response
 */
export function createVersionedResponse<T>(
  _data: T,
  options?: {
    status?: number;
    headers?: HeadersInit;
    version?: string;
  }
): NextResponse {
  const {
    status = 200,
    headers = {},
    version = API_VERSION_CONFIG._currentVersion,
  } = options || {};

  const response = NextResponse.json(_data, { status, headers });

  // Add standard API version headers
  response.headers.set('X-API-Version', version);
  response.headers.set('X-API-Response-Time', new Date().toISOString());

  return response;
}

/**
 * Helper to handle version-specific logic in route handlers
 */
export function withApiVersion<T extends (...args: unknown[]) => unknown>(
  _handler: T,
  options?: {
    minVersion?: string;
    maxVersion?: string;
  }
): T {
  return (async (...args: Parameters<T>) => {
    const [request] = args as unknown as [NextRequest];
    const version =
      extractApiVersion(request.nextUrl.pathname) ||
      API_VERSION_CONFIG._currentVersion;

    // Version validation
    if (options?.minVersion && version < options.minVersion) {
      return NextResponse.json(
        {
          _error: 'Version Too Low',
          _message: `This endpoint requires API version ${options.minVersion} or higher. You are using ${version}.`,
          _currentVersion: API_VERSION_CONFIG._currentVersion,
        },
        { status: 400 }
      );
    }

    if (options?.maxVersion && version > options.maxVersion) {
      return NextResponse.json(
        {
          _error: 'Version Too High',
          _message: `This endpoint supports API version ${options.maxVersion} or lower. You are using ${version}.`,
          _currentVersion: API_VERSION_CONFIG._currentVersion,
        },
        { status: 400 }
      );
    }

    // Call the original handler with version context
    return _handler(...args, { _apiVersion: version });
  }) as T;
}

import { NextRequest, NextResponse } from 'next/server';

import { logger } from '@/lib/utils/logger';

/**
 * CSRF Protection Middleware
 * Implements Double Submit Cookie pattern for CSRF protection
 */

const CSRF_COOKIE_NAME = 'prisma-csrf-token';
const CSRF_HEADER_NAME = 'x-csrf-token';
const CSRF_TOKEN_LENGTH = 32;

/**
 * Methods that require CSRF protection
 */
const PROTECTED_METHODS = ['POST', 'PUT', 'PATCH', 'DELETE'];

/**
 * Routes that are exempt from CSRF protection
 */
const EXEMPT_ROUTES = [
  '/api/auth/callback', // OAuth callbacks
  '/api/integrations/github/callback', // GitHub OAuth callback
  '/api/webhooks', // Webhook endpoints
];

/**
 * Generate a cryptographically secure CSRF token
 */
export function generateCSRFToken(): string {
  const buffer = new Uint8Array(CSRF_TOKEN_LENGTH);
  crypto.getRandomValues(buffer);
  return Array.from(buffer)
    .map(b => b.toString(16).padStart(2, '0'))
    .join('');
}

/**
 * Verify CSRF token from request
 */
export function verifyCSRFToken(request: NextRequest): boolean {
  // Skip CSRF check for non-protected methods
  if (!PROTECTED_METHODS.includes(request.method)) {
    return true;
  }

  // Skip CSRF check for exempt routes
  const pathname = new URL(request.url).pathname;
  if (EXEMPT_ROUTES.some(route => pathname.startsWith(route))) {
    return true;
  }

  // Get token from cookie
  const cookieToken = request.cookies.get(CSRF_COOKIE_NAME)?.value;

  // Get token from header or body
  const headerToken = request.headers.get(CSRF_HEADER_NAME);

  if (!cookieToken || !headerToken) {
    logger.warn('CSRF token missing', {
      method: request.method,
      url: request.url,
      hasCookie: !!cookieToken,
      hasHeader: !!headerToken,
    });
    return false;
  }

  // Verify tokens match
  const tokensMatch = cookieToken === headerToken;

  if (!tokensMatch) {
    logger.warn('CSRF token mismatch', {
      method: request.method,
      url: request.url,
    });
  }

  return tokensMatch;
}

/**
 * CSRF protection middleware
 */
export function csrfMiddleware(request: NextRequest): NextResponse | null {
  // Generate CSRF token for GET requests (to set cookie)
  if (request.method === 'GET') {
    const token = generateCSRFToken();
    const response = NextResponse.next();

    // Set CSRF cookie
    response.cookies.set(CSRF_COOKIE_NAME, token, {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'strict',
      path: '/',
      maxAge: 60 * 60 * 24, // 24 hours
    });

    // Add token to response header for client to read
    response.headers.set(CSRF_HEADER_NAME, token);

    return response;
  }

  // Verify CSRF token for protected methods
  if (!verifyCSRFToken(request)) {
    logger.error('CSRF validation failed', {
      method: request.method,
      url: request.url,
      ip:
        request.headers.get('x-forwarded-for') ||
        request.headers.get('x-real-ip'),
    });

    return NextResponse.json({ error: 'Invalid CSRF token' }, { status: 403 });
  }

  return null;
}

/**
 * Helper to get CSRF token for client-side requests
 */
function getCSRFToken(): string | null {
  if (typeof document === 'undefined') return null;

  const cookies = document.cookie.split(';');
  const csrfCookie = cookies.find(cookie =>
    cookie.trim().startsWith(`${CSRF_COOKIE_NAME}=`)
  );

  return csrfCookie ? (csrfCookie.split('=')[1] || '').trim() : null;
}

import { NextRequest, NextResponse } from 'next/server';

import { logger } from '@/lib/utils/logger';

/**
 * Edge-compatible rate limiter for Vercel deployment
 * Uses in-memory storage with sliding window algorithm
 */

interface RateLimitConfig {
  windowMs: number; // Time window in milliseconds
  max: number; // Max requests per window
  message?: string; // Custom error message
  skipFailedRequests?: boolean;
  skipSuccessfulRequests?: boolean;
}

// Default configurations for different endpoint types
const defaultConfigs = {
  api: {
    windowMs: 60 * 1000, // 1 minute
    max: 100, // 100 requests per minute
    message: 'Too many requests, please try again later.',
  },
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 5, // 5 attempts per 15 minutes
    message: 'Too many authentication attempts, please try again later.',
  },
  ai: {
    windowMs: 60 * 1000, // 1 minute
    max: 10, // 10 AI requests per minute
    message: 'AI rate limit exceeded, please try again later.',
  },
} as const satisfies Record<string, RateLimitConfig>;

// In-memory store for rate limit data
// In production, this should be replaced with a distributed store
const rateLimitStore = new Map<string, { count: number; resetTime: number }>();

// Cleanup function that runs on each request
export function cleanupExpiredEntries(): void {
  const now = Date.now();
  const maxSize = 10000; // Prevent unbounded growth

  // Clean up expired entries
  for (const [key, value] of rateLimitStore.entries()) {
    if (value.resetTime < now) {
      rateLimitStore.delete(key);
    }
  }

  // If still too large, remove oldest entries
  if (rateLimitStore.size > maxSize) {
    const entries = Array.from(rateLimitStore.entries()).sort(
      (a, b) => a[1].resetTime - b[1].resetTime
    );

    const toRemove = entries.slice(0, entries.length - maxSize);
    toRemove.forEach(([key]) => rateLimitStore.delete(key));
  }
}

/**
 * Get client identifier from request
 */
export function getClientId(request: NextRequest): string {
  // Try to get real IP from various headers
  const forwardedFor = request.headers.get('x-forwarded-for');
  const realIp = request.headers.get('x-real-ip');
  const cfConnectingIp = request.headers.get('cf-connecting-ip');

  // Use the first available IP
  const ip =
    forwardedFor?.split(',')[0]?.trim() ||
    realIp ||
    cfConnectingIp ||
    'unknown';

  // Include the path in the key to have separate limits per endpoint
  const path = new URL(request.url).pathname;
  return `${ip}:${path}`;
}

/**
 * Determine rate limit config based on path
 */
export function getConfigForPath(path: string): RateLimitConfig {
  if (path.startsWith('/api/auth/')) {
    return defaultConfigs.auth;
  }
  if (
    path.startsWith('/api/ai/') ||
    path.includes('/enhance') ||
    path.includes('/optimize')
  ) {
    return defaultConfigs.ai;
  }
  if (path.startsWith('/api/')) {
    return defaultConfigs.api;
  }

  // No rate limiting for non-API routes
  return { windowMs: 0, max: Infinity };
}

/**
 * Edge-compatible rate limiting middleware
 */
export function edgeRateLimitMiddleware(
  request: NextRequest,
  customConfig?: Partial<RateLimitConfig>
): NextResponse | null {
  const path = new URL(request.url).pathname;
  const baseConfig = getConfigForPath(path);
  const config = { ...baseConfig, ...customConfig };

  // Skip if no rate limiting for this path
  if (config.max === Infinity) {
    return null;
  }

  const clientId = getClientId(request);
  const now = Date.now();

  // Run cleanup occasionally (1% of requests)
  if (Math.random() < 0.01) {
    cleanupExpiredEntries();
  }

  // Get or create rate limit data
  let rateLimitData = rateLimitStore.get(clientId);

  if (!rateLimitData || rateLimitData.resetTime < now) {
    // Create new window
    rateLimitData = {
      count: 0,
      resetTime: now + config.windowMs,
    };
    rateLimitStore.set(clientId, rateLimitData);
  }

  // Increment counter
  rateLimitData.count++;

  // Check if limit exceeded
  if (rateLimitData.count > config.max) {
    logger.warn('Rate limit exceeded', {
      clientId,
      path,
      count: rateLimitData.count,
      max: config.max,
    });

    // Calculate retry after
    const retryAfter = Math.ceil((rateLimitData.resetTime - now) / 1000);

    return NextResponse.json(
      {
        error: config.message || 'Too many requests',
        retryAfter,
      },
      {
        status: 429,
        headers: {
          'Retry-After': retryAfter.toString(),
          'X-RateLimit-Limit': config.max.toString(),
          'X-RateLimit-Remaining': '0',
          'X-RateLimit-Reset': new Date(rateLimitData.resetTime).toISOString(),
        },
      }
    );
  }

  // Add rate limit headers to successful responses
  const remaining = config.max - rateLimitData.count;
  const response = NextResponse.next();
  response.headers.set('X-RateLimit-Limit', config.max.toString());
  response.headers.set('X-RateLimit-Remaining', remaining.toString());
  response.headers.set(
    'X-RateLimit-Reset',
    new Date(rateLimitData.resetTime).toISOString()
  );

  return null; // Continue to next middleware
}

/**
 * Create a custom rate limiter with specific configuration
 */
function createRateLimiter(config: RateLimitConfig) {
  return (request: NextRequest) => edgeRateLimitMiddleware(request, config);
}

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { getCSPDirectives, generateCSPHeader } from '@/lib/security/csp';

/**
 * Security Headers Middleware
 * Implements comprehensive security headers to protect against common vulnerabilities
 */

/**
 * Apply security headers to the response
 */
export function applySecurityHeaders(
  _request: NextRequest,
  response: NextResponse
): NextResponse {
  const headers = response.headers;

  // X-Content-Type-Options: Prevent MIME type sniffing
  headers.set('X-Content-Type-Options', 'nosniff');

  // X-Frame-Options: Prevent clickjacking
  headers.set('X-Frame-Options', 'DENY');

  // X-XSS-Protection: Enable XSS filter (for older browsers)
  headers.set('X-XSS-Protection', '1; mode=block');

  // Referrer-Policy: Control referrer information
  headers.set('Referrer-Policy', 'strict-origin-when-cross-origin');

  // Permissions-Policy: Restrict browser features
  headers.set(
    'Permissions-Policy',
    'camera=(), microphone=(), geolocation=(), payment=(), usb=(), magnetometer=(), gyroscope=(), accelerometer=()'
  );

  // Content-Security-Policy: Comprehensive CSP
  const cspDirectives = getCSPDirectives();
  const cspHeader = generateCSPHeader(cspDirectives);

  // Add upgrade-insecure-requests for production
  const finalCSP =
    process.env.NODE_ENV === 'production'
      ? `${cspHeader}; upgrade-insecure-requests`
      : cspHeader;

  headers.set('Content-Security-Policy', finalCSP);

  // Strict-Transport-Security: Force HTTPS (only in production)
  if (process.env.NODE_ENV === 'production') {
    headers.set(
      'Strict-Transport-Security',
      'max-age=31536000; includeSubDomains; preload'
    );
  }

  // X-DNS-Prefetch-Control: Control DNS prefetching
  headers.set('X-DNS-Prefetch-Control', 'on');

  // X-Permitted-Cross-Domain-Policies: Restrict Adobe Flash/PDF policies
  headers.set('X-Permitted-Cross-Domain-Policies', 'none');

  return response;
}

/**
 * Check if the request path should have security headers applied
 * Skip for Next.js internal routes and static assets
 */
function shouldApplySecurityHeaders(pathname: string): boolean {
  // Skip Next.js internal routes
  if (pathname.startsWith('/_next/')) {
    return false;
  }

  // Skip static file extensions that don't need headers
  const staticExtensions = [
    '.ico',
    '.png',
    '.jpg',
    '.jpeg',
    '.svg',
    '.gif',
    '.webp',
  ];
  if (staticExtensions.some(ext => pathname.endsWith(ext))) {
    return false;
  }

  // Apply to all other routes
  return true;
}

/**
 * Comprehensive Security Middleware
 *
 * Combines all security features into a single middleware:
 * - CSRF protection
 * - Rate limiting
 * - Security headers
 * - Request validation
 * - IP blocking
 *
 * @module middleware/security
 */

import { NextRequest, NextResponse } from 'next/server';

import { env } from '@/lib/config';
import { logger } from '@/lib/utils/logger';

import { csrfMiddleware } from '../csrf';
import { edgeRateLimitMiddleware } from '../edge-rate-limiter';
import { applySecurityHeaders } from '../security-headers';

/**
 * Security configuration
 */
const SECURITY_CONFIG = {
  // Blocked IPs (could be loaded from database)
  blockedIPs: new Set<string>(),

  // Suspicious patterns in URLs
  suspiciousPatterns: [
    /\.\.\//g, // Directory traversal
    /<script/gi, // Script injection
    /javascript:/gi, // JavaScript protocol
    /on\w+=/gi, // Event handlers
  ],

  // Maximum request size (10MB)
  maxRequestSize: 10 * 1024 * 1024,

  // Allowed origins for CORS
  allowedOrigins: env.CORS_ALLOWED_ORIGINS?.split(',') || [],
};

/**
 * Check if IP is blocked
 */
function isIPBlocked(request: NextRequest): boolean {
  const ip =
    request.headers.get('x-forwarded-for') ||
    request.headers.get('x-real-ip') ||
    'unknown';

  return SECURITY_CONFIG.blockedIPs.has(ip);
}

/**
 * Check for suspicious patterns in request
 */
function hasSuspiciousContent(request: NextRequest): boolean {
  const url = request.url;
  const pathname = new URL(url).pathname;

  // Check URL for suspicious patterns
  for (const pattern of SECURITY_CONFIG.suspiciousPatterns) {
    if (pattern.test(url) || pattern.test(pathname)) {
      return true;
    }
  }

  // Check query parameters
  const searchParams = new URL(url).searchParams;
  for (const [key, value] of searchParams) {
    for (const pattern of SECURITY_CONFIG.suspiciousPatterns) {
      if (pattern.test(key) || pattern.test(value)) {
        return true;
      }
    }
  }

  return false;
}

/**
 * Validate request size
 */
async function isRequestTooLarge(request: NextRequest): Promise<boolean> {
  const contentLength = request.headers.get('content-length');

  if (contentLength) {
    const size = parseInt(contentLength, 10);
    if (size > SECURITY_CONFIG.maxRequestSize) {
      return true;
    }
  }

  return false;
}

/**
 * Apply CORS headers based on configuration
 */
function applyCORSHeaders(
  request: NextRequest,
  response: NextResponse
): NextResponse {
  const origin = request.headers.get('origin');

  // Allow configured origins or same origin
  if (
    origin &&
    (SECURITY_CONFIG.allowedOrigins.includes(origin) ||
      origin === new URL(request.url).origin)
  ) {
    response.headers.set('Access-Control-Allow-Origin', origin);
    response.headers.set('Access-Control-Allow-Credentials', 'true');
  }

  // Set other CORS headers
  response.headers.set(
    'Access-Control-Allow-Methods',
    'GET, POST, PUT, DELETE, OPTIONS'
  );
  response.headers.set(
    'Access-Control-Allow-Headers',
    'Content-Type, Authorization, X-CSRF-Token'
  );
  response.headers.set('Access-Control-Max-Age', '86400'); // 24 hours

  return response;
}

/**
 * Log security events
 */
function logSecurityEvent(
  event: string,
  request: NextRequest,
  details?: Record<string, any>
) {
  const ip =
    request.headers.get('x-forwarded-for') ||
    request.headers.get('x-real-ip') ||
    'unknown';

  logger.warn(`Security Event: ${event}`, {
    ip,
    method: request.method,
    url: request.url,
    userAgent: request.headers.get('user-agent'),
    ...details,
  });
}

/**
 * Comprehensive security middleware
 */
export async function securityMiddleware(
  request: NextRequest
): Promise<NextResponse | null> {
  try {
    // 1. Check if IP is blocked
    if (isIPBlocked(request)) {
      logSecurityEvent('BLOCKED_IP', request);
      return new NextResponse(null, { status: 403 });
    }

    // 2. Check for suspicious content
    if (hasSuspiciousContent(request)) {
      logSecurityEvent('SUSPICIOUS_CONTENT', request);
      return NextResponse.json({ error: 'Invalid request' }, { status: 400 });
    }

    // 3. Check request size
    if (await isRequestTooLarge(request)) {
      logSecurityEvent('REQUEST_TOO_LARGE', request);
      return NextResponse.json(
        { error: 'Request entity too large' },
        { status: 413 }
      );
    }

    // 4. Apply rate limiting
    const rateLimitResponse = edgeRateLimitMiddleware(request);
    if (rateLimitResponse) {
      logSecurityEvent('RATE_LIMITED', request);
      return rateLimitResponse;
    }

    // 5. Apply CSRF protection for API routes
    if (request.nextUrl.pathname.startsWith('/api/')) {
      const csrfResponse = csrfMiddleware(request);
      if (csrfResponse) {
        logSecurityEvent('CSRF_FAILED', request);
        return csrfResponse;
      }
    }

    // 6. Handle preflight requests
    if (request.method === 'OPTIONS') {
      const response = new NextResponse(null, { status: 204 });
      return applyCORSHeaders(request, response);
    }

    // Request passed all security checks
    return null;
  } catch (error) {
    logger.error('Security middleware error', { error });
    // Fail closed - return error if security checks fail
    return NextResponse.json(
      { error: 'Security check failed' },
      { status: 500 }
    );
  }
}

/**
 * Apply security headers to response
 */
export function applySecurityToResponse(
  request: NextRequest,
  response: NextResponse
): NextResponse {
  // Apply security headers
  response = applySecurityHeaders(request, response);

  // Apply CORS headers
  response = applyCORSHeaders(request, response);

  // Add additional security headers
  response.headers.set('X-Content-Type-Options', 'nosniff');
  response.headers.set('X-Frame-Options', 'DENY');
  response.headers.set('X-XSS-Protection', '1; mode=block');
  response.headers.set('Referrer-Policy', 'strict-origin-when-cross-origin');

  if (env.NODE_ENV === 'production') {
    response.headers.set(
      'Strict-Transport-Security',
      'max-age=31536000; includeSubDomains'
    );
  }

  return response;
}

/**
 * Security utilities for use in API routes
 */
export const securityUtils = {
  /**
   * Validate API key
   */
  validateAPIKey(request: NextRequest, apiKey: string): boolean {
    const providedKey = request.headers.get('x-api-key');
    return providedKey === apiKey;
  },

  /**
   * Get client IP address
   */
  getClientIP(request: NextRequest): string {
    return (
      request.headers.get('x-forwarded-for') ||
      request.headers.get('x-real-ip') ||
      'unknown'
    );
  },

  /**
   * Check if request is from allowed origin
   */
  isAllowedOrigin(request: NextRequest): boolean {
    const origin = request.headers.get('origin');
    if (!origin) return true; // Same-origin requests

    return (
      SECURITY_CONFIG.allowedOrigins.includes(origin) ||
      origin === new URL(request.url).origin
    );
  },

  /**
   * Add IP to blocklist
   */
  blockIP(ip: string): void {
    SECURITY_CONFIG.blockedIPs.add(ip);
    logger.warn('IP added to blocklist', { ip });
  },

  /**
   * Remove IP from blocklist
   */
  unblockIP(ip: string): void {
    SECURITY_CONFIG.blockedIPs.delete(ip);
    logger.info('IP removed from blocklist', { ip });
  },
};

#!/usr/bin/env ts-node

import * as fs from 'fs';
import * as path from 'path';
import { execSync } from 'child_process';

interface CoverageData {
  [filePath: string]: {
    path: string;
    statementMap: any;
    fnMap: any;
    branchMap: any;
    s: { [key: string]: number };
    f: { [key: string]: number };
    b: { [key: string]: number[] };
  };
}

/**
 * Get files with low test coverage
 */
function getUncoveredFiles(): string[] {
  try {
    // Run coverage report and output to JSON
    execSync('pnpm test -- --coverage --coverageReporters=json --silent', {
      stdio: 'pipe',
    });

    const coveragePath = path.join(
      process.cwd(),
      'coverage',
      'coverage-final.json'
    );

    if (!fs.existsSync(coveragePath)) {
      console.error('Coverage report not found. Running tests first...');
      return [];
    }

    const coverageData: CoverageData = JSON.parse(
      fs.readFileSync(coveragePath, 'utf8')
    );
    const uncoveredFiles: string[] = [];

    // Find files with less than 50% coverage
    for (const [filePath, data] of Object.entries(coverageData)) {
      const statements = Object.values(data.s);
      const covered = statements.filter(count => count > 0).length;
      const total = statements.length;
      const coverage = total > 0 ? (covered / total) * 100 : 0;

      if (
        coverage < 50 &&
        !filePath.includes('__tests__') &&
        !filePath.includes('.test.')
      ) {
        uncoveredFiles.push(filePath);
      }
    }

    return uncoveredFiles.sort();
  } catch (error) {
    console.error('Error getting coverage data:', error);
    return [];
  }
}

/**
 * Generate test template for a given file
 */
function generateTestTemplate(filePath: string): string {
  const fileName = path.basename(filePath);
  const fileNameWithoutExt = fileName.replace(/\.(ts|tsx|js|jsx)$/, '');
  const isComponent =
    filePath.includes('/components/') || fileName.endsWith('.tsx');
  const isApiRoute = filePath.includes('/api/');
  const relativePath = path.relative(process.cwd(), filePath);

  if (isComponent) {
    return `import { render, screen } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import { ${fileNameWithoutExt} } from '@/${relativePath.replace(/\.(ts|tsx)$/, '')}';

describe('${fileNameWithoutExt}', () => {
  it('should render without crashing', () => {
    render(<${fileNameWithoutExt} />);
    // Add specific element checks based on the component
  });

  it('should handle user interactions', async () => {
    const user = userEvent.setup();
    render(<${fileNameWithoutExt} />);

    // Add interaction tests
  });

  it('should apply correct styles', () => {
    const { container } = render(<${fileNameWithoutExt} />);

    // Add style assertions
  });
});`;
  } else if (isApiRoute) {
    return `import { NextRequest } from 'next/server';
import { GET, POST, PUT, DELETE } from '@/${relativePath.replace(/\.(ts|tsx)$/, '')}';
import { createClient } from '@/lib/supabase/server';

jest.mock('@/lib/supabase/server', () => ({
  createClient: jest.fn(),
}));

describe('${fileNameWithoutExt} API Route', () => {
  const mockSupabase = {
    auth: {
      getUser: jest.fn(),
    },
    from: jest.fn(),
  };

  beforeEach(() => {
    jest.clearAllMocks();
    (createClient as jest.Mock).mockResolvedValue(mockSupabase);
  });

  describe('GET', () => {
    it('should handle GET request successfully', async () => {
      mockSupabase.auth.getUser.mockResolvedValue({
        data: { user: { id: 'user-123' } },
        error: null,
      });

      const request = new NextRequest('http://localhost:3000${filePath}');
      const response = await GET(request);

      expect(response.status).toBe(200);
    });

    it('should return 401 for unauthenticated requests', async () => {
      mockSupabase.auth.getUser.mockResolvedValue({
        data: { user: null },
        error: null,
      });

      const request = new NextRequest('http://localhost:3000${filePath}');
      const response = await GET(request);

      expect(response.status).toBe(401);
    });
  });
});`;
  } else {
    // Regular TypeScript file
    return `import { ${fileNameWithoutExt} } from '@/${relativePath.replace(/\.(ts|tsx)$/, '')}';

describe('${fileNameWithoutExt}', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('should export expected functions/classes', () => {
    expect(${fileNameWithoutExt}).toBeDefined();
  });

  // Add specific tests based on the module's functionality
});`;
  }
}

/**
 * Create test file for a given source file
 */
function createTestFile(filePath: string): void {
  const relativePath = path.relative(process.cwd(), filePath);
  const testDir = path.join(
    process.cwd(),
    '__tests__',
    path.dirname(relativePath)
  );
  const fileName = path.basename(filePath);
  const testFileName = fileName.replace(/\.(ts|tsx|js|jsx)$/, '.test.$1');
  const testFilePath = path.join(testDir, testFileName);

  // Skip if test already exists
  if (fs.existsSync(testFilePath)) {
    console.log(`Test already exists: ${testFilePath}`);
    return;
  }

  // Create directory if it doesn't exist
  fs.mkdirSync(testDir, { recursive: true });

  // Generate and write test template
  const testContent = generateTestTemplate(filePath);
  fs.writeFileSync(testFilePath, testContent);

  console.log(`Created test: ${testFilePath}`);
}

/**
 * Main function
 */
async function main() {
  console.log(' Generating tests for uncovered files...\n');

  const uncoveredFiles = getUncoveredFiles();

  if (uncoveredFiles.length === 0) {
    console.log('No files with low coverage found!');
    return;
  }

  console.log(`Found ${uncoveredFiles.length} files with < 50% coverage:\n`);

  // Group files by directory
  const filesByDir = uncoveredFiles.reduce(
    (acc, file) => {
      const dir = path.dirname(file);
      if (!acc[dir]) acc[dir] = [];
      acc[dir].push(file);
      return acc;
    },
    {} as Record<string, string[]>
  );

  // Display files and create tests
  let created = 0;
  for (const [dir, files] of Object.entries(filesByDir)) {
    console.log(`\n${dir}:`);
    for (const file of files) {
      console.log(`  - ${path.basename(file)}`);
      try {
        createTestFile(file);
        created++;
      } catch (error) {
        console.error(`   Error creating test for ${file}:`, error);
      }
    }
  }

  console.log(`\n Created ${created} test files`);
  console.log('\n Next steps:');
  console.log('1. Review and customize the generated tests');
  console.log('2. Run: pnpm test to verify they work');
  console.log('3. Add specific test cases for your business logic');
}

// Run the script
main().catch(console.error);

